	['kills'] = [[Muertes]],
	['sureFriendlyFire'] = [[¡Estás a punto de disparar a tus aliados!<new_line>¿Seguro que quieres hacer eso?]],
	['npc'] = {
		['default'] = {
			['episode'] = {
				['1003'] = [[Si tienes todos los materiales necesarios, puedo hacer cualquier cosa que precises.]],
				['1005'] = [[Puedo hacer]],
				['1002'] = [[Si tienes algo que yo necesite, puedo cambiártelo por algo que quieras tú.]],
				['1001'] = [[La vida pasa penosamente en el asentamiento. La gente está ocupada con sus propias tareas y a veces me lanzan miradas desinteresadas.]],
				['1004'] = [[Puedo vender]]
			},
			['name'] = {
				['1'] = [[Comerciante]],
				['2'] = [[Especialista]]
			},
			['choice'] = {
				['1003'] = [[Ir a ver al comerciante]],
				['1005'] = [[Salir]],
				['1006'] = [[Volveré pronto.]],
				['1002'] = [[Ir a ver al líder del asentamiento]],
				['1001'] = [[Mirar por ahí y hablar con los lugareños]],
				['1004'] = [[Ir al barracón]]
			}
		},
		['spiridon'] = {
			['episode'] = {
				['14'] = [[Bueno, el caso es que me recordaste a un viejo amigo... Espera un momento, ¿qué acabas de decir? ¿Ufá? ¡Está lleno de abejas!<new_line>De ninguna manera, no pienso ir a Ufá, ¡sigo queriendo vivir! Ve a buscar a otro idiota.]],
				['15'] = [[Vale, perdona. Mi Luba no es precisamente un regalito, pero es lo que tengo, y hay muchas abejas. ¡No pienso ir a Ufá de ninguna manera!]],
				['12'] = [[(La esposa vuelve a darle una bofetada a su marido, lo suficientemente fuerte como para hacerle gritar.)<new_line><new_line>¡Toma reconocimiento! Y te puedes ir a la mierda, cabrón, cerdo, me has arruinado la vida, ¡y pensar que le di mis mejores años! Mi madre tenía razón, que Dios la tenga en su gloria: debería haberme casado con Ptichkin... ¡Entonces tal vez te hubieras emborrachado hasta caerte muerto!]],
				['13'] = [[(Luba da un portazo y se encierra en la cocina. Espiridón Kugel me mira con el ceño fruncido.)<new_line><new_line>Lleva nerviosa desde esta mañana por alguna razón. ¿Sabes qué? Diles a los muchachos que no saldré hoy. No me es posible, como puedes ver. ¡Mañana sí, seguro!]],
				['20'] = [[No es necesario que me lleves de vuelta. He visto una moto en el garaje. Casi nueva, y una serie limitada. Llegaré a casa mucho más rápido montado en ella. Le he mejorado algunas cosas, así que ni siquiera las abejas podrán alcanzarme. ¡Nos vemos!]],
				['21'] = [[(Kugel niega con la cabeza a modo de reproche.)<new_line><new_line>Muy bien, camarada, la próxima vez. Porque no quieres que desarme el sistema otra vez, ¿verdad? Es solo que ya me debes cuatro botellas.]],
				['11'] = [[¿Qué quieres decir con si soy bueno? ¡El país siempre necesita buenos ingenieros! ¡Incluso tengo un reconocimiento oficial del comité regional del partido!]],
				['10'] = [[¿Ayuda? ¿Para qué podría ser bueno este borracho? Para nada en absoluto, ¡solo se queda sentado en casa! Espiridón, ¡vuelve a la cocina!]],
				['9'] = [[¡Ah, ahora me lo creo! ¡Él tampoco te conoce! ¿Me tomas por tonta, otra vez?<new_line><new_line>(Luba Kugel le da una fuerte bofetada a su marido.)]],
				['8'] = [[(Un hombre mucho más pequeño asoma por detrás de la fornida mujer. Su rostro revela inteligencia, pero está ajado.)<new_line><new_line>Luba, espera. No le conozco; no es de nuestro asentamiento.]],
				['7'] = [[¡Sé por qué estás aquí! Para emborracharte, rugir y gritar, ¡para eso estás aquí! ¿Te vas a ir o necesitas ayuda? ¡Yo puedo dártela! Te ayudaré...]],
				['6'] = [[¡Qué disparate! ¡No se pierde una habilidad así! Y en mi campo, la edad es una ventaja. ¡Los jóvenes de hoy en día son todos unos chimpancés manazas!<new_line>¡Prepárate, nos vamos a Ufá!]],
				['1'] = [[(Los Kugels viven en el primer piso del bloque de cinco plantas. Una mujer muy robusta me abre la puerta, con una expresión feroz en su semblante.)<new_line><new_line>¡De vuelta! ¡Sal de aquí, maldito borracho! ¡Ya hemos tenido suficiente! Por última vez os lo digo: ¡no va a ir a ninguna parte!]],
				['27'] = [[¿Otra vez tú? Ya te dije que no voy a ir a ninguna parte, de ninguna manera, ¡ni lo sueñes!]],
				['26'] = [[Buena suerte. ¿Cómo te va? ¿Bien? Bueno, nos vemos, Luba no está de buen humor hoy y nos atizaría a los dos si pudiera.]],
				['5'] = [[¿Un sistema de misiles? ¿Qué? ¡Puedo reparar un tanque con los ojos cerrados! ¡Incluso me invitaron a Severodvinsk a construir el submarino nuclear!]],
				['4'] = [[Ah... Lo siento... ya sabes... No puedo dejar a Luba aquí sola... Pero avísame si surge algo.]],
				['3'] = [[¿Qué Boris? ¿En qué tenía razón?]],
				['2'] = [[(Kugel se humedece los labios y suelta un chasquido.)<new_line><new_line>¿De una destilería? Ese es del bueno. Nuestro vodka casero no está a la altura.<new_line>Está bien, me interesa. Tres botellas: ese es el trato. Mientras tanto, tengo que averiguar dónde puedo esconderlas de Luba.]],
				['23'] = [[(Tras superar una breve pero desagradable conversación con Luba, puedes hablar nuevamente con Espiridón.)]],
				['25'] = [[Entiendo... Está bien, esperaré un poco más.]],
				['24'] = [[(Espiridón me saluda deliberadamente en voz muy alta, mirando de reojo a la puerta tras la que se esconde Luba.)<new_line>¡Hola, hola, hola!<new_line>(Luego, bajando mucho la voz, continúa.)<new_line>Bueno, ¿conseguiste el vodka? Cuatro botellas, ¿verdad?]],
				['17'] = [[Echa un vistazo. Bonito, ¿verdad? El trípode está un poco cojo, así que tienes que ponerle un ladrillo debajo. El coordinador se descentra, pero eso es un fallo de diseño; todos los que recibíamos tenían el mismo problema. Y no se asienta del todo bien en las ranuras, así que hay que sujetarlo suavemente. Y otra cosa...]],
				['22'] = [[¡Gracias! ¡Material de calidad! Mañana podré agasajar a los muchachos, aunque sé un buen amigo y no digas nada delante de mi esposa. Mi amorcito tiene un gancho de derecha que pa' qué. ¿Quién lo hubiera dicho? En su día era dulce como un corderito. Ah, en aquellos tiempos...]],
				['19'] = [[Yo no trabajo a cambio de agradecimientos. Dame el vodka.]],
				['18'] = [[Por cierto, dile a Boris que el viejo maestro sigue al pie del cañón. Puede venir a visitarme cuando quiera. Luba le preparará una empanada y podremos sentarnos a rememorar los viejos tiempos.]],
				['16'] = [[Vamos, date prisa, ¿no sabes lo que me hará mi esposa?]]
			},
			['name'] = [[Espiridón Kugel]],
			['choice'] = {
				['19'] = [[Gracias, me has ayudado mucho.]],
				['15'] = [[¡Trato hecho!]],
				['17'] = [[Tranquilo.]],
				['13'] = [[No sé de qué hablas. Realmente necesito tu ayuda. Tengo que encontrar y armar una cosa. En Ufá.]],
				['20'] = [[Gracias, me has ayudado mucho.]],
				['21'] = [[Se lo diré.]],
				['28'] = [[Sí, aquí están, toma. Según lo acordado, con intereses.]],
				['9'] = [[¡Oye, no le pegues, que está diciendo la verdad! Estoy aquí por negocios. Necesito ayuda.]],
				['6'] = [[Espera, me has confundido con otra persona. He venido a ver a tu esposo.]],
				['1'] = [[¿Y si te traigo algo de vodka? Del de verdad, de una destilería.]],
				['27'] = [[No, Espiridón, lo siento. No lo tengo aún. Como bien sabes, algo así no se encuentra precisamente tirado en medio de la calle.]],
				['5'] = [[Eso fue hace mucho tiempo, camarada. La edad, la bebida...]],
				['4'] = [[Boris, el comandante de Stepnoy. Discutíamos sobre si Kugel, el ingeniero que recibió el reconocimiento oficial del comité regional, sería capaz de construir un sistema de misiles antitanque y Boris dijo que habrías perdido tu toque hace mucho tiempo.]],
				['3'] = [[En que no hay razón para acabar con los ingeniosos ingenieros que el país necesita desesperadamente... Qué lástima... Tendré que arreglármelas solo.]],
				['2'] = [[(Le manipulas.)<new_line>Lástima. Parece que Boris tenía razón. Te estás haciendo viejo, ingeniero.]],
				['26'] = [[Hola.]],
				['25'] = [[Vale, vale, no le diré nada.]],
				['24'] = [[¿Cuatro? ¡Qué cabrón! Las traeré tan pronto como pueda.]],
				['23'] = [[Aquí tienes, las tres botellas que te prometí.]],
				['22'] = [[Escucha, lo siento, pero no lo llevo encima. Te lo daré la próxima vez.]],
				['14'] = [[¿Y dónde voy a encontrar otro ingeniero militar?]],
				['16'] = [[¿A Ufá? Vale, lo que tú digas.]],
				['18'] = [[Vamos, vamos.]]
			}
		},
		['boris'] = {
			['episode'] = {
				['14'] = [[¿Alguna pregunta?]],
				['15'] = [[Escucha primero. No encontrarás nada, pero si lo haces, tendrás que armarlo. Hay un ingeniero militar en el asentamiento cerca de Cheliábinsk: Espiridón Kugel. Trabajó en su día en la unidad de Ufá. Él debería ayudarte. Si es que no se ha muerto ya de una borrachera. Mándale saludos de su viejo compañero de botella.]],
				['12'] = [[(Boris se ríe quedamente.)<new_line><new_line>Dices eso como si estuvieras pidiendo una pistola Makárov. ¿Tienes alguna idea de lo que supone?]],
				['13'] = [[Espera, tengo una idea. Si estás totalmente desesperado, ve a Ufá, a la base militar. En el 83, hicieron un envío de sistemas Metis directamente desde la fábrica. Dudo que todos se pusieran en servicio y, desde que las abejas gigantes llegaron a Ufá, nadie ha estado allí.]],
				['20'] = [[(Boris me da una palmada en el hombro.)<new_line><new_line>Bebe, vamos. Tienes que coger fuerzas y un buen vino es el mejor cóctel de vitaminas que hay.<new_line>¡Sí, fue un buen combate, según todas las reglas de la guerra! Me siento veinte años más joven.<new_line>Has sido de gran valor: si no hubiéramos tenido el sistema de misiles, no habríamos podido conseguirlo.]],
				['21'] = [[Nuestras pérdidas son mayores de lo que esperaba, desde luego, y esos tres huyeron en el UAZ, pero tenemos al resto y destruimos la base, que no es cualquier cosa.]],
				['11'] = {
					['1'] = [[(Boris se frota la barbilla.)<new_line><new_line>En teoría, podríamos. La puerta sur es más débil que la principal. Podríamos abrirla, pero no te van a dejar caminar hasta la puerta con dinamita en la mano. Hay unas viejas trincheras y luego un espacio abierto. Una ametralladora destrozaría a nuestros muchachos.]],
					['2'] = [[Teníamos un sistema de misiles antitanque, Metis. Un cabeza de chorlito, no diré quién, tuvo la genial idea de tirarlo al pantano. Tenemos un par de misiles, pero nada para lanzarlos.]]
				},
				['10'] = [[(Boris guarda silencio.)<new_line><new_line>Sí, desde luego, pero todos estos chicos, mis soldados, son también hijos de alguien, y sería como enviar corderos al matadero. No puedo hacerlo.]],
				['9'] = [[¿Cómo iba a saberlo? Escucha, lo siento por ti, de verdad, pero... será mejor que lo asumas, para poder vivir. No recuperarás a tu hija. En primer lugar, porque necesitarías todo un ejército para enfrentarte a los abakanos. Y en segundo lugar...<new_line><new_line>(Viendo al comandante, me doy cuenta de cuánto deseaba atrapar a esos canallas, así que decido probar otra vez.)]],
				['8'] = [[Créeme: todos aquí queremos matar a estos cabrones, pero no hay manera de conseguirlo. Han tomado una antigua base militar y sus fortificaciones son diez veces mejores que las de Stepnoy. Amaba a mi hijo, pero no puedo enviar a mis hombres a una muerte segura.]],
				['7'] = [[(Boris suspira.)<new_line><new_line>¿Tu hija? Ya veo. También mataron a mi hijo. Los mismos de Abakán. Y nos tiraron su cabeza por encima de la puerta. Mi Vasili fue un auténtico héroe para nosotros, nacido para mandar, todos estábamos orgullosos de él. Aún sueño con cortarle el gaznate al que lo mató.]],
				['6'] = [[No, de la zona no. De aquí no, de Abakán. ¿Por qué la buscas? ¿Es pariente tuya?]],
				['1'] = [[(Al principio no querían dejarme entrar en el cuartel. Me cachearon y confirmaron que no era un espía de una de las bandas locales; solo entonces me dejaron ver a su líder. Tuve que dejarles temporalmente mis armas en el almacén y me escoltaron durante todo el trayecto dos hombres armados.)]],
				['29'] = [[(Boris, en silencio, se enfrenta a mi mirada, sin preguntar nada. Probablemente lo hace como una muestra de simpatía.<new_line> No tenía nada que decirle.)]],
				['19'] = [[(Esta vez, no hay nadie en el centro de mando aparte de Boris. Cuando entro, veo una cara botella de vino y dos vasos. Boris dice que debería tomarme un trago con él; está de muy buen humor y yo no me niego.)<new_line><new_line>¡Por la victoria! ¡Que todas las victorias sean como esta!]],
				['27'] = [[(El comandante se rasca la cabeza. Luego, se me acerca y me pone una mano en el hombro.)<new_line><new_line>De acuerdo. Eres su padre, después de todo. Pero, sabes, ni siquiera puedo imaginar lo que sentiría si mi Vasili hubiera... hecho las cosas que hizo Sasha para los bandidos. Le habría matado con mis... Bueno... espera, es lo que digo.]],
				['5'] = [[(Boris sonríe con suficiencia. Iba a decir algo más, pero se lo piensa mejor en el último momento.)<new_line><new_line>Buena suerte, entonces. No conozco ningún lugar más cercano o más seguro. Tendrás que encontrarlo por tu cuenta.]],
				['4'] = [[Bueno, esa es otra historia completamente diferente. No encontrarás nada, pero si lo haces, tendrás que armarlo. Hay un ingeniero militar en el asentamiento cerca de Cheliábinsk: Espiridón Kugel. Trabajó en su día en la unidad de Ufá. Él debería ayudarte. Si es que no se ha muerto ya de una borrachera. Mándale saludos de su viejo compañero de botella.]],
				['3'] = [[Sí, me acuerdo de ella. Era un espíritu rebelde... Pero has llegado demasiado tarde. Por uno o dos años (Boris niega con la cabeza). Los bandidos la capturaron tras su última incursión. Nunca la volvimos a ver.]],
				['2'] = [[(Boris, un antiguo comandante y jefe del cuartel de Stepnoy, me dispensa una fría bienvenida. Fumando un cigarrillo aplastado, me dice que me dé prisa y le diga lo que quiero.)]],
				['26'] = [[(Boris se sienta y comienza a tamborilear con los dedos sobre la mesa.)<new_line><new_line>Escucha. Hay una guerra y no es momento de mostrar debilidad. Tu hija y los bandidos participaron juntos. Ella mató a mi gente. ¡Maldita sea, casi acaba contigo! ¿Y ahora me vienes quejándote?]],
				['25'] = [[Bueno, lo llamamos la cárcel, aunque no es más que un sótano cualquiera. No estará encerrada allí mucho tiempo. Va a ir al paredón.]],
				['24'] = [[Casi. Tiene los reflejos de un gato salvaje; la bala no afectó a ningún órgano vital, pero aun así cayó. Los chicos la retuvieron y te vendaron. Luego te trajeron aquí tan rápidamente como pudieron. Pensé que no ibas a lograrlo.]],
				['23'] = [[Puedes imaginártelo. Vi que estabas en problemas, faltaba un segundo para que esa chica te matara, pero yo disparé primero. Eso significa que me debes una, amigo.]],
				['22'] = [[Bueno, empecé a preocuparme, preguntándome dónde había ido mi mejor combatiente. Pregunté a los hombres y empezamos a peinar el territorio circundante.]],
				['18'] = [[(El comandante no está aquí. Parece que se está preparando para la ofensiva.)]],
				['16'] = [[(Boris está realmente sorprendido de verme. No pensaba que lograría regresar.)<new_line><new_line>Tienes agallas. ¿De verdad tienes el sistema?]],
				['17'] = [[(Boris está a punto de decir algo, pero se sume en sus pensamientos. Luego balancea el brazo y golpea la mesa con el puño.)<new_line><new_line>Decidido. ¡Vamos a darles su merecido! Ve a domir y yo prepararé a los muchachos. Saldremos mañana al amanecer. ¡Vengaremos a Vasili y a los demás!]]
			},
			['name'] = [[Boris]],
			['choice'] = {
				['14'] = [[No.]],
				['15'] = [[Lo conseguí, lo he armado y lo he traído. Pongámonos manos a la obra; no hay tiempo que perder.]],
				['12'] = [[Sí... Necesito tu información sobre Ufá.]],
				['13'] = [[Genial, eso haré.]],
				['20'] = [[Boris, ¿cómo conseguiste salvarme? ¡Allí, en el último piso del edificio!]],
				['21'] = [[¿Y luego?]],
				['11'] = [[Lo intentaré, de todos modos.]],
				['10'] = [[¿Tal vez pueda hallar otro sistema?]],
				['9'] = [[¿No habrá alguna manera de poder tomar la base de Abakán sin un ejército? ¿Podríamos hacerlo?]],
				['8'] = [[¿Quieres vengarte de la muerte de tu hijo?]],
				['7'] = [[Vale. ¿Crees que Sasha seguirá todavía allí?]],
				['6'] = [[¿Y a qué esperas? ¿Por qué no has atacado para vengarte? Tienes los hombres y potencia de fuego de sobra.]],
				['1'] = [[¡Hola! Vengo del páramo. Estoy buscando a una chica, Sasha. Puede que viniera desde Barnaúl en 1986...]],
				['26'] = [[Vale, lo intentaré.]],
				['5'] = [[Es mi hija.]],
				['4'] = [[¿Se la llevaron los bandidos? ¿De la zona?]],
				['3'] = [[Y puedo entender por qué... Por eso no envías a tu gente allí. Y es un camino muy largo. Buscaré el sistema en algún lugar donde no haya abejas gigantes.]],
				['2'] = [[Parece que voy a Ufá.]],
				['25'] = [[Boris, ¿puedo verla? ¿Una última vez?]],
				['24'] = [[¿Vas a ejecutarla? ¿Sasha?]],
				['23'] = [[¿Dónde está ella ahora?]],
				['22'] = [[Tú... ¿la has matado?]],
				['19'] = [[Me encantó poder ayudar.]],
				['18'] = [[Por la victoria... (tomas un sorbo)]],
				['17'] = [[Lo tengo, comandante.]],
				['16'] = [[No, no lo tengo. Nos vemos.]]
			}
		},
		['gavrilov'] = {
			['episode'] = {
				['14'] = [[(Escucho el nombre y siento una chispa de emoción. ¡Director del Instituto de Virología! Parece que por fin obtendré respuestas a todas mis preguntas.)]],
				['15'] = [[¿A qué viene ese silencio? ¿Qué queríais decirme?]],
				['17'] = [[¿La verdad? ¿Y qué vas a hacer con esa verdad, vagabundo anónimo? ¿Contársela a todo el mundo?]],
				['16'] = [[(El investigador y el director se miran en silencio. Zaitsev frunce el ceño y está a punto de hablar, pero Gavrilov le hace un gesto para que se calle. Se acerca a mí y me examina de la cabeza a los pies.)<new_line><new_line>¿Día R? ¿Por qué lo preguntas? ¿Saberlo cambiaría algo? Al fin y al cabo, fue hace tanto tiempo.]],
				['20'] = [[En Kiev dijiste que nos ayudarías a lidiar con los lobos, así que hazlo. Entonces te lo contaremos todo.]],
				['21'] = [[Nada excesivo. Solo participa en la caza. Probablemente no sepas lo que pasó aquí, ¿verdad?]],
				['11'] = [[(Los desconfiados ojos de Gavrilov me recorren de la cabeza a los pies y niega con la cabeza.)<new_line><new_line>No, nos las arreglaremos solos.]],
				['10'] = [[¿Qué? ¿Has venido hasta aquí desde Moscú? ¿Y por qué deberíamos creerte? Yo también sé inventarme cosas.]],
				['9'] = [[¿Un amigo? El caso es que no me acuerdo de ti. ¿De qué campamento eres?]],
				['8'] = [[(Los tres se giran rápidamente hacia mí. Ahora las tres Makárov apuntan directamente a mis entrañas.)<new_line><new_line>¡Eh! ¿Quién diablos eres tú?]],
				['7'] = [[(El hombre se inclina amenazadoramente sobre el chaval.)<new_line><new_line>Si alguien no hubiera cogido la última arma y perseguido al lobo como un imbécil, ¡Kirill aún seguiría con vida!]],
				['6'] = {
					['1'] = [[(El joven parece evaluar a Gavrilov y al segundo hombre, y luego, lentamente, saca un arma y les apunta.)]],
					['2'] = [[Eres... ¡eres solo un cobarde! ¡Kirill se sacrificó para que el lobo no te atrapara, Gavrilov! ¿Qué, no puedes asumir la verdad? ¡Entonces, lo que vamos a hacer es ir ahora mismo y masacrarlos a todos!]]
				},
				['1'] = [[Nikita, entiendo lo que sientes, pero tenemos que irnos. Llevaremos a Kirill a casa y lo enterraremos.<new_line>Nikita, ¿me estás escuchando? Nos vamos. Ya. Nos vamos a casa en Horenychi. Es muy peligroso estar aquí y Seva apenas si puede caminar, ¡necesita que le vea un médico!]],
				['12'] = [[¿Dónde has oído eso?]],
				['19'] = [[(El exinvestigador rumia mi respuesta mirándome fijamente.)<new_line><new_line>Trato hecho. Conocerás toda la verdad, pero antes debes cumplir tu promesa.]],
				['5'] = [[¿Continuar? ¡Solo quedamos tres! Y apenas logramos sobrevivir siendo cuatro. Ya vengarás a tu padrastro. Tendrás tu oportunidad, pero no ahora. ¡Ahora tenemos que volver!]],
				['4'] = [[(El joven alza sus perplejos ojos inyectados en sangre hacia el hombre y da un salto repentino.)<new_line><new_line>¿Nos vamos? ¿Es muy peligroso? ¿Qué dices, Gavrilov? ¡Tenemos que continuar! ¡Todavía hay algunos de esos monstruos por ahí! Tenemos cartuchos; suficientes para uno o dos.]],
				['3'] = [[Nikita, vamos a volver. Puedes venir con nosotros o matar a los lobos por tu cuenta.]],
				['2'] = [[(Gavrilov entorna los ojos.)<new_line><new_line>Has despertado mi interés. Bien, lo discutiremos. Cuando regresemos a Horenychi, al centro comunitario.]],
				['18'] = [[De acuerdo (enlaza sus manos a la espalda y comienza a caminar por la habitación). ¿Y qué estás dispuesto a hacer para descubrir esa verdad? ¿Arriesgar tu vida, por ejemplo?]],
				['22'] = {
					['1'] = [[Estas bestias, los lobos negros, aparecieron en nuestras tierras hace unos tres años. Una loba y dos cachorros, enormes, negros como la noche y ciegos. Los primeros dos años fueron pacíficos, pero luego todo se tornó salvaje. Tal vez los lobos se contagiaron de la rabia en alguna parte, o fue por otra cosa pero, fuera como fuese, el caso es que se pusieron a cazar personas.]],
					['4'] = [[No podemos demorarnos más. La loba se esconde en un viejo matadero y vamos a atacarla allí. Esta es nuestra única oportunidad de exterminarlos de una vez por todas.]],
					['3'] = [[Aun así, seguimos teniendo esperanza. Todos los lobos que hemos matado han sido machos, lo que significa que si matamos a la loba no nacerán más cachorros.]],
					['2'] = [[Nos protegimos lo mejor que pudimos, pero fueron creciendo en número mientras morían más y más personas. La semana pasada, entraron en una casa por la noche y aniquilaron a toda una familia. Enviamos a todo un pelotón a cazar a la bestia, pero no regresó ni uno solo de sus integrantes.]]
				},
				['23'] = [[Ya lo hemos intentado. No funciona.<new_line>Entonces, ¿lo harás? Si nos ayudas salvarás muchas vidas y, entonces, te lo contaremos todo. Si no lo haces, tendrás que buscar en otra parte.]],
				['24'] = [[Magnífico. A juzgar por tu equipo, eres un luchador experimentado, así que tenerte de nuestro lado aumenta nuestras posibilidades. Cuando estés listo para la caza, coge munición, comida y vendas y ve al punto de partida. Te estaré esperando allí.]],
				['25'] = [[(La boca de Gavrilov se curva en una sonrisa, como si hubiera estado esperando esa respuesta.)<new_line><new_line>Depende de ti, por supuesto, pero eso significa que mis labios están sellados. Un trato es un trato. Si cambias de opinión, puedes regresar. Aún tenemos un poco de tiempo.]],
				['13'] = {
					['1'] = [[(Pavel Gavrilov me estaba esperando en la oficina del director del antiguo centro comunitario. Hay alguien más en la habitación con él, un hombre de unos sesenta años, que dormita en un sillón junto a la estufa. Cuando me ve, Gavrilov llena un vaso de agua, despierta al anciano y le da una pastilla para que se la tome.)]],
					['2'] = [[Aquí está. Siéntete como en tu casa, como suele decirse, pero, primero, permíteme presentarte a mi amigo. Él también está dispuesto a hablar contigo. Timofey Zaitsev, exdirector del Instituto de Virología. Al igual que yo, quería irse a Polonia, pero no llegó a tiempo.]]
				},
				['27'] = [[Tú mismo. Supongo que así es como los solitarios logran sobrevivir tanto tiempo.]],
				['26'] = [[¿Y bien? ¿Has pensado en ello? Mi oferta sigue en pie.]]
			},
			['name'] = [[Gavrilov]],
			['choice'] = {
				['14'] = [[¿Qué quieres de mí?]],
				['15'] = [[No, no sé.]],
				['12'] = [[Ya he pasado por mucho para llegar hasta aquí. Ya he arriesgado mi vida.]],
				['13'] = [[¿Mi promesa? ¿De qué hablas?]],
				['20'] = [[Nos entendemos.]],
				['21'] = [[Nop. Sigue sin apetecerme suicidarme.]],
				['11'] = [[Eso es mi problema.]],
				['10'] = [[Aun así... Quiero saber la verdad.]],
				['9'] = [[Quiero saber sobre los científicos ejecutados. Sobre la epidemia que estalló simultáneamente en todo el país. Y sobre el 17 de agosto de 1984. El Día R.]],
				['8'] = [[...]],
				['7'] = [[Espera, no te vayas. Eres Pavel Gavrilov, el antiguo investigador, ¿verdad?]],
				['6'] = [[¿Cómo puedo probarlo? No sé... Puedo ayudarte a transportar el cuerpo. Guárdalo contra los lobos.]],
				['1'] = [[No es importante. Necesito hablar de algo contigo. Sobre los científicos del Instituto de Virología.]],
				['5'] = [[(Mientes) Soy de Tver.]],
				['4'] = [[Tranquilos, soy un amigo.]],
				['3'] = [[(Toses para llamar su atención.)]],
				['2'] = [[Bien, hablaremos allí.]],
				['16'] = [[Ya veo. ¿Pero por qué no quemáis el matadero con la loba dentro? ¿O la asfixiáis con el humo?]],
				['17'] = [[Me apunto. Hay que hacer esto. Esos lobos negros son demasiado peligrosos.]],
				['18'] = [[Y de otra manera estarías completamente jodido... Bien, estoy contigo.]],
				['19'] = [[No. He visto esos lobos y sé de lo que son capaces. Estoy dispuesto a arriesgar mi vida, pero lo que propones, entrar en la guarida de toda una manada, es directamente un suicidio, no un riesgo calculado.]]
			}
		},
		['sasha'] = {
			['episode'] = {
				['14'] = [[(Al escuchar mi voz, Sasha levanta la cabeza y hace una mueca, como aquejada por un repentino dolor de muelas.)<new_line><new_line>¿Tú? ¡Esperaba que estuvieras muerto!]],
				['24'] = [[(Sasha está sentada en lo profundo de su celda de esa cárcel improvisada y me mira con leve desprecio. Es difícil saber si me ha creído o no.)]],
				['12'] = [[(El charco de sangre a mis pies parece un océano. Postrado e impotente, todo lo que puedo hacer es esperar. Segundos que transcurrían como si fueran horas...)]],
				['16'] = [[¿Eh? ¿A qué viene esto ahora?]],
				['20'] = [[¿Y por qué no deberían hacerlo? ¿Pensabas que me tratarían bien, Judas? (La amargura se filtra en su voz por un momento). Bueno, Boris no fue amable conmigo.]],
				['30'] = [[Entonces... entonces nos atacaron. Por la noche. Ya había una docena heridos cuando la alarma nos despertó a todos. Varios bandidos atravesaron la puerta, las chicas estaban asustadas, pero noqueé a uno de esos cabrones, cogí su arma y empecé a disparar al resto.]],
				['28'] = [[¿Que qué me sucedió? Bueno, primero mamá me parió.]],
				['10'] = [[(Se queda en silencio por un momento y se inclina sobre mí nuevamente.)<new_line><new_line>Ni siquiera puedes imaginarte, papá, lo que voy a disfrutar metiéndote una bala en la cabeza. Una y otra y otra vez, hasta que no queden cartuchos en este puto edificio.]],
				['9'] = [[(Los labios de Sasha se curvan en una mueca de disgusto.)<new_line><new_line>Bah. Pero venga, llora, papá. Quizás ayude. Tal vez te perdone. Ah, sí, casi lo olvido. Tienes mi cuchillo. Voy a recuperarlo ahora.<new_line><new_line>(Sasha agarra el mango violeta y tira de él violentamente. La sangre brota de la herida.)]],
				['8'] = [[Papá, vamos, no seas tonto. ¿Por qué te disculpas? Bueno, mamá murió por tu culpa. Vaya cosa. Y el tío Artur y la tía Marina. Y, bueno, el abuelo también, pero él era bastante viejo. Y ahora has aparecido y matado a mis amigos, pero ¿y qué? ¡Siempre puedo buscarme otros nuevos! ¿Verdad? ¿Verdad?]],
				['7'] = [[(Sasha se acerca y aproxima su cara hasta que casi que se tocan nuestras narices. Sonríe dulcemente.)<new_line><new_line>Ay, ¿me buscabas? Lo siento, papá, pero llegas un poco tarde.]],
				['6'] = [[(Sasha me mira inquisitivamente. Entonces su mirada cobra mayor intensidad. Finalmente, la comprensión aflora en su rostro, aunque pronto se ve reemplazada por un odio ardiente fuera de lo común. Jamás había visto a una persona mirar a nadie de esa manera.)<new_line><new_line>Anda, mira por dónde. Papá está en casa.]],
				['32'] = [[Sí, claro, como si alguna vez dijera una palabra en contra de su precioso hijo. Ya sabes, él...]],
				['42'] = [[Sí. Dijo que tenía agallas, que valía por diez de sus combatientes. Que nunca habría hecho lo que Vasili, abandonando a un soldado solo contra todo un pelotón. Dijo que el campamento de Novokuznetsk tenía todas las de ganar, pero que les faltó valor. Dijo muchas cosas como esa. Yo no dije nada. Quería contradecirle, pero...]],
				['34'] = [[(Sasha está mucho mejor. Es capaz de tenerse en pie e incluso caminar mientras se apoya en mi brazo.)]],
				['44'] = [[Sí, me di cuenta pero, ¿sabes?: lo que dijo era verdad. Y también me di cuenta de eso. Vino a verme todos los días durante una semana y me habló de él y de su familia. Y, luego, sobre algunos de los otros combatientes. Finalmente, le hablé de mí. Me escuchó de verdad y me hizo preguntas.]],
				['49'] = {
					['1'] = [[Sí, pero no salieron a tiempo. Cogí el cuchillo de Ruslán y les rebané el cuello.]],
					['2'] = [[(Una pausa.)<new_line><new_line>Ni siquiera matar a los monstruos que asesinaron al abuelo me resultó tan dulce como estas muertes. Les vi desangrarse y me sentí liberada. Contenta. Fuerte. No era sed de sangre, ni venganza por su traición: era justicia.]]
				},
				['19'] = [[¿Hacer qué?]],
				['15'] = {
					['1'] = [[(Sasha se echa a reír mientras las lágrimas corren por su rostro.)<new_line><new_line>¡¿Y que Boris me tilde de sucia traidora?! ¡¿A mí?! ¡Tú eres cien veces peor! ¡Traicionar es tan fácil para ti como respirar! Boris te salvó la vida ¡¿y ahora quieres ayudar a su prisionera a escapar?!]],
					['2'] = [[(La risa histérica de Sasha se troca repentinamente en sordos sollozos. Había escuchado ese sonido antes: hacía mucho tiempo, cuando le dije a mi hija que me iba a ir para siempre. Sasha recobra el control, reprimiendo sus lágrimas con un rugido furioso, y se aleja de los barrotes.)]]
				},
				['23'] = [[Boris. ¿Qué estás planeando?]],
				['13'] = [[(En el oscuro sótano, todo es frío y humedad; ni las ratas vivirían allí. A la luz de las antorchas, más allá de los gruesos barrotes, veo tres bancos de madera maciza. Sasha está sentada en uno de ellos. El guardia me deja ir a la celda mientras fuma afuera)]],
				['31'] = {
					['1'] = [[Les repelimos. Después de eso, me devolvieron mi arma, me dejaron ir a las expediciones de caza e incluso me alistaron en un pelotón de combate. El jefe del pelotón era Vasili. Sí, el hijo de Boris.]],
					['4'] = [[Acabé con cuatro de ellos, pero me quedé sin munición y me atraparon. Querían matarme allí mismo, pero su líder les detuvo. Les dijo que me trajeran a su base.]],
					['3'] = [[Para resumir, el cuento de hadas se acabó enseguida. Nos enfrentamos a los abakanos. Sí, nos tendieron una emboscada, pero nosotros éramos más y estábamos mejor armados. El caso es que ese cerdo cobarde de Vasili, nos ordenó que nos retirásemos, a pesar de ver que había quedado aislada del resto del pelotón.]],
					['2'] = [[Me llevaba bien con el pelotón. Me dijeron que me respetaban, que yo era una de ellos. Y yo, como idiota que soy, me arrojaba la primera ante las balas para demostrarles de qué pasta estaba hecha. (Sasha aprieta los dientes). Dios, qué estúpida era...]]
				},
				['21'] = [[Quería saber quién mató a su orgullo y alegría: Vasili. No se lo dije.<new_line><new_line>(Sasha se ríe entre dientes.)<new_line><new_line>Cuando tenga la espalda contra el muro, se lo contaré todo. Con detalle. ¿Ya estás contento? Pues lárgate de aquí.]],
				['11'] = [[(Empiezo a ver borroso y escucho un rugido en mis oídos conforme me debilito rápidamente por la pérdida de sangre. Sasha levanta el rifle de nuevo.)<new_line><new_line>Es una pena que la casa del abuelo se quemara. Habrías encontrado muchos dibujos de ti, querido papá. Dibujos hechos por tu hijita. Y en todos... y cada uno... de ellos... te estabas... muriendo...]],
				['29'] = {
					['1'] = [[Resulta extraño. No consigo recordar que te hayas interesado por mi vida hasta ahora. (Sasha niega con la cabeza). Vale. Los de Novokuznetsk me llevaron a su campamento, pero no me dejaron pelear; incluso se llevaron el arma del abuelo. Fui a hablar con el comandante, pero dijo que no había lugar para una niña entre sus combatientes, así que me asignó a las tareas domésticas.]],
					['2'] = [[Pero el trabajo en la cocina era muy desagradable. Me negaba a trabajar allí, así que las chicas empezaron a cogerme manía y se quejaron al líder de mi «pereza», pero el muy cabrón solo dijo: «Vale, ya cambiará después de unos días sin su ración...».]]
				},
				['48'] = {
					['1'] = [[No había terminado. Una semana después, llevaron a algunos de sus «inocentes» a mi habitación: Vasili y otros dos de Novokuznetsk.]],
					['2'] = [[Ruslán les hizo una oferta: contarle todo lo que sabían sobre Stepnoy. Si le revelaban los efectivos, el arsenal y los puntos débiles de las fortificaciones, Ruslán les dejaría ir.<new_line>No había terminado de hablar cuando esos gusanos, pálidos y temblorosos, ya lo habían largado todo.]]
				},
				['47'] = [[No, papá. No somos bandidos en absoluto. Somos algo mucho más interesante.]],
				['46'] = {
					['1'] = [[(Sasha ríe con desprecio como respuesta.)<new_line><new_line>¿Gente inocente? ¡¿Gente inocente?! ¡¿Dónde ves tú la inocencia en esas personas?! ¿O es así como llamas a las ovejas perezosas, glotonas y estúpidas que solo balan inútilmente mientras las matan?]],
					['2'] = [[Mira a tu alrededor. No queda país. No hay un corral seguro, donde puedas rumiar tu comida en paz y hacer crecer tu lana bien bonita y densa para que te esquilen. Estamos en el bosque y aquí o eres una oveja... o un lobo.]]
				},
				['41'] = {
					['1'] = [[No. Me salvaste, a pesar de que intenté matarte, así que mostraremos misericordia. Es solo lo justo. Justicia por encima de todo.<new_line>Ruslán, ayúdame.]],
					['3'] = [[(Ruslán mete con cuidado a Sasha dentro del coche y luego se sienta al lado del conductor. El tercer bandido está a punto de cerrar la puerta, pero Sasha la mantiene abierta.)<new_line><new_line>Nunca lo olvidaré y nunca te perdonaré por abandonarnos a mamá y a mí. Si no lo hubieras hecho, nada de esto habría sucedido. ¿Lo entiendes? ¡Nada de esto!]],
					['2'] = [[(Uno de los hombres ayuda a Sasha a ponerse de pie. Un motor retumba entre los matorrales y un viejo UAZ aparece sobre las piedras.)<new_line><new_line>Estamos en paz. Una vida por otra vida.<new_line>Ahora vete. Vete lejos, lo bastante como para que nunca vuelvan a cruzarse nuestros caminos. No quiero volver a verte ni saber nada de ti nunca más.]]
				},
				['43'] = [[No, solo fumaba y hablaba. Volvió al segundo día. Dijo que mi gente se negaba a pagar el rescate por mí y que todo lo que había pedido era un par de pistolas y una caja de latas. Dijo que no había habido ni un solo intento de rescate. Y más cosas así. Después de eso, me trajo la comida él mismo. Raciones mucho más grandes que las de nuestra gente y más ricas.]],
				['33'] = [[(Se escucha el sonido distante de un disparo procedente de Novokuznetsk. Luego, otro. Cojo mi arma y escalo la colina más cercana, desde donde oteo a lo lejos. El yermo gris se extiende ante mí, tan estéril como siempre.<new_line>Cuando vuelvo con Sasha, ya está dormida.)]],
				['45'] = [[No. Todavía no, pero sí pude ver que él me entendía. Me entendía mucho mejor que cualquier escoria de Novokuznetsk. Los que... me usaron y me dejaron tirada como una colilla.]],
				['35'] = {
					['1'] = [[Está bien, entonces escucha (acepta con cansancio). Me llevaron a la base de Abakán y me encerraron. Pensé que iban a forzarme, pero no lo hicieron. La primera semana, vino una chica todos los días a traerme comida, pero no vi a nadie más.]],
					['2'] = [[Entonces, finalmente, un día vino a verme su líder, Ruslán. Descubrí más tarde que era un antiguo prisionero político. Estuvo callado durante mucho tiempo, y luego, sin más, me preguntó qué había hecho mal para que mis compañeros me abandonaran.]]
				},
				['1'] = [[(Subo corriendo hasta el último piso. Los cuerpos de nuestros hombres están despedazados y, entre ellos, yace una niña herida. Su cara está cubierta con una mezcla de pintura de camuflaje y sangre. Cuando me ve, saca un delgado cuchillo con el mango violeta.)]],
				['40'] = [[No, somos muchos. Miles. Y pronto habrá aún más. (Sasha sonríe de repente). Y de todos modos, tres o cuatro personas son capaces de algunas cosas. ¡Detrás de ti!<new_line><new_line>(Me vuelvo. Hay dos bandidos en pie a unos metros de mí, apuntándome a la cabeza.)]],
				['38'] = [[Respeto, reconocimiento... Pero eso fue solo al principio. Más tarde, me dieron mucho más. Cosas que nunca hubiera recibido de tus muchachos de Novokuznetsk.]],
				['39'] = [[¡La diferencia entre vivir y sobrevivir! (Los ojos de Sasha brillan febrilmente). La palabra «misión», ¿significa algo para ti?<new_line>No somos bandidos, papá. Somos colonizadores. Estamos arrancando los restos podridos del viejo mundo de la faz de la tierra y construyendo un mundo nuevo, uno mucho mejor y más justo.<new_line>]],
				['5'] = [[(La niña escupe y se limpia el hilo de sangre que corre por su cara. Luego, coge el rifle de uno de los muertos y apunta.<new_line>Es entonces cuando ves su rostro claramente.<new_line>No...<new_line>No puede ser...)]],
				['4'] = [[(Le disparo, pero se aparta rápidamente y luego viene volando hacia mí con increíble agilidad y me clava el cuchillo.<new_line>Jadeo, suelto el arma y caigo al suelo. La chica me da una patada en la cara.)<new_line><new_line>¡Tan retorcido como los demás!<new_line><new_line>(señala los cuerpos destrozados)]],
				['3'] = {
					['1'] = [[(La furia cae de su cara como una máscara. La niña se convulsiona en un ataque de tos y gira la cabeza al tiempo que baja el cuchillo. Me acerco y le tiendo mi mano.<new_line>En un instante, me clava el cuchillo.<new_line>Doy un grito ahogado, suelto mi arma y caigo al suelo. La chica salta y me da una patada en la cara.)]],
					['2'] = [[Siempre igual. Soy una niña, así que eso significa que soy una víctima indefensa, ¿verdad?]]
				},
				['2'] = [[¡No te acerques! ¡Un paso más y te destripo!]],
				['37'] = [[No importa lo que diga. No lo entenderías.]],
				['36'] = [[Al día siguiente, Ruslán regresó y puso una pistola frente a mí. Dijo que ya no me iba a dar de comer gratis. Que no necesitaba prisioneros; lo que necesitaba eran combatientes. Me dio la opción de unirme a él o llevarme de vuelta a Novokuznetsk. Y yo elegí.]],
				['25'] = [[(Sasha ya no gime. Ahora ha caído en un extraño letargo, con sus ojos moviéndose de un lado a otro.)]],
				['17'] = [[Idiota, yo... ¡Estaba saboreando el momento!]],
				['18'] = [[(Sasha resopla y se da la vuelta. La luz de la antorcha iluminó brevemente sus brazos, cubiertos de moratones. Hematomas nuevos, muy recientes.)<new_line><new_line>Piensa eso si te hace feliz. Me importa una mierda. Mañana me van a fusilar.]],
				['22'] = [[(Sasha no reacciona.)]],
				['26'] = [[(Sasha se cansa de mirarme y suspira pesadamente.)<new_line><new_line>¿De qué?]],
				['27'] = [[(Sasha se aleja.)<new_line><new_line>No es un gran tema.]]
			},
			['name'] = [[Sasha]],
			['choice'] = {
				['14'] = [[No podías hacerlo.]],
				['24'] = [[Deja de bromear. Cuéntame qué pasó después de que mataras a los bandidos en casa del abuelo.]],
				['12'] = [[No deberías haber tenido esperanza, deberías haber disparado mientras tuviste la oportunidad. Pero no pudiste.]],
				['22'] = [[¿De qué? ¿Quizás de cómo me querías matar y te salvé de un pelotón de fusilamiento?]],
				['20'] = [[Saldrás esta noche. Estate preparada.]],
				['30'] = [[Aún no me lo has contado todo.]],
				['28'] = [[¡Tranquila!]],
				['10'] = [[¿Sasha?]],
				['9'] = [[Sasha...]],
				['8'] = [[Sasha... espera...]],
				['7'] = [[Me... me dijeron que te habían hecho prisionera. Yo... Trataba... de salvarte...]],
				['6'] = [[Sasha, lo siento, perdóname...]],
				['32'] = [[¿Estás contenta con tu elección?]],
				['42'] = [[¿Y querías ser un lobo? ¿Un bandido?]],
				['34'] = [[¿Qué no lo entenderé?]],
				['44'] = [[¿Y Ruslán los liberó?]],
				['25'] = [[¿Y después de eso?]],
				['15'] = [[¿Quién te ha hecho esto?]],
				['23'] = [[Eso creía yo. Entonces, si no quieres hablar de eso, cuéntame lo que te sucedió.]],
				['13'] = [[No podías matarme. Lo último que recuerdo es el silencio. Dudaste. Podrías haberme matado y haber huido, pero te quedaste parada. Boris llegó y te hirió.]],
				['31'] = [[Qué locura...]],
				['21'] = [[¿Podemos hablar?]],
				['11'] = [[Puedo ayudarte a escapar.]],
				['29'] = [[(La dejas en paz)]],
				['41'] = [[¡Es despreciable! ¡Un canalla! ¡Tiene las manos manchadas de sangre de gente inocente!]],
				['43'] = [[¿De qué hablas?]],
				['33'] = [[Te equivocas. Puedo entender por qué lo hiciste. Querías respeto y reconocimiento, querías ser libre. Y tus nuevos amigos te dieron eso.]],
				['35'] = [[¿No es eso un poco ambicioso? Solo quedáis tres o cuatro.]],
				['1'] = [[No tengas miedo de mí. ¡Te sacaré de aquí!]],
				['40'] = [[¿Comenzaste a confiar en él?]],
				['39'] = [[¿No viste que intentaba convencerte?]],
				['38'] = [[¿Te presionó?]],
				['5'] = [[Sasha, yo... Sé que es culpa mía. Es culpa mía que tú, y tu madre, y...]],
				['4'] = [[Sasha... Llevo buscándote mucho tiempo...]],
				['3'] = [[¿Sasha?]],
				['2'] = [[(Disparas a la niña)]],
				['37'] = [[¿Él te habló de la escaramuza?]],
				['36'] = [[¿Me vas a matar?]],
				['16'] = [[Tus brazos. ¿Te han golpeado?]],
				['17'] = [[Pero por qué...]],
				['18'] = [[¿Quién tiene la llave de la celda?]],
				['19'] = [[¡¿Quién tiene la llave de la celda?!]],
				['26'] = [[¿Repeliste el ataque?]],
				['27'] = [[Boris nunca me contó que los tuyos te abandonaron.]]
			}
		},
		['nadezhda'] = {
			['episode'] = {
				['14'] = [[¿Otra vez tú? ¿Qué quieres de mí?]],
				['15'] = [[Ah, eres tú. ¿Qué hay de Gena?]],
				['12'] = [[(El marido de esta mujer era el único hilo que podría conducirme a la verdad sobre los acontecimientos sucedidos hacía tanto tiempo. Tenía que convencerla de que hablara.)<new_line><new_line>Gena. Mi hijo.]],
				['13'] = {
					['1'] = [[(Nadezhda deja escapar un sollozo y se lleva un pañuelo a la nariz. De nuevo, se hace un tenso silencio. Finalmente, Nadezhda me mira otra vez.)<new_line><new_line>Salió con uno de sus amigos, Vania, a Novomoskovsk. Hace dos días, para buscar comida, pero Vania regresó solo. Dijo que el edificio se derrumbó, que Gena murió...]],
					['2'] = [[No pedía mucho. Solo quería enterrar a mi hijo. Mirar su rostro una última vez, ¿sabes? Le pedí a nuestra gente que trajeran su cuerpo de vuelta, pero se negaron. ¿Por qué arriesgar vidas por un cadáver? Dijeron que no merecía la pena. Fui y se lo pedí, pero no había esperanza.]]
				},
				['11'] = [[Si quieres ayudarme, entonces vete, ¡ya!]],
				['10'] = [[(Recuerdo las miradas hostiles de los residentes del campamento. ¿Podrían estar implicados en la desaparición del compañero de habitación de Nadezhda? Tras beber un poco de agua y calmarse ligeramente, Nadezhda se vuelve hacia mí.)<new_line><new_line>¿Aún no te has ido? ¿Qué quieres de mí? ¡Déjame en paz!]],
				['9'] = [[(Nadezhda se queda sentada completamente quieta durante varios minutos. Luego, se pone en pie con dificultad y se sirve un poco de agua de una jarra. Es entonces cuando noto que había dos tazas sobre la mesa.<new_line>La segunda tiene el dibujo de un cachorro de león. Una revista juvenil asoma bajo la almohada en el segundo colchón.)]],
				['8'] = [[(El labio de la mujer se estremece y me mira enojada.)<new_line><new_line>¿Importante? ¿No podías elegir mejor momento? ¡Vete!]],
				['7'] = [[(Nadezhda se estremece ante la mención de su marido. Luego, frunce el ceño y se aleja de mí.)<new_line><new_line>Vete, por favor.]],
				['6'] = [[¿Quién eres? ¿Qué quieres de mí?]],
				['1'] = [[(La casita está fría y oscura. Sobre un viejo colchón, apoyada contra la pared, se sienta una mujer menuda y canosa. Debe de ser Nadezhda Kulik. Hay otro colchón en la pared opuesta, pero está sola.<new_line>Me acerco a Nadezhda.)]],
				['5'] = [[(Nadezhda probablemente ansía creer que un extraño está dispuesto a ayudarla, cuando los otros habitantes del campamento habían rechazado hacerlo, pero se controla y luego habla en un susurro.)<new_line><new_line>Bueno, entonces... vuelve tan pronto como puedas. Y ten cuidado]],
				['4'] = [[Vete, por favor...]],
				['3'] = [[(Nadezhda me mira con escepticismo.)<new_line><new_line>¿De verdad que lo harás? ¿Traerme a Gena?]],
				['2'] = [[(La anciana –bueno, prematuramente envejecida, para ser exactos– no se sobresalta. Me mira con desconfianza con sus cansados ​​ojos grises y no dice nada.)]],
				['17'] = [[Ah, eres tú. Disculpa, estoy un poco ocupada, pero gracias de nuevo por todo...]],
				['16'] = [[Ah, bueno, solo... date prisa.]]
			},
			['name'] = [[Nadezhda]],
			['choice'] = {
				['1'] = [[¿Nadezhda? No tengas miedo, no voy a hacerte daño.]],
				['12'] = [[¿Qué le sucedió?]],
				['13'] = [[Nada... Disculpa.]],
				['5'] = [[Perdón por la intromisión. Necesito tu ayuda. ¿Me puedes dedicar un par de minutos? Luego me iré y no te molestaré más.]],
				['4'] = [[Sí, lo traeré.]],
				['3'] = [[Te comprendo...]],
				['2'] = [[Puedo hallar el cuerpo de tu hijo y traértelo.]],
				['9'] = [[(Sigues esperando)]],
				['8'] = [[(Te retiras hacia la puerta y esperas)]],
				['7'] = [[Nadezhda, escúchame, es importante...]],
				['6'] = [[Quería pedirte una cosa. Se trata de tu marido.]],
				['14'] = [[No, aún no he ido a buscarlo.]],
				['11'] = [[Ha desaparecido algún ser querido. ¿Quién?]],
				['10'] = [[Solo quiero ayudarte.]]
			}
		},
		['zaycev'] = {
			['episode'] = {
				['14'] = [[¿Qué quieres que te diga? Gueorgui Nesterov fue un científico de renombre nacional, el orgullo de nuestro instituto. Trabajamos alcanzando grandes éxitos con él, Kulik y Shashkov durante muchos años. Karbyshev nos transfirió desde Leningrado. Beletsky, el más joven del equipo, se incorporó más tarde, a finales del 83.]],
				['15'] = [[Podría explicártelo, pero aun así no lo entenderías.]],
				['12'] = [[De acuerdo, como quieras. Recuérdame: ¿qué es lo que querías saber?]],
				['13'] = [[(El director asiente, se quita las gafas y empieza a limpiarlas metódicamente con un trapo. Se tira varios minutos limpiándolas.)]],
				['20'] = [[¿Hum? ¿Quieres saber algo más?]],
				['11'] = [[Para serte sincero, no lo sé. Discúlpame, pero hemos hablado mucho rato: es hora de que me tome mis pastillas.]],
				['10'] = [[(Zaitsev se ríe con amargura.)<new_line>No. En aquel momento todas las naciones actuaron por su cuenta. Estados Unidos empezó a bombardear Europa, India atacó China. Prendieron los conflictos profundamente arraigados... Era como si todo el mundo se hubiera vuelto loco: el único lugar a salvo era la Antártida. Pero, como siempre, la humanidad consiguió empeorar las cosas...]],
				['9'] = [[Sí, lanzamos todo nuestro arsenal inmediatamente sobre todos los objetivos. Pese a verse condenado, el país no quería morir. La ola de refugiados infectados aprovechó el caos y desbordó a los ejércitos aliados. Y así fue como el R17 se extendió al resto del mundo.]],
				['8'] = {
					['1'] = [[Efectivamente. Por cada persona que mataban, llegaban dos más. Además, la frontera es enorme, por lo que con suficiente astucia o desesperación siempre había forma de colarse. Cuando surgieron los primeros brotes de la epidemia en China, Mongolia, Turquía, Polonia y Finlandia, se decidió tomar medidas extremas.]],
					['2'] = [[A finales de octubre se celebró una reunión secreta del Club Nuclear y se aprobó una moción para limpiar el «territorio infectado». Usaron toda la potencia de fuego que tenían. Incluso las aldeas fueron objeto de «desinfección» por bomba. Pero volvieron a calcular mal.]]
				},
				['7'] = [[Por supuesto. Y casi todos ellos estaban infectados. Nuestros vecinos cerraron sus fronteras, impusieron la ley marcial, reunieron a sus tropas y mataron a miles de refugiados...]],
				['6'] = [[Exacto. Y tras derramar un poco de sangre, tendrían acceso a las interminables y ricas tierras de nuestra patria. Pero el R17 les traicionó. La epidemia se extendió demasiado lentamente, su período de incubación era demasiado largo y había demasiadas personas inmunes. Ya en octubre, cientos de miles de personas se lanzaban hacia las fronteras de la URSS...]],
				['1'] = [[(El exdirector del Instituto de Virología dormita en el mismo sillón que la última vez. Le despierto y me siento en la silla que hay junto a él. Zaitsev me mira atontado. Luego le ofrezco el colmillo de la loba.)<new_line><new_line>¿Esto qué es?]],
				['5'] = [[Vale, vale, pero es todo muy simple. Evidentemente, las agencias de inteligencia estadounidenses sobrestimaron la eficacia de su pequeño asesino: el R17. O tal vez lo subestimaron. Pensaron que dejaría a la gente de la Gran Nación de los Soviets al borde de la extinción, fuera de sí y debilitada por la enfermedad...]],
				['4'] = [[(El director me mira confundido. Luego, asiente de nuevo y comienza a limpiar sus gafas.)]],
				['3'] = [[Sí, así comenzó la pandemia que aniquiló a la mayoría de la población de la URSS.<new_line>¿Estás satisfecho?]],
				['2'] = [[(Zaitsev gira el colmillo en sus manos y me mira. Parece un poco incómodo.)<new_line><new_line>¿Dónde está Pavel?]],
				['19'] = [[Por desgracia, sí. Por lo que parece, Nesterov ya había logrado entregar un cultivo a la CIA. El 17 de agosto de 1984, agentes enemigos dispersaron el R17 en el aire en varias ciudades usando unos botes de espray especiales.]],
				['18'] = {
					['1'] = [[Yo... No tuve elección. Les denunciamos a las autoridades. A los tres. Beletsky y Shashkov también firmaron la denuncia.<new_line>El asunto se atajó rápidamente. Nesterov, Kulik y Karbyshev fueron arrestados.]],
					['3'] = [[La audiencia se celebró a puerta cerrada, pero me permitieron quedarme como testigo. Acabó enseguida. Las pruebas eran abrumadoras y Karbyshev hizo una confesión completa. Artículo 64: traición a la patria. Les llevaron directamente desde el tribunal ante el pelotón de fusilamiento.]],
					['2'] = [[Luego, vinieron unos hombres del KGB que nos interrogaron y registraron todo el instituto. Se llevaron todo lo que pudieron del laboratorio de Nesterov: hasta el letrero de la puerta. Destruyeron todo el material biológico.]]
				},
				['16'] = {
					['1'] = [[Genética molecular. Estudiaban las proteínas y los genes de los virus más extendidos. Una actividad completamente inofensiva, siempre y cuando no cruces la línea, claro.<new_line>Nesterov la cruzó. Empezó a experimentar con mutaciones e hizo que Kulik y Karbyshev se involucraran en ello.]],
					['2'] = [[Ocultaron su trabajo a los demás. Sospechaba que no iban al laboratorio a pasar las tardes solo para tomar el té, pero decidí no inmiscuirme, aunque finalmente tuve que hacerlo.]]
				},
				['17'] = {
					['1'] = [[Beletsky encontró el escondite oculto de Nesterov. Cogió el cuaderno de Nesterov y me lo trajo. Y una grabación de voz. ¡Al principio no podía creerlo, pero eran su letra y su voz!]],
					['2'] = [[Nesterov y sus amigos estaban desarrollando una nueva plaga. Bueno, no una mera plaga; este virus, el R17, era mucho más peligroso que la Yersinia pestis. Y lo que es más, ¡estaba hecho por encargo! ¡Para la CIA! ¡Nesterov había creado una nueva arma biológica para nuestro enemigo!]]
				}
			},
			['name'] = [[Zaitsev]],
			['choice'] = {
				['14'] = [[¿En qué trabajaban?]],
				['15'] = [[Intenta que lo comprenda.]],
				['12'] = [[Háblame de Nesterov y de los otros científicos que trabajaron con él. Y cuéntame todo lo demás.]],
				['13'] = [[Adelante, no seas tímido.]],
				['20'] = [[Bien, gracias por hablar conmigo.]],
				['21'] = [[Sí, hay algo más: ¿cómo empezó la guerra nuclear?]],
				['11'] = [[Los lobos acabaron con Gavrilov. Te escucho.]],
				['10'] = [[¿Significa eso que tampoco queda nada en los otros continentes? ¿El mundo entero es un páramo?]],
				['9'] = [[¿Y no había nada que se pudiera hacer?]],
				['8'] = [[¿La URSS se defendió?]],
				['7'] = [[Pero había demasiados...]],
				['6'] = [[¿Para huir del país moribundo?]],
				['1'] = [[Ese era nuestro acuerdo. He cumplido con mi parte: la loba está muerta. Ahora te toca a ti.]],
				['5'] = [[Y matarían a los supervivientes ellos mismos.]],
				['4'] = [[Estoy esperando.]],
				['3'] = [[No, hay algo más. ¿Cómo comenzó la guerra nuclear?]],
				['2'] = [[Eso fue el Día R.]],
				['22'] = [[No, he averiguado todo lo que quería. Gracias.]],
				['19'] = [[Sí, gracias por hablar conmigo. Me tengo que marchar ya...]],
				['18'] = [[Pero el virus seguía operativo.]],
				['17'] = [[¿Y qué hiciste?]],
				['16'] = [[¿Qué pasó?]]
			}
		},
		['varya'] = {
			['episode'] = {
				['1'] = [[(No hay fuentes de luz en el granero, aparte de los solitarios rayos de sol que entran por los agujeros en el techo. Las tablas del suelo crujen como protesta ante mis pisadas.<new_line>La prisionera se sienta encogida en el mismo rincón oscuro.)]],
				['5'] = [[Ya lo ves.]],
				['4'] = [[(Varia da un traspiés y se tambalea. Se agarra a la pared.)<new_line><new_line>Lo siento, mis piernas no están por la labor. ¿Qué hacemos? ¿Podrías... llevarme? Solo por un rato, hasta que mis piernas vuelvan a la normalidad.]],
				['3'] = [[¿Les has matado? (Su boca se abre con asombro.) ¿Tu solo? ¿Contra todos ellos? ¡No puede ser! ¿Cómo lo has hecho? Por cierto, me llamo Varia.]],
				['2'] = [[(Al principio, la chica me mira con odio, pero luego cambia su expresión.)<new_line><new_line>¡Eh! ¡No te recuerdo! ¿No eres uno de ellos?]],
				['9'] = [[(Te deja tan asombrado que casi la dejas caer al suelo.)<new_line><new_line>Sí, ¿por qué no? Eres fuerte, valiente, puedes protegerme. En fin, arriesgaste tu vida para salvarme. ¡Ahora tengo que recompensártelo! ¿Qué voy a hacer en casa de mi tía? ¡Morirme de aburrimiento!]],
				['8'] = [[Por supuesto, pero ¿sabes lo que acabo de pensar? No quiero volver con mi tía. ¿No podría viajar contigo?]],
				['7'] = [[Decía que solo estaba celosa de mí.]],
				['6'] = [[(Mientras caminas hacia el bosque, Varia te relata sus experiencias y tribulaciones a manos de los bandidos. Luego, se pasa a la historia de su atroz secuestro y, por último, te cuenta en detalle los horrores de su tía, con quien vivía en un pueblo a las afueras de Briansk. Apenas si la escuchas porque estás demasiado absorto en tus propios pensamientos.)]],
				['11'] = [[Aburrida y sin nada que hacer... Bueno, no quiero hacer nada, supongo. Oye, ¿de qué calibre tienes los cartuchos? ¿Grandes como mi tío o más pequeños?]],
				['10'] = [[¿De vuelta con mi tía? Hum... Vale. Oye, ¿puedes enseñarme a disparar?]]
			},
			['name'] = [[Varia]],
			['choice'] = {
				['1'] = [[(La liberas)]],
				['5'] = [[(Levantas a Varia en brazos y la llevas fuera del granero.)]],
				['4'] = [[Eh... ¿en serio no puedes andar sola?]],
				['3'] = [[Encantado de conocerte, Varia. Te lo contaré todo por el camino. Entonces, ¿puedes caminar sin ayuda?]],
				['2'] = [[No, no soy uno de ellos. Y ahora ya no están, les he matado. Levántate, tenemos que salir de aquí. ¿Puedes andar sola?]],
				['9'] = [[Eh... vamos a hacer una cosa: yo te llevo de vuelta con tu tía y me marcho por un tiempo. Así podrás pensar cómo quieres agradecérmelo. ¿Vale?]],
				['8'] = [[¡¿Conmigo?!]],
				['7'] = [[No, antes de eso. ¿Hay un pueblo de supervivientes en las afueras de Briansk? ¿Puedes mostrarme el camino?]],
				['6'] = [[Perdona, ¿qué decías?]],
				['10'] = [[(Suave pero firmemente terminas la conversación)]]
			}
		},
		['ignat'] = {
			['episode'] = {
				['14'] = [[Tú solo empieza y yo te echaré una mano.]],
				['15'] = [[Nunca creí que fueras a cumplir tu palabra.<new_line>Por cierto, mi esposa casi se vuelve loca cuando le conté lo de la forja. No te lo pienso permitir, me dijo. Bueno, ya es demasiado tarde, ¡que lave la ropa otro!]],
				['12'] = [[No muy lejos de aquí, junto al río, hay un viejo almacén. Uno bueno, de ladrillo. No se puede desear una ventilación mejor.]],
				['13'] = [[No hay pared.]],
				['20'] = [[¡Vaya! Eso sí que mola. ¡Gracias, hombre!<new_line>Si necesitas el horno, ya sabes dónde lo tienes. Mantendré el fuego encendido.]],
				['21'] = [[¡Gracias por todo, hombre! ¡Siento que he recuperado mi vida!]],
				['11'] = [[¿Vas a traerme esos materiales pronto o qué? No creo que pueda soportar seguir haciendo la colada mucho más tiempo.]],
				['10'] = [[Voy a ser herrero... Igual que el abuelo. Vale, hombre, trato hecho. Construiremos una fragua y te enseñaré lo que pueda recordar.]],
				['9'] = [[(Los ojos de Ignat se iluminan por un segundo, pero luego deja caer nuevamente la cabeza.)<new_line><new_line>No lo sé. Han pasado demasiados años y nunca he trabajado exactamente en una fragua, aunque vi a mi abuelo forjar cosas. No podré hacerlo.]],
				['8'] = [[Fundidor, ajustador, incluso llegué a ser soldador en una época. He trabajado con el hierro toda mi vida.]],
				['7'] = [[Bueno, yo era un udarnik, un trabajador ejemplar. El primero de nuestra fundición en lograr ese título. Y luego todo se fue a la mierda: la epidemia se llevó a mis hijos, cayeron las bombas... Pensé que todo eso había quedado atrás, pero no. Ahora mis pulmones no funcionan. Mi esposa es mi enfermera y me daría de comer con una cucharada si la dejara, pero no soy un anciano, ¡ni siquiera he cumplido los cuarenta!]],
				['6'] = [[Voy a morir muy pronto. Aquí no hacen más que preocuparse por mí, me alimentan, me medican y soy como un parásito, aquí sentado sin hacer nada. Hoy me han hecho un gran favor: me han dejado lavarme mis propia ropa. Dicen que no debería esforzarme estando enfermo.]],
				['1'] = {
					['1'] = [[(Mi atención se dirige hacia un hombre pálido de mediana edad sentado en un taburete de madera. Está lavando la ropa en un lavabo de estaño.<new_line>De repente, emite una fea tos y luego se lleva un áspero trapo gris a los labios. Se da cuenta de que le miro y se sonroja.)]],
					['2'] = [[¿Tú qué miras? Piérdete.]]
				},
				['5'] = {
					['1'] = [[(Ignat se levanta de repente y camina hacia mí. Se acerca y escupe en el suelo, a sus pies; por poco me da en los zapatos.)<new_line><new_line>¿Y si me hace falta?]],
					['2'] = [[(El rostro de Ignat se pone gris. Respirando pesadamente, baja la cabeza y hace una mueca. Regresa a su taburete y se sienta, con la mirada fija en el suelo. Un minuto después, alza sus ojos casi suplicante.)<new_line><new_line>Oye, ven aquí.]]
				},
				['4'] = [[Un bandido, entonces (Ignat tose de nuevo). Nadie más sobrevive en el páramo. Oye, bandido, ¿tienes un cigarrillo?]],
				['3'] = [[Tal vez sí, tal vez no. No es asunto tuyo. Por lo menos, dime de dónde vienes. ¿Cuál es tu campamento?]],
				['2'] = [[No. Tengo todo un campamento lleno de ayudantes. Me traen pastillas y cuencos de agua caliente para hacer vahos. Y, cuando llegue el momento, llorarán por mí y me enterrarán. Lo último que necesito es otro guardaespaldas.]],
				['16'] = [[Gracias por todo, hombre. Realmente me has ayudado mucho, no sé ni qué decir. ¡Si todos los bandidos fueran como tú!]],
				['18'] = [[Pero aun así me darás un cigarrillo, ¿verdad? No he puesto los ojos en uno en cinco años. Ni siquiera voy a encenderlo, solo quiero sentirlo en mis labios. No me hará ningún daño.]],
				['19'] = [[Eres muy listo... Bien, gracias de todos modos. Estaré en deuda contigo por la fragua hasta que me muera.<new_line>Si necesitas el horno, ya sabes dónde lo tienes. Mantendré el fuego encendido.]],
				['17'] = [[Bien, si dices que no eres un bandido, no eres un bandido. Entra y echa un vistazo. Estoy haciendo un fregadero de forja, para que mi esposa vea que esto no es tan malo. Ahora va a tener que lavar la ropa ella.<new_line>Algo es algo.]]
			},
			['name'] = [[Ignat]],
			['choice'] = {
				['14'] = [[Falta poco, falta poco, aún no he encontrado todo.]],
				['15'] = [[Lo he traído, todo. ¡Echa un vistazo!]],
				['12'] = [[¿Hay muchas ventanas?]],
				['13'] = [[(Vas al viejo almacén para construir la fragua)]],
				['20'] = [[Nos vemos, Ignat.]],
				['11'] = [[Comencemos a construirte una fragua. Ladrillos, metal, cemento; tenemos todo lo necesario.]],
				['10'] = [[Además, podrás recompensar a tu esposa y al resto por cuidarte, pero tómatelo con calma o te harás daño.]],
				['9'] = [[Lo harás ¿Qué puedes perder? Si no funciona, puedes simplemente volver a tu taburete, aferrarte a las faldas de tu esposa y pedirles cigarrillos a los bandidos.]],
				['8'] = [[Tengo una idea. Te ayudaré. Te construiré una fragua. Ya sabes, como las que se ven en los pueblos. Con su yunque, su horno, su fuelle... Podrás seguir trabajando con metal. Y, a cambio, me enseñarás.]],
				['7'] = [[¿Entonces eres fundidor?]],
				['6'] = [[Entonces, ¿estás cansado de eso?]],
				['1'] = [[Me preguntaba si tal vez necesitarías ayuda con algo...]],
				['5'] = [[¿Qué necesitas?]],
				['4'] = [[Con esa tos, un cigarrillo es lo último que necesitas. Una calada y estarás muerto.]],
				['3'] = [[No tengo campamento. Soy un vagabundo.]],
				['2'] = [[¿Planeas morir?]],
				['17'] = [[¿Qué?]],
				['19'] = [[Muy bien, puedo darte uno.]],
				['18'] = [[Lo siento, amigo, pero el asma es una cosa seria.]],
				['16'] = [[Deja de decir eso: no soy un bandido.]]
			}
		},
		['belaz'] = {
			['episode'] = {
				['1'] = [[Oye, eres mi nuevo ayudante, ¿verdad? ¡Estupendo! Mira: siempre soñé con un vehículo todoterreno que no consumiera combustible. Podría ser... ¡como una ciudad personal itinerante! El caso es que aún me faltan algunos detalles, y no tengo a nadie que me ayude. ¡Casi he perdido la esperanza! Necesito conseguir todas estas cosas: mira la lista. Si me ayudas a hacer esto, puedo convertirte en mi copiloto. ¡Podrías viajar conmigo y con mi familia adonde quisieras!]],
				['7'] = [[La casa está vacía. Vladímir y su familia viven ahora en BelAZ.]],
				['6'] = [[Así que no pierdas el tiempo, ¡continúa!]],
				['5'] = [[¡Increíble! ¿Será posible? ¡Mi sueño hecho realidad! No tienes ni idea de lo feliz que me siento. Te lo prometí: ¡bienvenido a bordo! En cuanto nuestra ciudad itinerante esté lista para ponerse en marcha, podrás elegir la ruta que desees. Aceptaremos todas y cada una de tus decisiones.]],
				['4'] = [[¿Y bien? ¿Encontraste algo?]],
				['3'] = [[Lo sabía. Todos decís siempre lo mismo, y nadie quiere averiguar más. ¿Pero qué os pasa a todos? Vais en busca del dinero fácil, ¿verdad? Ahora tengo que trabajar. Adiós.]],
				['2'] = [[Vale, aquí está la lista. Tienes que traerme algo de la central nuclear de Chernóbil. Yo trabajaba antes allí y ahora me hacen falta unas piezas de repuesto especiales. Además, necesito ciertos detalles y algunas plantas para el cultivo hidropónico.]]
			},
			['name'] = [[Vladímir]],
			['choice'] = {
				['1'] = [[Vaya... ¡Es un plan realmente ambicioso! No sé cómo empezar, pero vale la pena intentarlo. ¿Qué tengo que hacer?]],
				['5'] = [[Nos vemos.]],
				['4'] = [[Aún no.]],
				['3'] = [[¡Sí! ¡He encontrado todo lo que buscabas!]],
				['2'] = [[¿Estás loco? Eso es imposible. No pienso hacerlo.]]
			}
		},
		['vanya'] = {
			['episode'] = {
				['1'] = [[(Vania está sentado, fumando, en el porche. En cuanto empieza a hablar me doy cuenta de que está un poco bebido.)<new_line><new_line>Hola. Hace mucho tiempo que no tenemos a nadie nuevo por aquí. ¿De dónde vienes, desde qué agujero te arrastraste?]],
				['5'] = [[En un edificio de apartamentos de cinco plantas. Al lado de unos garajes. En un apartamento del tercer piso. ¿Y a ti qué te importa?]],
				['4'] = [[(Vania se ríe y arroja su cigarrillo a los arbustos. Luego, me lanza una mirada burlona. ¡Valiente idiota!)<new_line><new_line>¿Y qué desea saber, milord?]],
				['3'] = [[Es sobre Gena, ¿verdad? He visto que visitabas a su madre, pero no voy a decirte nada. He tenido que contarle la historia a todos al menos tres veces y estoy harto de eso.]],
				['2'] = [[(Vania silba.)<new_line><new_line>¿Un vagabundo? Pensaba que no quedaban idiotas que recorrieran solos el páramo.]],
				['9'] = [[(Vania está en casa y, evidentemente, no está solo. Entrar ahí ahora es lo último que quiero hacer.)]],
				['8'] = [[Escucha, eh, vagabundo, ahora mismo, Gena no va a ir a ninguna parte, pero esta priva no va a esperar. ¿Has cambiado de opinión?]],
				['7'] = [[¿Qué? ¡¿Y tú dices que eres ruso?!]],
				['6'] = [[(Vania se ríe de nuevo.)<new_line><new_line>¿De verdad vas a ir a Novomoskovsk a buscar un cadáver? (El joven se calla y luego me mira intrigado). Espera, vagabundo, tengo una idea: vayamos a mi casa. Me traje algo de alcohol de Novomoskovsk. La etiqueta dice «Bryanskaya», ¿has oído hablar de esa marca? El único problema es que no tengo a nadie con quien beberlo: solo hay mujeres por aquí.]]
			},
			['name'] = [[Vania]],
			['choice'] = {
				['1'] = [[Soy un vagabundo.]],
				['7'] = [[Gracias, en otro momento.]],
				['6'] = [[Bueno, la verdad es que no tengo ganas de beber contigo.]],
				['5'] = [[Quiero recuperar el cuerpo de Gena para darle un entierro digno.]],
				['4'] = [[Solo dime en qué edificio le dejaste.]],
				['3'] = [[Bueno, tendrás que hacerlo una vez más.]],
				['2'] = [[Pues mira, sí los hay. Quiero preguntarte una cosa.]]
			}
		},
		['svetlana'] = {
			['episode'] = {
				['14'] = [[(La puerta de la habitación de Svetlana está cerrada, pero parece que ella está adentro. Podría llamar, pero creo que quiere estar sola un rato.)]],
				['18'] = [[«Activar Día R el 17 de agosto según las instrucciones»... ¿Qué es esto? ¿De dónde lo has sacado?]],
				['12'] = [[Pero tendrás que trabajar por el bien de la comunidad. Aquí nos organizamos todos para arrimar el hombro y aún queda mucho trabajo por hacer. Nos vendrá bien otra persona que eche una mano.]],
				['22'] = {
					['1'] = [[Lo comprendo, pero tú tienes que entenderme a mí. No tengo parientes cercanos y ya no hay nadie que necesite la cura para la enfermedad. ¿Qué me queda? Sobrevivir. Mi objetivo en la vida es solo vivir un poco más. ¡Y ya estoy harta de esta existencia!]],
					['2'] = [[Pero podemos hacer una cosa: hallar la verdad. Descubrir quiénes son los responsables de la muerte de millones de personas y de convertir nuestro país, quizás incluso todo el mundo, en un páramo. Y, por supuesto, descubrir por qué lo hicieron. Eso es importante, ¿no? No solo para mí, sino para todos nosotros.<new_line>Ya sé que no tienes por qué hacerlo. Solo puedo pedirte que lo hagas y ofrecerte una recompensa.]]
				},
				['20'] = [[Era microbiólogo. Uno de los mejores del país. Trabajaba en Moscú, en el Instituto de Virología. Esa fue la última vez que lo vi: murió de un ataque al corazón en junio de 1984. Mi madre me lo dijo por teléfono y ni siquiera tuve la oportunidad de despedirme de él. Tampoco pude asistir al funeral, porque estaba guardando reposo por una amigdalitis.]],
				['30'] = {
					['1'] = [[(Svetlana habla aún más bajo.)<new_line><new_line>¡La epidemia! ¡La epidemia estalló al mismo tiempo, en diferentes ciudades, en extremos opuestos de la URSS! Ningún virus en la historia se ha propagado así. Una enfermedad contagiosa siempre se puede localizar, establecerse cuarentenas y adoptar medidas preventivas. Al menos, puedes intentarlo. Pero no con esta enfermedad.]],
					['2'] = [[Lo comprobé. El virus atacó a todos y en todas partes al mismo tiempo. No había forma de detenerlo.<new_line>Parece una locura, pero ¿puede que alguien iniciase deliberadamente la epidemia? ¿Infectando a personas en diferentes ciudades al mismo tiempo?]]
				},
				['28'] = [[Necesito descubrir la verdad. La verdad sobre la catástrofe.]],
				['10'] = [[¿No recuerdas nada? ¿Qué pasó?]],
				['9'] = [[Entiendo, pero... ¿por qué me buscabas?]],
				['8'] = [[(Svetlana frunce el ceño.)<new_line><new_line>Sí, efectivamente. ¿Pero cómo sabes todo esto?]],
				['7'] = [[¿En serio? (La mujer está asombrada). Es un viaje largo y peligroso. Bueno, entra y ponte cómodo. Por cierto, me llamo Svetlana.]],
				['6'] = [[(Svetlana se agarra la cabeza con las manos.)<new_line><new_line>No lo entiendes...]],
				['32'] = [[(Svetlana se levanta y me mira con una extraña furia en sus ojos.)<new_line><new_line>¡Lo sé! ¡Pero no es también una locura ir al páramo a buscar a tu familia!]],
				['42'] = [[Ya sabes, primeriza. ¿No lo has adivinado? ¡Que está embarazada!]],
				['34'] = {
					['1'] = [[(La sonrisa de Svetlana brilla ahora sin disimular su alegría.)]],
					['2'] = [[Voy a prepararme para el viaje. ¿Me esperarás un poco más?]]
				},
				['44'] = {
					['1'] = [[Vale. Ya veo que tienes algo que decirme. Adelante. ¿Has llegado a alguna conclusión? ¿Sobre lo que hablamos?]],
					['2'] = [[(Viendo el rostro concentrado pero feliz de Svetlana, titubeo, sin saber cómo empezar. Murmuro algo ininteligible. Mi voz se vuelve ronca de repente. Svetlana me da un vaso de agua. Lo vacío de un trago y tomo una decisión.)]]
				},
				['25'] = [[¿Seguro?]],
				['15'] = [[(Cuando despierto, Svetlana está a mi lado, de pie. Me mira atentamente, mordiéndose el labio con nerviosismo.)<new_line><new_line>¿Has cambiado de opinión? ¿Hay alguna posibilidad de que te quedes?]],
				['17'] = [[(Svetlana se calma y vuelve a bajar la voz).<new_line><new_line>Por otra parte, sí tenemos algo seguro. Los primeros fallecimientos por el brote datan del 2 o 3 de septiembre. Teniendo en cuenta el período de incubación, creo que esas personas se infectaron entre el 17 y el 19 de agosto. Eso significa...]],
				['13'] = [[(Svetlana guarda silencio, luego se levanta y camina hacia la ventana.)<new_line><new_line>¿Por qué no?]],
				['31'] = [[No lo sé. Llevo tratando de darle sentido desde que los bandidos me hicieron prisionera. He intentado encontrarle una explicación racional. Una vez aquí, en las afueras de Tver, pregunté a los demás refugiados. Miré en los periódicos, buscando artículos sobre la enfermedad...]],
				['21'] = [[(Svetlana aguanta la respiración.)<new_line><new_line>Entonces, ¿aceptas ayudarme?]],
				['11'] = [[(Svetlana me mira con genuina simpatía.)<new_line><new_line> ¡Eso es horrible! ¡Es un milagro que hayas llegado tan lejos! (sonríe amablemente). Pero eso ya pertenece a tu pasado. Nosotros nos encargaremos de ti. Te daremos una habitación y comida y, poco a poco, tus recuerdos volverán; estoy segura de ello.]],
				['29'] = [[Leíste mi diario, ¿verdad? En una de las páginas que perdí, anoté una extraña idea. Aún no sé cómo no caí antes en ello. Pero esa idea... me tiene sin dormir.]],
				['38'] = [[(Cuando llamo a la puerta de Svetlana, no abre de inmediato.<new_line>Hay una joven sentada en el pequeño sofá de la consulta. Cuando me ve, se pone colorada, le da las gracias a Svetlana y sale corriendo. Svetlana se ríe y me guiña un ojo.)<new_line><new_line>¡Probrecita, qué boba! Bueno, ¿qué hay de nuevo?]],
				['48'] = [[Vete, por favor. Tengo trabajo que hacer.]],
				['47'] = [[Sí, la verdad, pero lo que me estás contando es una asquerosa mentira, de principio a fin. No sé quién está detrás de ella, aunque espero que no seas tú, pero no pienso escuchar nada más.]],
				['41'] = [[(Svetlana se echa a reír de nuevo. Está de muy buen humor.)<new_line><new_line>No hagas caso. Sencillamente, está rara. Es su primera vez.]],
				['43'] = [[No es solo una alegría para ella, sino también para todos nosotros. Sobrevivimos a una epidemia y una guerra nuclear. ¡Luego nos pasamos años luchando contra el hambre! Pero hay un enemigo aún peor que el hambre: perder la esperanza. ¿Y sabes qué? Hoy ese enemigo no ha logrado ganar. Será el primer bebé en Dios sabe cuánto tiempo. ¡Significa que tenemos futuro!]],
				['33'] = [[(Cuando Svetlana me ve, sonríe por un segundo antes de reprimir el impulso y poner un semblante de fría indiferencia.)<new_line><new_line>Bueno, ¿cómo va la búsqueda de tu esposa y de tu hija?]],
				['45'] = [[(Cuando termino, noto que Svetlana mira fijamente a la pared detrás de mí. Esperaba alguna reacción, pero no esa. Muevo la cabeza, tratando de llamar su atención, pero no responde. Se produce un tenso silencio.)]],
				['35'] = [[Todo bien.<new_line><new_line>(El tono de Svetlana es frío y distante. Está claramente molesta.)]],
				['1'] = [[(La mujer levanta sus tristes ojos grises hacia los míos y sonríe cálidamente.)<new_line><new_line>Hola. Hace algún tiempo que no vemos caras nuevas. ¿Quién eres? ¿De dónde vienes?]],
				['46'] = [[(Svetlana se vuelve hacia mí. Sus ojos son completamente indiferentes, pero sus manos tiemblan.)<new_line><new_line>¿Y por qué me cuentas todo eso?]],
				['40'] = {
					['1'] = [[(Svetlana me sujeta repentinamente de la pechera de la camisa y me tira hacia atrás.)<new_line><new_line>¡Basta! ¡No digas ni una palabra sobre mi padre! ¡Era una persona maravillosa! Me quería, y amaba también a mi madre y a su país. Murió de un ataque al corazón, ¿te enteras? ¡Mi madre me escribió! Un infarto, ¿oyes?... Un infarto...]],
					['4'] = [[Gracias por descubrir la verdad. Siento haberte gritado. Tú no tienes la culpa de nada. Como dijiste: es mi dolor y mi problema. Solo míos<new_line>Gracias de nuevo y... adiós.]],
					['3'] = [[(Poco después, Svetlana se calma, se limpia la nariz con un pañuelo y se sienta en el sofá. Trato de sentarme a su lado pero ella levanta su mano y me pide que no lo haga. Asiento y me dirijo hacia la puerta. Ahora Svetlana tiene que replantearse muchas cosas y en eso no puedo ayudarla.)<new_line><new_line>Espera...]],
					['2'] = [[(Svetlana afloja su presa y comienza a sollozar. Un llanto amargo y triste que me causa un profundo dolor. Ella llora y yo, como un idiota, me limito a mirarla sin saber qué decir. En un momento dado, incluso comienzo a recriminarme en silencio no haber sido capaz de inventarme una mentira convincente en lugar de destrozar a Svetlana con la verdad.)]]
				},
				['39'] = [[No hay por qué ser tan formal. Al fin y al cabo, ahora somos como viejos amigos, ¿verdad?]],
				['5'] = [[(Svetlana me mira a los ojos un buen rato y luego aparta su mirada.)<new_line><new_line>Luché todo lo que pude. Creí que todavía podríamos hacer algo para enmendar lo que había salido mal...]],
				['4'] = [[(Svetlana me mira con tristeza.)<new_line><new_line>Bueno, cuando llegué aquí, también traté de encontrar a mis parientes y a otros supervivientes. Busqué la forma de ponerme en contacto con mis colegas, para obtener más información sobre esta maldita epidemia, sobre el bombardeo. Viajé de pueblo en pueblo, hasta que me quedé sin gasolina. Pero todo fue para nada. ¡Todos mis esfuerzos fueron en vano!]],
				['3'] = [[Claro, hay más asentamientos, pero ¿por qué iba a irme de Tver? Tenemos suficiente comida y los bandidos nos dejan en paz. ¡No seas idiota y quédate!]],
				['2'] = [[(Svetlana se da la vuelta.)<new_line><new_line>Mira por la ventana. Es un páramo. Primero la epidemia, luego la guerra nuclear... La Tierra ha muerto. No se puede sobrevivir ahí afuera. Radiación, depredadores, locos sin nada que perder, bandidos. No encontrarás a nadie ahí, pero si te vas no volverás. No intentaremos retenerte aquí, ¡solo reflexiona por un segundo!]],
				['36'] = [[(Svetlana me saluda con una sonrisa.)<new_line><new_line>¡Hola! ¿Y bien? ¿Alguna noticia?]],
				['37'] = [[Estupendamente. Si no te importa perder la esperanza.]],
				['16'] = [[(Svetlana asiente lentamente. Mi respuesta no la ha sorprendido. Pero ¿por qué está tan ansiosa?)<new_line><new_line>Lo siento por ti, vagabundo. Las personas como tú son las primeras en morir. Gente que ama, que se arriesga para salvar a otros. Creía que toda la gente como tú había muerto en el 84, que los únicos que quedaron fueron los bandidos y las personas como yo, los que dejaron de preocuparse por completo.]],
				['19'] = [[No, no lo sé, pero he escuchado antes ese nombre. A mi padre. Cuando visitó el instituto, habló con alguien por teléfono y mencionó el «Día R». Le pregunté qué significaba, pero se salió por la tangente haciendo un chiste. El caso es que me pareció importante recordarlo.]],
				['23'] = [[Voy a prepararme para el viaje. ¿Me esperarás un poco más?]],
				['24'] = [[(Svetlana frunce el ceño, luego se vuelve y camina hacia la ventana.)<new_line><new_line>Entonces, regresa cuando encuentres el tiempo.]],
				['26'] = [[(Svetlana sonríe extrañamente.)<new_line><new_line>¿Y qué? No arriesgo nada. Bueno, pues tú mismo: si quieres irte, vete. Pero ya que has decidido marcharte...<new_line><new_line>(Hace una pausa para pensar, eligiendo sus palabras.)]],
				['27'] = [[(Svetlana baja la voz, como si temiera que alguien la oyera.)<new_line><new_line>Hay una cosa que puedes hacer. Empecé a hacerlo hace mucho tiempo, pero nunca llegué a terminarlo. No pude, me rendí a medio camino (Svetlana hace una mueca). ¡Pero tú podrías hacerlo!]]
			},
			['name'] = [[Svetlana]],
			['choice'] = {
				['14'] = [[No, tengo que irme]],
				['24'] = [[Svetlana, es una mala idea. El páramo es demasiado peligroso para alguien que no está debidamente preparado. No puedo protegerte y dejarás sin médico a la gente de aquí. Es mejor que te quedes.]],
				['12'] = [[Svetlana, yo...]],
				['22'] = [[Está bien, veré lo que puedo hacer.]],
				['20'] = [[Lo intentaré, pero no te prometo nada. Mi principal prioridad es encontrar a mi esposa y a mi hija.]],
				['30'] = [[Svetlana, eso es una locura. ¿Quién iba a querer algo así? ¿Qué obtendría con ello?]],
				['28'] = [[No lo pillo. ¿Qué más tienes que descubrir? Tú lo sabes todo. Estalló la epidemia, luego hubo una guerra nuclear...]],
				['10'] = [[Busco supervivientes, gente que pueda contarme la verdad sobre lo que sucedió. En 1984, quiero decir. Ya ves, no me acuerdo de nada... Hasta he olvidado mi propio nombre.]],
				['9'] = [[Te estaba buscando. Encontré tu grabadora en Leningrado y también estuve en Nóvgorod, en la base de los bandidos...]],
				['8'] = [[De Leningrado, ¿verdad? Tú eres la Svetlana que descubrió la cura para la epidemia, la que fue capturada por los bandidos, escapó e hizo todo el camino a pie hasta Tver.]],
				['7'] = [[¿Que quién soy? Un vagabundo. Vengo del norte.]],
				['6'] = [[Probablemente, no. De todos modos, ha sido un placer conocerte, Svetlana. Espero volver a verte algún día... (Sales)]],
				['32'] = [[Seguro.]],
				['42'] = [[Sí, estoy de acuerdo.]],
				['34'] = [[Despacio, pero va... Solo he venido a ver cómo estabas.]],
				['44'] = [[¿Svetlana?]],
				['25'] = [[Perdona, Svetlana, pero no me pareces alguien que haya dejado de preocuparse. Intentas salvarme, después de todo.]],
				['15'] = [[(Guardas silencio)]],
				['23'] = [[No te lo tomes a mal, pero... no estoy seguro de tener tiempo para esto...]],
				['13'] = [[Svetlana, gracias. Gracias por el ofrecimiento, de verdad, pero no puedo quedarme aquí con vosotros.]],
				['31'] = [[Svetlana, aunque tuvieras razón y la epidemia la provocase el hombre, e incluso si se tratase de una especie de conspiración... ¿cómo ibas a descubrir la verdad? Han pasado muchos años. Es una locura.]],
				['21'] = [[No, espera un segundo. No he decidido nada todavía. Al fin y al cabo, mi prioridad es encontrar a mi esposa y a mi hija.]],
				['11'] = [[Precisamente ese es el problema: que no lo sé. Me desperté en Níkel, sin saber quién era, de dónde era ni cómo había llegado allí. No había más que cadáveres podridos y ruinas por todas partes, así que empecé a buscar respuestas. Quería encontrar gente que pudiera darme explicaciones, pero nadie sabe qué ocurrió ni por qué.]],
				['29'] = [[No te sigo.]],
				['47'] = [[Adiós (te vas cerrando silenciosamente la puerta tras de ti).]],
				['46'] = [[Svetlana, escucha... Recuerdo que tu madre te escribió contándote que tu padre había sufrido un ataque al corazón, pero quizá no quería...]],
				['41'] = [[Ah, era eso...]],
				['43'] = [[(Le dices todo)]],
				['33'] = [[Para serte sincero, la verdad es que no. He estado pensando... Supongo que me dirigiré a Moscú, al Instituto de Virología. Buscaré alguna pista por allí. ¿Quién sabe? Tal vez sirva para avanzar en mi propia búsqueda.]],
				['45'] = [[¿Cómo que por qué? Tú eras la que quería averiguar la verdad.]],
				['35'] = [[Hola. No, aún nada. ¿Y tú qué tal?]],
				['1'] = [[Tengo esposa y una hija. No sé dónde están, pero estoy convencido de que están vivas y esperándome. Tengo que dar con ellas.]],
				['40'] = [[Su primera vez ¿de qué?]],
				['39'] = [[Sí, claro. El caso es que... eh... ¿por qué me tenía tanto miedo esa chica?]],
				['38'] = [[Tu padre...]],
				['5'] = [[¿Pero no estás haciendo ya algo? Llevas una bata de doctora. Cuidas a la gente. No te has dado por vencida.<new_line>Y... ¿te rendiste cuando los bandidos te hicieron prisionera? No, les envenenaste, ¡aun arriesgando tu vida en ello!]],
				['4'] = [[Lo lamento de veras y sé que solo tengo una pequeña posibilidad, pero es una posibilidad al fin y al cabo. Seguiré buscando. Si tengo que hacerlo, iré más allá de los Urales, a otros países...]],
				['3'] = [[Svetlana, todo lo que dices es verdad, pero... si estuvieras en mi lugar, ¿no irías a buscar a tu familia sin importar lo peligroso que fuera?]],
				['2'] = [[Tienes razón, pero he de intentarlo. No puedo abandonar a mi familia. Y, al fin y al cabo, bueno, probablemente haya otros asentamientos. Voy a seguir buscando hasta descubrir qué ha pasado.]],
				['37'] = [[Hola de nuevo, Svetlana.]],
				['36'] = [[Nos vemos.]],
				['16'] = [[17 de agosto... Espera... He visto antes esa fecha en alguna parte... (muestras a Svetlana el telegrama del hombre muerto en Nóvgorod).]],
				['17'] = [[Estaba en un cadáver en Nóvgorod. Pero hay más. Encontré un bote de espray que también tenía escrito en él «Día R».<new_line>¿Sabes lo que significa?]],
				['18'] = [[Muy interesante. ¿Quién era tu padre?]],
				['19'] = [[En el Instituto de Virología, ¿dices? ¿En Moscú? Esta es mi única pista, así que me parece que me voy a Moscú.]],
				['26'] = [[¿Sí? Te escucho.]],
				['27'] = [[Vale, y ¿qué es?]]
			}
		}
	},
