	['barQuest'] = {
		['passenger'] = {
			['name'] = [[Pasajera]],
			['step'] = {
				['1'] = [[Uno de los habitantes locales me contrató para que la llevara sana y salva a otra base.]],
				['3'] = [[No pude proteger a mi acompañante. Murió antes de llegar a su destino.]],
				['2'] = [[Mi acompañante logró llegar a su destino sin daño.]]
			},
			['text'] = [[«¡Hola! ¿Puedes llevarme al próximo asentamiento? Viajar sola por los páramos es peligroso, y pareces un compañero de fiar».]]
		},
		['gigahive_ufa'] = {
			['step'] = {
				['1'] = [[Tengo que ir hasta Izhevsk y preguntarle al tabernero sobre la colmena de Ufa.]],
				['3'] = [[He matado a la abeja reina. Ahora tengo que llevarle la cabeza al tabernero en Izhevsk para recoger mi recompensa.]],
				['2'] = [[Hay una colmena en Ufa gobernada por una abeja reina. Para poder llegar hasta allí, me hará falta un antídoto. Para eso tendré que matar a algunas abejas madre y llevar la cera de vuelta al especialista de Izhevsk para elaborarlo. Además, el tabernero me ha prometido una recompensa si le llevo la cabeza de la reina.]]
			},
			['name'] = [[Abejas de las malas]]
		},
		['undertaker'] = {
			['name'] = [[El enterrador]],
			['text'] = [[Se han vuelto a alzar los muertos de otro cementerio. Los zombis ya se han calmado, pero necesitan ataúdes nuevos. Llévate al enterrador contigo. Encuentra los materiales y él construirá los ataúdes. Luego llévalos al lugar adecuado.]],
			['step'] = {
				['1'] = [[Tengo que encontrar materiales y pedirle al enterrador que construya ataúdes.]],
				['2'] = [[Tengo que llevar los ataúdes nuevos al cementerio en ruinas.]]
			}
		},
		['intelligence'] = {
			['name'] = [[Datos de reconocimiento]],
			['step'] = {
				['1'] = [[Los científicos de Progreso me han encargado que consiga los datos de reconocimiento de la base militar.]],
				['2'] = [[He conseguido los datos de reconocimiento, toca volver al asentamiento.]]
			},
			['text'] = [[«Hemos descubierto una base militar conectada al proyecto EMBA. Deberían tener datos sobre las instalaciones secretas de esta región. Podríamos usar estos datos para averiguar dónde están los documentos secretos necesarios para el proyecto».]]
		},
		['new_year_tree_quest'] = {
			['name'] = [[Entrega los árboles de Navidad]],
			['step'] = {
				['1'] = [[Tengo que recoger unos árboles de Navidad y entregarlos en el asentamiento. Esto debería levantarle el ánimo a todo el mundo este duro invierno.]],
				['2'] = [[Ya tengo los árboles, pero ahora tengo que llevarlos al asentamiento.]]
			},
			['text'] = [[«¡Debería haber un árbol de Navidad en cada casa en invierno! Las fiestas son uno de los pocos placeres que nos quedan por aquí. Ve a visitar a los leñadores, nos tienen algunas cosas guardadas».]]
		},
		['forester'] = {
			['name'] = [[Leñador]],
			['text'] = [[Vino un tipo y dejó un encargo para ti. Hay un leñador que necesita tu ayuda. Parece una tarea fácil con una recompensa decente. Yo si fuera tú, iría.]],
			['step'] = {
				['1'] = [[Alguien ha dejado un encargo personal para mí. Tengo que ir a ayudar a un leñador. Me ha prometido una buena paga.]]
			}
		},
		['gift_delivery'] = {
			['name'] = [[Entrega de regalos]],
			['text'] = [[Hay que entregar los regalos cueste lo que cueste. ¿Podrías repartir estas cajas? Esa pobre gente necesita más apoyo que nunca.]],
			['step'] = {
				['1'] = [[Me han pedido que entregue unos regalos.]]
			}
		},
		['winter_queen'] = {
			['text'] = [[¡Hay que salvar a Ded Moroz! Usa la estrella polar cuando lo tengas todo listo. Iremos al palacio de hielo y le plantaremos cara la reina del invierno.]],
			['name'] = [[La reina del invierno]]
		},
		['progress_kurgan'] = {
			['step'] = {
				['1'] = [[Sorprendentemente, ya estaba familiarizado con la gente de Kurgán. Resulta que son ellos quienes dirigen todo el sistema ferroviario. Si un hombre de Kurgán no está manejando un ferrocarril, es porque lo está arreglando. Así que resulta evidente con quién hay que evitar los altercados.<new_line><new_line>Sus trenes blindados son prácticamente invulnerables a los ataques de los bandidos y de los mutantes, pese a que en los páramos puede pasar cualquier cosa. Por eso también es importante para Kurgán mantener las buenas relaciones con sus socios, aunque solo sea por el combustible y los servicios de mantenimiento de sus trenes.]]
			}
		},
		['progress_gorenichi'] = {
			['step'] = {
				['1'] = [[Durante la catástrofe, las tierras aledañas fueron objetivo de bombardeos masivos, pero algunas zonas de tierra negra fértil sobrevivieron.<new_line>Los lugareños encontraron estas zonas de tierra y comenzaron a cultivarlas.<new_line><new_line>Entonces, apareció una nueva amenaza, el legado de la radiación: oleada tras oleada de criaturas mutantes sitiaron el asentamiento. Sin embargo, ahora, los lobos negros se han establecido en la cúspide de la cadena alimenticia y han diezmado a la competencia. Pero las fuerzas de los supervivientes humanos se están desvaneciendo bajo su feroz ataque.]]
			}
		},
		['exp_recovery'] = {
			['step'] = {
				['1'] = [[En el umbral de la muerte, perdí el conocimiento y, milagrosamente, me salvé. Pero no recuerdo nada de los acontecimientos anteriores. Para recuperar la memoria, tengo que regresar al lugar en el que sucedió todo.]],
				['2'] = [[He recuperado la memoria.]]
			},
			['name'] = [[Inconsciente]]
		},
		['halloween_boss_2022_3'] = {
			['text'] = [[El Conde Drácula es un antiguo vampiro, conocedor de las artes y aficionado a la compañía de damas hermosas. La gente dice que es galante y educado, pero eso no es óbice para que cace a la gente y se dé un festín con su sangre.]],
			['step'] = {
				['1'] = [[Hay noticias de que unos vampiros atacaron a un grupo de viajeros. Tengo que ir al lugar de los hechos.]],
				['3'] = [[Me percaté de que era parte del menú del Conde Drácula. Me invitó a relajarme y a disfrutar, pero después de eso las cosas se pusieron feas.]],
				['2'] = [[Encontré a una superviviente del ataque de los vampiros. Primero tengo que escoltarla hasta su casa, luego le pediré algunos detalles de la emboscada que les tendieron.]]
			}
		},
		['catch_thief'] = {
			['name'] = [[Atrapa al ladrón]],
			['text'] = [[«El encargado del almacén se queja de que hay ladrones. Él no puede atraparlos a todos por su cuenta, ve a ayudarlo».]],
			['step'] = {
				['1'] = [[Lo primero es lo primero: tengo que ir al almacén y hablar con el encargado.]],
				['5'] = [[Hice un trato con los bandidos y los dejé entrar en el almacén. Una vez allí, los maté a todos.]],
				['4'] = [[He logrado capturar al ladrón con vida.]],
				['3'] = [[El ladrón cayó en mi trampa y ha muerto.]],
				['2'] = [[He eliminado el campamento de bandidos que estaban robando nuestra base. A partir de ahora todo debería ir como la seda.]],
				['9'] = [[El capataz del almacén me ha pedido otro favor: ocuparme de los cómplices del ladrón.]],
				['8'] = [[El capataz del almacén me ha pedido otro favor: traer de vuelta lo que saquearon.]],
				['7'] = [[Como sé que la culpa le caerá a los ladrones, he cogido un par de cosas del almacén para mí.]],
				['6'] = [[No tengo habilidad para ser guardia. El ladrón ha logrado escapar.]],
				['10'] = [[Tengo que devolver lo que saquearon al almacén.]]
			}
		},
		['cold_footprints'] = {
			['text'] = [[Las liebres han encontrado el campamento de los forajidos de la nieve que saquearon el térem, así que te lo marcaré en el mapa. Ve y averigua lo que puedas.]],
			['name'] = [[Un rastro helado]]
		},
		['halloween_boss_2022_4'] = {
			['text'] = [[El Rey Calabaza es el espíritu de la temporada de cosecha. Cuida de sus súbditos calabazas y se asegura de que la gente que trabaja la tierra no dañe la naturaleza. El Rey Calabaza es un espíritu, sí, pero también es una persona de sangre noble, por lo que solo la Espada del Verdugo puede hacerle daño.]],
			['step'] = {
				['1'] = [[Un granjero afirma que su cosecha ha desaparecido. Parece obra del Rey Calabaza, pero habría que investigarlo.]],
				['3'] = [[Tengo que derrotar al Rey Calabaza. El contrato decía que solo la Espada del Verdugo puede hacerle daño.]],
				['2'] = [[Voy tras la pista. Los fugitivos lograron escapar... Debo seguirlos.]]
			}
		},
		['flying_dutchman'] = {
			['name'] = [[El Holandés Errante]],
			['text'] = [[¿Y dices que el libro no se puede encontrar en tierra firme? Si hacemos caso a los rumores, hace poco que el Holandés Errante regresó a nuestras aguas. ¿Coincidencia? Lo dudo. Si estás listo a meter la cabeza en la madriguera del conejo, toma esta campana. Hazla sonar en la costa para revelar un camino hasta el barco.]],
			['step'] = {
				['1'] = [[El tabernero me ha dado una campana que me transportará al barco fantasma donde puede que Igor se oculte con el libro.]]
			}
		},
		['progress_olonec'] = {
			['step'] = {
				['1'] = [[El primer asentamiento poblado de mi viaje. Es muy estresante encontrarse de nuevo entre la gente. Aquí todos se han acostumbrado a confiar en los demás y en sus propios recursos para hacer frente a las adversidades. Aquí probablemente vivan de la misma forma que hace un siglo.<new_line><new_line>Los pescadores no van más allá del lago de Ládoga, y dudo que aquí encuentre respuestas a todas mis preguntas, pero es un buen lugar en el que conseguir información y prepararse para continuar viajando.]]
			}
		},
		['drakula'] = {
			['name'] = [[Conde Drácula]],
			['text'] = [[Cabeza de calabaza se ha enfrentado con el Anciano en el páramo. Drácula debe de andar cerca de allí. Encuéntralo y destrúyelo antes de que haga algo.]],
			['step'] = {
				['1'] = [[Solo falta la batalla final y mi adversario es el Conde Drácula.]]
			}
		},
		['comrade_in_trouble'] = {
			['name'] = [[Camarada en peligro]],
			['step'] = {
				['14'] = [[El explorador se quedó sin gasolina. Debería volver al asentamiento e informar.]],
				['18'] = [[Lleva la caja restante al asentamiento.]],
				['17'] = [[Al explorador le llevó mucho tiempo recoger todo el botín y ahora se dirige a casa. Debería volver al asentamiento e informar.]],
				['13'] = [[El explorador se quedó sin gasolina. Le di algo de combustible. Debería volver al asentamiento e informar.]],
				['20'] = [[Los bandidos siguieron al explorador hasta una casa medio en ruinas, pero los está manteniendo a raya. Debería volver al asentamiento e informar.]],
				['30'] = [[El explorador está muerto. Debería volver al asentamiento e informar.]],
				['11'] = [[Los depredadores atacaron al explorador. Debería volver al asentamiento e informar.]],
				['10'] = [[Protegí al explorador de los depredadores. Debería volver al asentamiento e informar.]],
				['9'] = [[Salva al explorador sin ayuda.]],
				['8'] = [[El explorador recibió una dosis de radiación. Debería volver al asentamiento e informar.]],
				['7'] = [[El explorador recibió una dosis de radiación. Le di medicamentos. Debería volver al asentamiento e informar.]],
				['6'] = [[Haz fuego en el campamento.]],
				['1'] = [[Tengo que averiguar qué pasó con el explorador desaparecido. El jefe de la guardia me indicó en el mapa dónde encontrarlo.]],
				['5'] = [[La respuesta era simple: el explorador se fue de juerga. Debería volver al asentamiento e informar.]],
				['4'] = [[La respuesta era simple: el explorador se fue de juerga. Eché algo de leña a su fuego por si acaso. Debería volver al asentamiento e informar.]],
				['3'] = [[El explorador se detuvo para pintar al aire libre. Menos mal que no se metió en problemas. Debería volver al asentamiento e informar.]],
				['2'] = [[El explorador está sano y salvo. Debería volver al asentamiento e informar.]],
				['31'] = [[Lleva el cuerpo del explorador al asentamiento.]],
				['29'] = [[El explorador está muerto. Decidí llevar su cuerpo a casa.]],
				['26'] = [[El explorador estaba herido. Debería volver al asentamiento e informar.]],
				['25'] = [[El explorador estaba herido. Le proporcioné primeros auxilios. Debería volver al asentamiento e informar.]],
				['23'] = [[El explorador se quedó atrapado en una vieja casa cuando se derrumbó. Debería volver al asentamiento e informar.]],
				['22'] = [[El explorador se quedó atrapado en una vieja casa cuando se derrumbó. Lo ayudé a salir de allí. Debería volver al asentamiento e informar.]],
				['19'] = [[El explorador hizo una barricada para protegerse de los bandidos en una casa medio en ruinas. Me encargué de la situación. Debería volver al asentamiento e informar.]],
				['16'] = [[Al explorador le llevó mucho tiempo recoger todo el botín y ahora se dirige a casa. Accedí a entregar la caja restante. Debería volver al asentamiento e informar.]]
			},
			['text'] = [[Se suponía que uno de nuestros muchachos regresaría ayer de una expedición, pero no ha dado señales. Ve a comprobar si le ha ocurrido algo y si necesita ayuda.]]
		},
		['lair'] = {
			['text'] = [[Me han llegado informes de que alguna bestia ha construido una guarida por aquí cerca. Encárgate de ella.]],
			['step'] = {
				['1'] = [[Tengo que encontrar la guarida de la criatura y, si parece hostil, acabar con ella.]]
			}
		},
		['cover_the_fighter'] = {
			['name'] = [[Proporcionar cobertura]],
			['step'] = {
				['1'] = [[El explorador ha encontrado un lugar demasiado peligroso como para acercarse solo. Tengo que ayudarlo por si lo atacan.]],
				['3'] = [[Fuerza la caja fuerte que al explorador no le dio tiempo a abrir.]],
				['2'] = [[Conseguimos acceder al lugar y el explorador está razonablemente seguro. Puedo volver al asentamiento.]]
			},
			['text'] = [[El explorador encontró un lugar abandonado, pero explorar solo un lugar como ese es peligroso. Proporciónale cobertura.]]
		},
		['progress_ryazan'] = {
			['step'] = {
				['1'] = [[«Tienes que ver Riazán antes de morir», es el chiste de mal gusto que se cuenta a cientos de kilómetros alrededor del asentamiento. Hace tiempo, un clan de bandidos tomó el control de la refinería de petróleo e inició una actividad comercial de combustible con su propia moneda: el rublo negro. Pronto, alrededor de la refinería, creció todo un pueblo comercial.<new_line><new_line>Ahora este es el centro de comercio de los páramos. Pero sus entrañas criminales no se encuentran lejos de la superficie: el engaño, la violencia y el desenfreno asoman tras los letreros brillantes y el lujo de los casinos.]]
			}
		},
		['destroy_lair'] = {
			['name'] = [[Destruye la guarida]],
			['step'] = {
				['1'] = [[Tengo que encontrar la guarida y destruir a sus moradores.]],
				['2'] = [[He logrado destruir la guarida.]]
			},
			['text'] = [[El jefe de la guardia señaló el mapa que tenía colgado tras él. «Esta zona está infestada de bestias peligrosas. Ve y destruye su guarida».]]
		},
		['ambush'] = {
			['name'] = [[Emboscada]],
			['text'] = [[«¡Oye, tú! Nuestros muchachos están planeando una emboscada. ¿Por qué no vas a ayudarlos? Tal vez te lleves parte del pastel».]],
			['step'] = {
				['1'] = [[En el mapa está marcado el lugar en el que se planea tender la emboscada. Tengo que ir allí.]],
				['6'] = [[La emboscada ha sido un fracaso.]],
				['5'] = [[La emboscada ha salido bien, pero hemos sufrido algunas bajas.]],
				['4'] = [[He demostrado mi valía en la batalla y he ayudado a los combatientes heridos.]],
				['3'] = [[La emboscada fue todo un éxito.]],
				['2'] = [[Es hora de volver a la base para informar sobre la emboscada.]]
			}
		},
		['take_stash'] = {
			['name'] = [[Hazte con el alijo]],
			['text'] = [[«Oye, camarada, tengo un encargo aquí para ti. Bueno, no exactamente aquí... Está enterrado junto a la carretera. ¿Podrías traérmelo?».]],
			['step'] = {
				['1'] = [[En el mapa está marcado el lugar en el que está escondido el alijo. Tengo que ir allí y desenterrar la caja.]],
				['6'] = [[Alguien del asentamiento la fastidió y escondió mal el alijo, así que los bandidos se lo llevaron. Tengo que volver e informar de esto.]],
				['5'] = [[Recupera lo que robaron los bandidos.]],
				['4'] = [[Tras registrar un poco la caja durante el camino de vuelta, decidí quedármela para mí.]],
				['3'] = [[Cuando volví a la base, le entregué la caja al tabernero.]],
				['2'] = [[He encontrado el lugar y desenterrado la caja. La llevaré a la base y ya veré qué hacer cuando llegue.]]
			}
		},
		['halloween_boss_2022_6'] = {
			['name'] = [[Burro Parlante]],
			['text'] = [[Hay un Burro Parlante aterrorizando a la población local: está devorando sus cosechas, intentando vender monedas falsas y ya ha hablado a tres granjeros hasta matarlos.<new_line>Los aldeanos ya habrían usado sus horcas para hacer justicia con el Burro hace tiempo, pero una criatura que vive en el pantano cercano protege al Burro.]],
			['step'] = {
				['1'] = [[El Burro se esconde en el pantano. Tengo que dar con él, a ser posible sin toparme con la bestia que lo protege.]],
				['3'] = [[La Parca me tendió una trampa con un contrato y ahora quiere hacerse con mi alma. Descubrí que mi Gigavatiador puede cerrar sus portales.<new_line>Tengo que derrotar a la Parca en batalla.]],
				['2'] = [[El Burro logró escapar y la bestia del pantano que lo protege podría aparecer en cualquier momento. ¡Rápido, no hay mucho tiempo!]]
			}
		},
		['progress_magnitogorsk'] = {
			['step'] = {
				['1'] = [[Durante el bombardeo de los Urales, quienes hoy lideran el Frente férreo se refugiaron en las minas de Magnitogorsk. Ahora mucha gente trabaja en esas minas, pero muchos de ellos no llevan a cabo ese agotador trabajo voluntariamente.<new_line><new_line>Seguro que soy lo bastante fuerte como para instalarme aquí como socio y no como mano de obra gratuita.]]
			}
		},
		['ice_brigands_camp'] = {
			['name'] = [[Campamento de forajidos]],
			['text'] = [[Ha aparecido otro campamento de forajidos. Tendrás que hacerles una visita antes de que empiecen a causar problemas.]],
			['step'] = {
				['1'] = [[Hay que destruir el campamento de los forajidos de la nieve.]]
			}
		},
		['place_stash'] = {
			['name'] = [[Oculta el alijo]],
			['text'] = [[«Toma esta caja. ¿Ves esta cruz en el mapa? Entiérrala allí, pero que ni se te ocurra abrirla, ¿está claro?».]],
			['step'] = {
				['1'] = [[Tengo que ir a la ubicación señalada y esconder la caja.]],
				['4'] = [[He escondido la caja. Con suerte, el destinatario podrá encontrarla.]],
				['3'] = [[Ya he cumplido con mi trabajo. He escondido la caja exactamente donde me pedían. Ya puedo volver a la base.]],
				['5'] = [[¿Qué sentido tiene todo esto? Me voy a quedar la caja para mí.]]
			}
		},
		['new_year_boss_2022_2'] = {
			['step'] = {
				['1'] = [[Tengo que cavar un hoyo. Papá Cavernoel podría estar planeando tender una trampa.]],
				['5'] = [[Estamos tras la pista del Papá Noel desaparecido. La sabandija que escapó de la emboscada podría llevarnos a nuestro objetivo. Será mejor que lo sigamos.]],
				['4'] = [[Descubrimos que Papá Noel se había convertido en Krampus Noel, y su banda ahora está destruyendo todo lo relacionado con la Navidad. Tenemos que encontrar a Papá Noelgocios para decidir qué hacemos ahora.]],
				['3'] = [[Estamos tendiendo una trampa, y se supone que yo soy el cebo.]],
				['2'] = [[Tengo que tapar el hoyo de Papá Cavernoel con hierba.]]
			}
		},
		['hunt'] = {
			['name'] = [[Cacería]],
			['text'] = [[«Nos acaba de llegar un pedido de carne. ¿Quieres irte de caza?».]],
			['step'] = {
				['1'] = [[Me han pedido que vaya a por carne. Tengo que salir de caza.]],
				['4'] = [[No he traído carne suficiente y el cliente se ha negado a pagarme.]],
				['3'] = [[Todo ha salido bien y el cliente está feliz.]],
				['2'] = [[Tengo que llevarle la carne al cliente.]]
			}
		},
		['search_killer'] = {
			['name'] = [[Encuentra al asesino]],
			['text'] = [[El asesino se escabulló a través del portal al mundo superior. Tienes que encontrarlo y detenerlo.]],
			['step'] = {
				['1'] = [[Tengo que seguir el rastro del asesino y detenerlo. Está en algún lugar del mundo superior.]]
			}
		},
		['firebird_feather'] = {
			['text'] = [[Cuentan las leyendas que a las aves de fuego les encantan las manzanas doradas. No tenemos ninguna, pero les he hecho un conjuro a unas normales y corrientes. Si las cubres de miel, el ave no notará la diferencia y seguro que se te acerca.]],
			['name'] = [[La pluma del ave de fuego]]
		},
		['provision'] = {
			['name'] = [[Suministros]],
			['step'] = {
				['1'] = [[Uno de los trabajadores de Progreso me ha pedido que consiga provisiones para la expedición.]],
				['2'] = [[He reunido todo lo necesario, toca darle las provisiones a los archivistas del asentamiento.]]
			},
			['text'] = [[«Los exploradores van a buscar instalaciones conectadas con el proyecto EMBA. Ayúdalos consiguiendo provisiones para la operación».]]
		},
		['set_up_devices'] = {
			['name'] = [[Instalación del equipo]],
			['step'] = {
				['1'] = [[Progreso está observando la zona que rodea al asentamiento. Tengo que instalar el equipo en el exterior de la base.]],
				['3'] = [[He instalado y ocultado el equipo.<new_line>El paquete biomutante que me podría haber detectado es historia y los dispositivos ya están a salvo. Debo informar de esto al trabajador de Progreso.]],
				['2'] = [[He instalado y ocultado el equipo. Debo informar de esto al trabajador de Progreso.]]
			},
			['text'] = [[«Estamos estudiando el comportamiento de los biomutantes y el modo en que se propagan las esporas. Instala este equipo en el asentamiento. La información que reunamos será útil para la investigación del proyecto EMBA».]]
		},
		['traveler_salvation'] = {
			['name'] = [[Al rescate]],
			['text'] = [[Una ardilla ha dicho que hay un hombre muriéndose de frío en el bosque, tienes que ir a ayudarlo cuanto antes. Espero que siga vivo...]],
			['step'] = {
				['1'] = [[Un hombre está a punto de morir de frío en algún rincón del bosque. Tengo que ir a rescatarlo.]],
				['3'] = [[condición de viajeros ha mejorado. Ahora tengo que llevarlo a un lugar seguro.]],
				['2'] = [[El hombre es muy frío y agotado, que necesita ayuda.]]
			}
		},
		['progress_arzamas'] = {
			['step'] = {
				['1'] = [[Un hombre deambuló por los páramos durante muchos años. Finalmente, cuando se desprendió de las cadenas de su mundana vida, una zarza ardiente de hierbas sagradas apareció ante él. Envolviendo al hombre en humo, la zarza lo llamó Profeta y le ordenó que creara un Nuevo Paraíso.<new_line><new_line>Eso es lo que cuentan los residentes del antiguo monasterio de Arzamas. Nadie sabe a ciencia cierta qué hacen allí. Unos pocos miembros de la comunidad están en contacto con el mundo exterior y hacen algunos envíos a Riazán, pero eso es todo lo que se sabe.]]
			}
		},
		['camp_defence'] = {
			['name'] = [[Defensa del campamento]],
			['text'] = [[«Los bandidos se han reunido y vienen a atacar nuestro campamento. Hazme un favor y ve a ayudar a defenderlo».]],
			['step'] = {
				['1'] = [[Tengo que ir lo antes posible al campamento de nuestros aliados y ayudarlos a defenderlo.]],
				['6'] = [[Cuando llegué allí, ya era demasiado tarde.]],
				['5'] = [[Hemos logrado ganar, pero no sin perder a unos cuantos de los nuestros.]],
				['4'] = [[Bueno, hemos ganado, pero ha habido bajas.]],
				['3'] = [[Logramos superar con facilidad a los bandidos y defender el campamento.]],
				['2'] = [[He hecho todo lo que he podido. Es hora de volver a la base e informar de lo sucedido en el campamento.]]
			}
		},
		['new_year_deliver_cloth'] = {
			['name'] = [[Entrega la ropa]],
			['step'] = {
				['1'] = [[He decidido entregar ropa de abrigo a los que trabajan fuera del asentamiento.]],
				['2'] = [[Tengo que llevar las cosas a otro grupo de habitantes que se fueron por otros asuntos.]]
			},
			['text'] = [[Los niños han tejido gorros y calcetines abrigados para la gente que trabaja en el frío exterior del asentamiento. ¿Puedes llevarles un par de paquetes?]]
		},
		['science_polygon'] = {
			['name'] = [[Zona de prueba]],
			['step'] = {
				['1'] = [[Tengo que ayudar a los científicos de Progreso a lidiar con los biomutantes.]]
			},
			['text'] = [[«Estamos llevando a cabo experimentos en la zona de prueba para comprender mejor el proyecto EMBA. No obstante, tenemos problemas con los biomutantes que nos atacan. ¿Podrías ayudarnos a acabar con la amenaza, tú que tienes experiencia en combate?».]]
		},
		['wood_delivery'] = {
			['name'] = [[Entrega de madera]],
			['text'] = [[Un montón de leña puede salvarle la vida a alguien en una ola de frío como esta. Ve a por madera y déjala en un sitio a la vista de todos.]],
			['step'] = {
				['1'] = [[La doncella de las nieves me ha pedido que vaya a por madera y se la dé a la gente.]]
			}
		},
		['new_year_boss_2022_4'] = {
			['step'] = {
				['1'] = [[Tenemos que conseguir que se le pase la borrachera a Cibernoel. Necesita agua.]],
				['4'] = [[Supongo que hemos cumplido la misión. Tengo que alcanzar a Cibernoel y averiguar qué ha descubierto.]],
				['3'] = [[Me vi envuelto en las confusas correrías de Cibernoel. Ahora tengo que conectar un cable a alguna parte y esperar a que llame.]],
				['2'] = [[Cibernoel nos ayudará a descubrir más sobre la superarma. Tengo que irrumpir en el lugar donde guardan la información.]]
			}
		},
		['polar_star'] = {
			['text'] = [[La esfera que has encontrado es la estrella polar. Ded Moroz la usaba en los viejas largos, pero tendrás que descubrir cómo funciona sin ayuda. Coge la estrella, ve al páramo en una noche despejada y colócala en dirección al cielo.]],
			['name'] = [[Estrella polar]]
		},
		['new_year_boss_2022_6'] = {
			['step'] = {
				['1'] = [[Me deshice del malvado Escarchín. Pero, ¿de verdad ocurrió un milagro navideño? ¿Ha regresado Papá Noel?]],
				['2'] = [[En la batalla final, yo asumiré el papel principal. ¡Vamos allá!]]
			}
		},
		['new_year_boss_2022_5'] = {
			['step'] = {
				['1'] = [[Hemos hecho todo lo posible antes de la lucha contra Krampus Noel y su superarma. Ha llegado la hora de enfrentarse a él.]],
				['2'] = [[Derrotamos a Krampus Noel, pero el malvado Escarchín se apoderó de la energía mágica que liberamos. Tengo que hablar con mis compañeros Noeles para ver qué hacemos ahora.]]
			}
		},
		['progress_tula'] = {
			['step'] = {
				['1'] = [[El Kremlin de Tula recibe a los viajeros con una batería de cañones modernos que sobresalen entre las antiguas almenas de los muros de la fortaleza. Ganarse la confianza de los antiguos trabajadores de la planta de armas no será fácil.<new_line><new_line>Hay varios grupos que observan con avidez las abundantes existencias de armas de Tula, especialmente las bandas que gobiernan Riazán. Sin embargo, los fieros habitantes de Tula se mantienen al margen de los negocios y alianzas de otra gente, satisfechos simplemente con comerciar y mantener una fuerte vigilancia desde sus muros.]]
			}
		},
		['new_year_boss_2022_1'] = {
			['step'] = {
				['1'] = [[Un nuevo Papá Noel ha aparecido junto al árbol. Tengo que averiguar qué puede ofrecer.]]
			}
		},
		['progress_briansk'] = {
			['step'] = {
				['1'] = [[Durante la catástrofe, hubo un gran flujo de refugiados a través de Briansk, y la tasa de mortalidad entre ellos era increíblemente alta. Sin embargo, como consecuencia, la cantidad de supervivientes también fue extraordinariamente alta y habían desarrollado inmunidad.<new_line><new_line>El problema es que continúan atrapados en los alrededores de Briansk, sin ninguna organización ni apoyo real. Varios grupos le han echado ya el ojo al asentamiento; son muchos los deseos de explotar recursos humanos así. Y, tal y como muestra la amarga experiencia, no para propósitos nobles.]]
			}
		},
		['new_year_boss_2022_3'] = {
			['step'] = {
				['1'] = [[Se cometió un gran error en el parque de atracciones. Pero aún podemos negociar con los representantes de la banda de Krampus.]]
			}
		},
		['progress_novosibirsk'] = {
			['step'] = {
				['1'] = [[Pregunta a cualquiera en Siberia cuál es el mejor lugar para recibir el tratamiento adecuado y todos te enviarán a Novosibirsk. Dicen que todo es gracias al centro de investigación local.<new_line><new_line>Por supuesto, se escuchan todo tipo de rumores... Por ejemplo, dicen que pagarás tu tratamiento durante los próximos años, y tendrás suerte si es suficiente con trabajos eventuales corrientes. Sin embargo, no encontrarás atención médica y farmacología de tan alto nivel en ningún otro lugar, lo que sitúa a Novosibirsk en un puesto de honor en S.O.V.A.]]
			}
		},
		['halloween_boss_2022_5'] = {
			['text'] = [[El científico Víctor Frankenstein creó un monstruo con las partes del cuerpo de otras personas y le dio vida usando electricidad.<new_line>El monstruo de Frankenstein y su compinche jorobado Igor llegaron a este mundo no hace mucho, pero han dejado un reguero sangriento de asesinatos horribles: ha habido varios casos de cadáveres con órganos amputados o extraídos.]],
			['step'] = {
				['1'] = [[La Parca me dio notas del Libro de la Muerte sobre aquellos que morirán en la zona en la que Igor y el monstruo mataron por última vez. Tengo que investigar a todos los que aparezcan en la página que arrancó, uno de ellos podría ser la próxima víctima.]],
				['3'] = [[He localizado a Igor y al monstruo de Frankenstein. Tengo que derrotarlos.]],
				['2'] = [[Tengo que ir al siguiente lugar que aparece en la página del Libro de la Muerte.]]
			}
		},
		['order'] = {
			['text_workshop'] = [[«No me quedan suministros suficientes para terminar el trabajo. ¿Podrías traérmelos?».]],
			['text_nurse'] = [[«Algunas de las medicinas se están acabando. Pero la gente sigue enfermando. ¿Me ayudas a conseguir más?».]],
			['step'] = {
				['1'] = [[Acepté el trabajo por el asentamiento. Tengo que conseguirles suministros esenciales.]],
				['3'] = [[He entregado todos los suministros que me pedían.]],
				['2'] = [[He conseguido todo lo que me pidieron. Hora de volver al asentamiento.]]
			},
			['text_barman'] = [[«Tenemos que reabastecer la cocina. ¿Puedes traer algo de comida?».]],
			['text'] = [[«Tengo otro pedido de algunos suministros. No hay prisa. ¿Puedes conseguirme todo esto? Aquí tienes la lista».]],
			['name'] = [[Pedido]]
		},
		['stash_polar_fox'] = {
			['step'] = {
				['1'] = [[El zorro polar ha encontrado algo interesante cerca. Merece la pena echar un vistazo.]]
			}
		},
		['forest_feeder'] = {
			['name'] = [[Comederos del bosque]],
			['text'] = [[Los animales no estaban listos para el invierno y nuestro deber es ayudarlos. He colocado comederos en el bosque, pero ahora hay que llenarlos.]],
			['step'] = {
				['1'] = [[Me han pedido que llene unos comederos que están repartidos por el bosque.]]
			}
		},
		['winter_fishing'] = {
			['name'] = [[Pesca invernal]],
			['text'] = [[Estoy preparando comida para las fiestas, pero necesito algo de pescado. ¿Y si te tomas un descansito y vas a pescar?]],
			['step'] = {
				['1'] = [[La doncella de las nieves me ha pedido que vaya a pescar para hacer la comida de las fiestas.]]
			}
		},
		['halloween_boss_2022_1'] = {
			['name'] = [[Demonio Balthazar]],
			['text'] = [[Los sectarios están convocando al Demonio Balthazar para servirle. Balthazar los recompensará con dones de tal poder que cualquier humano que los asimile podría desafiar al mismo Demonio.]],
			['step'] = {
				['1'] = [[Los sectarios se reúnen en un granero abandonado en algún lugar del yermo. Tengo que encontrar una forma de llegar hasta allí.]],
				['7'] = [[Tengo que destruir a este Demonio y a su secta.]],
				['3'] = [[Para entrar necesito un disfraz. Podría hacerme una túnica si tuviera los materiales adecuados.]],
				['5'] = [[Los sectarios me exigen una donación antes de entrar. Tengo que darles una botella de vodka o una vela artesanal. Sea como sea, el portero acepta sobornos.]]
			}
		},
		['scientist_rescue'] = {
			['name'] = [[Trae al científico]],
			['step'] = {
				['1'] = [[Tengo que llegar a la estación ecológica y traer al científico de vuelta al asentamiento.]],
				['5'] = [[He encontrado al científico; está esperando a que lo lleve de vuelta al asentamiento.]],
				['4'] = [[He encontrado al científico, ahora tengo que llevarlo de vuelta al asentamiento.<new_line><new_line>En el camino de regreso, recogimos el equipamiento que le robaron al científico.]],
				['3'] = [[Llévate equipamiento de los bandidos.]],
				['2'] = [[He encontrado al científico, ahora tengo que llevarlo de vuelta al asentamiento.]],
				['9'] = [[Quita el dispositivo que queda del techo.]],
				['8'] = [[He encontrado al científico, ahora tengo que llevarlo de vuelta al asentamiento.<new_line><new_line>Antes de partir, ayudé al científico a quitar el dispositivo que quedaba.]],
				['7'] = [[Ayuda al científico a curar una herida leve.]],
				['6'] = [[He encontrado al científico, ahora tengo que llevarlo de vuelta al asentamiento.<new_line><new_line>Antes de partir, ayudé al científico a curar una herida leve.]]
			},
			['text'] = [[Nuestro científico está de turno en la estación ecológica. Se ha puesto en contacto con nosotros para decirnos que está teniendo problemas con los bandidos locales. Ve a por él y tráelo a casa.]]
		},
		['progress_voronej'] = {
			['step'] = {
				['1'] = [[Los soldados paracaidistas enviados desde Riazán poco antes de la catástrofe protegen el asentamiento de Vorónezh. El aeródromo superviviente se convirtió en la base de su asentamiento.<new_line><new_line>Los soldados llevan mucho tiempo queriendo regresar a Riazán y arrebatarle el control a la banda que gobierna allí. Parece que tienen el personal y el equipo para hacerlo, pero algo los está frenando.]]
			}
		},
		['progress_tymen'] = {
			['step'] = {
				['1'] = [[«Claro que allí viven bien con su oro negro». Eso es lo que más a menudo escucho sobre Tiumén. La riqueza del asentamiento proviene de las grandes capacidades de sus refinerías de petróleo.<new_line><new_line>Quién sabe... ¿Puede que los habitantes de Tiumén se hayan vuelto perezosos? Sin las materias primas y el apoyo logístico que le proporcionan sus socios del Sindicato, este asentamiento no sobreviviría ni un año.]]
			}
		},
		['bandit_camp'] = {
			['name'] = [[Campamento de bandidos]],
			['text'] = [[«Los bandidos han estado muy activos estos días. Han montado un campamento justo a nuestras puertas. ¿Puedes encargarte de ellos?».]],
			['step'] = {
				['1'] = [[El jefe de la guardia me dijo dónde estaba el campamento de los bandidos. Tengo que ir allí.]],
				['6'] = [[Logré hacer un trato con los bandidos. No volverán a molestarnos.]],
				['5'] = [[He matado a todos los bandidos.]],
				['4'] = [[Ahora que me he encargado de los bandidos, toca volver a la base.]],
				['3'] = [[Los bandidos ya deberían haberse ido. Vamos al campamento para comprobarlo.]],
				['2'] = [[Un trato es un trato. Tengo que darle tiempo a los bandidos para que se vayan.]]
			}
		},
		['leprechaun_debts'] = {
			['name'] = [[Deudas del duende]],
			['text'] = [[Hay un duende que me debe mucho dinero, no paga lo que bebe. Sigue el arcoíris para encontrarlo. Consigue mi oro y tráemelo. Pero no intentes gastártelo por ahí... está maldito.]],
			['step'] = {
				['1'] = [[Me han pedido que recoja el oro de un duende endeudado.]]
			}
		},
		['halloween_boss_2022_2'] = {
			['text'] = [[El doctor de la peste lleva a cabo sus experimentos diabólicos ocultándose tras sus gases venenosos: resucita a los muertos y propaga enfermedades entre los vivos. La armadura demoníaca puede protegerte de las maldiciones del doctor de la peste, pero llegar hasta él no será tan sencillo... Hordas de zombis protegen a ese astuto fabricante de pociones.]],
			['step'] = {
				['1'] = [[Unos zombis están acosando a una pequeña comunidad. Una señal inequívoca de que el doctor de la peste se encuentra en las cercanías. Tengo que hablar con esta gente y hacerles unas cuantas preguntas.]],
				['3'] = [[Tengo que derrotar al doctor de la peste. La Parca sugirió que el disfraz de demonio me protegería de sus ataques.]],
				['2'] = [[La gente dice que el doctor de la peste se instaló en el cementerio local, así que es ahí adonde tengo que ir.]]
			}
		},
		['pumpkin_seed'] = {
			['name'] = [[Semilla de calabaza]],
			['text'] = [[Si la cabeza dice la verdad, estamos jodidos. Pero existe una posibilidad, aunque esperaba no tener que usarla nunca. Toma esta semilla de calabaza y plántala en el páramo.]],
			['step'] = {
				['1'] = [[Tengo una semilla que debo plantar en el páramo. No sé de qué tiene miedo el tabernero, ni cómo nos va a ayudar esto. Perece bastante inofensiva.]]
			}
		},
		['construction_team'] = {
			['name'] = [[Brigada de construcción]],
			['step'] = {
				['1'] = [[El intendente me envió a ayudar a los trabajadores a una obra cerca del asentamiento.]],
				['12'] = [[Investiga qué está pasando en un par de lugares alrededor del puesto avanzado.]],
				['4'] = [[Comparte cigarrillos con los trabajadores.]],
				['3'] = [[Aún me queda trabajo por hacer en la obra. Tengo que recuperar fuerzas y volver con el capataz.]],
				['10'] = [[Ocúpate de las incursiones de bandidos.]],
				['8'] = [[Elimina la guarida de mutantes.]],
				['6'] = [[Elimina la guarida de alimañas.]],
				['14'] = [[Trae un cargamento de suministros.]],
				['2'] = [[El capataz pidió ayuda para la obra.]]
			},
			['text'] = [[No tenemos suficientes trabajadores en la obra. Cumple con tu parte por el bien común y te recompensaremos.]]
		},
		['help_hunter'] = {
			['name'] = [[Ayuda al cazador]],
			['text'] = [[Un cazador se ha metido en líos. Ve a ayudarlo. Te lo señalaré en el mapa.]],
			['step'] = {
				['1'] = [[Tengo que ir a ayudar a otro cazador.]]
			}
		},
		['march_skeletons'] = {
			['name'] = [[Marcha de esqueletos]],
			['text'] = [[¿Alguna vez has tenido que levantar a los muertos? Necesito a esqueletos de empleados. Llévate este pergamino, ahí están todas las instrucciones. Ojo no la líes.]],
			['step'] = {
				['1'] = [[Me han pedido que reúna a varios esqueletos y que lleve a cabo un ritual usando el pergamino.<new_line><new_line>Reúne diez esqueletos y colócalos formando un círculo, con los cráneos apuntando al centro. Espolvorea cenizas sobre los cráneos mientras das círculos tres veces hacia el oeste e invocas al espíritu de Tetriandok. Los esqueletos sabrán volver solos una vez acabe el ritual.]]
			}
		},
		['sled_repair'] = {
			['name'] = [[Reparación del trineo]],
			['text'] = [[He recorrido tantos kilómetros contigo que el trineo ha acabado hecho un desastre. ¿Puedes arreglármelo?]],
			['step'] = {
				['1'] = [[La doncella de las nieves me ha pedido que le arregle el trineo.]]
			}
		},
		['progress_rostov'] = {
			['step'] = {
				['1'] = [[Cuando las estepas de alrededor del Don se convirtieron en páramos, el Atamán reunió a todos los supervivientes bajo su control. Un grupo de aventureros se agrupó en torno a él; algunos tenían antepasados cosacos, mientras que otros simplemente estaban atrapados en el romanticismo de la libertad.<new_line><new_line>Y así fue como la estepa volvió a la vida, a la vida cosaca. O, al menos, a la interpretación del Atamán. Bajo la protección de la druzhina, la gente corriente pudo volver a cultivar y, finalmente, todo Rostov se salvó de la inanición.]]
			}
		},
		['progress_omsk'] = {
			['step'] = {
				['1'] = [[Omsk se separó de S.O.V.A. y ahora la gobierna el ejército. La única ruta comercial entre el este y el oeste pasa por el asentamiento.<new_line>Y eso parece ser lo que permite que un asentamiento tan cerrado como este sobreviva.<new_line><new_line>Sin embargo, hay indicios y rumores que sugieren que el ejército tiene algo más que mantiene a raya a las otras potencias de los Urales y de Siberia. Puedes intentar descubrir más sobre Omsk, pero son pocas las personas que logran establecer relaciones incluso con el perímetro exterior de la ciudad.]]
			}
		},
		['progress_leningrad'] = {
			['step'] = {
				['1'] = [[El campamento de turistas abandonado se encuentra a las afueras de los bosques abrasados por la radiación y los páramos que rodean Leningrado. Algunos temerarios de Tver instalaron un punto de parada aquí.<new_line><new_line>Los «turistas» (como se denomina a los exploradores veteranos de la zona de peligro) realizan expediciones para saquear Leningrado, regresan con el botín a la base y luego lo envían río abajo hasta Tver.]]
			}
		},
		['frankenstein'] = {
			['name'] = [[Monstruo de Frankenstein]],
			['text'] = [[A tenor de la nota del enterrador, Igor está intentando otra vez dar vida a un monstruo maldito. Sé dónde podría estar. He oído rumores de una casa sobre la que no paran de caer rayos. Ya que te has metido en esto, será mejor que lo acabes.]],
			['step'] = {
				['1'] = [[Me han dicho que detenga la reanimación del monstruo de Frankenstein.]]
			}
		},
		['doll'] = {
			['name'] = [[Marioneta]],
			['text'] = [[Me habías preguntado sobre la marioneta. Solo conozco a una persona que sepa controlarlas. El maestro titiritero es un tipo despreciable, pero no tenemos alternativa. Te señalaré en el mapa dónde puedes encontrarlo.]],
			['step'] = {
				['1'] = [[El tabernero ha señalado en el mapa el taller del maestro titiritero. Tal vez allí encuentre respuestas.]]
			}
		},
		['snowman_giant'] = {
			['text'] = [[Ha aparecido un muñeco de nieve en el horizonte. Hay que detenerlo antes de que llegue a los asentamientos y empiece a causar estragos. Tengo algo que contarte, pero nos veremos allí.]],
			['name'] = [[El rey de los muñecos de nieve]]
		},
		['progress_krasnoyarsk'] = {
			['step'] = {
				['1'] = [[El asentamiento de Krasnoyarsk es como una utopía científica en la vida real. Sus habitantes trabajan en invernaderos dirigidos por científicos. El sistema BIOS-4 que han desarrollado hace que puedan obtener cosechas sin precedentes e incluso alimentar a otros asentamientos. Aunque no gratis, por supuesto.<new_line><new_line>Todo tiene un precio: el proceso implica un gran consumo de electricidad y de agua limpia. Además, la tecnología que se utiliza es tan alta que la pérdida de un solo trabajador clave puede provocar que todo el sistema se derrumbe.]]
			}
		},
		['gravedigger'] = {
			['name'] = [[Enterrador]],
			['text'] = [[Eres justo la persona que necesitaba. Tengo un trabajo para ti. Hay un enterrador que se ha vuelto loco, está desenterrando cadáveres en lugar de enterrarlos. Tenemos que acabar con esto ya. ¿Puedes encargarte?]],
			['step'] = {
				['1'] = [[Me han dicho que me encargue del enterrador loco.]]
			}
		},
		['progress_izhevsk'] = {
			['step'] = {
				['1'] = [[Los montes Urales y kilómetros de tierra infectada rebosante de mutantes separan Izhevsk del Frente férreo. Probablemente ese sea el motivo por el que el Frente férreo todavía no ha metido las manos en la industria armamentística local.<new_line><new_line>Por eso, los habitantes de Izhevsk están muy interesados en mantener el contacto con Riazán: se encuentra muy lejos y pueden intercambiar pacíficamente sus armas y los productos de su industria apícola.]]
			}
		},
		['snowmen'] = {
			['text'] = [[Esos muñecos de nieve malvados están sembrando el caos y atacando a la gente, así que acaba con ellos. Desmóntalos para que reflexionen sobre lo que han hecho y Ded Moroz y yo ya nos encargaremos de volver a colocarlos luego.]],
			['step'] = {
				['1'] = [[Tengo que ir a ese sitio y cargarme a los muñecos de nieve.]]
			}
		},
		['progress_irkutsk'] = {
			['step'] = {
				['1'] = [[En ocasiones ocurren milagros, y uno de ellos tuvo lugar en Irkutsk. Ni la central hidroeléctrica de Angará ni las aguas del lago Baikal resultaron afectadas por la catástrofe. Ahora, los habitantes de Irkutsk cuidan la planta hidroeléctrica como si de su propio hijo se tratara. Incluso se instalaron en el dique y ahora proporcionan electricidad y agua limpia a todas las zonas de S.O.V.A.]]
			}
		},
		['order_delivery'] = {
			['name'] = [[Entrega]],
			['text'] = [[«Tenemos que transportar un cargamento de otra base. ¿Te interesa el trabajo?».]],
			['step'] = {
				['1'] = [[Tengo que ir a la base y recoger el cargamento.]],
				['6'] = [[El cliente se ha negado a comprar el cargamento y me lo ha encasquetado a mí.]],
				['5'] = [[No he logrado entregar el cargamento.]],
				['4'] = [[He entregado el cargamento y me lo han pagado por completo.]],
				['3'] = [[He entregado el cargamento de una pieza.]],
				['2'] = [[Tengo el cargamento. Ahora de vuelta a la base.]]
			}
		},
		['progress_tver'] = {
			['step'] = {
				['1'] = [[La gente acudió a la estación fluvial abandonada y la llenó de vida. Ahora, los barcos de Tver surcan el río hacia el norte y el sur, mientras que en el ruidoso edificio de la estación se pueden comprar artículos raros obtenidos corriendo grandes riesgos en Moscú o conseguidos mediante el comercio en Riazán.<new_line><new_line>Los residentes del asentamiento se ocupan de sus propios asuntos, pero les complace aceptar a viajeros errantes en su bulliciosa vida, con su característico olor a pescado y alquitrán. Los únicos que no son bienvenidos son los holgazanes.]]
			}
		},
		['immortal_love'] = {
			['name'] = [[Momia]],
			['text'] = [[He descifrado la última anotación en el cuaderno de bitácora. Habla de una chica que fue secuestrada por encargo de una momia. Tenemos que salvarla. Sigue a los escorpiones para encontrar la guarida de la momia. Atráelos para que salgan, y te llevarán ante su maestro.]],
			['step'] = {
				['1'] = [[Mi encargo es salvar a una chica de la guarida de una momia. Para encontrarla, tengo que atraer a los escorpiones y seguirlos hasta allí.]]
			}
		},
		['ice_brigands'] = {
			['name'] = [[Forajidos]],
			['text'] = [[Según los animales, hay una pandilla de forajidos causando estragos. Son una amenaza y tienes que detenerlos.]],
			['step'] = {
				['1'] = [[Tengo que derrotar a la banda de forajidos de la nieve.]]
			}
		},
		['letter_of_hope'] = {
			['name'] = [[Cartas de esperanza]],
			['text'] = [[La gente le sigue escribiendo cartas a Ded Moroz incluso en estos momentos tan difíciles y las dejan en todo tipo de escondrijos y rincones. Ve a buscarlas y tráemelas.]],
			['step'] = {
				['1'] = [[La doncella de las nieves me ha pedido que vaya a por las cartas que le han escrito a Ded Moroz.]]
			}
		},
		['mirror_lady'] = {
			['name'] = [[Dama en el espejo]],
			['text'] = [[He oído rumores de que Igor está cortejando a María la Sanguinaria. Puede que se esté escondiendo en su casa. Pero ándate con cuidado, es muy peligrosa. Tendrás que encender una vela y llamarla frente a un espejo. Entonces aparecerá.]],
			['step'] = {
				['1'] = [[El tabernero dijo que tal vez Igor se esté escondiendo con su amante. Debería ir a comprobarlo.]]
			}
		},
		['progress_sverdlovsk'] = {
			['step'] = {
				['1'] = [[El Frente férreo considera a Sverdlovsk su capital armamentística y la fuente de sus mejores tropas. Estos luchadores fuertemente armados y altamente motivados parten desde aquí para asaltar los páramos.<new_line><new_line>Los atacantes afirman que solo capturan a bandidos peligrosos que luego reeducan haciéndoles trabajar duro en la industria. Sin embargo, lo cierto es que incluso los pacíficos viajeros solitarios están en peligro en estos lugares.]]
			}
		},
		['progress_surgut'] = {
			['step'] = {
				['1'] = [[Después de la catástrofe, Surgut se vio rodeada por el desbordante río Obi. Su única conexión con el mundo exterior es a través de la carretera superviviente a Tiumén.<new_line><new_line>Lo único que puede hacer el asentamiento es suministrar petróleo crudo a sus socios del Sindicato. Por suerte, el petróleo sigue fluyendo sin obstáculos. Las rutas de transporte fluvial permitirían a Surgut comerciar con otras ciudades de Siberia, pero ¿quién les proporcionará transporte fluvial?]]
			}
		},
		['lost_soul'] = {
			['name'] = [[Alma perdida]],
			['text'] = [[Hay un hombre en peligro en algún lugar del bosque. Encuentra el campamento y síguele el rastro por si necesita ayuda.]],
			['step'] = {
				['1'] = [[Alguien necesita ayuda en algún rincón del bosque. He de darme prisa.]]
			}
		},
		['headless_horseman'] = {
			['name'] = [[Jinete sin cabeza]],
			['text'] = [[La chica a la que salvaste estuvo aquí, y relató una conversación que oyó por casualidad en el barco. Resulta que Drácula quiere usar este libro para despertar al Anciano y destruir la frontera entre los mundos. Hay que detenerlo. Tienes que interceptar al jinete y hacerte con el libro.]],
			['step'] = {
				['1'] = [[Tengo que interceptar al jinete y hacerme con el libro. Me han dado un ovillo de hilo para ayudarme a encontrarlo.]]
			}
		},
		['reinforcement'] = {
			['name'] = [[Refuerzos]],
			['text'] = [[«Ha llegado un mensaje urgente: una unidad de nuestra base está pidiendo refuerzos. Ve a ayudarlos».]],
			['step'] = {
				['1'] = [[Tengo que darme prisa y ayudar a nuestros hombres. No sabemos cuánto durarán si no llegan refuerzos.]],
				['4'] = [[Los hemos rechazado, pero no sin sufrir bajas.]],
				['3'] = [[Trabajando juntos, hemos logrado derrotarlos.]],
				['2'] = [[La batalla ha terminado. Ya puedo volver a la base a por mi recompensa.]]
			}
		},
		['witch_delivery'] = {
			['name'] = [[Entrega para la bruja]],
			['text'] = [[Necesito que le lleves una caja de ingredientes a una bruja local y que recojas un pedido de pociones que te va a dar.]],
			['step'] = {
				['1'] = [[Tengo que entregarle una caja de ingredientes a una bruja, recoger un pedido de pociones y regresar al portal.]],
				['2'] = [[Tengo las pociones. Ahora tengo que darles al camarero.]]
			}
		},
		['toy_story'] = {
			['text'] = [[Gracias a ti, ha vuelto parte de la magia y he conseguido encontrar el camino hacia el térem, pero me temo que habrá trampas de por medio... ¿Podrías ir a echar un vistazo? Eso sí, ten cuidado.]],
			['name'] = [[Cuento de juguetes]]
		},
		['camp_supply'] = {
			['name'] = [[Suministros del campamento]],
			['text'] = [[«La gente del campamento de nuestros aliados lo está pasando mal. Hemos decidido ayudarlos y enviarles algunos suministros. ¿Puedes llevárselos?».]],
			['step'] = {
				['1'] = [[Me han encargado que lleve suministros al campamento de nuestros aliados.]],
				['5'] = [[Soy el único que queda con vida. Ya no queda nadie a quien entregar suministros.]],
				['4'] = [[He ayudado a nuestros aliados a defenderse de un ataque y luego les he dado los suministros.]],
				['3'] = [[He entregado los suministros. Nuestros aliados están contentos por nuestra ayuda.]],
				['2'] = [[He entregado los suministros, más no puedo hacer. Me voy a casa.]],
				['9'] = [[Cura al aliado herido en el campamento.]],
				['8'] = [[Ayuda a los aliados a defenderse del ataque.]],
				['7'] = [[Están atacando el campamento de nuestros aliados. Debería decirle al asentamiento que envíe ayuda.]],
				['6'] = [[Cuando llegué al campamento, estaban todos muertos. No había nadie a quien darle los suministros.]]
			}
		},
		['snowmen_fortress'] = {
			['name'] = [[Fortaleza de nieve]],
			['text'] = [[Los muñecos de nieve han construido una fortaleza y están desolando la zona. Ve a pararles los pies cuanto antes.]],
			['step'] = {
				['1'] = [[Me toca cargarme la fortaleza de nieve y librarme de los muñecos.]]
			}
		},
		['stash_handcar'] = {
			['step'] = {
				['1'] = [[He encontrado un pequeño pedazo de papel en el que hay escrita la dirección de una casa en Kola. Tendría que ir a echar un ojo: tal vez encuentre un alijo de suministros allí.]]
			}
		},
		['impostor'] = {
			['name'] = [[Impostor]],
			['text'] = [[Me han llegado rumores acerca de un impostor: hay un bandido haciéndose pasar por Ded Moroz. Atraca a la gente y está arruinando nuestra reputación. Tienes que detenerlo como sea.]],
			['step'] = {
				['1'] = [[Ha aparecido alguien haciéndose pasar por Ded Moroz y se dedica a atracar a la gente. Hay que detenerlo.]],
				['3'] = [[El saqueo de botín está conmigo. Se lo debo regresar al pueblo.]],
				['2'] = [[Ahora sé dónde encontrar al impostor, que es hora de hacerle una visita.]]
			}
		},
		['herb_collect'] = {
			['name'] = [[Hierbas curativas]],
			['text'] = [[Los animales lo pasan mal en invierno y siempre intento ayudarlos. Ve a por hierbas medicinales y tráemelas al claro.]],
			['step'] = {
				['1'] = [[La doncella de las nieves me ha pedido que vaya a por hierbas para ayudar a los animales.]]
			}
		},
		['progress_chelyabinsk'] = {
			['step'] = {
				['1'] = [[Chelyábinsk es un asentamiento industrial densamente poblado. Es la base desde la que el Capataz controla la producción y todos los aspectos de la vida en el Frente férreo.<new_line><new_line>Tiene sentido: allí es donde se origina el enlace ferroviario con otras ciudades. El Frente férreo llena de metal la parte occidental de Siberia y, a cambio, compra comida a los siberianos, ya que aquí no hay forma de establecer la agricultura.]]
			}
		},
		['secret_documents'] = {
			['name'] = [[Documentos secretos]],
			['step'] = {
				['1'] = [[Debo inspeccionar el laboratorio abandonado, donde se encuentran los documentos secretos que necesita el biólogo para continuar la investigación.]],
				['2'] = [[Ya he despejado el camino, solo falta abrir la caja fuerte y coger los documentos secretos.]]
			},
			['text'] = [[«¡Tus esfuerzos no han sido en vano, camarada! Al menos hemos descubierto cómo se relacionan los documentos secretos con la siguiente bioestación. Ve al laboratorio abandonado, los documentos deberían estar allí».]]
		},
		['supply'] = {
			['name'] = [[Envío]],
			['text'] = [[«La base vecina nos ha pedido algunos suministros. ¿Puedes conseguirlo todo y llevárselo?».]],
			['step'] = {
				['1'] = [[Me han encargado encontrar todo lo que necesita la otra base.]],
				['5'] = [[No me han dado nada como recompensa. Bueno, supongo que esto podría serme útil.]],
				['4'] = [[Me han recompensado por mi trabajo, pero podrían haber sido más generosos.]],
				['3'] = [[Tanto trabajo duro ha dado sus frutos.]],
				['2'] = [[He encontrado todo lo que les hace falta. Ahora solo tengo que llevárselo a la base.]]
			}
		},
		['frozen'] = {
			['name'] = [[Congelados]],
			['text'] = [[Los forajidos de la nieve han secuestrado a varias personas y los han congelado en bloques de hielo. La lechuza te guiará hasta ellos, así que sálvalos.]],
