				['18'] = [[Síguelo]],
				['17'] = [[Finge estar muerto]],
				['16'] = [[«Eh... De acuerdo...». (Túmbate junto al hoyo)]]
			},
			['episode'] = {
				['14'] = [[«Ahora, cuando hablas con Papá Noelgocios, él escucha el mismo tipo de discurso que me escuchabas a mí antes...».<new_line><new_line>Papá Noelgocios nos miró un momento, agitó la mano y se alejó.]],
				['15'] = [[«De acuerdo, necesitamos un cebo. Túmbate junto al hoyo para atraer a los secuestradores».]],
				['12'] = [[«En efecto. Verás, un individuo no puede adquirir tanto conocimiento como para trascender su propio marco mental al encontrarse en un entorno extraño.<new_line>Pero volvamos a nuestra misión. El tiempo es vital».]],
				['13'] = [[«¡Uhhh, ahh, jo, jo, jo! Ah ee ooga, ee».]],
				['20'] = [[Durante la batalla, ¡uno de los miembros de la banda se escapó sigilosamente y salió corriendo!]],
				['11'] = [[Papá Cavernoel me pone una especie de trapo en la cabeza.<new_line>«Lo siento, amigo. Llegó un momento en el que se volvió evidente que nuestra comunicación había llegado a un punto muerto».]],
				['10'] = [[Papá Cavernoel se levantó lentamente, recogió su garrote y... ¡me golpeó en la cabeza!]],
				['9'] = [[«Uhh eh...».<new_line>Papá Cavernoel se pone de pie, se balancea y cae bruscamente de espaldas.]],
				['8'] = [[Sin levantarse, Papá Cavernoel niega con la cabeza.<new_line><new_line>«No, no. Se refiere a otra cosa... Intenta pensar como si fueras un poco... Quiero decir, prueba con otra cosa».]],
				['7'] = [[Ahora, Papá Cavernoel se acuesta junto al hoyo con las piernas y los brazos extendidos.]],
				['6'] = [[«¡Oh, ya lo entiendo! Quiere que cubramos el hoyo con algo. ¡Para ocultarlo!».<new_line>Papá Cavernoel sonrió y asintió.]],
				['1'] = [[«¡Oh, ooh! Ah, buh».<new_line>Papá Cavernoel golpea el suelo con el garrote. Parece que intenta decirnos algo...]],
				['5'] = [[Papá Cavernoel examinó cuidadosamente el hoyo que acabo de cavar.<new_line>«Ooh. ¡Uh vah, uhh vah!».<new_line><new_line>Mientras nos miraba, arrancó algunos trozos de hierbas largas y los colocó sobre el hoyo.]],
				['4'] = [[«Lo has entendido, ¿no? ¡Quiere cavar un hoyo para tender una trampa! La naturaleza es asombrosa, socio».]],
				['3'] = [[«¡Eee!».<new_line>Cavernoel ha empezado a asentir con alegría. Por lo que parece, necesita que cavemos un hoyo aquí.]],
				['2'] = [[Sacude la cabeza e intenta raspar la capa superior de tierra con el mango del garrote.]],
				['19'] = [[¡No me lo puedo creer! En cuanto me tumbé junto al hoyo, oí un traqueteo y el rugido de los motores.]],
				['17'] = [[«¡Nadie morderá el anzuelo si te quedas ahí tirado! Para fingir ser un animal herido, debes convertirte en un animal herido».]],
				['16'] = [[«Confía en mí. No hay tiempo para enseñarte cómo intuir la gran semejanza fractal de la unidad del ser».]]
			}
