		['emba_navigator_fight'] = {
			['episode'] = {
				['1'] = [[Hay una manada de biomutantes caminando por la carretera. Buscan algo para comer.]],
				['3'] = [[«Resumiendo: para mejorar el laboratorio, necesitas esporoflores y genofrutos. Crecen en ciudades científicas cubiertas de maleza.<new_line>Necesitarás un traje AIDA y un herbicida, los científicos te los darán en el departamento de consumibles a cambio de disquetes.<new_line>Para que el biólogo fabrique armas y otras cosas, necesitas biomasa, que se puede extraer de los biomutantes. Y eso es todo. ¡Saludos, hasta la próxima!».]],
				['2'] = [[«Bien hecho, te he estado observando. Bueno, ve con el biólogo, tienes que ayudarlo con la investigación. Entrégale la caja negra. Y mientras estás con él, ayúdalo en el laboratorio.<new_line>No olvides extraer la biomasa de los trozos de micelio. Puedes hacerlo con un cuchillo, pero te recomiendo que uses un extractor. Puedes intercambiar disquetes por un extractor con los científicos en el departamento de consumibles.<new_line>¿Qué más...? ¿Lo has memorizado todo sobre la ciudad científica? ¿O te lo repito?».]]
			},
			['choice'] = {
				['1'] = [[«Repítemelo».]],
				['2'] = [[«Lo he memorizado todo. Hasta la próxima».]]
			}
		},
		['boatman'] = {
			['episode'] = {
				['1'] = [[Había un hombre con una máscara antigás de pie junto al barco.]],
				['5'] = [[«Tenemos un plan, pero no tenemos balas. Escucha, hay una ferretería en el pueblo. Está cerrada, pero seguro que dentro hay azufre y salitre. Puedo usarlos para hacer pólvora...».]],
				['4'] = [[Cuando el barquero vio que había vuelto solo, se le ensombreció la cara. Le entregué el diario y le conté lo que había sucedido.<new_line>«Esa escoria... Se llevaron hasta la radio. Tenemos que ajustar cuentas con esos desgraciados y recuperar lo que robaron».]],
				['3'] = [[«Bueno, del campamento... de la aldea pesquera. Está al oeste de Petrozavodsk, bastante lejos de aquí. No suelo navegar tan lejos, pero tenía que venir a buscar a Vasili. A los dos nos gusta mucho la radio, así que llevamos un tiempo usándola para comunicarnos. ¿Tú de dónde eres?».]],
				['2'] = [[El barquero se dio la vuelta sorprendido.<new_line>«No, Vasili es amigo mío. Estoy aquí para llevarlo de vuelta a nuestro asentamiento, pero no responde».]],
				['9'] = [[«No hay ningún camino de Kandalaksha a Belomorsk. Hay demasiada radiación. La única forma de llegar es yendo al sur: por el agua.<new_line>Oye, ¿crees que podrías ir a buscar a Vasili? Tráelo aquí y cruzaremos juntos al otro lado de la zona radiactiva».]],
				['8'] = [[No tengo nada más que decirle al barquero.]],
				['7'] = [[El barquero colocó el equipo de Vasili en una caja de madera y me pidió ver el mapa.<new_line>«Esta de aquí es mi aldea. Te llevaré a Belomorsk como te prometí. No sé por qué quieres ir a ese infierno de ciudad, pero eso es asunto tuyo».]],
				['6'] = [[«Toma, llévate esta palanca. Es vieja, pero servirá para abrir la puerta.<new_line>Tú encárgate de eso y yo iré a por armas que nos sirvan para el trabajo. Nos reuniremos en el manicomio».]],
				['10'] = [[«Bien, lo hemos conseguido. Mucha suerte con tu búsqueda. Te esperaré en la aldea pesquera».]]
			},
			['choice'] = {
				['1'] = [[«¿Eres Vasili? He oído tu señal de radio».]],
				['5'] = [[«Yo puedo ir».]],
				['4'] = [[«Parece que son muchos. Nos hará falta trazar un plan».]],
				['3'] = [[«Espera un segundo... ¿Eres del campamento?».]],
				['2'] = [[«Vale, trato hecho»]],
				['9'] = [[«Soy de Murmansk, pero voy de camino a Belomorsk».]],
				['8'] = [[«Sí, vamos».]],
				['7'] = [[«Sigo teniendo asuntos que atender aquí».]],
				['6'] = [[«Hasta la próxima».]],
				['10'] = [[«Hasta la próxima».]]
