		['newyearEvent_craft_easy'] = {
			['episode'] = {
				['1'] = [[«¡Jo, jo, jo! ¡Hola, querido amigo! Necesito tu ayuda. La cuestión es que los niños no son los únicos que se merecen regalos, algunos adultos también. Pero en mi fábrica no puedo crear regalos aptos para los adultos de aquí, no tengo los planos ni los materiales. ¿Podrías fabricar algo por mí?».]],
				['5'] = [[«Quería presentarte al nuevo Papá Noel de tu zona. Te ha traído un pequeño regalo. Él se quedará a cargo de la magia y de los milagros de aquí».]],
				['4'] = [[Una mujer abrió la puerta.<new_line>«¿Quién es? Oh, eres… ¡Te he dicho mil veces que no voy a ir a tu fábrica!».]],
				['3'] = [[Cogí el regalo y caminamos hasta una de las casas del pueblo. Noelgocios llamó a la puerta.<new_line>«¡Jo, jo, jo!».]],
				['2'] = [[«¡Oh, qué interesante! Vaya, qué bonito... ¿Por qué no entregamos este regalo juntos? Creo que la destinataria estará muy contenta de conocer al artesano que lo ha hecho».]],
				['9'] = [[«Ha habido un pequeño error con el regalo, pero aún estoy aprendiendo sobre tu mundo, y echo de menos a mis leales elfos. Reflexionaré sobre los errores y, la próxima vez, el regalo será mejor. ¡Aun así, has hecho un acto muy bueno!».]],
				['8'] = [[En un abrir y cerrar de ojos, la mujer arrancó el alegre papel de regalo y abrió el paquete. «Oh, ¿de verdad es para mí? Qué bonito. Gracias. Puede que me haya equivocado contigo. Me pensaré lo del trabajo».]],
				['7'] = [[En un abrir y cerrar de ojos, la mujer arrancó el alegre papel de regalo y abrió el paquete.<new_line>«¿Estáis de broma? Gracias, claro, pero creo que será mejor que os vayáis antes de que mi perro se despierte».]],
				['6'] = [[«Oh, ¿es para mí? ¿Qué es?».]]
			},
			['choice'] = {
				['1'] = [[«Aquí, toma esto».]],
				['6'] = [[Vete de la casa]],
				['5'] = [[Entrega el regalo]],
				['4'] = [[«No, gracias, tengo que irme». (Vete)]],
				['3'] = [[«De acuerdo, ¡vamos allá!».]],
				['2'] = [[«Veré lo que puedo hacer». (Vete)]]
			}
		},
		['hunt_quest'] = {
			['episode'] = {
				['1'] = [[Una mujer se me acercó con un niño pequeño en brazos. No dijo nada, solo se quedó de pie a mi lado mirándose los pies.]],
				['5'] = [[El cliente frunció el ceño.<new_line>«No, a ver, este no era el trato. Aquí no hay carne suficiente. No pienso darte ni una moneda».]],
				['4'] = [[La mujer se quedó mirándome asombrada.<new_line>«¡Gracias! ¡Eres muy amable!».]],
				['3'] = [[La mujer me miraba con los ojos llenos de desesperación.<new_line>«¿Podría ser que... tuvieras algo de comida que te sobre?».]],
				['10'] = [[«Bien, tú ganas. Te pagaré más».]],
				['9'] = [[El cliente asintió satisfecho.<new_line>«Bueno, ¿todo ha salido bien?».]],
				['8'] = [[A la mujer se le descompuso el rostro.<new_line>«Siento haberte molestado».<new_line>]],
				['7'] = [[La mujer asintió agradecida.<new_line>«Gracias. Con esto, debería tener para unos días...».<new_line>]],
				['6'] = [[Mi jefe me escudriñó atentamente.<new_line>«¿Lo has traído?».]],
				['11'] = [[«Teníamos un trato. ¡Dame la carne o sal de mi vista!».]]
			},
