				['3'] = [[«Te puedo dar un pedazo de carne»]],
				['2'] = [[«¿Necesitas algo?»]],
				['9'] = [[«No tengo nada»]],
				['8'] = [[«Toma, unas latas de comida»]],
				['7'] = [[«Todo está listo»]],
				['10'] = [[Trata de negociar]]
			}
		},
		['new_year_boss_2_2'] = {
			['choice'] = {
				['1'] = [[«¿Qué es...?».]],
				['8'] = [[«¡Eso es lo que está haciendo ese viejo!».]],
				['7'] = [[«¿Es tóxico?».]],
				['6'] = [[«¿¡Qué le has hecho!?».]],
				['5'] = [[Salta fuera del humo]],
				['4'] = [[«¡Déjalo en paz!».]],
				['3'] = [[Pregúntale al anciano qué está pasando]],
				['2'] = [[«¡Eh! ¡Basta ya!».]]
			},
			['episode'] = {
				['1'] = [[Correr tras el fugitivo me llevó a un pueblo decorado para las fiestas. Justo ante mis ojos, esos canallas quitaban las luces de los postes, arrancaban los adornos de los árboles y desaparecían detrás de las casas.]],
				['5'] = [[De repente, el viejo se giró hacia mí y empezó a reírse.<new_line>«Ah, ¿quieres hablar con el anciano? ¡Eh, sabandijas, tirad toda esta basura al fuego!».]],
				['4'] = [[Era como si nadie me escuchara. El regalo cayó en el fuego, estallaron unas llamas azules y salió un humo azul. El viejo y su banda se pusieron alrededor del fuego e inhalaron el humo con los ojos en blanco.]],
				['3'] = [[«¡Eh, anciano! ¿Ya has cogido los regalos de todo el mundo? ¡Déjalos aquí!».<new_line>El viejo con la máscara aterradora le quitó una caja con un lazo navideño al hombre y la tiró al fuego.]],
				['2'] = [[Esos canallas están amontonando los adornos navideños en la plaza del pueblo, junto a una hoguera. Hay algunos lugareños de pie pegados a las paredes de las casas. Los lidera un hombre sombrío con un par de regalos en las manos.]],
				['9'] = [[«¡Qué horror! No pude ayudarte por culpa de ese terrible humo».]],
				['8'] = [[Como respuesta, el viejo se echó a reír de nuevo. Sin dejar de reírse, salió corriendo hacia su moto y se largó de la plaza.<new_line>Los matones, liderados por el anciano desfigurado, se acercaron a mí.]],
				['7'] = [[El humo se disipó. Junto al viejo, donde antes se encontraba el anciano, ahora había una especie de monstruo.<new_line>«¡Sabandijas, la banda de Krampus Noel tiene un nuevo recluta! Novato, ¡tu primer trabajo será acabar con esta escoria!».]],
				['6'] = [[Un montón de adornos navideños volaron hacia el fuego, que soltó una nube de humo acre aún más espesa que la primera. El viejo agarró al pobre anciano del pueblo y lo acercó hacia sí mismo.]],
				['11'] = [[«Oh, ¿no lo has reconocido? ¡Ese viejo es Papá Noel! No sé cómo acabó convertido en eso...<new_line>Ni siquiera consigo acostumbrarme a esa abominación. Quizás Papá Noelgocios sepa qué hacer. Tenemos que encontrarlo».]],
				['10'] = [[«Eso no viene al caso. La forma que tienen de ingerirlo... mientras destruyen todo lo relacionado con la Navidad. ¿Todo tu mundo está así de corrompido?».]]
			}
		},
		['emba_craft_easy'] = {
			['episode'] = {
				['1'] = [[«¡Hola, camarada! Hemos estado conduciendo por los páramos en los todoterrenos y los ingenieros están empezando a quedarse sin repuestos para repararlos.<new_line>¿Podrías ayudarnos con los suministros?».]],
				['2'] = [[«¡Gracias! Aunque no te involucres mucho en la investigación, estás ayudando a que la ciencia avance, y eso tiene mucho valor».]]
			}
		},
