		['emba_search_easy2'] = {
			['episode'] = {
				['1'] = [[«¡Hola, camarada! ¿Quieres ayudar en una tarea científica? Estamos estudiando el modo en el que el micelio interactúa con las plantas.<new_line>¿Podrías entregarnos alguna que otra muestra típica de la flora local?».]],
				['3'] = [[El científico cogió las plantas y se metió en el todoterreno con ellas, volviendo un tiempo después.<new_line><new_line>«¡Esto promete, camarada! El micelio empezó a interactuar activamente con el tejido desconocido a los pocos segundos de combinarlos.<new_line>En fin, ¡vuelvo a las observaciones! ¡Espero que podamos seguir colaborando!».]],
				['2'] = [[«Dime, ¿has traído el herbario? ¡Nos morimos de ganas de empezar el experimento!».<new_line>]]
