				['1'] = [[Irónicamente, la carta está en el buzón... pero ya no hay casa a la que le corresponda. Lo único que queda del edificio es un muro, muro del que cuelga el buzón. Tendré que encontrar algo para abrirlo.]],
				['5'] = [[Cuervo graznó en voz alta, dejó caer la carta y batió sus alas.]],
				['4'] = [[No le interesa ni lo más mínimo.]],
				['3'] = [[El cuervo me mira desde el árbol, y veo que lleva la carta en el pico cuando gira esa cabeza de color azabache. Debería intentar cambiársela por algo.]],
				['2'] = [[La carta está en una botella al fondo de una cuneta cubierta por una capa de hielo bastante gruesa. No sé cómo habrá acabado ahí, pero me va a costar hacerme con ella.]]
			}
		},
		['new_year_boss_6_2'] = {
			['choice'] = {
				['1'] = [[«Lo que importa es que has vuelto».]],
				['7'] = [[«¡Hurra!».]],
				['6'] = [[«¡Feliz Navidad!».]],
				['5'] = [[«Eso sin duda».]],
				['4'] = [[«¡Pero solo un año!».]],
				['3'] = [[«¿Qué insinúas?».]],
				['2'] = [[«Si algo nos sobra, son Noeles».]]
			},
			['episode'] = {
				['1'] = [[«Amigos, no sé qué decir en mi defensa. Al principio quería lo mejor. Pero… En cuanto a lo que pasó después, no tengo excusa».]],
				['8'] = [[«¡Ee eeee!».]],
				['7'] = [[«Muy bien, lo celebraremos, ¡y luego volveremos al trabajo! A todos os encanta una fiesta, pero, como siempre, soy yo quien tiene que hacerlo todo. En cualquier caso, ¡feliz Navidad!».]],
				['6'] = [[«Te ayudaremos, socio. No puedo creer que esté diciendo esto, pero ¡basta ya de negocios!<new_line>Nos merecemos nuestra propia fiesta. ¡Tenemos mucho que celebrar!».]],
				['5'] = [[«Hiciste un buen trabajo con mis deberes todo este tiempo. Obviamente mejor que yo... Y Zarpa tiene razón: ahora ya solo sirvo para jubilarme».]],
				['4'] = [[«¡Así es! Para que la gente celebre la Navidad, tenemos mucho por hacer. Y no creo que Papá Noel esté en condiciones en estos momentos».]],
				['3'] = [[«¡Todo el mundo pone excusas siempre! Pero ¿¡quién va a arreglar las cosas!? ¿Va a venir la Navidad ella solita, sin ayuda de Papá Noel?».]],
				['2'] = [[«Siempre pasa lo mismo, chum».]]
			}
		},
		['new_year_boss_5_4'] = {
			['choice'] = {
				['1'] = [[«¡Ataquemos pues!».]],
				['4'] = [[«¿Y podré hacerlo yo solo?».]],
				['3'] = [[«¿Mi portal?».]],
				['2'] = [[«¿Sugieres que nos rindamos?».]]
			},
			['episode'] = {
				['1'] = [[«Percibo un rastro de la magia que nos robaron... Escarchín se ha escondido en algún lugar cercano y está esperando a que contrataquemos».]],
				['6'] = [[«Tampoco nos quedan más opciones. De acuerdo, chaval, alegra esa cara. Creo que tienes una pequeña oportunidad. Decora el árbol por última vez mientras lo preparo todo».]],
				['5'] = [[«Sí, en el que llevas todo este tiempo, genio. No nos queda energía para más que eso.<new_line>Luego, una vez que estés en la guarida de Escarchín, solo tendrás que confiar en ti mismo».]],
				['4'] = [[«De acuerdo. Como de todas formas estoy atrapado aquí con vosotros..., también puedo sugerir un plan tonto.<new_line>Supongo que podría redirigir tu portal directamente a la guarida de Escarchín».]],
				['3'] = [[«¿Qué otra opción nos queda? ¡Tenemos las cuentas en números rojos!».]],
				['2'] = [[«¡Pusimos casi todas nuestras energías en acabar con esa aspiradora! Caer directamente en la trampa de Escarchín no solo es una locura, es un suicidio».]]
			}
		},
		['boatman_camp_chat'] = {
			['episode'] = {
				['1'] = [[El barquero estaba preparando sopa de pescado en un gran recipiente. Al verme, asintió:<new_line>«Parece que lo has conseguido. ¿Has encontrado lo que buscabas en Belomorsk?».]],
				['3'] = [[«¡Mira que eres tozudo! Ya te lo he dicho, ¡es peligroso y no estás listo! Deberías quedarte por aquí un tiempo más y pulir tus habilidades primero. Ayuda a los locales, acepta un par de encargos del bar. Vende mercancía, habla con el capataz. Entonces, cuando estés listo, vuelve y te lo contaré todo».]],
				['2'] = [[«¿Sopa? —Ofreció el barquero con una sonrisa—. No hace falta armar tanto jaleo. He oído hablar de un instituto científico donde seguramente trabajaban en ese virus. Pero el lugar es peligroso: radiación, mutantes... gente. No deberías poner un pie en esa zona».]]
			},
			['choice'] = {
				['1'] = [[«No, quiero saber qué ha ocurrido. Quién empezó la guerra, qué hay detrás de esta epidemia...».]],
				['3'] = [[«Me has convencido. Hasta la próxima».]],
				['2'] = [[«¿Dónde está ese instituto?»]]
			}
		},
