		['halloween_boss_2022_4_2'] = {
			['choice'] = {
				['1'] = [[Sigue a los fugitivos]],
				['5'] = [[Acércate]],
				['4'] = [[Escucha]],
				['3'] = [[«¿Y ahora qué?».]],
				['2'] = [[«La cosecha del granjero ha desaparecido».]]
			},
			['episode'] = {
				['1'] = [[No había dado ni cinco pasos hacia el edificio cuando oí un susurro a la vuelta de la esquina: «¡Shh! Ha venido a hacer sopa con nosotras. ¡Cállate, silencio!».]],
				['8'] = [[«Bueno, ¡ahora la feria del condado está a salvo! Por cierto, pásate a verme a mí también, tengo una oferta exclusiva para ti del mundo de los muertos».]],
				['7'] = [[«Quería castigar al granjero cuando llegara aquí, pero como has aparecido tú en su lugar, dejaremos que tu destino le sirva de lección a él».]],
				['6'] = [[«Lo sé. Les di vida a todas las calabazas para que pudieran escapar de allí. Ese granjero estaba criando a mis súbditos en medio de un páramo radiactivo sin ningún tipo de protección. Así que tomé el control de la calabaza que plantó en esa ciudad en ruinas e irradiada... y todo para ganar un premio».]],
				['5'] = [[Llegué a un claro en el bosque. Justo en medio había una figura alta con una enorme calabaza donde debería tener la cabeza y una corona en la parte superior. Una llama se encendió en los ojos de la calabaza y la criatura se volvió hacia mí: «¿Por qué persigues a mis súbditos, humano?».]],
				['4'] = [[Hice todo lo posible por caminar por los campos de maíz sin hacer ruido, pero pisé algo que crujió y de inmediato escuché: «¡Rápido, vayamos hacia el bosque! ¡Oh, Dios mío! ¡Acabar convertido en pastel de calabaza es la mayor de mis fobias!».]],
				['3'] = [[«¡Aaah! ¡Corre! ¡Se beberá todos nuestros jugos de calabaza!».<new_line>Logré distinguir a unos hombres pequeños con la cabeza redonda escondidos detrás de la pared. Pero desaparecieron rápidamente tras unos árboles frutales y no me dio tiempo a verlos bien.]],
				['2'] = [[«¡Cállate tú! ¿Se ha ido ya el devorador de calabazas? No veo nada».]]
			}
		},
		['emba_time_medium'] = {
			['episode'] = {
				['1'] = [[«¡Saludos, camarada! ¿Quieres contribuir a la educación científica de la nación? Tendrás que trabajar como cámara mientras grabo las escenas panorámicas».]],
				['7'] = [[«¡Corten! Todos lo habéis hecho muy bien. Una docena más de escenas como esta y lograremos un resultado fantástico. También tengo un montón de ideas, ¡suceden tantas cosas a nuestro alrededor!<new_line>Lo has hecho de maravilla. Te llamaré para grabar la próxima serie. Vas a formar parte de la historia del cine. Bueno, si es que el mundo sigue existiendo».]],
				['6'] = [[«¡Me vas a agradecer que te haya hecho cámara de esta película! ¿Empezamos?».]],
				['5'] = [[«¡Corten! Ahora vamos con la parte más importante. ¡La escena de combate! Hasta te he traído a unos cuantos extras. Pero ten cuidado, mi presupuesto no da para mucho».]],
				['4'] = [[«¡Corten! Venga, ahora ve allí y graba la carretera. ¿Ves lo que están haciendo? Han encontrado los restos de una vaca y se los están zampando. Venga, vamos a enseñarle a la gente lo más interesante. ¡Acción!».]],
				['3'] = [[«Toma, cógela así y pulsa aquí. Mira, hay biomutantes en ese claro. Debes grabar lo que están haciendo. Pero no te acerques demasiado. Venga, ve, necesito mucho material».]],
				['2'] = [[«No la menosprecies, es una gran película. ¡Observa los tiempos en los que vivimos! ¿Crees que la gente solo necesita vacunas e inyectores? ¡Pues la subestimas! Es importante que entiendan lo que sucede. Y eso no se consigue con un artículo insulso, sino gracias al trabajo de un artista. Es decir, yo. Y tú puedes ayudarme».]]
			},
			['choice'] = {
				['1'] = [[«¿Una especie de película?».]],
				['5'] = [[«Vale, lo tengo todo listo».]],
				['4'] = [[«¿Luchar? Pero ¿qué clase de película estás grabando?».]],
				['3'] = [[Empieza a grabar.]],
				['2'] = [[«Vale, dime qué debo pulsar».]]
			}
		},
		['newyearEvent_energy_medium'] = {
			['episode'] = {
				['1'] = [[«¡Jo, jo, hola, querido amigo mío! En nuestra fábrica, si algo sobra, son problemas, y por fin descubrí qué es lo que ocurre. ¡Nuestra maldita competencia está interfiriendo en nuestras operaciones! ¿Puedes ayudarme a sabotear un poco sus fábricas?».]],
				['5'] = [[Resultó que había varias entradas ocultas en el bloque de hielo y entramos por una de ellas. Pareció que acabamos en un garaje con varios vehículos.<new_line>«¡Coge un mazo y rompe todo lo que hay aquí!».]],
				['4'] = [[Tuvimos que caminar unos kilómetros hasta que llegamos a un gran bloque de hielo. Noelgocios me habló en voz baja.<new_line>«Sígueme. Ah, y aquí hay un pergamino del portal para ti. Si crees que no puedes hacerlo, lee el pergamino y te sacará de aquí».]],
				['3'] = [[«¡Estoy cien por cien seguro! La competencia es fundamental. Nuestra civilización descansa sobre pilares. ¿Y cómo sabemos quién es el pilar y quién es el trepa recién llegado si no competimos entre nosotros? O ellos ganan y demuestran que tienen razón, ¡o ganamos nosotros! De acuerdo, ¿estás listo?».]],
				['2'] = [[«Sabandijas. Nuestra fábrica utiliza la Navidad y los milagros para la publicidad, y están en contra de eso. Así que son nuestra competencia directa, y tenemos que encargarnos de ellos. ¿Listo para hacer justicia?».]],
				['9'] = [[Cuando terminamos de recoger los adornos, nos echamos los sacos a los hombros y nos dirigimos hacia la salida. Sin embargo, resultó que una banda de sabandijas nos bloqueaba el camino. Tras ponernos a cubierto en una esquina, Noelgocios me susurró:<new_line>«Podemos marcharnos por otra salida, pero será difícil, los sacos pesan bastante. ¿O deberíamos encargarnos de esta banda e ir por aquí?».]],
				['8'] = [[«No te preocupes, querido amigo mío. Llevo años brindando bondad y milagros a la gente. Sé muy bien lo que está bien y lo que está mal. Coge un saco y mete los adornos dentro».]],
				['7'] = [[Cuando terminamos de colocar las minas, continuamos avanzando y dimos con una habitación con adornos navideños.<new_line>«¡Oh, qué regalo tan bonito! Menos mal que vinimos aquí. Mételo todo en sacos».<new_line>Acto seguido, Noelgocios sacó dos sacos rojos.]],
				['6'] = [[Cuando terminamos de destrozar los vehículos, seguimos explorando el bloque de hielo. En la habitación contigua se almacenaba el combustible.<new_line>«¡Genial, podemos poner minas aquí! Coge los fuegos artificiales».<new_line>Noelgocios me lanzó una caja de material pirotécnico.]],
				['11'] = [[Saqué el pergamino y comencé a leerlo. Cuando Noelgocios me vio, comenzó a hacer lo mismo rápidamente. Una vez que terminé de leerlo, vi un destello de color azul brillante y, segundos después, me encontraba en un páramo.]],
				['10'] = [[Cuando nos encontrábamos a una distancia segura del bloque de hielo, Noelgocios sacó el detonador, lo pulsó y se produjo una fuerte explosión.<new_line>«¡Eso es! ¡Hemos hecho un buen trabajo y un buen acto! Con la competencia fuera de juego, ¡la fábrica prosperará!».]]
			},
			['choice'] = {
				['1'] = [[«¿Qué competencia? ¿Cómo ha llegado aquí?».]],
				['5'] = [[Rompe todo con un mazo]],
				['4'] = [[Sigue a Papá Noelgocios]],
				['3'] = [[«Son tipos malos... Pero ¿estás seguro de que están detrás de los problemas de la fábrica?».]],
				['2'] = [[«Estoy listo».]],
				['9'] = [[«¿No se suponía que robar estaba mal?».]],
				['8'] = [[Mete los adornos en el saco]],
				['7'] = [[Coloca minas en la habitación]],
				['6'] = [[Escapa por un portal]],
				['10'] = [[Busca otra salida]]
			}
		},
