			['choice'] = {
				['1'] = [[Trata de conversar]],
				['2'] = [[Acércate]]
			},
			['episode'] = {
				['1'] = [[¡Encontré a una persona viva en la casa! Viste harapos y está escuálido. Movía los ojos rápidamente de un lado a otro, y en cuanto me vio, soltó un grito ahogado y se escondió tras una mesa rota.]],
				['3'] = [[¡Encontré a una persona viva en la casa! Viste harapos y está escuálido. Movía los ojos rápidamente de un lado a otro, y en cuanto me vio, soltó un grito ahogado y se escondió tras una mesa rota.]],
				['2'] = [[Por lo visto, no le gustó lo que estaba haciendo. Emitió un chillido delirante y luego se tiró hacia mí con una lanza.]]
			}
