		['embacity_square'] = {
			['episode'] = {
				['1'] = [[El lugar está cubierto de maleza y moho. Los habitantes de la plaza se mueven tambaleándose, en un estado de animación suspendida. Despejar la zona es arriesgado, pero merece la pena intentarlo.]],
				['2'] = [[En la plaza cubierta de maleza, hay varios mutantes absortos en absorber las formaciones de moho. Despejar la zona es arriesgado, pero merece la pena intentarlo.]]
			}
		},
		['bandit_camp_quest_pt2'] = {
			['episode'] = {
				['1'] = [[Pasados unos días, volví al campamento de los bandidos. Habían cumplido con su palabra. Solo dejaron una nota:<new_line>«Gracias por tratar este asunto de forma pacífica».]],
				['6'] = [[«Calma. Nos hemos retrasado un poco, pero ya nos vamos. Agradecemos que te estés encargando de esto de forma pacífica. Toma, un detalle como muestra de agradecimiento».<new_line>Los bandidos terminaron de recoger sus cosas y abandonaron rápidamente el campamento.]],
				['5'] = [[«He cambiado de idea», dijo el líder de los bandidos con una sonrisa.]],
				['4'] = [[¿Debería ir a ver qué ocurre en el campamento de los bandidos?]],
				['3'] = [[Fue todo un placer encargarme de esas sabandijas.]],
				['2'] = [[Pasados unos días, volví al campamento. Los bandidos no se habían ido a ninguna parte y todas sus cosas seguían allí.]]
			},
			['choice'] = {
				['6'] = [[«Ese no era el trato»]],
