				['2'] = [[Ataca]]
			}
		},
		['bandit_camp_quest'] = {
			['episode'] = {
				['1'] = [[Encontré un pequeño campamento en el bosque. ¿Cómo demonios se atreven esos bandidos a montar un campamento tan cerca de la base?]],
				['5'] = [[El líder de los bandidos levantó las manos. Una señal de paz.<new_line>«No queremos pelear. Danos unos pocos días y nos iremos».]],
				['4'] = [[No ha sido tan difícil. Tengo que coger todo lo que pueda ser útil y largarme de aquí.]],
				['3'] = [[Una rama seca crujió bajo mis pies. Uno de los bandidos dio unos pasos hacia donde me encontraba, tratando de distinguir algo entre los árboles.<new_line>«¡Eh! ¿Quién anda ahí? ¿Qué quieres?».]],
				['2'] = [[Una rama seca crujió bajo mis pies y todos los bandidos se giraron para mirar a los matorrales. Está claro que saben que estoy aquí.]]
			},
			['choice'] = {
				['1'] = [[Acércate disimuladamente al campamento]],
				['4'] = [[«Salid de aquí mientras podáis»]],
				['3'] = [[Registra el campamento y márchate]],
				['5'] = [[«Vale, trato hecho»]]
			}
		},
		['halloween_boss_2022_6'] = {
			['choice'] = {
				['1'] = [[Tírale una cuerda]],
				['4'] = [[Sigue al Burro]],
				['3'] = [[Salta y atrápalo]],
				['2'] = [[Lánzale una granada]]
			},
			['episode'] = {
				['1'] = [[Me topé con una cabaña en el pantano. ¡Y allí estaba el Burro! Estaba buscando a tientas entre los arbustos y murmurando para sí mismo:<new_line>«Flor azul, espinas rojas...».<new_line>Me acerqué lo bastante como para llevar a cabo mi plan.]],
				['5'] = [[Oí un estruendo y un rugido que provenía de un pequeño baño exterior de madera que ni siquiera había visto antes. ¡La bestia del pantano!<new_line>Pero entonces la Parca apareció junto a la cabaña y cerró la puerta con la guadaña.<new_line>«Bueno, mortal, ¡es la última vez que te ayudo! Ve tras el Burro, ¡se está escapando! Yo me quedaré a sujetar la puerta!».]],
				['4'] = [[El Burro me escuchó saltar y se apartó a un lado.<new_line>Salió corriendo, rebuznando con fuerza en busca de ayuda.]],
				['3'] = [[Atrapó la granada con los dientes y volvió a tirarla hacia mí. Mientras lo hacía, me percaté de que algo se le cayó de la boca.<new_line>El Burro salió corriendo, rebuznando con fuerza en busca de ayuda.]],
				['2'] = [[Le tiré la cuerda alrededor del cuello, ¡pero el Burro la mordió! Mientras lo hacía, me percaté de que algo se le cayó de la boca.<new_line>El Burro salió corriendo, rebuznando con fuerza en busca de ayuda.]]
			}
		},
		['construction_team_fight'] = {
			['episode'] = {
				['1'] = [[Cerca de su campamento, vi todo tipo de cosas del campo de trabajo amontonadas en una pila. Cuando me vieron, los bandidos se rieron:<new_line>«Vaya, ahora los trabajadores nos traen las herramientas ellos mismos. Entonces, ¿estás listo para el trabajo duro y un combate feroz?».]]
