		['firebird_feather'] = {
			['episode'] = {
				['1'] = [[Dejé las manzanas encantadas en la nieve, las cubrí de miel y, acto seguido, me escondí entre unos arbustos. Me cuesta creer que vaya a aparecer un ave de cuento, pero la esperanza es lo único que me queda.]],
				['4'] = [[El ave de fuego, cansada tras la pelea, decide retirarse y sale volando hasta que no se vislumbra más que una mota resplandeciente en el cielo. Lo único que queda atrás es una pluma en la nieve derretida.]],
				['3'] = [[Me acerco al ave de fuego sin apenas hacer ruido y la agarro de la cola... pero tengo que apartar la mano inmediatamente porque quema. El ave chilla y sale volando disparada, pero no tarda en dar la vuelta y cargar contra mí. Me da que tendré que luchar para conseguir la pluma.]],
				['2'] = [[Pasado un rato, vi aparecer al ave. Era una criatura bastante grande que se asemejaba a un pavo real, solo que irradiaba luz y calor. Se posó junto a las manzanas y empezó a inspeccionarlas.]]
			},
			['name'] = [[pájaro de fuego]],
			['choice'] = {
				['1'] = [[Acercarse disimuladamente]]
			}
		},
		['rabbit_teleport'] = {
			['episode'] = {
				['1'] = [[Al acercarme a la madriguera de conejos, oí una voz en mi cabeza:<new_line>«Hola, trotamundos. Veo que te vas de viaje».]],
				['4'] = [[«Ahora, salta».]],
