				['1'] = [[«¿Qué? No te entiendo».]],
				['2'] = [[«Adelante».]]
			},
			['episode'] = {
				['1'] = [[«¿Uhhh? ¡Oooh!».]],
				['4'] = [[«Uh... ¡Eh, aah buh oooh!».<new_line><new_line>Parece que Papá Cavernoel entendió algo. Nos agita la mano para que lo sigamos.]],
				['3'] = [[Papá Noelgocios señala con el dedo el árbol a medio decorar, luego me señala a mí y levanta las manos. El Papá Noel recién llegado se rasca la cabeza con su garrote.]],
				['2'] = [[«Jo, jo, jo, este nos lo va a poner difícil. Pero, ya sabes, cuando la naturaleza priva a alguien... de ciertas habilidades, les da... ¡Lo que quiero decir es que es un rastreador estupendo! Deja que lo intente».]]
			}
		},
		['halloween_def_hard'] = {
			['episode'] = {
				['1'] = [[«¿Has venido a ayudar o solo piensas mirar?».]],
				['12'] = [[«Abracada... Lo bastante cerca. Servirá. Las bestias se están acercando. Prepárate».]],
				['13'] = [[«Abracada... Lo bastante cerca. Servirá. Las bestias se están acercando. Prepárate».]],
				['5'] = [[El mago agitó el bastón con bastante pereza.<new_line>«Abracadabúm. Hecho. Espero no haber desperdiciado todo este poder en ti para nada».<new_line><new_line>Ahora el aullido de las bestias parece estar muy cerca.]],
				['4'] = [[«He luchado durante días sin descanso ¡¿y ahora quieres que trace un plan?! Dios, eres un inútil. De acuerdo, creo que los monstruos se acercan de nuevo. ¿Por qué no te echo un hechizo e intentas ser un poco más útil? Dime qué hechizo quieres que te eche».]],
				['3'] = [[«En marcha. No te quedes ahí parado».]],
				['2'] = [[«¡No te quedes ahí parado! ¿Tengo que derrotar a las hordas de monstruos y defender la aldea yo solo? Alguien podría mover aunque solo fuera un dedo para ayudarme, ¿no?».]],
				['9'] = [[«Intentaste ayudarme, pero no sirvió de mucha ayuda. Tal vez ahora puedas trazar un plan un poco más inteligente, ¿no? Elige qué hechizo quieres esta vez».]],
				['7'] = [[El mago agitó el bastón con bastante pereza.<new_line>«Abracadabúm. Hecho. Espero no haber desperdiciado todo este poder en ti para nada».<new_line><new_line>Ahora el aullido de las bestias parece estar muy cerca.]],
				['10'] = [[El mago me empujó con su bastón y dijo:<new_line>«Abracadabra. Hecho».<new_line><new_line>Las bestias se están acercando de nuevo.]],
				['11'] = [[«Solo fuiste un 5 % útil. ¿Por qué tendría que malgastar magia contigo? Tal vez puedas esforzarte un poco más, ¿no? Piensa bien qué hechizo quieres que te eche para que puedas ser útil de verdad».]],
				['14'] = [[El mago bostezó con bastante fuerza.<new_line>«Uf. He hecho un buen trabajo. Hay otros que deberían tomar notas... Por desgracia, el códice del mago establece que cualquier persona que supuestamente me ayude debe recibir una nota. Al leerla, puede invocarme en un momento de necesidad, bla, bla, bla...<new_line>Pero recuerda, si no se trata de una necesidad real, te arrepentirás. Fin de la historia».]]
			},
