				['1'] = [[«He venido para ayudar».]],
				['5'] = [[«Quiero ser fuerte como Hércules».]],
				['4'] = [[«Quiero volar cual pájaro».]],
				['3'] = [[«¿Cuál es el plan? ¿Qué hacemos?».]],
				['2'] = [[«Solo pasaba por aquí».]],
				['9'] = [[«Gracias». (Vete)]],
				['8'] = [[Ataca]],
				['7'] = [[«Quiero correr cual lobo».]],
				['6'] = [[«Quiero tener la vista de un águila».]]
			}
