				['3'] = [[«A por ello, entonces».]],
				['2'] = [[«¿Todos listos?».]]
			},
			['episode'] = {
				['1'] = [[«Aún queda una segunda esclusa de energía. Mismo plan de acción. Elige a tus ayudantes. Pero deja que los que lucharon la última vez descansen aquí mientras vigilan».]],
				['4'] = [[«¡Eee aaaaa!».]],
				['3'] = [[«¡Es hora de darle a la aspiradora de Krampus más magia de la que puede absorber!».]],
				['2'] = [[«¡Jo, jo, jo, buen trabajo! ¿Ya hemos minimizado los riesgos?».]]
			}
		},
		['halloween_def_medium'] = {
			['episode'] = {
				['1'] = [[«¡Hola! Gracias por responder a nuestras invocaciones. Estas bestias nos han puesto de los nervios. Antes podíamos encargarnos de ellas nosotros mismos, pero capturaron a nuestro comandante hace poco en una escaramuza. Temo que, sin él, esta podría ser nuestra última batalla. Pareces un hombre competente. ¿Puedes reemplazar a nuestro comandante al menos en esta batalla?».]],
				['15'] = [[«¡Sí, señor! ¡Nos moveremos a sus órdenes!».]],
				['12'] = [[«¡Gracias por ayudarnos! Te recordaremos como nuestro mejor comandante. ¡Después de nuestro preferido, claro!».]],
				['13'] = [[«¡Esperamos un ataque enemigo a gran escala desde la dirección de la sauna! El enemigo hará uso de todas las fuerzas que tenga a su disposición. Los habitantes de la aldea están asustados. Si el enemigo se aproxima, podrían asustarse aún más. Tenemos que contraatacar».]],
				['5'] = [[«¡Sí, señor, comandante, señor! Oigo a las bestias. ¿Cuál es nuestra táctica de batalla?».]],
				['2'] = [[«¡Sí, señor!».<new_line>Los cabezas de calabaza formaron filas.]],
				['19'] = [[«Vaya, ¿tú otra vez, niño? ¿Es que no puedes hacer nada tú solito? Cuando tenía tu edad, yo lo hacía todo por mi cuenta. De acuerdo. Muéstrame quién se está portando mal contigo».]],
				['14'] = [[«¡Sí, señor! ¡Terminando insubordinación!».]],
