		['newyearEvent_commander_hard'] = {
			['episode'] = {
				['14'] = [[«Ha sido una batalla feroz, ¡y así es como nos gustan! Ahora dejemos descansar a nuestro contendiente preferido. ¡Veamos la próxima batalla!».]],
				['15'] = [[«¡Y ya estamos de vuelta! Y ahora, sin más eventos ordinarios, ¡la batalla del campeonato! ¡La batalla en la que se enfrentarán los mejores entre los mejores!<new_line>¡El único, el inigualable, el asombroso, el preferido por los seguidores, Papá Noel se enfrentará al gran y sabio Mago! Esta será la batalla del siglo, así que no perdamos más el tiempo. ¡A luchar!».]],
				['12'] = [[«¡En la primera batalla de las semifinales estarán el entrañable Papá Noel y el nada entrañable Papá Noelgocios! Veamos quién de ellos pasa a la siguiente ronda».]],
				['13'] = [[«¡En la primera batalla de las semifinales estarán el entrañable Papá Noel y el encantador Papá Cavernoel! Veamos quién de ellos pasa a la siguiente ronda».]],
				['11'] = [[Mientras descansaba, tuvieron lugar algunas batallas. Después hubo un breve descanso y luego, por fin, todos se reunieron de nuevo.<new_line><new_line>«¡Es un placer daros la bienvenida a las semifinales! Hemos perdido al elfo reincidente, a la banda de muñecos de nieve, a yeti y a la panda de sabandijas».]],
				['10'] = [[El público enloquece y Cibernoel vuelve a la carga.<new_line>«¡Menuda batalla! ¡Le deseamos al perdedor un buen viaje de vuelta a casa y le damos la bienvenida a Papá Noel a las semifinales!».]],
				['9'] = [[«Y en la batalla inicial de los cuartos de final, ¡tenemos a la panda de sabandijas contra el inimitable Papá Noel!».]],
				['8'] = [[«Y en la batalla inicial de los cuartos de final, ¡tenemos a la banda de muñecos de nieve contra el inimitable Papá Noel!».]],
				['7'] = [[«Y en la batalla inicial de los cuartos de final, ¡tenemos a yeti contra el inimitable Papá Noel!».]],
				['6'] = [[«Y en la batalla inicial de los cuartos de final, ¡tenemos al elfo reincidente contra el inimitable Papá Noel!».]],
				['1'] = [[«¡Chum! ¡Ya pensaba que no ibas a venir! El torneo está a punto de empezar. ¡Date prisa y apúntate! Tienes que ganarte la reputación de Papá Noel molón».]],
				['5'] = [[«Os presentaré a los luchadores de hoy. El terrible y codicioso corporativista, ¡Papá Noelgocios! Un visitante de la Edad de Piedra, ¡Papá Cavernoel! El gran cultivador de la magia, ¡Mago Noel! ¡Una panda de sabandijas despiadadas!<new_line>Tan fría como mi corazón, ¡la banda de muñecos de nieve! ¡El misterioso yeti! ¡El rebelde elfo reincidente! Y el único e inigualable, el campeón de vuestra ciudad natal, ¡Papá Noel!».]],
				['4'] = [[La cantidad de personas que hay alrededor de la cúpula no para de crecer. Algunos de ellos incluso se están subiendo a ella. Pasado un rato, Cibernoel aparece en el escenario junto a la cúpula.<new_line>«¡Qué pasa, chums! ¡Es un honor daros la bienvenida al torneo Navidades Geniales!».]],
				['3'] = [[«Bueno, tengo que ir al micrófono a presentar. Espera junto a la entrada de la cúpula».]],
				['2'] = [[«Básicamente, estas son las reglas: todos usan equipo previamente autorizado, así que no puedes fiarte de los ciberterminales. También tienes que consultar la lista de trucos prohibidos... ¡Y no se permiten mascotas!».]],
				['18'] = [[«¡Eres estupendo, chum! Si alguna vez las dudas sobre tu genialidad me sobrecargan la CPU, siempre recordaré este momento. Has alegrado a la gente y les has devuelto un poco la fe de que tendrán a su increíble Papá Noel».]],
				['17'] = [[«¡Y ya estamos de vuelta! Y ahora, sin más eventos ordinarios, ¡la batalla del campeonato! ¡La batalla en la que se enfrentarán los mejores entre los mejores!<new_line>¡El único, el inigualable, el asombroso, el preferido por los seguidores, Papá Noel se enfrentará a... Mmm... Eh... Debe tratarse de un error... ¿No? Se enfrentará a Papá Noelgocios. Seguro que será pan comido para nuestro mejor contendiente».]],
				['16'] = [[«¡Y ya estamos de vuelta! Y ahora, sin más eventos ordinarios, ¡la batalla del campeonato! ¡La batalla en la que se enfrentarán los mejores entre los mejores!<new_line>¡El único, el inigualable, el asombroso, el preferido por los seguidores, Papá Noel se enfrentará al alegre hombretón duro como la piedra, Papá Cavernoel! Esta será la batalla del siglo, así que no perdamos más el tiempo. ¡A luchar!».]]
			},
			['choice'] = {
				['1'] = [[«¿Qué tipo de torneo?».]],
				['6'] = [[Descansa]],
				['5'] = [[Ve a la arena]],
				['4'] = [[Aplaude]],
