		['new_year_boss_6'] = {
			['choice'] = {
				['1'] = [[Sal de detrás de la pila]],
				['5'] = [[«¡Eres un monstruo!».]],
				['4'] = [[«¿Porque Krampus Noel se los llevó?».]],
				['3'] = [[«Jamás».]],
				['2'] = [[«¿La misma oferta que le hiciste a Papá Noel?».]],
				['9'] = [[«¡Habéis venido!».]],
				['8'] = [[«¡Sí!».]],
				['7'] = [[«Krampus, fuiste Papá Noel una vez, ¡vamos!».]],
				['6'] = [[«¡Eres un embustero! ¡Te voy a matar!».]],
				['10'] = [[«Te has convertido en...».]]
			},
			['episode'] = {
				['1'] = [[«De acuerdo, ¿estás listo para teletransportarte o tengo que esperarte aquí afuera congelándome de frío?».]],
				['12'] = [[«¡Nuestra magia se liberó y tenía que asegurarme de que no lo estropearas todo! Mmm, quiero decir... Gracias por restaurar nuestra magia o lo que sea que hayas hecho».]],
				['13'] = [[«¿Eh? Qué ocurre?».]],
				['5'] = [[«¡Escúchame! Solo necesito adornos de ese árbol tuyo. Son las piezas clave de la superarma. ¿Por qué crees que no había adornos en el árbol la primera vez que te lo encontraste?».]],
				['4'] = [[Krampus Noel se estremeció y Escarchín dejó escapar una risa repulsiva.<new_line>«Ja, tenía razón: no eres tonto. Este saco de basura no pudo encargarse de ti ni cuando lo tenía todo a su favor.<new_line>¡Imagina qué clase de poder podríamos alcanzar tú y yo si uniéramos nuestras fuerzas!».]],
				['3'] = [[«¡Eh! ¿Cómo pasaste mis... Parece que no eres tan tonto como pensaba. Tengo una oferta que hacerte».]],
				['2'] = [[En un sótano, hay una pila de adornos y de todo tipo de tornillos y de herramientas.<new_line><new_line>Desde el otro lado de la pila, oigo la voz de Escarchín.<new_line>«No, ¡ahora yo estoy al mando! ¡Gracias a ti, estamos de nuevo en un sótano mohoso!».]],
				['9'] = [[«Destruí casi toda la Navidad con mis propias manos. Tengo el cerebro envenenado y ya no se pueden arreglar las cosas... ¡Pero no permitiré que la esperanza muera!».<new_line><new_line>Krampus Noel agita su bastón y golpea la pieza que Escarchín sacó del arma destrozada.]],
				['8'] = [[«Mmm, parece que las primeras impresiones sí que son las más exactas. Después de todo, eres tonto. Krampus, ¡deja de jugar con la batería mágica y encárgate de este imbécil!».]],
				['7'] = [[«¡Ninguno sacará provecho de pelear! En vez de eso, unamos nuestras fuerzas.<new_line>Escucharé tus sugerencias sobre cómo actuar de manera más humana. ¡Yo mismo tengo un par de ideas! Entonces, ¿qué? ¿Hay trato?».]],
				['6'] = [[«¡Sí! Fue obra de este viejo idiota. Por aquel entonces, aún se hacía llamar "Papá Noel" y quería desperdiciar el poder de estos adornos en tonterías: protección contra la radiación, fuentes de energía alternativas. Pfff.<new_line>Pero, por suerte, conoció a un genio como yo a tiempo y logré encaminarlo hacia la dirección correcta: el poder».]],
				['14'] = [[«¡Hablamos más tarde, socio! Después de esa gran descarga de magia, ¡todo este lugar está empezando a derrumbarse! ¡Cojamos a Krampus y larguémonos de aquí!».]],
				['11'] = [[«¡Eee! ¡Aagaaah!».]],
				['10'] = [[«¡Cómo te atreves! ¡Viejo inútil! ¡La magia de los Noeles es mía! ¡Yo gobernaré sobre el invierno nuclear y vosotros moriréis! ¡Todos vosotros moriréis!».]]
			}
		},
		['boatman_olonec'] = {
			['episode'] = {
				['1'] = [[Concentrado en su tarea, el barquero trastea con el motor de la vieja lancha motora.]],
				['5'] = [[«Por cierto, mientras te encargabas de acomodarte y sentirte como en casa, he arreglado mi vieja lancha para ayudarte con la misión. Tendrás que encargarte de conseguir combustible, aunque hay un comerciante que lo vende en la aldea.<new_line><new_line>Una vez en el lago, navega al oeste. Espero volver a verte. Cuídate».]],
				['4'] = [[«Se encuentra en la zona sur de Leningrado. Espero no tener que recordarte lo peligroso que es ese sitio».]],
				['3'] = [[«Hola. En la aldea todo el mundo habla de ti. Buen trabajo ayudando a los locales».]],
