	['barQuest'] = {
		['headless_horseman'] = {
			['step'] = {
				['1'] = [[Aku harus mencegat Penunggang Kuda dan mengambil bukunya. Aku mendapatkan bola benang untuk membantuku menemukannya.]]
			},
			['name'] = [[Penunggang Kuda Tanpa Kepala]],
			['text'] = [[Gadis yang kau selamatkan tadi di sini dan ia menceritakan percakapan yang ia dengar di kapal. Nampaknya Drakula ingin menggunakan buku ini untuk membangkitkan Sang Dewa Kuno dan menghancurkan perbatasan antar dunia. Ia harus dihentikan. Cegat Penunggang Kuda dan ambil bukunya.]]
		},
		['snowmen_fortress'] = {
			['step'] = {
				['1'] = [[Tugasku adalah menghancurkan benteng salju dan menyingkirkan boneka-boneka salju di sana.]]
			},
			['name'] = [[Benteng Salju]],
			['text'] = [[Boneka-boneka salju itu membangun benteng salju utuh dan merusak wilayah. Tolong pergi ke sana dan hentikan ulah mereka.]]
		},
		['catch_thief'] = {
			['step'] = {
				['9'] = [[Mandor gudang meminta bantuan tambahan—untuk mengatasi komplotan si pencuri.]],
				['8'] = [[Mandor gudang meminta bantuan tambahan—untuk membawa kembali barang yang dijarah.]],
				['7'] = [[Mengetahui bahwa orang lain akan disalahkan, Aku mengambil beberapa barang dari gudang.]],
				['6'] = [[Aku tidak punya keahlian menjadi penjaga. Pencuri berhasil kabur.]],
				['5'] = [[Aku membuat kesepakatan dengan para bandit dan membiarkan mereka masuk ke gudang, lalu aku mengeluarkan mereka semua.]],
				['4'] = [[Aku berhasil menangkap pencuri itu hidup-hidup.]],
				['3'] = [[Pencuri itu terjebak dalam perangkapku dan mati.]],
				['2'] = [[Aku membubarkan kamp bandit yang mencuri dari markas. Ini harus berjalan lancar mulai sekarang.]],
				['1'] = [[Hal paling pertama, aku harus pergi ke gudang dan berbicara dengan kepala.]],
