		['progress_magnitogorsk'] = {
			['step'] = {
				['1'] = [[Selama pemboman Ural, di tambang Magnitogorsk itulah mereka yang saat ini memimpin Iron Front berlindung. Sekarang banyak orang bekerja keras di tambang itu, tetapi banyak dari mereka tidak melakukan pekerjaan yang melelahkan ini dengan sukarela.<new_line><new_line>Aku yakin aku cukup kuat untuk menempatkan diriku di sini sebagai mitra, dan bukan sebagai sumber tenaga kerja gratis.]]
			}
		},
		['stash_polar_fox'] = {
			['step'] = {
				['1'] = [[Rubah kutub menemukan sesuatu yang menarik di dekatnya. Coba cari tahu.]]
			}
		},
		['progress_omsk'] = {
			['step'] = {
				['1'] = [[Omsk memisahkan diri dari S.O.V.A., dan sekarang dijalankan oleh militer. Satu-satunya jalur perdagangan antara Timur dan Barat melewati permukiman.<new_line>Tampaknya itulah yang memungkinkan permukiman tertutup seperti ini bertahan.<new_line><new_line>Namun, petunjuk dan rumor menunjukkan bahwa militer memiliki hal lain yang menghalangi kekuatan Ural dan Siberia lainnya. Kamu bisa mencoba mencari tahu lebih banyak tentang Omsk, tetapi hanya sedikit orang yang berhasil menjalin hubungan bahkan dengan batas luar kota.]]
			}
		},
		['sled_repair'] = {
			['step'] = {
				['1'] = [[Gadis Salju memintaku memperbaiki kereta luncurnya.]]
			},
			['name'] = [[Perbaikan Kereta Luncur]],
			['text'] = [[Aku sudah menempuh jarak sejauh ini bersamamu hingga kereta luncurku terlihat mengerikan. Bisakah kau memperbaikinya?]]
		},
		['progress_leningrad'] = {
			['step'] = {
				['1'] = [[Kamp Wisatawan yang terbengkalai berada tepat di luar hutan yang hangus karena radiasi dan padang gurun di sekitar Leningrad. Beberapa pemberani dari Tver membuat pos pemberhentian di sini.<new_line><new_line>Para "Wisatawan" (sebutan penjelajah veteran zona bahaya) meluncurkan ekspedisi menjarah area Leningrad, membawanya kembali ke markas, lalu mengirimkannya lewat sungai ke Tver.]]
			}
		},
		['supply'] = {
			['step'] = {
				['5'] = [[Aku tidak menerima imbalan apa pun. Aku kira barang ini bisa berguna.]],
				['4'] = [[Aku diberi imbalan untuk pekerjaanku, tetapi mereka bisa saja sedikit lebih murah hati.]],
				['3'] = [[Kerja kerasku tidak sia-sia.]],
				['2'] = [[Aku telah menemukan semua yang aku butuhkan. Sekarang, aku hanya perlu mengantarkan kargo ke markas.]],
				['1'] = [[Aku telah dikirim untuk menemukan semua kebutuhan markas lainnya.]]
			},
			['name'] = [[Pengiriman]],
			['text'] = [[Markas tetangga telah meminta beberapa persediaan kepada kita. Bisakah kau mengumpulkan semuanya dan membawanya?']]
		},
		['forester'] = {
			['step'] = {
				['1'] = [[Seseorang meninggalkan pesan pribadi untukku. Aku harus membantu seorang penebang kayu. Ia menjanjikan bayaran besar.]]
			},
			['name'] = [[Penebang Kayu]],
			['text'] = [[Seorang pria datang dan meninggalkan pesan untukmu. Seorang penebang kayu butuh bantuan. Sepertinya kerja mudah dengan hadiah lumayan. Menurutku ambil saja.]]
		},
		['progress_olonec'] = {
			['step'] = {
				['1'] = [[Permukiman dengan penduduk pertama di perjalananku. Rasanya menegangkan bisa berada di antara orang-orang lagi. Di sini, setiap orang telah terbiasa mengandalkan satu sama lain dan pada sumber daya mereka sendiri untuk mengatasi kesulitan. Mereka hidup dengan cara yang sama di sini seperti yang mungkin mereka lakukan seabad yang lalu.<new_line><new_line>Nelayan tidak pergi melampaui Danau Ladoga, dan aku ragu bisa menemukan jawaban atas semua pertanyaanku di sini, tetapi ini adalah tempat bagus untuk mendapatkan berita dan mempersiapkan perjalanan lebih jauh.]]
			}
		},
		['progress_izhevsk'] = {
			['step'] = {
				['1'] = [[Izhevsk dibagi dari Iron Front oleh Pegunungan Ural dan berkilo-kilo bumi yang terinfeksi, yang dipenuhi mutan. Mungkin itu sebabnya Iron Front masih belum menguasai industri senjata lokal.<new_line><new_line>Jadi, para pemukim dari Izhevsk ingin mempertahankan kontak dengan Ryazan—jaraknya jauh dan mereka bisa dengan tenang memperdagangkan senjata mereka dan produk dari industri pembantaian lebah mereka.]]
			}
		},
		['progress_irkutsk'] = {
			['step'] = {
				['1'] = [[Terkadang keajaiban memang terjadi, dan salah satunya terjadi di Irkutsk. Baik Pembangkit Listrik Tenaga Air Angara maupun perairan Danau Baikal tidak terpengaruh oleh bencana tersebut. Orang-orang dari Irkutsk sekarang menjaga pembangkit listrik tenaga air ini seperti anak mereka sendiri. Mereka bahkan bermukim tepat di bendungan dan sekarang menyediakan listrik dan air bersih ke seluruh S.O.V.A.]]
			}
		},
		['immortal_love'] = {
			['step'] = {
				['1'] = [[Tugasku adalah menyelamatkan seorang gadis dari sarang mumi. Untuk menemukannya, aku harus memancing kalajengking dan mengikuti mereka.]]
			},
			['name'] = [[Mumi]],
			['text'] = [[Aku berhasil memecahkan kode entri terakhir dari catatan. Tertulis tentang seorang gadis yang diculik atas perintah mumi. Kita harus menyelamatkannya. Ikuti kalajengking untuk menemukan sarang mumi. Pancing mereka keluar, dan mereka akan membawamu ke tuan mereka.]]
		},
		['ice_brigands'] = {
			['step'] = {
				['1'] = [[Aku harus mengalahkan sekelompok penjarah es yang berbahaya.]]
			},
			['name'] = [[Penjarah]],
			['text'] = [[Binatang-binatang melapor ada sekelompok penjarah es menyerbu wilayah sekitar. Para penjarah ini sangat berbahaya dan kamu harus menghentikan mereka.]]
		},
		['passenger'] = {
			['step'] = {
				['2'] = [[Pemukim berhasil mencapai tujuannya dalam keadaan utuh.]],
				['1'] = [[Salah satu pemukim lokal mempekerjakanku untuk mengantarnya dengan aman ke markas lain.]],
				['3'] = [[Aku tidak bisa melindungi rekanku. Dia mati sebelum mencapai tujuannya.]]
			},
			['name'] = [[Penumpang]],
			['text'] = [["Hai! Bisa aku menumpang ke permukiman berikutnya? Bepergian melalui padang gurun sendirian berbahaya, dan kau terlihat seperti teman yang bisa diandalkan."]]
		},
		['cold_footprints'] = {
			['text'] = [[Kelinci-kelinci berhasil menemukan kamp para penjarah es yang menyerbu terem. Aku akan menandainya di petamu. Segera ke sana dan cari tahu tentang apa yang ada di sana sebisamu.]],
			['name'] = [[Di Jalan Dingin]]
		},
		['toy_story'] = {
			['text'] = [[Berkatmu, sebagian dari sihir sudah dipulihkan dan aku menemukan jalan menuju terem kami, tapi aku takut jika di sana ada jebakan. Tolong lihat dan cari tahu apa yang ada di sana, tapi kumohon, berhati-hatilah.]],
			['name'] = [[Kisah Mainan]]
		},
		['reinforcement'] = {
			['step'] = {
				['2'] = [[Pertempuran berakhir. Aku bisa kembali ke markas untuk mendapatkan hadiahku.]],
				['1'] = [[Aku harus cepat dan membantu orang-orang kita. Kita tidak tahu berapa lama mereka akan bertahan tanpa bantuan.]],
				['4'] = [[Kita melawan mereka, tapi bukan tanpa korban.]],
				['3'] = [[Dengan bekerja sama, kita berhasil mengalahkan mereka.]]
			},
			['name'] = [[Bantuan]],
			['text'] = [[Pesan mendesak tiba: sebuah unit dari markas kita meminta bantuan. Pergi bantu mereka.']]
		},
		['gift_delivery'] = {
			['step'] = {
				['1'] = [[Aku diminta mengirimkan beberapa hadiah.]]
			},
			['name'] = [[Pengiriman Hadiah]],
			['text'] = [[Hadiah harus dikirimkan apa pun yang terjadi. Ambil kotak-kotak ini dan berikan kepada orang-orang. Saat ini, mereka membutuhkan dukungan lebih dari sebelumnya.]]
		},
		['pumpkin_seed'] = {
			['step'] = {
				['1'] = [[Aku mendapatkan biji yang harus kutanam di padang gurun. Entah apa yang ditakuti bartender, atau bagaimana ini bisa membantu kami. Terlihat tidak berbahaya sama sekali.]]
			},
			['name'] = [[Biji Labu]],
			['text'] = [[Jika kepala itu berkata sejujurnya, tamatlah kita. Tetapi ada satu kesempatan, walaupun aku berharap tak pernah perlu menggunakannya. Bawa biji labu ini dan tanam di padang gurun.]]
		},
		['bandit_camp'] = {
			['step'] = {
				['6'] = [[Aku berhasil membuat kesepakatan dengan para bandit. Mereka tidak akan mengganggu kita lagi.]],
				['5'] = [[Aku sudah membunuh semua bandit.]],
				['4'] = [[Sekarang setelah aku selesai dengan para bandit, saatnya untuk kembali ke markas.]],
				['3'] = [[Para bandit seharusnya sudah pergi sekarang. Mari kita pergi memeriksa kamp untuk memastikan.]],
				['2'] = [[Kesepakatan adalah kesepakatan. Aku harus memberi waktu pada bandit untuk pergi.]],
				['1'] = [[Kepala penjaga memberi tahu aku lokasi kamp bandit itu. Aku harus pergi ke sana.]]
			},
			['name'] = [[Kamp Bandit]],
			['text'] = [[Para bandit sangat bertingkah akhir-akhir ini. Mereka mendirikan kemah tepat di depan pintu kita. Bisakah kau menghadapi mereka?']]
		},
		['letter_of_hope'] = {
			['step'] = {
				['1'] = [[Gadis Salju memintaku mengumpulkan surat-surat yang ditinggalkan untuk Kakek Salju.]]
			},
			['name'] = [[Surat Berisi Harapan]],
			['text'] = [[Bahkan di masa-masa sulit ini, orang-orang masih menulis surat untuk Kakek Salju. Mereka meninggalkannya di tempat tersembunyi dan sudut yang berbeda-beda. Kumpulkan surat-surat ini dan berikan padaku.]]
		},
		['progress_briansk'] = {
			['step'] = {
				['1'] = [[Selama bencana, ada arus besar pengungsi melewati Bryansk, dan tingkat kematian yang sangat tinggi di antara mereka. Namun, akibatnya, jumlah penyintas juga sangat tinggi, dan mereka sudah memiliki kekebalan.<new_line><new_line>Masalahnya adalah mereka masih terjebak di sekitar Bryansk, tanpa organisasi atau dukungan nyata. Berbagai pihak sudah mengincar permukimannya: banyak yang ingin mengeksploitasi sumber daya manusia seperti itu. Lalu, seperti yang ditunjukkan oleh pengalaman pahit, bukan untuk tujuan mulia apa pun.]]
			}
		},
		['gravedigger'] = {
			['step'] = {
				['1'] = [[Aku disuruh menghadapi penggali kuburan gila.]]
			},
			['name'] = [[Penggali Kuburan]],
			['text'] = [[Kaulah orang yang kubutuhkan. Aku ada pekerjaan untukmu. Seorang penggali kuburan menjadi gila. Ia menggali mayat, alih-alih menguburnya. Kita perlu mengakhiri itu. Bisakah kau tangani?]]
		},
		['new_year_boss_2022_1'] = {
			['step'] = {
				['1'] = [[Sinterklas baru muncul di sebelah pohon. Aku harus mencari tahu apa yang dia punya.]]
			}
		},
		['search_killer'] = {
			['step'] = {
				['1'] = [[Aku harus melacak sang pembunuh dan menghentikannya. Ia di suatu tempat di Dunia Atas.]]
			},
			['name'] = [[Temukan Pembunuhnya]],
			['text'] = [[Sang pembunuh mencuri lewat portal ke Dunia Atas. Kau harus mencari dan menghentikannya.]]
		},
		['order'] = {
			['name'] = [[Pesanan]],
			['text_barman'] = [["Kita perlu menstok lagi isi dapur. Bisakah kau membawakanku beberapa bahan makanan?"]],
			['step'] = {
				['2'] = [[Aku sudah mengumpulkan semua yang mereka minta. Saatnya kembali ke permukiman.]],
				['1'] = [[Aku setuju akan mengambil pekerjaan untuk permukiman ini. Aku perlu mendapatkan persediaan penting untuk mereka.]],
				['3'] = [[Aku mengirimkan semua persediaan yang diperlukan.]]
			},
			['text_nurse'] = [["Beberapa obat akan segera habis. Namun, orang-orang masih sakit. Bisakah kau membantu mengambil obat lagi?"]],
			['text_workshop'] = [["Persediaanku tidak cukup untuk menyelesaikan pekerjaanku. Bisakah kau mengambilkannya?"]],
			['text'] = [[Aku mendapat pesanan baru untuk beberapa persediaan. Tidak perlu terburu-buru. Bisakah kau menyiapkan semuanya untukku? Ini daftarnya.']]
		},
		['drakula'] = {
			['step'] = {
				['1'] = [[Inilah pertempuran terakhir, dan musuhku adalah Pangeran Drakula.]]
			},
			['name'] = [[Pangeran Drakula]],
			['text'] = [[Si Kepala Labu bertemu menghadapi Sang Dewa Kuno di padang gurun. Drakula pasti ada di sana. Temukan dan hancurkan dia, sebelum ia melakukan apa pun.]]
		},
		['doll'] = {
			['step'] = {
				['1'] = [[Bartender menandai bengkel dalang di peta. Mungkin jawabannya ada di sana.]]
			},
			['name'] = [[Boneka]],
			['text'] = [[Kau menanyakan soal boneka itu. Aku hanya tahu satu orang yang bisa mengendalikannya. Sang dalang adalah orang yang jahat, tetapi tidak ada pilihan lain. Akan kutandai di peta tempat kau bisa mencarinya.]]
		},
		['snowmen'] = {
			['step'] = {
				['1'] = [[Aku harus pergi ke tempat itu dan membasmi boneka-boneka salju.]]
			},
			['text'] = [[Boneka-boneka salju jahil mulai membuat masalah dan menyerang orang-orang, tumbangkan mereka. Biarkan mereka terkapar di sana dan merenungkan perilaku mereka—Kakek Salju dan aku akan menumpuk mereka lagi nanti.]]
		},
		['progress_krasnoyarsk'] = {
			['step'] = {
				['1'] = [[Permukiman Krasnoyarsk seperti utopia sains nyata. Orang-orang bekerja di rumah kaca, diarahkan oleh para ilmuwan. Sistem BIOS-4 yang mereka kembangkan memungkinkan untuk mendapatkan panen yang belum pernah terjadi sebelumnya dan bahkan memberi makan permukiman lain. Tidak gratis tentunya.<new_line><new_line>Semua ada harganya: prosesnya memerlukan penggunaan listrik dan air bersih yang signifikan. Selain itu, prosesnya sangat canggih sehingga kehilangan satu pekerja utama saja bisa meruntuhkan seluruh sistem.]]
			}
		},
		['flying_dutchman'] = {
			['step'] = {
				['1'] = [[Bartender memberiku lonceng yang akan membawaku ke kapal hantu di mana Igor mungkin bersembunyi dengan bukunya.]]
			},
			['name'] = [[Dutchman Terbang]],
			['text'] = [[Kau bilang bukunya tak bisa ditemukan di daratan kering? Jika rumornya benar, Dutchman Terbang baru-baru ini kembali ke perairan kita. Kebetulan? Kurasa tidak. Jika kau siap mengejar sampai jauh, ambil lonceng ini. Bunyikan di tepi laut untuk menemukan jalan ke kapalnya.]]
		},
		['firebird_feather'] = {
			['text'] = [[Di dalam dongeng kuno diceritakan bahwa Burung Api tertarik dengan apel emas. Kami tidak memiliki itu, tetapi aku berhasil menyihir beberapa buah apel biasa. Jika kamu mengolesinya dengan madu, burung itu tidak akan menyadari perbedaannya dan seharusnya terbang menghampirimu.]],
			['name'] = [[Bulu Burung Api]]
		},
		['winter_queen'] = {
			['text'] = [[Saatnya menyelamatkan Kakek Salju! Jika kamu sudah siap, gunakan Bintang Kutub itu. Kita akan berangkat menuju istana es bersama-sama dan melawan Ratu Es.]],
			['name'] = [[Ratu Es]]
		},
		['exp_recovery'] = {
			['step'] = {
				['2'] = [[Ingatanku yang hilang sudah kembali.]],
				['1'] = [[Di ambang kematian, aku kehilangan kesadaran dan dengan adanya keajaiban, aku diselamatkan. Namun, ingatanku tentang peristiwa sebelumnya hilang. Untuk mengingatnya kembali, aku harus kembali ke tempat semua hal itu terjadi.]]
			},
			['name'] = [[Tidak sadar]]
		},
		['frozen'] = {
			['step'] = {
				['1'] = [[Para penjarah es menculik orang-orang. Aku harus menyelamatkan mereka.]]
			},
			['name'] = [[Membeku]],
			['text'] = [[Para penjarah es menculik orang dan membekukan mereka dalam balok es! Jika bergegas, kamu masih bisa menyelamatkan mereka. Cepat pergi ke sana, burung hantu akan menunjukkan arahnya.]]
		},
		['mirror_lady'] = {
			['step'] = {
				['1'] = [[Bartender berkata Igor mungkin bersembunyi dengan kekasihnya. Sebaiknya kuperiksa.]]
			},
			['name'] = [[Si Cantik dalam Cermin]],
			['text'] = [[Aku mendengar rumor bahwa Igor sedang merayu Bloody Mary. Ia mungkin bersembunyi dengannya. Tetapi hati-hati, wanita itu sangat berbahaya. Kau harus menyalakan lilin dan memanggilnya di depan cermin. Kemudian ia akan muncul.]]
		},
		['progress_kurgan'] = {
			['step'] = {
				['1'] = [[Anehnya, aku sudah akrab dengan orang-orang Kurgan. Ternyata mereka yang menjalankan seluruh sistem kereta api. Jika ada orang Kurgan yang tidak naik kereta api, dia yang memperbaikinya. Jadi, jelas dengan siapa aku harus perselisihan.<new_line><new_line>Kereta lapis baja mereka kebal terhadap serangan bandit dan mutan, meskipun apa pun bisa terjadi di padang gurun. Oleh karena itu, penting juga bagi Kurgan untuk menjaga hubungan baik dengan mitranya, jika hanya untuk kepentingan layanan bahan bakar dan perawatan kereta apinya.]]
			}
		},
		['camp_supply'] = {
			['step'] = {
				['9'] = [[Sembuhkan sekutu yang terluka di kamp.]],
				['8'] = [[Bantu sekutu melawan serangan ini.]],
				['7'] = [[Kamp sekutu kita sedang diserang. Aku harus memberi tahu permukiman untuk mengirim bantuan.]],
				['6'] = [[Ketika aku tiba di kamp, semua orang sudah mati. Tidak ada seorang pun untuk diberikan perlengkapan.]],
				['5'] = [[Aku satu-satunya yang masih hidup. Sekarang, tidak ada yang memberikan perlengkapan.]],
				['4'] = [[Aku membantu sekutu menangkal serangan, lalu memberi mereka perlengkapan.]],
				['3'] = [[Aku mengirimkan perlengkapan. Sekutu kita senang atas bantuannya.]],
				['2'] = [[Perlengkapan telah dikirimkan dan itu yang terbaik yang bisa kulakukan. Aku kembali ke rumah.]],
				['1'] = [[Aku telah diperintahkan untuk membawa perlengkapan ke kamp sekutu kita.]]
			},
			['name'] = [[Perlengkapan Kamp]],
			['text'] = [[Kamp sekutu kita sedang mengalami masa sulit. Kita telah memutuskan untuk membantu mereka dan mengirimkan beberapa perlengkapan. Bisakah kau membawanya ke mereka?']]
		},
		['halloween_boss_2022_6'] = {
			['step'] = {
				['2'] = [[Keledai itu berhasil melarikan diri, dan binatang buas rawa yang melindunginya mungkin akan muncul. Cepat, waktunya tidak banyak!]],
				['1'] = [[Keledai itu bersembunyi di rawa. Aku harus menemukannya, sebaiknya tanpa bertemu dengan binatang buas yang melindunginya.]],
				['3'] = [[Pencabut Nyawa membuat kontrak denganku, dan sekarang dia ingin mengambil jiwaku. Aku mengetahui bahwa Gigawattor-ku bisa menutup portalnya.<new_line>Aku harus mengalahkan Pencabut Nyawa dalam pertempuran.]]
			},
			['name'] = [[Keledai Bisa Bicara]],
			['text'] = [[Ada Keledai Bisa Bicara yang meneror penduduk setempat: dia menghabiskan hasil panen mereka, mencoba menjual koin palsu, dan dia sudah berbicara panjang lebar kepada tiga petani.<new_line>Penduduk desa sudah sejak lama ingin menjatuhinya hukuman dengan garpu rumput, tapi keledai itu dilindungi oleh makhluk yang tinggal di rawa sekitar.]]
		}
	},
