	['randomEvent'] = {
		['quest_mad_man'] = {
			['episode'] = {
				['2'] = [[Jelas dia tidak menyukai yang aku lakukan. Dia mengeluarkan jeritan gila dan lalu menyerangku dengan tombak!]],
				['1'] = [[Aku menemukan seseorang di dalam rumah! Dia berpakaian lusuh dan tampak kurus. Matanya jelalatan, dan saat dia melihatku, dia berseru pelan dan bersembunyi di balik meja rusak.]],
				['3'] = [[Aku menemukan seseorang di dalam rumah! Dia berpakaian lusuh dan tampak kurus. Matanya jelalatan, dan saat dia melihatku, dia berseru pelan dan bersembunyi di balik meja rusak.]]
			},
			['choice'] = {
				['2'] = [[Mendekat]],
				['1'] = [[Cobalah mengobrol]]
			}
		},
		['emba_various_hard'] = {
			['episode'] = {
				['16'] = [[Aku membayar orang-orang itu untuk bantuan mereka.<new_line>Sekarang aku harus kembali ke ahli Biologi itu dan bertanya jika aku masih perlu mengajukan pertanyaan kepada yang lain.]],
				['15'] = [["Kau memintaku untuk berbicara dengan si mata empat itu lagi? Tidak, ada penyerbuan yang harus aku lakukan.<new_line>Tapi... jika kau mau menggantikan salah seorang pejuangku dalam penyerbuan itu, aku bisa mengutusnya untuk mengobrol dengan para ilmuwan itu."]],
				['14'] = [[Aku selesai membawa orang yang terluka dan mengambil kotak rekam medis pasien itu.<new_line>Sekarang aku harus kembali ke ahli Biologi itu dan mencari tahu jika aku masih perlu bertanya kepada yang lain.]],
				['13'] = [["Oh, maksudmu Alexander? Ya, aku kenal dia. Aku bisa memberimu rekam medis pasien lamaku. Tapi, aku juga perlu bantuan. Kamp ini diserang dan orang yang terluka harus dibawa ke rumah sakitku. Kau bisa melakukannya?"]],
				['2'] = [["Yah, aku sudah berkeliling dan mengajukan pertanyaan kepada banyak orang dan mendatangi banyak tempat... Aku sudah menghabiskan banyak waktu sampai aku menyelesaikannya. Aku tak bisa melakukannya lagi sendiri.<new_line>Bantulah aku mendapatkan bangkai biomutan untuk autopsi lebih dahulu. Bisa dibilang, aku perlu informasi langsung."]],
				['1'] = [["Hey! Apa kabarmu? Aku sedang ada masalah. Aku kehilangan laporan risetku, entah di mana. Dr. Isaev menugaskanku mengumpulkan informasi tentang biomutan dan miselium dari sumber eksternal."]],
				['19'] = [["Nah! Aku juga mengunjungi beberapa orang dan ini sudah cukup untuk laporan itu. Terima kasih banyak, aku tak ingin kehilangan muka di depan Dr. Isaev."]],
				['17'] = [["Ya, itu yang terjadi. Kami meninggalkan rumah kaca itu dengan segera karena risikonya terlalu besar jika mengganggunya. Aku bisa mengantarmu ke sana, tapi aku tak mau pergi tanpa proteksi. Kita harus membuat semacam filter pernapasan."]],
				['18'] = [[Aku menuliskan semua hal penting yang aku lihat di rumah kaca itu dan bahkan membuat beberapa sketsa miselium.<new_line>Sekarang, aku harus kembali ke ahli Biologi itu dan bertanya jika aku masih perlu mengajukan pertanyaan kepada yang lain.]],
				['9'] = [["Kau apa? Menjelaskan tentang miselium kepadamu? Mungkin kau ingin kunci apartemen tempat aku mengambil uangku?!<new_line>Baiklah, bawakan aku bahan untuk penyulingan, lalu aku akan menjelaskannya kepadamu."]],
				['8'] = [["Lihat, ada sebuah pertanian di balik ladang itu. Aku dengar miselium sudah menyelimuti tanaman mereka. Kau perlu mengajukan pertanyaan dan bahkan mereka mungkin bersedia menunjukkan tanaman yang terinfeksi kepadamu."]],
				['7'] = [["Aku sudah pernah berbicara dengan pemimpin pejuang dari kamp di sekitar. Aku tidak tahu apakah dia mau menceritakannya lagi, orang itu tidak terlalu suka bicara. Tapi kau harus mencobanya."]],
				['10'] = [["Ini, bawalah buku catatan ini, aku mencatatkan semua langkah pengembangbiakannya di sini. Dan katakan kepada orang sombong itu untuk mengembalikan catatan ini nanti. Atau aku akan memberinya... suntikan yang memabukkan!"]],
				['11'] = [["Menceritakannya kepadamu? Kau kira aku ini dosen? Semuanya ada di filmku! Duduklah, aku akan tunjukkan dua seri terakhirku."]],
				['3'] = [["Aku akan mengurus autopsi itu sekarang, sementara itu, kau mulailah berkeliling untuk mengajukan pertanyaan kepada orang-orang."]],
				['4'] = [["Aku dan kolegaku pernah mengunjungi seorang pria tua... untuk urusan bisnis. Ternyata dia sangat pintar! Dia hampir berhasil menumbuhkan miselium di ruang bawah tanahnya.<new_line>Intinya, dia bercerita tentang banyak hal kepada kami. Aku mengingat sebagian besar di antaranya, tapi dia enggan berbicara tentang pengembangbiakan miselium. Kau harus mencoba bertanya kepadanya."]],
				['5'] = [["Ada seorang sutradara yang membuat banyak film tentang mutan. Dia bahkan pernah mewawancaraiku, bisa kau bayangkan? Intinya, dia sudah mengumpulkan cukup banyak observasi yang berguna, jadi mintalah dia menceritakannya kepadamu."]],
				['6'] = [["Ada seorang dokter yang mengelola sebuah rumah sakit lapangan tidak jauh dari sini. Dia merawat orang-orang yang terinfeksi oleh spora miselium. Dia pasti punya banyak informasi berguna tentang perkembangan penyakit itu.<new_line>Dia tentu tidak tertarik dengan keuntungan materi, jadi dia pasti membantu kita."]],
				['12'] = [[Berusaha untuk tetap terjaga, aku mencatat bagian-bagian yang aku anggap penting dengan teliti.<new_line>Sekarang aku harus kembali ke ahli Biologi itu dan bertanya jika aku masih perlu mengajukan pertanyaan kepada yang lain.]]
			},
			['choice'] = {
				['9'] = [[Tonton film]],
				['8'] = [[Kembali ke Ahli Biologi]],
				['7'] = [[Pergi ke Petani]],
				['6'] = [[Pergi ke Pemimpin]],
				['5'] = [[Pergi ke Dokter]],
				['4'] = [[Pergi ke Sutradara]],
				['3'] = [["Aku harus pergi ke mana?"]],
				['2'] = [["Bagus. Aku akan pergi dan menembak yang satu itu." (Bertempur)]],
				['1'] = [["Sumber apa ini?"]],
				['11'] = [[Setujui (Bertempur)]],
				['10'] = [[Bawa orang yang terluka]]
			}
		},
		['snowmen_fortress'] = {
			['episode'] = {
				['1'] = [[Benteng buatan boneka salju ini ternyata terbuat dari susunan balok-balok es yang cukup besar. Para penjaga di menara sudah menatapku dengan curiga dari bawah ember mereka, membentuk gumpalan salju bergantian dari tangan satu ke tangan lainnya.]]
			}
		},
		['stock_bandit'] = {
			['episode'] = {
				['6'] = [[Para penjarah itu tak sempat bereaksi terhadap granat dari atas. Dua tewas seketika, dan yang ketiga kabur.]],
				['5'] = [[Tak lama kemudian, para penjarah pergi, membawa semua yang berharga di gudang itu bersama mereka.]],
				['4'] = [[Salah satu dari mereka mendaki naik dan mulai meneliti kotak itu, tetapi sejauh ini aku tidak ketahuan.]],
				['3'] = [[Dengan satu dorongan keras, aku menjatuhkan kotak berat itu. Kotak itu menimpa penjarah, membunuhnya seketika, dan pecah berkeping-keping saat mengenai lantai. Sekarang aku harus berhadapan dengan yang lain.]],
				['2'] = [[Tiga pria bersenjata masuk. Salah satunya duduk tepat di bawah persembunyianku dan memerintahkan yang lain untuk menggeledah gudang itu.]],
				['1'] = [[Saat aku mulai melihat-lihat gudang itu, aku mendengar suara mendekat dari luar. Tampaknya ada yang lain akan masuk—mungkin penjarah.]]
			},
			['choice'] = {
				['6'] = [[Tunggu]],
				['5'] = [[Kalahkan penjarah itu dan mulai melawan penjarah lain]],
				['4'] = [[Bersembunyi dan menunggu]],
				['3'] = [[Dorong kotak ke penjarah]],
				['2'] = [[Mendaki keluar dan bersembunyi di balik kotak]],
				['1'] = [[Sambut mereka dengan perlawanan]],
				['7'] = [[Kabur melalui pintu samping]]
			}
		},
		['newyearEvent_commander_hard'] = {
			['episode'] = {
				['16'] = [["Dan akhirnya kita lanjutkan! Acara berikutnya sangatlah spesial, pertarungan kejuaraan, di mana yang terbaik dari yang terbaik bersaing!<new_line>Kontestan favorit semua orang, Sinterklas, akan melawan pria ceria yang sekeras batu, Cave Santa! Ini akan menjadi pertempuran yang seru, jadi tidak perlu ditunda lagi. Mari kita mulai!"]],
				['15'] = [["Dan akhirnya kita lanjutkan! Acara berikutnya sangatlah spesial, pertarungan kejuaraan, di mana yang terbaik dari yang terbaik bersaing!<new_line>Kontestan favorit semua orang, Sinterklas, akan melawan Wizard yang hebat dan bijaksana! Ini akan menjadi pertempuran yang seru, jadi tidak perlu ditunda lagi. Mari kita mulai!"]],
				['14'] = [["Pertarungan yang sering—seperti yang kita harapkan! Sekarang petarung favorit kita akan beristirahat sejenak. Mari saksikan pertempuran selanjutnya!"]],
				['13'] = [["Pertarungan pembuka semifinal antara kontestan favorit Sinterklas dan Cave Santa yang penuh pesona! Kita lihat siapa yang akan lolos ke babak berikutnya."]],
				['2'] = [["Jadi, singkatnya, inilah aturannya: semua orang menggunakan peralatan yang telah diotorisasi sebelumnya, jadi kau tidak bisa mengandalkan cyberdeck. Kau juga harus memeriksa daftar trik yang dilarang... Dan hewan peliharaan tidak diizinkan!"]],
				['1'] = [["Choomba! Kukira kau tak akan datang! Turnamen akan dimulai sebentar lagi—cepat daftar! Kau harus mendapatkan kredibilitas jalanan sebagai preem Santa."]],
				['17'] = [["Dan akhirnya kita lanjutkan! Acara berikutnya sangatlah spesial, pertarungan kejuaraan, di mana yang terbaik dari yang terbaik bersaing!<new_line>Kontestan favorit semua orang, Sinterklas, akan melawan... Um... Pasti ada kesalahan... Tidak? Dia akan melawan Business Claus. Aku yakin dia akan jadi lawan yang mudah bagi petarung terbaik kita."]],
				['18'] = [["Kau sungguh keren, choomba! Jika keraguan akan kekerenanmu membebani CPU-ku, akan selalu kuingat momen ini. Kau memberikan kegembiraan dan sedikit lebih banyak keyakinan kepada orang-orang bahwa mereka memiliki Sinterklas yang hebat."]],
				['9'] = [["Dan dalam pertarungan pembuka perempat final, geng bandit akan bertarung melawan Sinterklas yang tak tertandingi!"]],
				['8'] = [["Dan dalam pertarungan pembuka perempat final, geng manusia salju akan bertarung melawan Sinterklas yang tak tertandingi!"]],
				['7'] = [["Dan dalam pertarungan pembuka perempat final, Yeti akan bertarung melawan Sinterklas yang tak tertandingi!"]],
				['10'] = [[Kerumunan jadi semakin riuh, dan Cybersanta hidup kembali.<new_line>"Pertempuran yang luar biasa! Selamat jalan bagi tim yang kalah dan kita sambut Sinterklas di semifinal!"]],
				['11'] = [[Saat sedang beristirahat, beberapa pertempuran terjadi. Setelah mereka istirahat sejenak, akhirnya semua orang berkumpul lagi.<new_line><new_line>"Sambutan meriah ditujukan bagi hadirin semua di semifinal! Elf dari Penjara, geng manusia salju, Yeti, dan geng bandit sudah gugur semua."]],
				['3'] = [["Yah, aku harus lari ke mikrofon. Tunggulah di pintu masuk stadion."]],
				['4'] = [[Jumlah orang di sekitar stadion terus bertambah. Beberapa di antaranya memanjat ke dalam stadion. Selang beberapa saat, Cybersanta muncul di atas panggung di sebelah kubah.<new_line>"'Hai, chooms! Senang bisa menyambut kalian semua di turnamen Christmas Coolness!"]],
				['5'] = [["Aku akan memperkenalkan petarung hari ini. Pekerja korporat yang tamak dan mengerikan, Business Claus! Pengunjung dari Zaman Batu, Cave Santa! Penyihir yang hebat, Wizard! Geng bandit berhati dingin!<new_line>Sedingin hatiku, geng manusia salju! Yeti yang misterius! Elf dari Penjara yang anti hukum! Daaan satu-satunya, juara dari kota kita, Sinterklas!!!"]],
				['6'] = [["Dan dalam pertarungan pembuka perempat final, Elf dari Penjara akan bertarung melawan Sinterklas yang tak tertandingi!"]],
				['12'] = [["Pertarungan pembuka semifinal antara kontestan favorit Sinterklas dan kontestan yang tidak terlalu favorit Business Claus! Kita lihat siapa yang akan lolos ke babak berikutnya."]]
			},
			['choice'] = {
				['6'] = [[Istirahat]],
				['5'] = [[Pergi ke arena]],
				['4'] = [[Tepuk tangan]],
				['3'] = [["..."]],
				['2'] = [[Baca aturan]],
				['1'] = [["Turnamen apa?"]]
			}
		},
		['emba_isaev_izhevsk'] = {
			['episode'] = {
				['6'] = [["Kita harus berangkat ke stasiun riset biologi, di mana Profesor Gerin seharusnya berada. Semoga dia masih hidup. Kita akan berangkat segera setelah kau siap."]],
				['5'] = [["Sebelum perang, stasiun riset biologi adalah pusat analitik biasa; tidak melakukan proyeknya sendiri. Sejumlah kecil pegawai menganalisis DNA miselium yang bermutasi. Mungkin hal-hal ini terhubung... tetapi kami belum tahu persis bagaimana kesemuanya ini terhubung."]],
				['4'] = [["Mutan padang gurun mewarisi proses reproduksi mereka dari hewan biasa. Mereka memiliki evolusi yang menyimpang. Di sini kami berurusan dengan semacam mutasi yang disengaja. Organisme ini telah mengubah dirinya sendiri menjadi seperti yang mereka butuhkan."]],
				['3'] = [["Aku memperoleh sampel dari makhluk yang kau temui. Dilihat dari catatannya, mutasi terjadi dengan sendirinya — setidaknya para ilmuwan tidak ikut campur dalam hal itu.<new_line>Satu-satunya catatan tentang hal ini dalam dokumen rahasia atau kotak hitam, adalah namanya: "Pengembang biak". Ini sangat menggelisahkan."]],
				['2'] = [["Sayangnya, tidak ada yang berhasil terungkap. Tidak ada yang menyebutkan tentang objek itu, bagaikan tidak ada apa pun yang pernah diciptakan di sana. Satu-satunya hal yang dapat kuperingatkan adalah bahwa ada dua tangki untuk memompa biomassa di kota sains ini.<new_line>Ingatlah hal itu saat kau memompanya keluar dari tangki stasiun riset biologi. Sayangnya, sisanya harus kau cari tahu sendiri selama dalam perjalanan. Kami telah menguraikan kode akses ke kompartemen rahasia lab. Kemarilah, kode aksesnya sudah kutuliskan untukmu."<new_line>(memberikan sehelai kertas berisikan kodenya)]],
				['1'] = [["Terkait objek yang akan kau temui, kami hampir tidak tahu apa-apa tentangnya. Namun, Kurganov sangat tertarik padanya, dan kami harus mencari tahu alasannya.<new_line>Kami telah kehilangan Navigator, dan aku tidak dapat mengirim dirimu ke kota sains begitu saja, dengan membabi buta. Cari dokumen rahasia agar kami bisa mendapatkan lebih banyak informasi."]]
			},
			['choice'] = {
				['2'] = [["Ada apa dengan benda itu? Ada banyak mutan di padang gurun."]]
			}
		},
		['immortal_love'] = {
			['episode'] = {
				['2'] = [[Si gadis keluar dari sarkofagus dan terlepas dari sang mumi sebelum menghilang ke dalam ruang bawah tanah. Sepertinya, ritual itu memberikannya kekuatan khusus. Sayang sekali kami belum sempat berbicara, ia terlihat familiar. Kuharap ia dapat pulang ke rumah. Saatnya aku kembali ke permukaan.]],
				['1'] = [[Setelah menyiapkan umpan dengan saksama, aku mulai menunggu. Sejenak kemudian, kalajengking mulai berdatangan, dan aku segera mengikuti mereka ke arah pintu masuk lubang yang besar. Aku terus menemukan ular, laba-laba, dan makhluk lainnya saat menelusuri jalan bawah tanah tersebut. Akhirnya, aku melihat cahaya obor di ujung sebuah lorong panjang dan mendengar rapalan mantra dari kejauhan. Sang mumi mengelilingi sarkofagus yang berdiri di tengah gua dan melanjutkan ritual. Saatnya menghentikan ini.]]
			}
		},
		['construction_team_fight'] = {
			['episode'] = {
				['1'] = [[Di dekat kamp mereka, aku melihat segala macam barang dari kamp kerja di tumpukan yang berantakan. Saat mereka menyadari kehadiranku, para bandit tertawa:<new_line>"Oh, jadi sekarang para pekerja membawa peralatan mereka sendiri kepada kita. Lalu apa, kamu sudah siap untuk kerja keras dan pertempuran sengit?"]]
			}
		},
		['scientist_rescue_bar'] = {
			['episode'] = {
				['2'] = [["Hidup dan baik-baik saja? Luar biasa! Asal tahu saja, kamu sudah menyelamatkan hidup seorang pria baik. Terima kasih juga untuk dosimeternya, ini model yang langka. Ini sedikit tambahan, untuk pekerjaan yang sangat bagus."]],
				['1'] = [["Hidup dan baik-baik saja? Luar biasa! Asal tahu saja, kamu sudah menyelamatkan hidup seorang pria baik. Ini imbalanmu."]],
				['3'] = [["Hidup dan baik-baik saja? Luar biasa! Asal tahu saja, kamu sudah menyelamatkan hidup seorang pria baik. Aku juga sudah mendengar tentang layanan medismu, jadi ini sedikit tambahan. Kami menghargai perawatan yang baik untuk orang-orang kami."]]
			}
