		['new_year_boss_2'] = {
			['episode'] = {
				['16'] = [["Percayalah padaku. Tidak ada waktu untuk mengajarimu tentang kemiripan fraktal yang hebat dari kesatuan semua makhluk."]],
				['15'] = [["Kita perlu umpan. Berbaringlah di dekat lubang itu untuk memancing penculik."]],
				['14'] = [["Saat berbicara dengan Business, dia mendengar ucapan yang kau dengar dariku sebelumnya..."<new_line><new_line>Business Claus menatap kami sebentar, melambaikan tangan, dan berjalan menjauh.]],
				['13'] = [["Uhhh, ahh, ho ho ho! Ah ee ooga, ee."]],
				['2'] = [[Dia menggelengkan kepala dan mencoba mengikis lapisan atas tanah dengan gagang tongkatnya.]],
				['1'] = [["Oh ooh! Ah buh."<new_line>Cave Santa menyentuh tanah dengan tongkatnya. Sepertinya dia mencoba memberi tahu kita sesuatu...]],
				['19'] = [[Aku tak percaya apa yang terlihat! Begitu berbaring di dekat lubang, terdengar suara dan deru mesin.]],
				['17'] = [["Tidak akan ada yang terpancing jika kau hanya berbaring di sana! Untuk berpura-pura menjadi hewan yang terluka, kau harus menjadi hewan yang terluka."]],
				['9'] = [["Uhh eh..."<new_line>Cave Santa berdiri, oleng, dan mendadak jatuh dengan posisi telentang.]],
				['8'] = [[Tanpa beranjak bangun, Cave Santa menggelengkan kepalanya.<new_line><new_line>"Bukan. Bukan itu... Coba bayangkan jika kau sedikit, mm... Maksudku, coba tebak lagi."]],
				['7'] = [[Kemudian Cave Santa berbaring di samping lubang itu dengan kaki dan tangan yang terentang.]],
				['20'] = [[Selama pertempuran, salah satu anggota geng diam-diam menyelinap pergi dan kabur!]],
				['10'] = [[Cave Santa berdiri perlahan, mengambil tongkatnya, dan... memukul kepalaku!]],
				['11'] = [[Cave Santa sepertinya tidak suka dengan isi kepalaku.<new_line>"Maaf, kawan. Cepat atau lambat, jelas komunikasi kita akan mencapai kebuntuan."]],
				['3'] = [["Eee!"<new_line>Santa mulai mengangguk dengan gembira. Rupanya, dia perlu lubang yang digali di sini.]],
				['4'] = [["Kau paham, kan? Dia ingin menggali lubang untuk membuat jebakan! Alam memang luar biasa, kawan."]],
				['5'] = [[Cave Santa secara saksama memeriksa lubang yang aku gali.<new_line>"Ooh. Uh vah, uhh vah!"<new_line><new_line>Sambil melihat kami, dia merobek beberapa rumput panjang dan meletakkannya di atas lubang.]],
				['6'] = [["Oh, aku paham! Dia ingin kita menutup lubang dengan sesuatu. Untuk menyembunyikannya!"<new_line>Cave Santa tersenyum dan mengangguk.]],
				['12'] = [["Benar. Tahukah kau, begitu banyak pengetahuan yang perlu didapatkan seseorang untuk meningkatkan kerangka mental mereka setelah berada di lingkungan yang asing.<new_line>Tapi mari kembali ke misi kita. Waktu sangatlah penting."]]
			},
			['choice'] = {
				['16'] = [["Mm, baiklah..." (berbaring di samping lubang)]],
				['15'] = [["Mengapa mereka ada di sini?"]],
				['14'] = [["Baiklah, ayo mulai bekerja."]],
				['13'] = [["Bisa diulang?"]],
				['2'] = [["Apa kita perlu menggali lubang?"]],
				['1'] = [["Apa kita akan menghajar seseorang di sini?"]],
				['17'] = [[Pura-pura mati]],
				['18'] = [[Ikuti dia]],
				['9'] = [["Apa kita hanya perlu menunggu?"]],
				['8'] = [["Bersembunyi untuk menyergap?"]],
				['7'] = [["Um, kita harus tidur?"]],
				['10'] = [["Serangan jantung? Ya?"]],
				['11'] = [["Kau... bisa bicara?"]],
				['3'] = [["Apa kau gila?"]],
				['4'] = [[Gali lubang]],
				['5'] = [["Itu artinya..."]],
				['6'] = [[Tutupi lubang dengan rumput]],
				['12'] = [["Misi kita adalah menggali lubang?"]]
			}
