		['halloween_boss_2022_3_2'] = {
			['episode'] = {
				['9'] = [[Aku duduk di sofa dan hampir tertidur saat mendengarkan musik kuartet itu.]],
				['8'] = [[Pelayan memberikan segelas anggur kepadaku:<new_line>"Selamat menikmati, ini anggur Falernia."]],
				['7'] = [[Pelayan menyajikan hidangan untukku dan berkata:<new_line>"Perch au naturel—kelezatan yang sesungguhnya. Bon appétit!"]],
				['6'] = [[Taman ini dihiasi dengan meja-meja yang penuh dengan makanan dan minuman. Ada kuartet musik gesek yang bermain di atas panggung, dikelilingi oleh sofa-sofa empuk.]],
				['5'] = [["Kami sedang mengadakan soirée. Makan, minum, bergembira. Setelah kau kenyang, kembalilah kepadaku dan kita akan dengan hormat... akan mengakhiri... yah, kau paham maksudku."]],
				['4'] = [["Tahu tidak, saat kau sudah hidup selama ratusan tahun, cara bicara kasar dan unjuk kekuatan mulai menipis. Kenapa kita tidak bersikap seperti pria sejati saja?"]],
				['3'] = [["Kau tahu, aku ini Count Drakula, dan menetap di rumah yang indah ini dengan pengantinku yang cantik. Aku pun tidak akan menyangkal bahwa kami memang membunuh pengembara sesekali yang melewati rumah kami."]],
				['2'] = [[Pintu manor terbuka, dan seorang pria jangkung berlari ke arah kami:<new_line><new_line>"Berhenti, berhenti, berhenti! Maafkan pengantinku—dia masih muda dan punya bakat dramatis. Ikutlah denganku ke kebunku, aku akan menjelaskan semuanya padamu."]],
				['1'] = [[Kami memasuki gerbang sebuah perkebunan mewah. "Di sinilah kau akan menemui ajalmu, Sayangku. Kau akan bertemu dengan Count Drakula. Mengenaskannya, sebagai kudapan."]],
				['11'] = [["Dia benar-benar pria yang ramah, aku bahkan merasa tidak enak padanya. Hah, tadi aku mau bilang apa...? Oh ya, jangan lupa untuk meningkatkan level Iblismu! Jika tidak, kau tidak akan bisa mengerjakan kontrakmu berikutnya."]],
				['10'] = [["Baiklah, Tamuku yang terhormat. Kuharap kau menikmati waktumu di sini. Sekarang, mari kita mulai..."]]
			},
			['choice'] = {
				['9'] = [["Dalam hal apa?"]],
				['8'] = [[Kembali ke Drakula]],
				['7'] = [[Dengarkan musik di sofa]],
				['6'] = [[Coba minumannya]],
				['5'] = [[Coba makanannya]],
				['4'] = [["Setuju."]],
				['3'] = [["Kenapa kau memberitahuku ini?"]],
				['2'] = [[Ikuti pria itu]],
				['1'] = [["Kita lihat saja nanti!"]]
			}
		},
		['frozen'] = {
			['episode'] = {
				['1'] = [[Selagi para penjarah menimbun balok-balok es besar ke atas kereta luncur, aku bisa berjalan mendekat. Dari es yang keruh aku bisa melihat bayangan orang-orang yang terkurung di dalamnya. Mereka harus segera dibebaskan.]]
			}
		},
		['halloween_def_easy2'] = {
			['episode'] = {
				['9'] = [["Tiga!"]],
				['8'] = [["Dua..."]],
				['7'] = [["Satu..."]],
				['6'] = [["Sepertinya semuanya sudah siap. Kita lompat pada hitungan ketiga. Siap?"]],
				['10'] = [["Oh, terima kasih banyak, Nak!"]],
				['11'] = [["Punggungku sakit. Aku sudah tidak muda lagi. Jangan tersinggung, ya. Kau sudah bekerja dengan baik! Ambil hadiahmu dan pergilah."]],
				['3'] = [["Aku sudah membuat ramuan khusus yang kurasa sangat mereka suka. Tahu tidak, aku senang ada tamu yang datang, tapi hanya jika mereka diundang. Aku punya perangkap menarik terbuat dari jamur yang bisa kita gunakan pada mereka. Haruskah aku memasangnya atau kita langsung tangani mereka saja?"]],
				['2'] = [[Saat penyihir wanita itu mengalihkan perhatian para monster, aku langsung berlari ke arah gubuk. Begitu aku masuk, rumah itu berdiri dengan kakinya.<new_line><new_line>"Itu lebih baik! Bisakah kau membantu wanita tua ini? Kita akan mengusir mereka lebih cepat bersama-sama."]],
				['1'] = [[Kerumunan orang berkumpul di sekitar gubuk seperti penggemar di konser musik rock.<new_line>Penyihir Wanita Tua menjulurkan kepalanya keluar dari jendela:<new_line>"Cepat lari ke sini, Nak! Aku akan mengalihkan perhatian mereka!"]],
				['4'] = [["Kau yang pilih, Nak. Bagaimana kalau minum ramuan sebelum bertarung? Omong-omong, aku belum sempat mengujinya."]],
				['5'] = [["Kau anak yang pintar! Sepertinya semuanya sudah siap. Kita lompat pada hitungan ketiga. Siap?"]]
			},
			['choice'] = {
				['9'] = [["Kenapa kau tidak lompat?"]],
				['8'] = [[Lompat]],
				['7'] = [[....]],
				['6'] = [["Tidak, terima kasih."]],
				['5'] = [[Minum ramuan]],
				['4'] = [["Kita bisa menangani mereka."]],
				['3'] = [["Pasang perangkap."]],
				['2'] = [["Bagaimana binatang buas ini bisa sampai ke tempat ini?"]],
				['1'] = [[Lari ke arah gubuk]]
			}
		},
		['gift_delivery'] = {
			['episode'] = {
				['2'] = [[Saat aku sampai di tempat itu, aku tidak melihat ada yang aneh. Aku sedang mencari tempat untuk meletakkan hadiah ini saat terdengar suara bolt senapan di belakangku, dan suara kasar tak dikenal berkata, «Letakkan kotak itu di bawah dan keluar dari sini. Jangan balikkan badan.»]],
				['1'] = [[Ini perkemahan kecil, dengan dahan-dahan pohon cemara yang disusun miring serta dilapisi salju. Apinya menyala dan ada air mendidih di dalam periuk. Sepertinya pria itu baru saja pergi. Ia akan sangat terkejut saat kembali dan melihat ada hadiah di sini.]],
				['3'] = [[Yang membuatku terkejut, ada pohon Tahun Baru kecil dengan dekorasi seadanya. Seseorang ada di sini, dan tidur dilapisi banyak selimut. Mungkin ada baiknya meletakkan hadiah di bawah pohon lalu keluar dari sini.]]
			},
			['choice'] = {
				['1'] = [[Letakkan hadiah]]
			}
		},
		['new_year_boss_3_2'] = {
			['episode'] = {
				['9'] = [["Oh tidak. Jangan bilang si tengik sombong itu! Apa tak bisa kita melakukannya tanpa dia?"]],
				['8'] = [["Ya, aku telah gagal. Maaf, kawan. Kabar tentang senjata memang terdengar menakutkan. Seharusnya kita mencari tahu apa yang mereka bicarakan. Kita butuh mata-mata."]],
				['7'] = [["Benar! Claws, kau baik-baik saja? Apa kau mendengar sesuatu? Jika mereka mendapatkan senjata super juga... tak akan ada lagi hari raya, begitu juga kita."]],
				['6'] = [["Saat kau berkelahi, beberapa bajingan mengambil semua hadiah! Tidak ada yang bisa aku lakukan."]],
				['5'] = [["Ha, kau lagi? Hei, geng, urus pecundang ini! Kalian boleh membakar dan melahap setengah dari hadiah. Lemparkan sisanya di bunker. Aku akan kembali untuk memeriksa!"<new_line><new_line>Krampus Claus mendudukkan manusia salju aneh di atas sepeda motornya, naik, dan menginjak gas. Gengnya bergegas ke arahku.]],
				['4'] = [[Santa Claws mengangkat tangannya dan berjalan ke arah Krampus.<new_line>"Tunggu, tunggu, bukan itu yang kita setujui! Ayo bahas bagaimana..."<new_line><new_line>Terdengar letusan keras, sesuatu mengenai kepala Santa Claws, dan dia jatuh ke tanah.]],
				['3'] = [["Wow, Krampus, mereka membawa begitu banyak barang. Bahkan cukup untuk mencoba senjata super kita."]],
				['2'] = [["Dasar idiot berbulu! Bagaimana bisa ini untuk distribusi? Orang-orangku butuh lebih banyak hadiah! Semua hadiah, semua energi mereka! Kami akan membakar Natal sepenuhnya untuk mendapatkan lebih banyak kekuatan!"]],
				['1'] = [["Krampus Claus, aku paham bahwa kau bekerja secara gratis dan tidak mendapatkan hasil kerjamu. Kami mengusulkan distribusi yang lebih adil untuk..."]],
				['10'] = [["Menyelamatkan hadiah-hadiah itu saja kita tidak bisa! Tidak, kita tak bisa melakukannya tanpa dia.<new_line>Jangan tanya, kawan—lebih mudah jika menemuinya. Kau perlu mendekorasi pohon lagi untuk memanggil pengunjung lain."]]
			},
			['choice'] = {
				['6'] = [["Maksudmu siapa?!"]],
				['5'] = [["Siapa?"]],
				['4'] = [["Mereka ingin mengujinya."]],
				['3'] = [["Apa yang mereka katakan tentang senjata?"]],
				['2'] = [["Sudah cukup—mampuslah kau!"]],
				['1'] = [["Dan siapa dia?"]],
				['7'] = [["Baiklah."]]
			}
		},
		['emba_time_medium2'] = {
			['episode'] = {
				['14'] = [["Sial, dia kabur! Sudahlah, suatu hari... Kapan-kapan. Dan karena kau sudah membantu, kau boleh pergi sekarang. Ada beberapa hal yang harus aku pikirkan sekarang."]],
				['13'] = [[Pemakan bangkai itu perlahan mendekat, mengeluarkan suara dan melepaskan awan spora dari rahangnya ke arah tanaman itu dan hanya mendekatinya setelah ini.<new_line>Hewan itu menginjak kawat, terdengar suara letupan dan awan asap putih. Namun, rupanya asap itu tidak berpengaruh pada biawak setelah mencampurnya dengan awan spora.<new_line>Setelah asap dan spora itu menipis, pemakan bangkai itu tak terlihat lagi.]],
				['12'] = [["Bagus. Nah, kita tinggalkan di sini sekarang, menjauhlah dan amati apa yang akan dilakukan biawak ini. Aku memasang perangkap kawat di sana."]],
				['9'] = [[Aku berkonsentrasi mengamati parasit ketiga. Dia tidak akan pergi ke mana-mana, jadi aku punya waktu untuk bersiap.]],
				['8'] = [[Wanita Pemburu itu terkekeh tanda ragu.<new_line>"Aku harap kepercayaan dirimu tidak akan menjadikanmu makan malam mereka suatu hari nanti. Kita akan melacak parasit itu.<new_line>Perilaku mereka agak rumit dan kita harus selalu waspada. Aku akan mengurus yang dua ini dan kau harus berusaha menjagai yang satu lagi dengan senapan serbu."]],
				['7'] = [["Dan sekarang, mari mengamati parasit ini.<new_line>Perilaku mereka agak rumit dan kita harus selalu waspada. Aku akan mengurus yang dua ini dan kau harus berusaha menjagai yang satu lagi dengan senapan serbu."]],
				['6'] = [["Mereka adalah yang paling cerdas di antara semuanya. Mereka tidak terlalu terinfeksi oleh miselium. Mungkin mereka berhasil bertahan paling lama dari pengaruhnya. Tetapi mereka tetap saja monster.<new_line>Selain itu, mereka paling cerdik dan berbahaya. Mereka belum lupa cara menggunakan senjata manusia. Paling tidak, mereka tak akan menginfeksimu karena mereka tidak memuntahkan spora."]],
				['5'] = [[Aku mengikuti kawanan grasscuttler itu, mencatat apa yang mereka lakukan ketika berhenti. Akhirnya, mereka kembali ke kawanan utama.<new_line><new_line>"Hmm, mereka kembali. Apakah mereka melakukan pengintaian? Ataukah mencari makanan? Apa pun itu, aku akan menganalisisnya nanti. Dan itu adalah parasit, kau lihat?"]],
				['4'] = [[Kawanan grasscuttler itu bergerak dengan perlahan saat ini. Aku selalu bisa mengikuti mereka, atau melakukan urusanku sendiri tanpa risiko kehilangan jejak mereka.]],
				['3'] = [["Bagus, mari berangkat. Kau lihat kawanan di sana? Yang paling besar di antara mereka adalah grasscuttler. Aku ragu mereka makhluk yang memiliki kesadaran, karena tubuh mereka terlalu banyak diselimuti oleh miselium. Lihat, mereka memisahkan diri dari kelompoknya.<new_line>Ikuti mereka. Jika mereka menyerang, ingatlah bahwa ketika grasscuttler tumbang, mereka melepaskan awan spora yang akan memengaruhi orang di sekitarnya. Jadi, jaga jarakmu dari mereka dalam pertempuran."]],
				['2'] = [["Aku sedang mengamati pergerakan biomutan. Aku sedang berusaha memahami logika dalam perpindahan mereka. Aku hanya ingin tahu apakah ada spesies yang memiliki kesadaran di antara mereka.<new_line>Aku perlu seorang partner agar aku tidak melewatkan apa pun."]],
				['1'] = [["Hey, kemarilah. Aku melihat caramu berburu biomutan. Aku berpikir untuk mengajakmu dalam sebuah misi."]],
				['11'] = [["Ya, pemakan bangkai adalah yang paling menarik di antara mereka. Miselium mengubah hewan biasa, membuatnya jadi lebih kuat dan lebih cerdas, serta memberinya banyak kemampuan luar biasa.<new_line>Omong-omong... Mari tangkap satu. Aku ingin meneliti pola makan mereka. Seperti tanaman apa yang mereka makan. Ayo, temukan makhluk itu, aku akan menunggu di sini."]],
				['10'] = [["Kau sudah mencatat semuanya? Bagus. Dua parasit itu membawa beberapa pemakan bangkai ke kawanan mereka.<new_line>Mereka benar-benar pintar..."]]
			},
			['choice'] = {
				['9'] = [[Pindah dan amati]],
				['8'] = [["Kita mengamati pemakan bangkai sekarang?"]],
				['7'] = [[Keluarkan catatan observasi]],
				['6'] = [["Paham."]],
				['5'] = [["Ya, aku tahu banyak tentang mereka."]],
				['4'] = [["Apa yang kau ketahui tentang mereka?"]],
				['3'] = [[Kejar grasscuttler]],
				['2'] = [[Ikuti grasscuttler]],
				['1'] = [["Pekerjaan apa?"]]
			}
		},
		['doll'] = {
			['episode'] = {
				['2'] = [[Dasar bodoh, sang dalang tertawa kasar. ]],
				['1'] = [[Ia duduk di kursi sedang mengerjakan sesuatu. Para boneka memberikan peralatannya dan memegangkan lilin untuknya. Setelah melihatku, sang dalang tersenyum, ]]
			}
		},
		['camp_defence_quest'] = {
			['episode'] = {
				['13'] = [[Jika kau mempercayai peta, aku makin dekat dengan kamp.]],
				['12'] = [[Aku tidak dapat membantu orang-orang ini, tetapi akan kubalas dendam mereka.]],
				['9'] = [[Tapi, secara keseluruhan, ini tidak terlalu buruk. Mereka akan menjadi lebih baik, itu hanya masalah waktu.]],
				['8'] = [[Kita berhasil mempertahankan posisi, tetapi beberapa pejuang kita terluka parah.]],
				['7'] = [[Kita mengatur segalanya di bawah kendali. Kita mempertahankan tanah kami. Tidak ada yang akan mengganggu kita dalam waktu dekat.]],
				['6'] = [[Ada bandit. Banyak sekali...<new_line>Tapi ada banyak dari kita juga. Dan kami siap!]],
				['5'] = [[Aku melihat tempat yang sempurna untuk lubang serigala di dekat kamp dan berhasil menggalinya dengan cepat.]],
				['4'] = [[Aku berhasil memasang batang kayu dengan tiang.]],
				['3'] = [[Di titik pertemuan, aku hanya melihat satu mayat tergeletak di tanah.]],
				['2'] = [[Saat aku mendekati pos terdepan, aku mendengar suara tembakan. Tidak diragukan lagi: para bandit sudah menyerang kamp.]],
				['1'] = [[Seorang pria bersenjata berdiri di pos terdepan:<new_line>'Apakah kau datang untuk membantu? Oke, ambil posisi. Mereka sudah dalam perjalanan.']],
				['11'] = [[Para bandit akhirnya pergi. Aku mencari di kamp, tetapi hanya menemukan mayat sekutu kami.]],
				['10'] = [[Mendengar suara, aku bersembunyi. Beberapa orang asing berkeliaran di sekitar kamp.<new_line>Aku terlambat. Para bandit sudah mengambil alih kamp.]]
			},
			['choice'] = {
				['16'] = [[Periksa mayatnya]],
				['10'] = [[Pertahankan kamp kita]],
				['14'] = [[Ikuti pertarungan]],
				['18'] = [[Menunggu bandit untuk pergi]],
				['17'] = [[Balas dendam rekan-rekan yang gugur]],
				['9'] = [[Ambil posisi dan tunggu]],
				['8'] = [[Lihat apa lagi yang harus dilakukan]],
				['7'] = [[Gali lubang serigala]]
			}
		},
		['comrade_in_trouble_gas'] = {
			['episode'] = {
				['1'] = [[Sebuah kamp didirikan di samping kendaraan. Ada seorang pria di sana, membongkar atau memuat segala macam barang.]]
			}
		},
		['santa_player'] = {
			['episode'] = {
				['2'] = [["Aku ragu dia punya waktu untuk pulih setelah dihajar habis-habisan. Tetapi, siapa yang tahu? Mungkin kau harus melawan keduanya. Semoga berhasil, kawan!"]],
				['1'] = [["Aku siap mengirimmu ke Frosty. Kau harus mengalahkannya dan menghancurkan bagian senjata yang menyerap kekuatan magis kami."]]
			},
			['choice'] = {
				['2'] = [["Terima kasih!"]],
				['1'] = [["Bagaimana dengan Krampus?"]]
			}
		},
		['blood_trail'] = {
			['episode'] = {
				['5'] = [[Satu jam kemudian, pemuda itu mulai siuman. Saat aku menjelaskan kepadanya yang telah terjadi, dia berterima kasih kepadaku dengan memberitahuku di mana menemukan wadah rahasia miliknya yang dekat.]],
				['4'] = [[Aku merawat dan memerban lukanya, lalu membersihkan genangan darah sebisaku. Sekarang dia punya peluang menyintas.]],
				['3'] = [[Aku menemukan seseorang di lantai di dalam. Seorang pemuda tergeletak dalam genangan darah. Tampaknya dia tadinya mencoba memerban lukanya, tetapi kehilangan darah membuatnya pingsan. Dia memerlukan bantuan segera.]],
				['2'] = [[Seperti yang aku takutkan, ada beberapa serigala di dalam, dengan lapar mencabik-cabik tubuh asing. Mereka tak menyadari kehadiranku.]],
				['1'] = [[Di depan pintu, aku melihat jejak kaki berdarah masuk ke dalam rumah. Aku tak tahu apa yang menantiku di dalam sana.]]
			},
			['choice'] = {
				['5'] = [[Tinggalkan air bersih untuknya dan lanjutkan perjalanan.]],
				['4'] = [[Tunggu hingga dia siuman.]],
				['3'] = [[Gunakan obat untuk membantu.]],
				['2'] = [[Dia bukan lagi penduduk. Cari dia dan ambil semua barangnya.]],
				['1'] = [[Ikuti jejak]]
			}
		},
		['gravedigger'] = {
			['episode'] = {
				['2'] = [[Si penggali kuburan tidak mau bicara. Aku menemukan catatan aneh di mantelnya. Seperti daftar anggota tubuh – tangan, kaki, dan kepala – dengan nama ]],
				['1'] = [[Penggali kuburan mudah ditemukan – pria seram itu menemuiku di gerbang kuburan. Di tangan kanannya ia memegang sekop, dan di tangan kiri, sebuah kaki yang membusuk. Ia menatapku dengan senyuman aneh, melemparkan kaki ke dalam gerobak penuh anggota tubuh, mengayunkan sekop ke atas pundaknya, dan berlari ke dalam kuburan. Saatnya membereskan orang itu!]]
			}
		},
		['letter_of_hope'] = {
			['episode'] = {
				['5'] = [[Raven mengaok keras, menjatuhkan surat itu dan mengepakkan sayapnya.]],
				['4'] = [[Burung gagak itu tidak tertarik.]],
				['3'] = [[Burung gagak itu bertengger di atas pohon, menatapku, dan memalingkan kepalanya yang hitam, surat itu dibawa erat oleh paruhnya. Satu hal yang mungkin bisa kulakukan adalah mencoba dan menukarnya dengan barang lain.]],
				['2'] = [[Surat itu ternyata ada di dalam botol di dasar parit, tertutup lapisan es tebal. Bagaimana surat itu bisa sampai di sana, aku tak tahu, tapi pasti akan sulit untuk mengambilnya.]],
				['1'] = [[Nahas, surat itu ada di dalam kotak surat. Namun satu hal, rumahnya sudah hancur. Dari seluruh bangunan, tinggal satu dinding yang masih berdiri, dengan kotak surat yang menggantung. Aku harus menemukan sesuatu untuk membukanya.]]
			},
			['choice'] = {
				['6'] = [[Beri kotak peluru]],
				['5'] = [[Beri makan burung]],
				['4'] = [[Ledakkan esnya]],
				['3'] = [[Pecahkan esnya]],
				['2'] = [[Ledakkan kotaknya]],
				['1'] = [[Cungkil kotaknya]],
				['8'] = [[Ambil surat]],
				['7'] = [[Puji burung]]
			}
		},
		['monstrous_bag'] = {
			['episode'] = {
				['1'] = [[Selama beberapa jam saya telah mendengarkan lapar gemuruh di belakang tas. Tampaknya kesabaran telah habis. Saya merasa gigi tajam di bahu saya! Aku menjatuhkan tas. Tapi itu telah mencium darah dan mendapat benar-benar gila. Aku harus menunjukkan siapa bosnya di sini.]]
			}
		},
		['emba_isaev_krasnoyarsk'] = {
			['episode'] = {
				['16'] = [["Dan kemudian jalan kita terpisah."]],
				['15'] = [["Halo, kawanku! Semuanya telah terkonfirmasi: purwarupanya memang telah dihancurkan."]],
				['14'] = [["Nanti kawanku, nanti."]],
				['13'] = [["Benar. Aku setuju dengan teman miselium kita di sini. Ambisi yang bercampur dengan niat terbaik umat manusia memberikannya semua alat yang diperlukan. Tanggung jawab siapa yang lebih agung?<new_line>Jawabannya sepertinya bukan kita. Itu sebabnya Utusan muncul begitu mirip dengan kita, belum lagi nasib Gerin dan Kurganov..."]],
				['2'] = [["Tentu aja, konflik militer bukan jawaban yang tepat. Tapi, kita punya rencana. Aku belum akan membahas detailnya dulu."]],
				['1'] = [["Akhirnya, kau ada di sini juga. Kita dalam masalah besar. Sindikat Minyak mengambil purwarupa mekanisme penghijauan kita. Kita harus menghentikan mereka agar tidak menggunakannya, atau seluruh padang gurun akan menjadi neraka hijau."]],
				['23'] = [["Sejujurnya, kami tidak sampai hati untuk menghancurkannya sepenuhnya. Kami menempatkannya di gua tempat makhluk itu berada selama ribuan tahun terakhir. Tanpa ada komputer. Kembali ke lingkungan alaminya.<new_line>Ahli biologi dan beberapa rekanku akan tinggal di sini untuk sementara, untuk mencopot semua teknologi dari stasiun riset biologi. Beberapa akan kami hancurkan, dan sisanya akan kami bawa bersama kami."]],
				['19'] = [["Orang perminyakan akan menggunakan mekanisme penghijauan yang dicuri untuk tujuan komersial.<new_line>Mereka tidak mau mengerti bahwa relik miselium itu akan segera mengambil alih dan bencana lain akan terjadi.<new_line>Jadi itu artinya tujuan kita adalah menyusup ke kota sains dan mengirimkan sinyal penghancuran diri ke purwarupa miselium yang kita gunakan dalam mekanisme kita."]],
				['22'] = [["Tidak, karena aku percaya bahwa orang-orang mungkin dapat belajar dari semua bencana ini. Sementara itu, kami menjadi Juru arsip dalam arti sebenarnya.<new_line>Kami akan menyimpan informasi dan mencegah kesalahan-kesalahan terburuk di jalan manusia menuju pengetahuan."]],
				['17'] = [["Kami telah dipaksa untuk menghentikan semua pengembangan dan mengubah arah pengembangan organisasi kami. Semua kisah dengan relik miselium ini...<new_line>Kau sendiri sudah melihatnya — manusia itu ambisius, serakah, dan sering tidak tahu bagaimana memilih di antara berbagai hal bahkan untuk tujuan yang baik."]],
				['18'] = [["Sayangnya, temanku, sayangnya. Tidak dengan bayaran sebesar ini. Namun kau telah memberikan kontribusi yang tak ternilai untuk mempertahankan dunia yang goyah ini. Sekarang, mungkin, dunia ini memiliki kesempatan kecil untuk bertahan. Semoga berhasil untukmu, teman, dan sampai jumpa!"]],
				['21'] = [["Kami telah memecahkan kode kotak hitamnya. Sinyalnya telah dihasilkan dan telah dikirim ke purwarupa miselium. Perangkat itu akan menghancurkan dirinya sendiri, dan orang perminyakan itu akan pulang dengan tangan kosong.<new_line>Kita butuh waktu untuk memeriksa ini kembali, dan kita akan meninggalkan tempat ini. Kembalilah sebentar lagi untuk mengucapkan selamat tinggal."]],
				['9'] = [["Bukan main! Menakjubkan! Luar biasa!"]],
				['8'] = [["Nah, semoga berhasil."<new_line>(memberikan selembar kertas berisikan kodenya)]],
				['7'] = [["Kau harus membuat rencana untuk menghadapi keadaan ini. Setidaknya cobalah untuk memperoleh kotak hitamnya dari sana.<new_line>Kau dan Ahli biologi telah melengkapi kami dengan laboratorium bergerak yang paling canggih, dan kami memiliki potensi untuk mengirim sinyal langsung dari laboratorium itu, mengandalkan sandi dari kotak hitam ini. Jadi, kawan, siap untuk satu misi terakhir?"]],
				['20'] = [["Sulit diprediksi. Yang kita tahu dari dokumennya adalah bahwa itu adalah kota sains yang sangat besar. Dan di pusatnya, di bawah stasiun riset biologi, ada relik miselium tersembunyi ini, terhubung ke superkomputer.<new_line>Bagaimana mutasi dan komputer tersebut dapat memengaruhi aktivitas vitalnya tidak dapat diprediksi."]],
				['10'] = [["Relik miselium ini benar-benar menyerupai akal. Bukan akal tunggal, seperti akal kita, tapi akal bersama. Mungkin itulah mengapa relik miselium itu begitu mudah berkomunikasi dengan superkomputer."]],
				['11'] = [["Relik miselium itu diambil dari perut bumi dan melalui seleksi yang ketat. Semua peristiwa ini tidak lain adalah upaya makhluk ini untuk melindungi dirinya dari umat manusia lagi.<new_line>Simbiosis dengan komputer itu tidak hanya memberinya peluang yang belum pernah ada sebelumnya, tetapi juga bisa dibilang didorong oleh metode kerja kami."]],
				['3'] = [["Sekarang setelah purwarupanya berada di tangan orang perminyakan, tidak masuk akal bagi kami untuk menutup proyek.<new_line>Kita harus berusaha mengamankan teknologi penghijauan itu dengan segala cara. Sekali lagi, aku harus memintamu untuk menemukan dokumen-dokumennya. Dokumen itu diperlukan untuk rencana kita dan juga untuk keseluruhan penelitian."]],
				['4'] = [["Halo, rekan! Berita besar! Menurut dokumennya, ada relik miselium di stasiun riset biologi, terhubung ke sebuah superkomputer. Di situlah pusat pengambilan keputusannya berada."]],
				['5'] = [["Awalnya, kami pikir miselium itu bermutasi secara spontan, dan masalah utamanya adalah keserampangan ini.<new_line>Namun dari informasi yang kau dapatkan, nampaknya cara kerja miselium itu berbeda dengan yang kami perkirakan. Miselium itu lebih stabil jika dibiarkan sendiri.<new_line>Relik miselium ini sudah ada selama ribuan tahun dalam bentuk yang stabil di gua-gua yang terisolasi."]],
				['6'] = [["Dan semua mutasinya merupakan reaksi terhadap rangsangan eksternal. Kesalahan fatal proyek E.M.B.A. adalah menghubungkan relik miselium ke superkomputer.<new_line>Relik miselium itu bereaksi secara radikal terhadap perubahan ini. Rupanya, munculnya berbagai biomutan dan individu otonom seperti Utusan adalah akibat dari miselium yang mendapatkan akses ke sirkuit logika komputer yang paling kuat itu."]],
				['12'] = [["Ya! Akhirnya semuanya masuk akal. Tidak ada yang bisa mencegah makhluk ini menyebarkan spora ke seluruh dunia dan menyelimuti semua kota. Terutama karena tampaknya makhluk ini mendapatkan energinya dari radiasi tinggi.<new_line>Tapi yang diinginkannya dari kita hanyalah kedamaian. Rupanya, dia tidak dapat membeda-bedakan manusia dan melihat manusia sebagai satu organisme. Setiap manusia adalah keseluruhan kemanusiaan baginya."]]
			},
			['choice'] = {
				['20'] = [["Jadi, kita hanya perlu mengunjungi stasiun riset biologi dan menerobos ruangan superkomputer?"]],
				['15'] = [["Saya siap".]],
				['14'] = [["Apa yang menantiku di stasiun riset biologi?"]],
				['21'] = [["Dan itu menganggap kita sebagai ancaman?"]],
				['2'] = [["Apa yang akan terjadi dengan pengembangannya? Kita ingin menyelamatkan dunia."]],
				['1'] = [["Bagaimana caranya kita menghentikan mereka? Mereka memiliki pasukan lengkap."]],
				['19'] = [["Selamat tinggal, Dr. Isaev."]],
				['17'] = [["Apa yang terjadi pada relik miselium?"]],
				['18'] = [["Jadi, padang gurunnya tidak bisa dihijaukan..."]],
				['9'] = [["Jadi, semua stasiun riset biologi itu..."]],
				['7'] = [["Ada apa, Dokter?"]],
