		['new_year_deliver_cloth'] = {
			['episode'] = {
				['12'] = [["Ya, kami semua sudah siap. Jangan lupa sampaikan salamku untuk anak-anak, begitu kau kembali ke permukiman! Kami akan membawakan mereka mainan atau sesuatu untuk dimainkan."]],
				['9'] = [[Aku berjalan ke sebuah bangunan tua. Kelihatannya seperti semacam gudang. Ada seorang pria melangkah melewati pintu reyot:<new_line>"Hei, aku melihatmu di markas! Jadi, mereka mengirimmu ke sini?"]],
				['8'] = [["Luar biasa! Tolong ucapkan terima kasih kepada mereka. Kami akan membawa kembali potongan besar daging, makanlah!"]],
				['7'] = [[Aku berjalan ke arah tungku api, saat orang-orang sedang sibuk mengurus bangkai binatang. Ada seorang gadis yang pertama melihatku mendekat:<new_line>"Hai! Kau salah satu dari kami, ya? Bagaimana kabar mereka di sana?"]],
				['6'] = [[Seorang pria berkamuflase melangkah keluar dari semak-semak.<new_line>"Oh, mereka baik sekali! Sangat baik...<new_line>Maafkan aku atas sambutan yang kasar. Kami menjaga jalan di sini, agar para penjarah tidak sampai ke permukiman."]],
				['5'] = [[Aku berjalan ke jalan tanah, terkejut karena tidak ada orang lain di sekitar...<new_line>Namun kemudian, aku mendengar teriakan dari semak-semak:<new_line>"Aku punya pistol! Tetap di tempatmu! Apa yang kau lakukan di sini?"]],
				['4'] = [["Oh wow, ini baru hadiah! Terima kasih! Mau masuk ke dalam dan menghangatkan diri?"]],
				['3'] = [[Saat aku tiba di tempat itu, aku menemukan gundukan kecil dengan palka di atasnya. Aku mengetuk palka itu. Seorang pria dengan rambut acak-acakan dan tak terawat mengintip keluar:<new_line>"Uuups, halo! Siapa kau?"]],
				['2'] = [["Oh, benarkah? Baik sekali, terima kasih! Maaf untuk... Yah, ini waktu yang sulit, kau tahu."]],
				['1'] = [[Ada konvoi truk di depanku. Seorang wanita muncul dari bawah salah satu truk, wajahnya berlumuran minyak.<new_line>"Hei, kami menemukannya lebih dulu! Menjarahlah di tempat lain."]],
				['11'] = [["Aku tak akan bisa memberi tahu semua orang. Beberapa orang kami pergi mengurus urusan mereka. Kau harus menemui mereka juga, tetapi aku tak yakin kapan mereka akan kembali. Terima kasih sekali lagi. Ini akan membuat kami tetap hangat!"]],
				['10'] = [["Astaga! Aku akan memberi tahu yang lain. Mereka akan senang mendengar kabar dari rumah. Kami sudah menghancurkan reruntuhan ini selama dua hari... Namun, kami menemukan beberapa hal yang menarik!"]]
			},
			['choice'] = {
				['5'] = [["Bagus. Bisakah kau memberikannya kepada yang lain?"]],
				['4'] = [["Tentu. Bisakah kau memberikannya kepada yang lain?"]],
				['3'] = [["Tidak, aku sedang terburu-buru. Bisakah kau memberikannya kepada yang lain?"]],
				['2'] = [["Baiklah. Bisakah kau memberikannya kepada yang lain?"]],
				['1'] = [["Anak-anak dari permukiman sudah merajut beberapa pakaian untukmu."]]
			}
		},
		['new_year_boss_4_2'] = {
			['episode'] = {
				['2'] = [["Fiuh, terima kasih, choom. Jadi ada apa?"]],
				['1'] = [["Uhh ah, ugh ooh..."<new_line>Cave Santa menepuk punggung Cybersanta dengan penuh simpati saat ia membungkuk di atas tempat sampah.]],
				['4'] = [["Choom, mengapa aku yang kelelahan tapi kaulah yang lamban?! Bukannya kau ingin mencari tahu tentang senjata super? Aku tahu di mana mereka menyimpan infonya. Kita akan pergi ke sana."]],
				['3'] = [["Ah, itu sebabnya kau ada di sini. Korporat sialan itu mengambil hadiah dari orang awam! Nah, aku berhasil mendapatkan beberapa info saat mereka berpesta. Ayo bergegas—kita harus selangkah lebih cepat dari mereka."]]
			},
			['choice'] = {
				['1'] = [[Beri dia air]]
			}
		},
		['easter_rabbit'] = {
			['episode'] = {
				['30'] = [["Lalat saja tidak terbang cuma-cuma. Jika kau membawakan sesuatu untukku, aku hanya akan melakukan amal dan membagikannya kepada orang-orang. Jadi aku bisa berbuat baik."]],
				['22'] = [["Aku dengar banyak orang menghilang di pertanian telantar di padang gurun. Konon itu adalah ulah ayam mutan. Kau bisa berkunjung ke sana, ayam pasti punya banyak telur."]],
				['28'] = [["Nah, sepertinya kau sudah membawa cukup keranjang dan pelanggan sudah habis sekarang. Artinya kau sudah mendapatkan hadiah dan aku bahkan akan menambahkan sesuatu. Dan aku sudah mendapatkan uang pensiun untuk diriku."]],
				['27'] = [["Aku pernah dipenjara satu kali. Aku pernah menikah dua kali. Lalu, aku menjadi tentara dan harus menghabiskan waktu dua setengah tahun hidup di ujung Kazakhstan tanpa alasan sama sekali.<new_line>Tulang wajahku pernah retak, ginjalku diangkat, dan ada serpihan tulang di pergelangan kakiku yang tak akan pernah sembuh. Aku sudah mengalami segala macam kesulitan dalam hidup ini..."]],
				['23'] = [["Cari mereka di padang gurun atau padang gurun radioaktif, kadang-kadang kau bisa menemukan ayam di sana. Di mana ada ayam, di situ ada telur."]],
				['24'] = [["Kau bisa membelinya dari pedagang dengan harga tiga kali lipat, tapi hanya lakukan itu kalau kau tidak bisa menemukannya di tempat lain."]],
				['16'] = [["Kau punya apa lagi?"]],
				['15'] = [["Tidak ada syarat khusus, aku akan membelinya dengan harga tinggi, lebih tinggi dari yang ditawarkan orang gila itu. Jika kau menjual cukup banyak, aku akan memberimu hadiah. Harganya bagus, jadi ingat saja itu."]],
				['14'] = [["Dengar, mengapa kau mau bekerja untuk orang gila itu? Mungkin, seharusnya kau menjual telur hias itu kepadaku, aku pastikan harganya cocok. Permintaannya sedang tinggi sekarang. Dan tidak orang yang akan membiarkan orang gila itu masuk ke rumah mereka."]],
				['13'] = [["Sst, kemarilah sebentar."]],
				['2'] = [["Apa maksudmu, "siapa kau", perhatikan baik-baik! Kelinci Pink, siap melayani."]],
				['1'] = [["Hai, Nak. Aku perlu bantuanmu, cepatlah kemari!"]],
				['25'] = [["Pergilah ke kota telantar dan lakukan pencarian di sana. Kau akan menemukan lubang kelinci di sana, gali lubang, dan ambillah catnya. Jangan lupa membawa sekop."]],
				['26'] = [["Dari mana asalnya susu pada sapi? Kalau kau tak mengajukan pertanyaan konyol, kau tidak harus mendengar jawaban bodoh."]],
				['19'] = [["Luar biasa, aku senang, ini bagianmu. Kau punya lagi?"]],
				['29'] = [["Aku hanya orang biasa sekarang, dan peristiwa yang terjadi sebelumnya bukan masalah lagi. Jika ada lagi keranjang, bawalah kemari — liburan belum berakhir. Aku juga punya sesuatu di tokoku yang ingin aku berikan kepadamu."]],
				['17'] = [["Bagus. Tunjukkan kepadaku yang kau punya."]],
				['18'] = [["Kau juga mungkin bisa mengumpulkan sejumlah besi tua. Ini bagianmu. Masih punya lagi?"]],
				['21'] = [["Nah..."]],
				['9'] = [["Aku akan tetap memberikan kostum itu kepadamu, tapi hanya jika kau berhasil membawakan jumlah keranjang yang cukup untuk memenuhi permintaan itu. Aku bersusah payah membuat kostum ini."]],
				['8'] = [["Aku tidak tahu, tapi kau tak akan bisa mendapatkan yang seperti ini lagi di padang gurun, ini langka. Aku bisa melihat kau mulai berpikir, jadi kenapa aku tidak berbagi sebagian laba untuk setiap keranjang denganmu?<new_line>Aku punya beberapa barang langka lainnya yang mungkin aku bagikan juga kepadamu."]],
				['7'] = [["Aku akan memberimu Kostum Kelinciku."]],
				['20'] = [["Oh, bagus, ini bagianmu. Punya lagi?"]],
				['10'] = [["Ini sekopnya. Berkelilinglah di kota, cari lubang kelinci di bangunan telantar dan gali lubang di sana. Di situlah kau akan menemukan cat itu."]],
				['11'] = [["Pikirlah sendiri. Kau bisa membelinya. Tapi, permintaannya sangat tinggi sekarang, begitu juga dengan harganya. Kau juga bisa mencarinya di tempat-tempat lain."]],
				['3'] = [["Kau agak lambat, ya? Apa yang dibutuhkan oleh kelinci pink? Telur hias, tentu saja."]],
				['4'] = [["Seberapa serius ini nantinya?"]],
				['5'] = [["Kenapa seekor penguin perlu membuang kotoran? Mereka mengeluarkanku dari Pelayanan Kelinci dan aku ingin mendapatkan kembali kepercayaan mereka agar menjadi indah dan lembut lagi."]],
				['6'] = [["Kau membuatku bingung dengan kata "sungguh"-mu! Pakai otakmu! Paskah akan segera tiba, dan kau memberi telur untuk Paskah. Aku membuat kostum kelinci dan berencana untuk menjualnya.<new_line>Aku akan berkeliling untuk mengunjungi keluarga kaya dan memberi mereka kado untuk mendapatkan laba. Tugasmu adalah menghiasi telur dan membuat keranjang kado dengan telur-telur itu."]],
				['12'] = [["Nah, jangan diam saja, pergilah dan penuhi pesanan itu, para pelanggan sudah mengantre."]]
			},
			['choice'] = {
				['23'] = [["Aku ingin menanyakan hal lain."]],
				['24'] = [["Mengapa ada cat di lubang-lubang itu? Lubang apa sebenarnya itu?"]],
				['16'] = [["Aku hanya lewat kemari." (Pergi)]],
				['15'] = [["Aku punya beberapa pertanyaan."]],
				['14'] = [["Aku ingin memberimu beberapa keranjang."]],
				['13'] = [["Baiklah." (Pergi)]],
				['2'] = [["Jadi, apa yang kau inginkan dariku, kelinci pink?"]],
				['1'] = [["Siapa kau sebenarnya?"]],
				['25'] = [["Mungkin kau bisa membuka kartumu? Siapa kau?"]],
				['26'] = [["Mungkin kau bisa memberikannya kepadaku secara gratis?"]],
				['19'] = [["Aku tidak bisa menemukan satu telur pun."]],
				['22'] = [["Aku tak punya pertanyaan lagi."]],
				['17'] = [[Berikan keranjang kepadanya]],
				['18'] = [["Itu saja untuk saat ini."]],
				['21'] = [["Ceritakan tentang dirimu."]],
				['9'] = [["Baiklah. Tapi, di mana aku bisa menemukan telur dan cat?"]],
				['8'] = [["Itu lebih baik."]],
				['7'] = [["Kenapa aku harus menginginkannya?"]],
				['20'] = [["Ingatkan aku tempat untuk menemukan cat itu."]],
				['10'] = [["Lalu, telurnya?"]],
				['11'] = [["Paham."]],
				['3'] = [["Kau serius?"]],
				['4'] = [["Lagi pula, untuk apa kau memerlukan telur hias?"]],
				['5'] = [["Sungguh?"]],
				['6'] = [["Kenapa aku harus peduli?"]],
				['12'] = [["Apa syaratnya?"]]
			}
		},
		['set_up_devices'] = {
			['episode'] = {
				['2'] = [[Juru Arsip ingin menempatkan perlengkapan itu di sini. Dan aku paham alasannya — aku melihat biomutan di kejauhan.]],
				['1'] = [[Aku sudah berada di posisi. Sekarang, aku harus menempatkan perlengkapan itu dan menyamarkannya dengan hati-hati.]],
				['3'] = [[Aku sudah menyelesaikan pemasangan dan berusaha menyamarkan perlengkapan itu. Secara bertahap, kawanan biomutan mulai mendekat. Entah bagaimana, perlengkapan itu mungkin memengaruhi mereka dan ada risiko mereka merusaknya.<new_line>Juru Arsip akan berterima kasih jika aku menjaga perlengkapan mereka. Di sisi lain, tugas utamaku sudah selesai.]]
