		['newyearEvent_energy_easy'] = {
			['episode'] = {
				['14'] = [[Kejernihan pikiranku berangsur kembali, dan mulai melihat siluet Cybersanta.<new_line>"Bangun, samurai. Sepertinya kau terlalu bersemangat. Tapi tak apa-apa: kita semua pernah mengalaminya setelah pertunjukan yang luar biasa. Pertunjukanmu berikutnya juga akan sukses."]],
				['13'] = [["Ini keajaiban Natal. Terima kasih telah berbuat baik. Kami meningkatkan semangat orang-orang, dan sekarang mereka bisa menjalani hidup dengan lebih banyak harapan."]],
				['12'] = [[Aku memukul drum dengan sekuat tenaga, dan drum mulai hancur berkeping-keping. Setelah penonton memberi kami tepuk tangan untuk terakhir kalinya, tirai di atas panggung ditutup. Cybersanta mendatangiku.<new_line><new_line>"Lihat, choomba? Percuma saja khawatir!"]],
				['9'] = [["Oohgravahahaaa!"]],
				['8'] = [[Kerumunan berteriak jauh lebih keras.<new_line><new_line>"Agravaaaaaaaaaaaaaaah ahmragaaaaahhhhhhhhha!"]],
				['7'] = [[Kerumunan mulai bersorak, dan Cybersanta melanjutkan.<new_line>"Agravaaaaaaaaaaaaaaah haaaaaaarrrrrrrrrrr!"]],
				['6'] = [[Cybersanta memetikkan tangan logamnya di sepanjang senar dan mulai meraung keras.<new_line>"Aaaaah grrrakhah aaaaaaaaaaah, graaaym haaaaaaaaa!"]],
				['5'] = [[Cybersanta berjalan ke mikrofon dan mengumumkan:<new_line>"Hari ini, tamu istimewa berkenan hadir di acara kecil kita. Mari kita sambut tokoh favorit kita, Sinterklas!!!"<new_line><new_line>Penonton berteriak histeris.]],
				['4'] = [["Tahu cara bermain bukanlah yang terpenting. Yang penting seberapa keren dirimu. Cukup pukul drum dan bermain sepenuh hati!"<new_line><new_line>Cybersanta melangkah ke arah mikrofon.]],
				['3'] = [["Tidak, choom, tak ada waktu untuk mencarinya. Duduklah di bangku itu dan ambil tongkatnya."]],
				['2'] = [["Tenang—aku hanya perlu drummer untuk band-ku. Entah mengapa sulit menemukan musisi di duniamu."]],
				['1'] = [["'Hai, choomba! Siap membantuku menggilas dunia ini menjadi atom?"]],
				['11'] = [[Anggota band lainnya terdiam, dan aku mulai mengikuti ritme dan mulai memainkan irama yang intens. Ketika selesai, penonton bersorak dengan energi yang luar biasa, dan Cybersanta memberikan encore.<new_line><new_line>"Agraaaaaaaaaaaaaaaaaaaaaaaaaaaaah!"]],
				['10'] = [["Aaaahaharrraharagaaaaaaaaaaaaaaaaaaah!"]]
			},
			['choice'] = {
				['14'] = [[Pingsan]],
				['13'] = [["Senang bisa membantu." (Pergi)]],
				['12'] = [["Apa itu tadi? Aku tak tahu cara memainkannya."]],
				['9'] = [[Blast Beat]],
				['8'] = [[Ketuk]],
				['7'] = [[Bergoyang]],
				['6'] = [[Mulai menabuh drum]],
				['5'] = [[Menyapa penonton]],
				['4'] = [[Duduk di belakang drum]],
				['3'] = [["Tapi aku bukan musisi."]],
				['2'] = [["Ingin aku carikan?"]],
				['1'] = [["Aku tak yakin."]],
				['11'] = [[Break drum]],
				['10'] = [[Solo]]
			}
		},
		['help_hunter3'] = {
			['episode'] = {
				['1'] = [[Sembari tertawa, si penyihir merapalkan mantra demi mantra ke arah si pemburu. Senjatanya tergeletak tak jauh dari situ. Aku harus menyerang si penyihir sebelum ia membunuh si pemburu.]]
			}
