		['boatman_camp_chat'] = {
			['episode'] = {
				['2'] = [["Sup?" Tukang perahu itu tersenyum. "Tak perlu kesal seperti itu. Aku pernah mendengar tentang sebuah lembaga ilmiah di mana mereka mungkin sedang menciptakan virus itu. Namun, di sana berbahaya. Radiasi, mutan,...manusia. Jangan menginjakkan kakimu di tempat-tempat itu."]],
				['1'] = [[Tukang perahu sedang membuat sup ikan di tong besar. Melihatku, dia mengangguk.<new_line>"Jadi, kamu berhasil. Apakah kau menemukan apa yang kau cari di Belomorsk?"]],
				['3'] = [["Kamu keras kepala sekali! Sudah kubilang, ini berbahaya dan kamu belum siap! Kau harus menetap di sini lebih lama dan mengasah keahlianmu terlebih dahulu. Bantu penduduk setempat dan ambil beberapa tugas dari bar. Jual beberapa barang, mengobrol dengan mandor. Kemudian, begitu kau sudah siap, kembalilah dan aku akan menceritakan semuanya kepadamu."]]
			},
			['choice'] = {
				['2'] = [["Di mana lokasi lembaga ini?"]],
				['1'] = [["Tidak, aku ingin tahu apa yang terjadi. Siapa yang memulai perang, ada apa di balik wabah ini..."]],
				['3'] = [["Kamu meyakinkanku. Sampai jumpa lagi di lain waktu".]]
			}
		},
		['newyearEvent_energy_medium'] = {
			['episode'] = {
				['9'] = [[Setelah selesai mengumpulkan ornamen, kami mengangkat karung ke atas bahu kami dan menuju ke pintu keluar. Namun, ternyata sekelompok bandit menghalangi jalan kami. Setelah berlindung di tikungan, Business berbisik:<new_line>"Kita bisa mencari jalan keluar lain, tapi akan sulit karena karungnya berat. Atau haruskah kita mengurus mereka dan maju ke depan?"]],
				['8'] = [["Jangan khawatir, kawan. Aku telah memberikan kebaikan dan keajaiban kepada orang-orang selama bertahun-tahun. Aku tahu betul mana yang benar dan mana yang salah. Ambil karung dan masukkan ornamennya."]],
				['7'] = [[Setelah selesai menanam ranjau, kami meneruskan misi dan menemukan sebuah ruangan dengan dekorasi Natal.<new_line>"Oh, hadiah yang bagus! Beruntung kita datang ke sini. Masukkan semuanya ke dalam karung."<new_line>Sambil mengatakannya, Business mengeluarkan dua karung merah.]],
				['6'] = [[Setelah selesai menghancurkan kendaraan, kami terus menjelajahi balok es. Ruangan berikutnya adalah tempat penyimpanan bahan bakar.<new_line>"Hebat, kita bisa memasang ranjau di sini! Ambil kembang api."<new_line>Business melemparkan sekotak kembang api.]],
				['5'] = [[Ternyata ada beberapa pintu masuk yang tersembunyi di balok es, dan kami masuk melalui salah satunya. Kami serasa masuk ke sebuah garasi berisi berbagai macam kendaraan.<new_line>"Ambil palu dan hancurkan semua barang di sini!"]],
				['4'] = [[Kami harus berjalan beberapa mil sebelum sampai di sebelah balok es yang besar. Business berbisik kepadaku.<new_line>"Ikuti aku. Oh, dan ini gulungan portal untukmu. Jika sadar bahwa kau tak bisa melakukannya, bacalah gulungan itu, dan itu akan membuatmu keluar dari sini."]],
				['3'] = [["Aku 100% yakin! Persaingan sangatlah baik. Peradaban kita bertumpu pada pilar. Dan bagaimana kita tahu siapa pilar dan siapa Johnny yang muncul akhir-akhir ini jika kita tak melakukan yang terbaik untuk bersaing satu sama lain? Entah mereka menang dan membuktikan bahwa mereka benar, atau kita yang melakukannya! Jadi, apa kau siap?"]],
				['2'] = [["Bajingan. Pabrik kita menggunakan Natal dan keajaiban untuk iklan, dan mereka menentangnya. Jadi, mereka adalah pesaing langsung, dan kita harus mengurusnya. Apa kau siap untuk menegakkan keadilan?"]],
				['1'] = [["Ho ho halo, kawan baikku! Pabrik kita memiliki berbagai masalah, dan akhirnya aku tahu apa yang salah. Pesaing sialan kita mengganggu operasi pabrik! Bisakah kau membantuku menyabotase pabrik mereka sedikit?"]],
				['11'] = [[Aku mengeluarkan gulungan dan mulai membacanya. Ketika Business melihatku, dia dengan cepat melakukan hal yang sama. Setelah selesai membacanya, aku melihat kilatan biru terang dan beberapa detik kemudian sampai di gurun.]],
				['10'] = [[Setelah kami menjauh dari balok es, Business mengeluarkan detonator dan menekannya. Terdengar ledakan keras.<new_line>"Itu dia! Kita melakukan pekerjaan dengan baik dan berbuat baik! Dengan mengalahkan pesaing, pabrik kita akan sukses!"]]
			},
			['choice'] = {
				['9'] = [["Bukankah mencuri itu dosa?"]],
				['8'] = [[Masukkan ornamen ke dalam karung]],
				['7'] = [[Tanam ranjau di dalam ruangan]],
				['6'] = [[Kabur melalui portal]],
				['5'] = [[Hancurkan semuanya dengan palu]],
				['4'] = [[Ikuti Business Claus]],
				['3'] = [["Mereka orang jahat... Tapi apa kau yakin mereka dalang masalah di pabrik?"]],
				['2'] = [["Aku siap."]],
				['1'] = [["Pesaing yang mana? Bagaimana mereka bisa sampai di sini?"]],
