		['newyearEvent_time_medium'] = {
			['episode'] = {
				['14'] = [["Yah, aku akan pergi dari sini. Duniamu membutuhkan revolusi! Sampai ketemu lagi!"]],
				['13'] = [[Ketika pekerjaanku selesai, seseorang menepuk bahuku.<new_line>"Halo kawan! Aku datang untuk meminta maaf karena kau harus membongkar seluruh tempat ini... tetapi aku tak bisa menghancurkan apa pun. Selain itu, aku baru pulih dari serangan Cave Santa. Tolong izinkan aku berbagi makan malam denganmu."]],
				['12'] = [[Sekarang aku harus membongkar benteng. Itu butuh waktu sekitar 12 jam.]],
				['9'] = [["Sayangnya, kawanku, kita harus membongkar semua ini. Para bandit memberikan denah itu pada Claws, jadi kau membangun sarang ini untuk mereka."]],
				['8'] = [["Ooh aaaaavaaaaah..."<new_line>Setelah serangan Cave, dia dan Claws bisa saling memahami. Sepertinya Claws dengan bersemangat mengeluhkan sesuatu kepada Cave. Tersesat dalam percakapan aneh, mereka meninggalkan desa.<new_line><new_line>Business dan aku ditinggalkan oleh mereka.]],
				['7'] = [[Business menyentuhkan jari ke kepalanya, dan sesaat kemudian, keajaiban terjadi: sebuah portal muncul, dan Cave Santa yang sama marahnya keluar dari sana.<new_line>"Ah oovaaah?!"<new_line>Cave sepertinya mengumpat dan memukuli Claws dengan tongkatnya.]],
				['6'] = [["Aku tidak ingin membahas pekerjaanku dengan para pengeksploitasi!"]],
				['5'] = [[Sebelum bisa berbicara, aku mendengar suara mesin di belakangku. Ternyata itu adalah mobil kecil kuno berwarna merah. Mobil itu berhenti, dan Business Claus melangkah keluar dengan tatapan marah.<new_line>"Apa yang telah kau lakukan, dasar Marxis sialan? Kau tahu siapa yang memberi rencana pembangunan itu, kan?"]],
				['4'] = [[Claws bekerja sangat cepat. Saat aku sibuk dengan gudang, dia membangun semua bangunan di sekitarnya. Kami menyelesaikan konstruksi, tetapi hasilnya terlihat aneh.<new_line>Claws membuat balok es, banyak jebakan, dan lubang api untuk ritual.<new_line>"'Seluruh pekerja di dunia, tetaplah hangat!' seperti yang mereka katakan."]],
				['3'] = [["Beristirahat secukupnya, lalu kita akan mulai. Pekerjaan ini butuh waktu sekitar 10 jam."]],
				['2'] = [["Ini cukup sederhana. Aku diberi denah. Kita hanya perlu melakukan konstruksi sesuai dengan denah itu!"]],
				['1'] = [["Halo kawan! Tak ada cuaca yang terlalu buruk untuk bekerja, kan? Bahkan, aku sangat merindukan pekerjaan berat! Maukah kau menolongku?"]],
				['11'] = [["Aku sangat ingin memanfaatkan bangunan ini, tapi kita tak punya pilihan. Penduduk setempat telah pergi atau ditangkap. Tak ada orang yang menempati tempat ini—kecuali pada bandit, tentu saja. Tolong bongkar semua."]],
				['10'] = [[Aku harus membongkar perangkap. Itu butuh waktu sekitar 8 jam.]]
			},
			['choice'] = {
				['13'] = [[Mulai bekerja (16 jam)]],
				['12'] = [[Mulai bekerja (14 jam)]],
				['9'] = [[Makan pai]],
				['8'] = [[Mulai bekerja (12 jam)]],
				['7'] = [["Kita tak bisa menggunakannya?"]],
				['6'] = [["Apa yang harus kulakukan sekarang?"]],
				['5'] = [["Sedang apa kau..."]],
				['4'] = [[Mulai bekerja (10 jam)]],
				['3'] = [["Kedengarannya mudah dan jelas."]],
				['2'] = [["Aku tak punya waktu." (Pergi)]],
				['1'] = [["Apa yang akan kita lakukan?"]],
