		['emba_energy_hard'] = {
			['episode'] = {
				['12'] = [[Yang terjadi berikutnya sama sekali tidak aku ingat. Aku terbangun di pekarangan, semuanya terbalik, ada pisau bengkok menancap di pagar.<new_line>Gorden menutupi semua jendela di rumah petani dan pintunya terkunci. Sepertinya eksperimen itu gagal.<new_line>Aku merasa bersalah. Aku mungkin salah menghitung dosisnya — bir miselium itu bukan main-main.]],
				['9'] = [["Na-a-a-h, kalau kau menggunakan dengan pop... prop... porsi yang tepat, aku bisa m-membantai sepuluh meh... muh... mutan itu. Mari keluar ke pekarangan dan aku akan m-menunjukkannya kepadamu! Tapi kita masih perlu sa... ssa... tu..."]],
				['8'] = [["Lihat kelakuan mi-se... lium ini. Dan kita ini orang yang serius. Mari s-s-selesaikan eks... perimen ini. Baiklah kalau begitu, demi ilmu pengetahuan!"]],
				['7'] = [["Pria tua itu benar-benar punya satu formula dan separuh lagi. Re... agen... murni. Tuliskan... ob... serr.. va-si-mu di jurnal ini, kita akan membawanya ke orang berpakaian kuning itu nanti. Ayo, lagi!"]],
				['6'] = [["Campuran pria tua itu masih punya banyak karakter yang belum dijelajahi. Nah, kalau begitu, demi ilmu pengetahuan. Kita mulai eksperimennya?"]],
				['5'] = [["Ini bir miselium, kawan. Para ilmuwan tidak peduli, tapi barang ini benar-benar punya efek fisik! Ini sesuatu yang penting!"]],
				['4'] = [[Orang tua itu memberiku sebuah bundel yang aku bawa kembali ke sang petani.<new_line><new_line>Dengan gembira dia membentangkan bundel itu, mengeluarkan botol cairan hijau dan mulai mencatatkan sesuatu di jurnalnya:<new_line>"Nah, sekarang. Kami melakukan eksperimen nomor... mm... Aku tidak ingat. Material... satu liter zat untuk diteliti. Baik, kau sudah siap?"]],
				['3'] = [["Apakah petani itu yang menyuruhmu? Aku sudah katakan kepadanya — tidak ada makanan, tidak ada barang. Kau bisa membawa makanan sendiri untuk ditukarkan denganku, kalau orang jorok itu terlalu malas."]],
				['2'] = [["Kau tahu, aku juga semacam ilmuwan. Tapi aku perlu material untuk diteliti. Aku sudah membuat kesepakatan dengan seorang pria tua, kau hanya perlu mengunjunginya dan mengambil barang itu."]],
				['1'] = [["Hei, aku ingat kamu. Kau mau melakukan eksperimen? Aku pikir kita pernah melakukan eksperimen bersama-sama."]],
				['11'] = [[Seolah-olah, ingatanku tentang peristiwa yang terjadi berikutnya sama sekali terpotong. Aku terbangun di pekarangan, semuanya terbalik, dan sebuah pisau bengkok menancap di pagar.<new_line>Semua jendela di pondok petani itu tertutupi gorden dan pintunya terkunci.Sepertinya eksperimen itu gagal.<new_line>Tapi aku tidak merasa terlalu buruk seperti yang aku duga — mungkin masih ada hal lain dengan bir miselium itu.]],
				['10'] = [[Setelah meminum tegukan terakhir, sang petani berusaha bangkit, mengambil dua langkah ke arah pintu, lalu terjatuh di lantai, dan langsung mengorok. Aku mengambil catatannya dari meja dan perlahan bergerak keluar. Aku akan membawanya ke para ilmuwan agar mereka dapat meninjau risetnya.]]
			},
			['choice'] = {
				['5'] = [["Kau benar-benar seorang ilmuwan..."]],
				['4'] = [["Apa ini?"]],
				['3'] = [["Tuangkan."]],
				['2'] = [[Pergi ke pria tua itu]],
				['1'] = [["Kau tertarik dengan ilmu pengetahuan?"]]
			}
		},
		['bee_hive'] = {
			['episode'] = {
				['9'] = [[Terowongan ke pusat sarang ada di depan sana. Ratu lebah mungkin ada di sana, dikelilingi para penjaganya.]],
				['8'] = [[Terowongan ini membawa ke terusan yang lebar. Tiga lebah penjaga berjaga di sana. Tampaknya ini menuju ke pusat sarang lebah.]],
				['7'] = [[Aku bisa melihat cahaya di ujung terowongan ini. Aku rasa aku menemukan jalan keluar.]],
