		['halloween_energy_hard'] = {
			['episode'] = {
				['6'] = [["Siapa yang membutuhkannya? Aku sudah memeriksa... Logamnya tidak berharga. Jadi tidak akan ada yang mengambilnya. Kita perlu beberapa gelas minuman lagi untuk membuat otak kita bekerja lebih baik."<new_line>Petani mengisi kedua gelas itu.]],
				['5'] = [["Aku sedang mencoba untuk memutuskan... Apa yang harus kulakukan dengan kekayaanku..."]],
				['4'] = [["Ayolah... Minuman ini akan membuat bulu kudukmu berdiri... Ayo bersulang untuk persahabatan!"<new_line>Petani mengisi kedua gelas itu.]],
				['3'] = [[Aku menenggak minuman itu sekaligus. Minuman keras ini sangat kuat dan membakar tenggorokanku saat kutelan.<new_line>"J... jadi... Bagaimana kau... hik... suka... minuman kerasku?"]],
				['2'] = [[Petani meletakkan gelas lain di atas meja dan mengisinya dengan minuman keras.<new_line>"B... bersulang..."]],
				['1'] = [["Oh, kamu rupanya. Maksudku, yang... Yah, persetan dengan itu... Maksudku, denganmu... Tidak, sial... Aku mendapat jackpot dan tidak akan bekerja sebagai petani lagi! Tapi jackpot itu menggunakan mata uang asing. Aku tak tahu harus bagaimana, jadi aku mencoba untuk memikirkan sesuatu. Mari... kita pikirkan bersama."]],
				['17'] = [[Aku terbangun di tempat yang kosong dan asing. Kepalaku berdenyut. Bagaimana aku bisa ada di sini? Apa yang terjadi kemarin?]],
				['9'] = [["Ya! Em... Emigrasi! Lalu aku bisa menghabiskan uangnya di dunia lain. Ayo kita bersulang untuk itu!"<new_line>Petani mengisi kedua gelas itu.]],
				['8'] = [["Apa?"]],
				['7'] = [["Fiuh... minuman yang enak... Mereka... para, eh, monyet itu berkata kepadaku... bahwa uang itu bagus. Bisa membeli satu peternakan. Tapi bagaimana caranya?"]],
				['15'] = [["Entahlah... hik... Kurasa kita harus mencoba minuman keras yang dikeluarkan penyihir itu."<new_line>Petani mengisi kedua gelas itu.]],
				['14'] = [["Apa aku terlihat seperti pemilik penginapan?! Baiklah, baiklah. Abrakadabra. nih. Aku sudah menyihir minuman kerasmu dengan api neraka. Jika kau memanggilku lagi, aku akan melakukan hal yang sama kepadamu."<new_line>Si penyihir menghilang secepat dia muncul.]],
				['12'] = [["Mimpimu terlalu tinggi. Kebijakan imigrasi di dunia lain bisa sangat kejam. Kau bisa pergi berlibur dengan uangnya, tapi bahkan itu butuh banyak dokumen. Sekarang enyah dari hadapanku!"]],
				['11'] = [[Aku membaca catatan itu dan penyihir itu muncul di ruangan.<new_line><new_line>"Mau apa kamu, Orang Desa?"]],
				['10'] = [["Aku punya teman... dia adalah penyihir! Dia memberiku sebuah catatan. Dia berutang padaku... Tapi teksnya agak buram... Jika kau membacanya, dia akan muncul..."]],
				['16'] = [["Kau tahu... kau orang yang hebat... Mungkin aku harus... memberimu... semua kekayaanku..."<new_line>Setelahnya, Petani itu tak sadarkan diri.]],
				['13'] = [["Sial... Yah, bisakah kamu setidaknya menyulap minuman untuk kami? Aku bisa membayar..."<new_line>Petani menawarkan beberapa koin kepada penyihir.]]
			},
			['choice'] = {
				['5'] = [["Mungkin kita bisa... menggadaikannya?"]],
				['13'] = [[Berikan]],
				['9'] = [["Petani ingin em... emigrasi... ke..."]],
				['8'] = [[Baca catatan]],
				['7'] = [["Em... emigrasi."]],
				['6'] = [["Em..."]],
				['10'] = [["Jadi apa yang akan kau lakukan sekarang?"]],
				['4'] = [["Ini kuat sekali."]],
				['12'] = [[Bangun dan pergi]],
				['2'] = [["Tak ada waktu."]],
				['1'] = [["Lanjutkan."]],
				['3'] = [[Minum]],
				['11'] = [[Ambil kekayaan itu]]
			}
		},
		['mirror_lady'] = {
			['episode'] = {
				['2'] = [[Setelah pertempuran, aku mulai mencari jejak Igor di dalam rumah. Boneka keramik yang duduk di lemari pakaian terlihat mencurigakan. Aku merasa ia mengawasiku. Saat aku mendekatinya untuk melihat lebih dekat, boneka itu menyerang wajahku dan menendangkan kaki kecilnya, kemudian menghilang tanpa jejak.]],
				['1'] = [[Seluruh rumah ini berisi cermin. Seperti yang diperintahkan bartender, aku menyalakan lilin di depan cermin dan mulai memanggil Bloody Mary. Awalnya, aku mencium bau parfum mengerikan, kemudian aku mendengar langkah kaki, dan suara pelan... -Siapa ini?]]
			}
		},
		['emba_craft_easy3'] = {
			['episode'] = {
				['2'] = [[Lubang ventilasi itu sangat sempit dan mustahil untuk masuk ke dalamnya. Artinya, tempat tumbuhnya tanaman ini akan tetap jadi misteri. Untuk saat ini, aku bisa menghancurkan bukaan dari lubang ini di permukaan.<new_line>Aku bisa melemparkan sesuatu yang mudah terbakar atau peledak ke sana, karena tidak ada pilihan lain.]],
				['1'] = [[Bukaan lubang ventilasi ada di tengah bola jalinan tanaman itu. Anehnya, tanaman itu tumbuh dari tempat lain dan hanya muncul ke permukaan di sini.]],
				['3'] = [[Ada suara bergemuruh di bawah tanah. Artinya, sesuatu runtuh di bawah sana. Tampaknya batang dan akar hitam itu bergetar sendiri dan bukan karena gerakan tanah.<new_line><new_line>Suasana mendadak hening dalam beberapa detik. Semua tanaman ini akan layu dan mati karena sudah terputus dari akarnya. Bahkan sekarang, dedaunan dan jamur itu tampak menggulung. Bagus.]]
			},
			['choice'] = {
				['5'] = [[Lemparkan Peledak]]
			}
		},
		['halloween_search_medium_2'] = {
			['episode'] = {
				['2'] = [["Baiklah, sejumput ini... Sedikit percikan itu... Dua, dua, susah payah dan masalah... Api membakar dan kuali menggelembung! Kurasa ini sudah siap. Cobalah sedikit, Nak."]],
				['1'] = [["Jadi, kau berhasil menemukan tumbuhan itu?"]],
				['3'] = [["Kau tidak akan melihat sedikit petunjuk pun untuk hadiahnya tanpa aku, Nak. Hadiah itu tersembunyi. Jadi, minumlah."]]
			},
			['choice'] = {
				['2'] = [["Tidak mungkin! Berikan aku hadiahnya, atau aku akan..."]],
				['1'] = [["Belum." (Pergi)]],
				['3'] = [[Minum ramuan]]
			}
		},
		['emba_isaev_intro'] = {
			['episode'] = {
				['16'] = [["Benar. Menurut informasi intelijen, kota sains ini benar-benar dibanjiri jamur dan dihuni oleh bentuk kehidupan yang tidak bersahabat. Kau telah menjumpai beberapa di antaranya baru-baru ini."]],
				['15'] = [["Saat ini kami tidak dapat menghubungi mereka, jadi kami membawa Navigator dari Vector untuk membantu. Selain proyek E.M.B.A., aku juga tertarik dengan nasib rekan seperjuanganku."]],
				['14'] = [["Belum lama ini, Progres mengirimkan ekspedisi ke wilayah ini; mereka mencari teknologi lain yang hilang. Tapi mereka malah tidak sengaja menemukan salah satu kota sains ini.”]],
				['13'] = [["Setelah terjadinya bencana global, proyek E.M.B.A. mengalami kerusakan, dan ekspedisi kita akan memperbaikinya."]],
				['2'] = [["Kami tidak bisa mendirikan laboratorium di lapangan terbuka, karena itu kami ingin mendirikannya di permukiman ini. Tapi penduduk setempat tidak percaya pada ilmuwan."]],
				['1'] = [["Aku percaya, kawan, kalau kau adalah agen potensial kami. Aku Dr. Victor Isaev. Senang berkenalan denganmu. Sayangnya, aku tidak bisa memberikanmu semua detailnya sebelum kita menyelesaikan masalah utamanya."]],
				['19'] = [["Coba kulihat. Hm, ini akan banyak membantu dalam riset kami. Bawa segera ke Ahli biologi di permukiman dan bantu dia melakukan risetnya."]],
				['17'] = [["Kami tidak tahu dari mana asalnya. Kuharap kau dapat membantu kami mencari tahu jawabannya."]],
				['18'] = [["Ini adalah perkumpulan para spesialis dari berbagai bidang ilmiah mutakhir. Mungkin yang terakhir dari jenisnya yang masih ada... Itulah mengapa kami kadang disebut sebagai "juru arsip" — kami mencoba mengumpulkan dan mengawetkan remah-remah dari apa yang selamat.<new_line>Namun tidak lama lagi pengetahuan dan sumber daya kami akan cukup untuk memberi dunia kesempatan keduanya!"]],
				['9'] = [["Fase pertama: memulihkan biosfer. Tujuan ekspedisi kami adalah menemukan teknologi terraforming. Menjelang terjadinya bencana global, para ilmuwan berhasil menciptakannya — proyek E.M.B.A."]],
				['8'] = [[Sang dokter menghela nafas dan melepas kacamatanya untuk menyekanya, seolah-olah bersiap untuk percakapan yang panjang.<new_line>"Seperti yang pasti kau ketahui sendiri, dunia sudah benar-benar hancur berantakan. Progres telah menyiapkan serangkaian langkah untuk mengatasi krisis itu."]],
				['7'] = [["Ancamannya telah dilenyapkan. Kepala penjaga mengizinkan lab ditempatkan di permukiman. Kita punya waktu luang, dan aku bisa menjawab pertanyaan-pertanyaanmu."]],
				['10'] = [["Turunan miselium yang dimodifikasi secara genetik ini adalah jamur yang mampu menyerap efek merugikan dari tanah. Padang gurun nuklir bisa menjadi tanah subur berkat jamur itu."]],
				['11'] = [["Tidak, pemimpin proyek E.M.B.A. adalah salah satu ilmuwan terbaik di zaman kita — Profesor Mark Gerin. Berkat karyanya, kita bisa membuat terobosan dalam perekayasaan kebumian.]],
				['3'] = [["Ini ada hubungannya dengan penelitian yang dilakukan di kota-kota sains — tipikal pusat-pusat ilmiah. Ada satu di dekatnya. Pengembangan ini menimbulkan ancaman biologis, dan jenis mutan baru telah muncul. Orang-orang menyalahkan kami, para ilmuwan."]],
				['4'] = [["Tidak, tapi aku bisa memahami apa yang dirasakan orang-orang biasa. Dan sekarang kita di sini untuk memperbaiki keadaan. Tapi tanpa persediaan, misi ini pasti akan gagal."]],
				['5'] = [["Kami di Progres sangat bersemangat menyambut aspirasi dan kontribusimu di masa mendatang untuk ekspedisi. Kami memiliki rencana untuk menyelesaikan masalah, dan kau dapat membantu."]],
				['6'] = [["Aku sudah menandai tempat itu di peta, Navigator ekspedisi akan menunggumu di sana. Dia akan memberikan informasi terbaru padamu."]],
				['12'] = [["Tidak akan mudah menemukannya. Riset itu dilakukan di stasiun riset biologi di wilayah kota sains tertutup dengan kerahasiaan yang sangat ketat. Jadi, bahkan kami sendiri tidak tahu lokasi semua kota sains tersebut berada."]]
			},
			['choice'] = {
				['15'] = [["Jadi, di mana ekspedisi pertamanya?"]],
				['16'] = [["Kalau begitu ada baiknya mulai dari kota sains ini."]],
				['17'] = [["Ya. Makhluk apakah itu?"]],
				['14'] = [["Apa yang terjadi pada proyek itu?"]],
				['13'] = [["Jadi, kita membutuhkan Gerin?"]],
				['9'] = [["Apa mereka benar?"]],
				['8'] = [["Kenapa?"]],
				['7'] = [["Aku menemukan dokumen di bungker."]],
				['6'] = [["Dan apa itu organisasi Progres?"]],
				['5'] = [["Apa yang akan kau lakukan?"]],
				['4'] = [["Apa tujuan utama ekspedisi ini?"]],
				['3'] = [["Ada yang bisa kubantu?"]],
				['12'] = [["Apa kau yang mengerjakan proyek itu?"]],
				['1'] = [["Apa yang terjadi?"]],
				['11'] = [["Apa gunanya?"]],
				['10'] = [["Ceritakan padaku rencanamu."]]
			}
		},
