		['cover_the_fighter'] = {
			['episode'] = {
				['12'] = [[Pengintai itu sudah mati. Aku gagal dalam misiku untuk melindunginya.]],
				['9'] = [[Pengintai itu sibuk memeriksa semua loker dan sudut gelap di tempat itu. Brankas itu terletak di sana tak tersentuh.]],
				['8'] = [[Setelah beberapa kali mencoba, aku berhasil membuka brankas itu. Di dalamnya kami menemukan beberapa perangkat elektronik dan kotak amunisi yang terjaga dengan sangat baik. Pengintai itu bersiul dengan takjub dan menepuk pundakku.]],
				['7'] = [["Woah, lihat brankas itu! Apa pun yang ada di balik kunci seperti itu pasti berharga. Sayang sekali tak ada waktu untuk tetap di sini. Kami sudah melakukan pekerjaan utama, tetapi jika kamu bisa membuka juga, orang-orang kami akan membayar lebih."]],
				['6'] = [[Rekanku menurunkan senjatanya dan, terengah-engah, memeriksa tempat itu:<new_line><new_line>"Fiuh, untung aku menunggumu, atau aku tidak akan keluar dari sini hidup-hidup. Sekarang aku perlu memeriksa semuanya di sini sebelum monster-monster itu berlari kembali. Terima kasih atas bantuanmu, dan semoga berhasil!"]],
				['5'] = [[Rekanku menurunkan senjatanya dan, terengah-engah, memeriksa tempat itu:<new_line><new_line>"Fiuh, untung aku menunggumu, atau aku tidak akan keluar dari sini hidup-hidup. Sekarang aku perlu memeriksa semuanya di sini sebelum monster-monster itu berlari kembali."]],
				['4'] = [[Kami mendengar suara gemeresik dan gesekan dalam kegelapan. Kami mengeluarkan senter kami tepat pada waktunya: beberapa makhluk jahat yang sudah membuat sarang mereka di sini bersiap untuk menyerang.]],
				['3'] = [[Pengintai itu duduk merokok di pintu masuk ke tempat perlindungan bom. Saat melihatku, dia terkejut:<new_line>"Bala bantuan. Itu berita bagus, Kawan! Aku sudah membuka kuncinya, bagaimana kalau kita mulai?"]],
				['2'] = [[Pengintai itu duduk merokok di pintu masuk ke hanggar berkarat. Saat melihatku, dia terkejut:<new_line>"Bala bantuan. Itu berita bagus, Kawan! Aku sudah membuka kuncinya, bagaimana kalau kita mulai?"]],
				['1'] = [[Pengintai itu duduk merokok di pintu masuk ke garasi yang panjang. Saat melihatku, dia terkejut:<new_line>"Bala bantuan. Itu berita bagus, Kawan! Aku sudah membuka kuncinya, bagaimana kalau kita mulai?"]],
				['11'] = [["Tidak ada tumpukan jarahan, tapi setidaknya ini semua milik kita. Ada pintu lain di sana. Mungkin kita akan menemukan sesuatu di balik pintu itu yang bisa membuat kita bertahan hidup. Namun dari suaranya, akan ada pertempuran terlebih dahulu. Apakah kita akan mengambil risiko ini atau pulang?"]],
				['10'] = [[Tidak ada orang di dalam. Pengintai itu melihat ke sekeliling ruangan dan kembali menghampiriku.]]
			},
			['choice'] = {
				['5'] = [[Masuk melalui pintu]],
				['1'] = [[Masuk ke bangunan]],
				['4'] = [[Ledakkan kuncinya]],
				['3'] = [[Buka kuncinya]]
			}
		},
		['forest_pithouse'] = {
			['episode'] = {
				['11'] = [[Di samping pondok, aku menemukan buah berry, daging, dan kulit mentah dibentangkan untuk dikeringkan dan sekeranjang arang.]],
				['10'] = [[Segera setelah aku keluar dengan karung penuh barang, aku melihat pemilik selter, pria itu mengarahkan senapannya kepadaku. Seketika itu terdengar tembakan, dan aku merasakan nyeri menusuk bahu kananku.]],
				['9'] = [[Di dalam selter, aku menemukan banyak persediaan: makanan, obat, peralatan, amunisi, dan banyak lagi. Aku hanya harus keluar sebelum pemiliknya pulang.]],
				['8'] = [[Pintunya digembok hanya dengan gerendel kecil, jelas untuk mencegah binatang masuk. Di dalam cukup hangat. Karung dan kotak berbagai barang diletakkan di sudut.]],
				['7'] = [[Saat tiba waktunya berpisah, dia bertanya jika aku ingin barter garam, rokok, atau amunisi.]],
				['6'] = [[Saat pria itu sadar aku bukanlah ancaman, dia melunak. Kami mengobrol dengan santai sambil makan dan minum. Kami bicara kebanyakan tentang masa lalu dan berbagi ide tentang masa depan.]],
				['5'] = [[Dia memandangku dengan curiga, tetapi lalu rileks. <new_line>]],
				['4'] = [[Lebih baik kamu terus berjalan,]],
				['3'] = [[Pria itu tiba-tiba berdiri dan mengarahkan senapannya kepadaku.<new_line>]],
				['2'] = [[Sebelum pemilik selter kembali. Dia adalah pria kecil yang memegang senjata dan keranjang penuh dengan jamur. Dia melihat ke sekitar dengan hari-hati sebelum memasukkan arang ke perapian.]],
				['1'] = [[Aku menjumpai selter peristirahatan di hutan. Arang di lubang api masih hangat, jadi seseorang pasti tinggal di sini dan tak mungkin jauh. Aku tak mendengar suara dari dalam.]],
				['12'] = [[Aku memasang granat dan bersembunyi di balik semak-semak di dekatnya. Tak lama kemudian, pemilik selter muncul. Segera setelah dia masuk, ledakan terjadi. Dia sudah tewas saat aku mendekat. Sekarang aku bisa menggeledah tempat ini sepenuhnya.]],
				['13'] = [[Saat aku hendak pergi dengan yang telah kudapatkan, sekawanan serigala muncul dari pepohonan. Tampaknya keributan dari ledakan dan penggeledahanku menarik perhatian mereka. Aku harus membela diri.]]
			},
			['choice'] = {
				['10'] = [[Apa masalahmu? Aku hanya ingin bicara!]],
				['9'] = [[Keluar]],
				['8'] = [[Ucapkan salam perpisahan dan pergi]],
				['7'] = [[Sudah lama aku tak bertemu siapa pun. Senang rasanya bisa mengobrol.]],
				['6'] = [[Aku hanya lewat]],
				['5'] = [[Keluar dari semak dan ucapkan salam]],
				['4'] = [[Pasang granat kawat jebakan]],
				['3'] = [[Cari di mana saja]],
				['2'] = [[Masuk]],
				['1'] = [[Bersembunyi dan menunggu]],
				['11'] = [[Baiklah, aku pergi]],
				['12'] = [[Cari luar dalam]]
			}
		},
		['emba_energy_easy'] = {
			['episode'] = {
				['6'] = [["Yah, sudah selesai sekarang! Akhirnya sampah ini terbakar dan aku bisa mengelola pertanianku lagi.<new_line>Terima kasih, kau orang baik. Aku akan membayar jasamu sebisa mungkin."]],
				['5'] = [[Seolah-olah miselium itu berusaha membungkus lumbung dalam balutan dahan dan akar, tapi kita sudah membereskannya.<new_line>Sekarang tinggal mengumpulkan tumbuhan mutan ini dan membakarnya. Petani itu pergi mengambil bensin dan menyuruhku mengumpulkan akar-akar itu.]],
				['4'] = [[Kita sudah menyelesaikan bagian pertama dan membersihkan ladang, tapi itu belum semua, kita harus melanjutkan ke lumbung.]],
				['3'] = [["Kawan, kau penyelamatku!<new_line>Lihat, kau harus memotong semua dahan ini sekaligus, tumpuk di perapian, dan bakar. Kau tak akan punya waktu untuk beristirahat, atau makhluk ini akan berakar lagi dan menyebarkan sporanya ke mana-mana. Mari mulai dengan ladang ini."]],
				['2'] = [["Ini kesalahanku sendiri! Tetanggaku bercerita tentang miselium yang bisa membuat ladang subur dan mendapatkan panen yang lebih bagus. Aku menanamnya di rumah kaca dan tumbuh sangat subur, aku bahkan tak bisa memotongnya sendiri sekarang. Tolonglah aku!"]],
				['1'] = [["Hey, tunggu! Aku perlu bantuan. Akar sialan ini menutupi seluruh pertanian. Mutan hijau itu mulai mengunjungi tempat ini seolah-olah kita ini kantin pribadi mereka."]],
				['7'] = [["Oh, sialan! Semuanya sia-sia, tumbuhan itu akan tumbuh lagi dan semua berulang lagi. Terserah. Terima kasih sudah berusaha, tapi kau harus tahu batas kemampuanmu..."<new_line><new_line>Petani itu berjalan menuju pondoknya dengan sedih, menendang akar yang tumbuh ke dalam tanah di hadapanmu.]]
			},
			['choice'] = {
				['2'] = [[Potong akar]],
				['1'] = [["Bagaimana bisa terjadi?"]],
				['3'] = [[Tumpuk akar]]
			}
		},
		['embacity_biolab_0'] = {
			['episode'] = {
				['2'] = [[Di belakang pintu itu terdapat sebuah tangga. Tangga itu membawaku ke aula luas yang tampak sudah lama diterlantarkan. Aku berjalan mengelilingi ruangan dan menemukan pintu yang tidak mencolok dengan kunci kode.]],
				['1'] = [[Hal pertama yang menarik perhatianku ketika memasuki lab adalah lampu berkedip merah di atas pintu di dinding ruangan. Pintu ini ternyata pintu yang dikendalikan secara otomatis yang berukuran sangat besar. Yang sekarang sedikit terbuka.]],
				['3'] = [[Bau apek menyeruak dari kompartemen yang terbuka. Aku melihat lebih dekat dan menemukan sebuah wadah, dan di atasnya, sebuah tanda bertuliskan "Perekam darurat." Inilah kotak hitam yang kucari.]]
