		['comrade_in_trouble'] = {
			['episode'] = {
				['24'] = [[Aku berbagi bahan bakarku dengan pria itu. Seharusnya lebih dari cukup untuk membawanya kembali ke permukiman.]],
				['16'] = [[Si bandit bersenandung, mengambil uang, dan membawa anak buahnya pergi. Seorang pria yang bahagia memanjat keluar dari jendela.<new_line>"Wow, Kawan. Wow, kau menyelamatkanku! Kukira hidupku sudah berakhir, aku akan kehabisan peluru dan tamatlah aku."]],
				['15'] = [[Seorang pria yang bahagia memanjat keluar dari jendela.<new_line>"Wow, Kawan. Wow, kau menyelamatkanku! Kukira hidupku sudah berakhir, aku akan kehabisan peluru dan tamatlah aku."]],
				['14'] = [[Itu benar-benar membuat para bandit terkejut, tetapi salah satu dari mereka tersadar dengan cepat:<new_line>"Hei, kamu di sana! Tenanglah! Apa dia temanmu yang terkurung di reruntuhan itu?<new_line>Jangan melakukan hal bodoh—beri kami sedikit uang dan kalian berdua bisa pergi dari sini dalam keadaan utuh."]],
				['13'] = [[Beberapa pria telah mengambil posisi di sisi lain rumah. Senjata mereka dilatih di bangunan itu. Tampaknya pengintai kita sedang dikepung di dalam.]],
				['2'] = [[Di kamp, ​​ada seorang pria duduk di kursi lipat di depan kuda-kuda. Dia terkejut saat melihatku, tetapi kemudian tersenyum bersalah dan merentangkan tangannya.]],
				['1'] = [[Seorang pria keluar dari kamp untuk menemuiku:<new_line>"Apa kamu salah satu dari kami? Jangan berisik, atau semua orang akan berlari ke sini. Katakan pada markas aku akan menetap di sini selama satu atau dua hari lagi—ternyata ini pekerjaan yang sulit."]],
				['25'] = [[Untung aku datang tepat waktu untuk membantunya, karena dia bisa saja kehilangan banyak darah. Namun sekarang hidupnya sudah tidak dalam bahaya.]],
				['23'] = [[Aku memberi pria itu obat radiasi dan kondisinya akan segera membaik.]],
				['19'] = [[Seorang pria merangkak dengan gembira menuju kebebasan, mendorong karung di depannya.<new_line>"Aku mencoba meledakkan brankas baja di sana," kata pengintai itu sambil mengangkat bahu. "Begitulah caraku memberi penghalang. Terima kasih!"]],
				['22'] = [[Mereka terlambat mengirimku. Pengintai itu sudah mati. Dilihat dari luka di tubuhnya, dia diserang oleh semacam pemangsa besar.]],
				['17'] = [[Sebuah kamp telah didirikan di reruntuhan bangunan satu lantai itu. Tidak ada seorang pun yang terlihat, tetapi pintu masuk ke rumah terhalang oleh tumpukan batu bata dan puing-puing.]],
				['18'] = [[Di sisi lain rumah ini aku menemukan jendela berjeruji. Begitu aku mendekatinya, terdengar suara teriakan dari dalam:<new_line>"Hei, hei! Aku di sini, terkurung! Bantu aku keluar dan orang-orang dari permukiman akan membayarmu."]],
				['21'] = [[Di dekat api unggun di kamp, ​​seorang pria mengaduk air dalam panci. Dia tersenyum padaku:<new_line>"Aaah, apakah orang di rumah mengkhawatirkanku? Tak ada yang perlu dikhawatirkan. Beri tahu mereka aku akan segera kembali. Ini ekspedisi yang sukses dan masih ada yang harus kuurus."]],
				['9'] = [[Pengintai itu turun dan mengulurkan tangannya yang kuat:<new_line>"Terima kasih! Aku benar-benar mengira akan jatuh lalu mati. Mereka menangkapku tanpa senjata. Yang bisa kupikirkan hanyalah memanjat lebih tinggi."]],
				['8'] = [[Ada suara-suara aneh yang datang dari sekitar perkemahan. Mengikuti suara ini, aku melihat bahwa para pemangsa sudah mengepung tiang berkarat, dan pengintai yang malang itu duduk di atasnya.]],
				['7'] = [[Menjauh beberapa meter, aku mendengar tangisan lemah. Seorang pria berkamuflase sedang merangkak menuju kamp. Saat dia melihatku, dia berkata dengan serak:<new_line>"Aku mendengar suara di kamp... Apakah kamu menyentuh perangkat keras itu? Ini berdengung sangat kencang! Aku bodoh dan meninggalkan meteran di ranselku, lalu pergi begitu saja ke kamp. Aku sudah terpapar dan sulit bernapas."]],
				['20'] = [[Ada seorang pria duduk di dekat api unggun. Kondisinya tak terlihat terlalu baik: ada rasa sedih di hatinya dan celananya basah berlumur darah.]],
				['10'] = [[Sebuah kamp didirikan di samping kendaraan. Ada seorang pria di sana, membongkar atau memuat segala macam barang.<new_line>"Apa, orang di rumah sudah merindukanku? Aku membawa setumpuk barang jarahan dan aku lupa memeriksa bensin—bahan bakarku habis di tengah jalan."]],
				['11'] = [[Ada peti dan kotak tersebar di seluruh kamp. Pengintai itu berusaha mengamankan semuanya ke sepeda motor tua.<new_line>"Lihat, tangkapan yang luar biasa, tapi aku tidak bisa membawa semuanya. Apa, orang di rumah sudah merindukanku? Baiklah, aku sedang dalam perjalanan.<new_line>Namun, peti besar itu tidak akan muat. Bantu aku dan ambil ini, ya?"]],
				['3'] = [[Saat mendekati kamp, aku mendengar dengkuran berderak. Di sebelah bara api unggun, aku menemukan seorang pria yang sedang tidur, dengan hangat memeluk sebuah botol kosong.<new_line>Tidak sulit untuk mengetahui penyebab penundaan itu. Mungkin aku harus menambah sedikit kayu ke api unggun? Lagi pula, malam ini cukup dingin.]],
				['4'] = [[Sekarang pengintai heroik kita tidak akan mati kedinginan.]],
				['5'] = [[Kamp itu kosong, tetapi tidak ada tanda-tanda perlawanan atau mundur dengan tergesa-gesa. Ada benda logam bengkok tergeletak di samping api unggun.]],
				['6'] = [[Item itu rusak, tampaknya karena ledakan. Tunggu, itu bagian dari misil militer! Meteranku jadi rusak—benda sialan ini radioaktif!]],
				['12'] = [[Sebuah kamp telah didirikan di reruntuhan bangunan satu lantai itu. Tidak ada seorang pun yang terlihat, tetapi pintu masuk ke rumah terhalang oleh tumpukan batu bata dan puing-puing. Tembakan dan sumpah serapah bisa terdengar dari sisi lain.]]
			},
			['choice'] = {
				['16'] = [[Ambil petinya]],
				['15'] = [[Bawa jenazah itu kembali ke permukiman]],
				['14'] = [[Berikan pertolongan pertama]],
				['13'] = [[Bersihkan puing-puing di pintu masuk]],
				['12'] = [[Gergaji jeruji jendela]],
				['9'] = [[Berbagi bensin]],
				['8'] = [[Bertempur!]],
				['7'] = [[Cari bantuan]],
				['6'] = [[Berikan obat radiasi]],
				['5'] = [[Menjauh dari proyektil]],
				['4'] = [[Cari pengintai itu di sekitar]],
				['3'] = [[Periksa barang itu]],
				['2'] = [[Tambahkan kayu ke api unggun]],
				['1'] = [[Kembali ke permukiman dengan berita]],
				['11'] = [[Cari para bandit dan lawan mereka]],
				['10'] = [[Periksa rumah itu]]
			}
		},
		['ambush_quest'] = {
			['episode'] = {
				['16'] = [[Kita berhasil menangkal mereka, tetapi beberapa orang kita terluka parah.]],
				['15'] = [[Benar. Baik, ambil kargonya.'<new_line>Pria itu menyerahkan kargo dan kelompoknya semua pergi.]],
				['14'] = [[Pria itu menyeringai ketika beberapa temannya keluar dari kendaraan, semuanya bersenjata.<new_line>'Kami tidak mundur semudah itu.']],
				['13'] = [[“Apakah itu ancaman?”<new_line>Beberapa temannya keluar dari mobil.]],
				['2'] = [[Aku tiba di lokasi dan menemukan mayat.]],
				['1'] = [[Ketika sampai di tujuan, aku melihat sekelompok orang di dekat pohon yang tumbang.<new_line>'Apakah kau di sini untuk membantu? Jangan hanya berdiri di sana. Mulai bekerja!']],
				['19'] = [[Yah, bagus untukmu! Sekarang, pergilah. Aku tidak ingin melihatmu di sekitar sini lagi.']],
				['17'] = [[Berkat kotak pertolongan pertamaku, kita bisa membawa para pejuang yang terluka kembali ke markas.]],
				['18'] = [[Kau cukup berani, ya?'<new_line>Para bandit lainnya keluar dari mobil. Ada lebih dari yang diperkirakan.<new_line>]],
				['9'] = [[Dua mobil berhenti di dekat pohon tumbang. Beberapa orang keluar untuk membersihkan jalan. Ketika mereka melihat penyergapan, mereka mengeluarkan senjata mereka.<new_line>'Apa yang kau inginkan?']],
				['8'] = [[Aku melihat tempat yang sempurna untuk lubang serigala di dekat pohon tumbang.]],
				['22'] = [[Aku makin dekat dengan tempat yang ditandai oleh bartender di peta.]],
				['20'] = [[Saat aku memerikan mayat, yang lain menurunkan kargo dari mobil.]],
				['11'] = [[Pria itu terus mengarahkan senjatanya ke arahku.<new_line>'Kami hanya pedagang. Kami tidak ingin ada masalah.']],
				['12'] = [[Turunkan senjatamu atau seseorang akan terluka.']],
				['3'] = [[Aku tidak berhasil menemukan sesuatu yang menarik.]],
				['4'] = [[Binatang buas sudah mengerumuni tubuh-tubuh tersebut, tertarik dengan baunya.]],
				['5'] = [[Aku berhasil mengusir binatang buas. Tidak ada yang tersisa untuk dilakukan di sini.]],
				['6'] = [[Aku mengikat beberapa tiang ke batang kayu.]]
			},
			['choice'] = {
				['17'] = [[Apakah barang-barang ini lebih penting daripada hidupmu?']],
				['32'] = [[Letakkan senjatamu]],
				['30'] = [[Terus bergerak]],
				['12'] = [[Kembali ke markas]],
				['7'] = [[Gantung batang kayu]],
				['6'] = [[Coba yang lain]],
				['5'] = [[Gantung batang kayu dengan tiang]],
				['4'] = [[Gali lubang serigala]],
				['3'] = [[Ikuti pertarungan]],
				['2'] = [[Tinggalkan]],
				['1'] = [[Periksa mayat]],
				['11'] = [[Tidak ada gunanya mati karena barang-barang ini']],
				['10'] = [[Beri aku kargo itu dan tidak ada yang akan terluka']]
			}
		},
		['embacity_biolab_5'] = {
			['episode'] = {
				['2'] = [["Ya... Seperti yang kukira. Kalian semua bahkan belum mencoba memahami realitas barunya. Kalian tidak bisa mendengar suaranya. Bahkan Kurganov juga tidak, dan dia bersedia melakukan banyak hal."]],
				['1'] = [["Wah, wah, wah, siapa ini? Subjek penelitian lainnya telah tiba, luar biasa. Bergabunglah dengan rekan-rekanku, aku yakin kau sama bodohnya dan tidak bisa melihat kenyataan yang ada di depan mata."]],
				['4'] = [[Aku berjalan mengelilingi ruangan dan menemukan pintu yang tidak mencolok dengan kunci kode. Kurasa untuk itulah kode itu.]],
				['3'] = [["Pasukan? Tidak, omong kosong militer ini akan tetap menjadi masa lalu. Sepertinya kau tidak bisa memahami masa depan. Tidak apa-apa, bersabarlah untuk sekarang ini, dan aku akan memperbaikimu. Cobalah untuk tidak melawan, jika kau bisa."]]
			},
			['choice'] = {
				['2'] = [["Apa kau juga sedang membangun pasukan?"]],
				['1'] = [["Apa yang kau lakukan?!"]]
			}
		},
		['science_polygon'] = {
			['episode'] = {
				['2'] = [["Aku punya ide. Kami punya beberapa tong bahan bakar yang disimpan di sini. Jika kau menggelindingkannya ke lapangan pengujian dan menuangkan satu karung substrat miselium di sekelilingnya, biomutan pasti akan terpancing.<new_line>Mereka akan mengerumuninya seperti ngengat melihat api. Saat itulah kau bisa memanggang mereka.]],
				['1'] = [[Seorang pria keluar dari kabin lapis baja itu untuk menemuiku.<new_line>"Halo, kamerad! Aku senang kau meluangkan waktu untuk kami. Biomutan memang menjengkelkan. Tapi kau bisa mengatasi mereka dengan mudah, bukan?"]],
				['4'] = [["Kau sangat membantu kami, kamerad! Ini hadiah untukmu."]],
				['3'] = [[Aku menggelindingkan tong-tong itu dan menuangkan zat kehijauan yang berbau jamur di antaranya.<new_line><new_line>Hanya perlu waktu lima menit hingga kawanan biomutan datang dan mulai memakani biomassa itu.]]
			},
			['choice'] = {
				['1'] = [[Gelindingkan tong]]
			}
		},
		['scientist_rescue_quest'] = {
			['episode'] = {
				['9'] = [[Seseorang dulunya memastikan perangkat itu terpasang dengan sangat kencang. Pengikatnya juga sedikit berkarat. Ini pasti akan menyulitkan.]],
				['8'] = [["Intinya, aku sudah siap. Aku mengambil semua instrumen, tapi masalah dengan satu instrumen di atap. Aku mencoba membukanya tapi malah memperburuk keadaan.<new_line><new_line>Jika ada waktu, bisakah kau mencoba melepasnya? Namun, hal terpenting adalah membawa pulang peralatan lainnya, jadi aku sepenuhnya mengandalkan pengalamanmu.]],
				['7'] = [[Ilmuwan itu menunggu dengan sabar dengan semua barang yang sudah dimasukkan dalam ranselnya kecuali kotak pertolongan pertama.]],
				['6'] = [["Maaf, tapi obat perang adalah sesuatu yang... kuhindari. Aku punya instrumen yang disterilkan di sini, semuanya sudah siap. Aku selalu bisa menunggu sampai kita tiba di pusat medis. Ini hanya luka kecil, sungguh."]],
				['5'] = [["Aku siap, tentu saja. Namun, ada satu hal. Beberapa bandit datang ke sini. Aku sudah mengusir mereka, tapi terus terang aku luka-luka sedikit. Sebenarnya ini bukan masalah besar."]],
				['4'] = [["Tidak begitu banyak, kurasa. Biar kutunjukkan padamu lokasi kamp mereka di peta, dan terserah padamu jika kamu akan pergi ke sana atau langsung pulang."]],
				['3'] = [["Dengar, jadi begini. Satu geng sudah berhasil mengambil perangkat dariku. Kita bisa langsung pulang, tapi kamu akan sangat membantu permukiman ini jika bisa mendapatkan peralatannya kembali juga."]],
				['2'] = [["Benar, aku akan memasukkan sampel tanah ke dalam ranselku dan aku akan siap membantumu. Apa tidak apa-apa jika pistolku terpasang di samping?"]],
				['1'] = [[Aku mengetuk pintu pos jaga. Ilmuwan itu membuka bautnya dan langsung mengarahkan senapan berburu ke arahku.<new_line><new_line>Aku harus meyakinkannya bahwa aku datang dari permukiman.]]
			},
			['choice'] = {
				['6'] = [[Lepaskan dengan alat]],
				['5'] = [[Pergi ke permukiman]],
				['4'] = [["Ayo kita perbaiki di jalan."]],
				['3'] = [["Apa ada banyak bandit di sini?"]],
				['2'] = [["Semua sudah siap, ayo pergi."]],
				['1'] = [["Siap untuk kembali?"]]
			}
		},
		['embacity_biolab_3'] = {
			['episode'] = {
				['16'] = [["Menilai dari uraianmu, ini adalah ekspedisi Progres pertama yang hilang. Mereka diselamatkan, Isaev tidak akan melupakan itu... Sudah terlambat bagiku. Kau tahu, senang kita bisa bertemu."]],
				['15'] = [["Apa kau menyadari kekuatan apa yang kumiliki di tanganku sekarang? Aku akan membangun kembali negeri kita yang hancur dari abu!<new_line>Dan kemudian aku akan menyebarkan wabah baru ke seluruh dunia. Tapi kau tidak akan ada di sini untuk menyaksikannya. Melanggar perintah hukumannya adalah kematian."]],
				['14'] = [["Sampai di sini saja, petarung. Sepertinya misiku berakhir disini. Apa yang terjadi setelah ledakan?"]],
				['13'] = [["Kau bilang dia merujuk pada profesor? Rupanya, mereka bertemu setelah terjadinya bencana. Aku penasaran apa peran Gerin dalam semua ini..."]],
				['2'] = [["Oh, ini dia sang kepala tukang ledeng Progres! Saat kau memompa semua biomassa keluar dari tangki, di bawah lampu merah di sana, pintunya terbuka, lihat?<new_line>Ada tangga di belakangnya. Aku tidak tahu bagaimana itu terhubung. Mungkin juga ada penjahat yang duduk di balik pintu itu, dan kau meninggalkannya tanpa manisannya."]],
				['1'] = [[Dengan hati-hati aku mengintip ke dalam lab. Hal pertama yang menarik perhatianku adalah lampu berkedip merah di atas pintu di dinding ruangan.<new_line>Pintu ini ternyata pintu yang dikendalikan secara otomatis yang berukuran sangat besar. Yang sekarang sedikit terbuka.]],
				['17'] = [["Kau tidak tahu berapa banyak kami semua berutang padamu. Selesaikan pekerjaan ini. Senang berjalan di jalan ini bersamamu, petarung."]],
				['9'] = [["Aaah!"<new_line><new_line>(terdengar gemuruh, dan ledakan melemparkanku ke dinding. Kepalaku terbentur, menyakitkan sekali, pandanganku menjadi gelap)]],
				['8'] = [["Ada kompartemen berisikan kotak hitam di belakangnya. Masukkan kode yang diberikan para ilmuwan kepada kita, ambil kotaknya — dan mari kita pergi sebelum sesuatu terjadi.<new_line>Aku akan berjaga-jaga di sini dan menangani sistem elektronik lokal. Mungkin aku bisa mencari tahu apa yang terjadi di sini."]],
				['7'] = [["Dan selanjutnya aula ini. Wow, lihat akar kuat yang tumbuh di sana! Aku yakin itu setebal kepala teman kita Isaev.<new_line>Sial, ini kosong juga... ini sudah tidak lucu lagi. Aku sama sekali tidak menyukainya, kita harus bertindak cepat. Di sebelah sana — kau lihat pintu yang ada kunci kodenya?"]],
				['10'] = [["Siapa yang menyusup ke fasilitas militer? Petarung baru untuk pasukanku? Bersiaplah!"]],
				['11'] = [["Sadar dari apa? Tinggal sedikit lagi, dan kita akan memperbaiki semuanya di sini. Dan kita akan mulai denganmu. Aku mempelajari metode yang luar biasa dari salah satu rekan ilmuwanku..."]],
				['3'] = [[Tangga di belakang pintu membawaku ke ruang bawah tanah yang luas. Di atasnya, sisa-sisa biomassa hijau mengalir dari pipa ke parit logam, dan akar yang ruwet membentang dari lantai ke langit-langit, menuju ke permukaan kota.]],
				['4'] = [[Di tengah aula, dikelilingi oleh mutan humanoid, sesosok mengerikan membungkuk di atas meja lab.<new_line>Dari kegelapan tangga, aku menatap jasad-jasad yang telah dimutilasi miselium, dan aku bisa mendengar dengusan hewani mereka. Tidak ada yang pantas mendapatkan nasib seperti itu.]],
				['5'] = [["Ayo pergi."]],
				['6'] = [["Mereka telah membangun rumah besar di bawah tanah! Kau sudah melihat pipa di langit-langit? Begitulah cara biomassa turun ke sini dari atas. Tapi kenapa tidak ada orang di sini...<new_line>Kalau bukan Penghancur Tulang yang memakan biomassa itu, lalu siapa yang memakannya? Dan apa pintu itu terbuka dengan sendirinya?"]],
				['12'] = [["Aku tidak akan mendiskusikan urusanku dengan Prajurit biasa! Kau akan mengetahuinya saat kau bergabung dengan pasukanku.<new_line>Temanmu yang penasaran telah bertemu dengan bahan peledakku dan sekarang dia sudah tidak ada gunanya. Tapi aku akan menjagamu. Ke ruang operasi."]]
			},
			['choice'] = {
				['9'] = [[Ambil kotak hitam dan pergi]],
				['8'] = [["Kita harus bergegas mencari pertolongan untukmu dan orang-orang itu."]],
				['7'] = [[Sebutkan nama Kurganov]],
				['6'] = [["Kau sudah gila, dan aku akan menghentikanmu!"]],
				['5'] = [["Dari Gerin? Apa yang kau lakukan pada profesor?"]],
				['4'] = [["Jenderal Kurganov? Apa yang kau lakukan, sadarlah!"]],
				['3'] = [["Aku melihatnya."]],
				['2'] = [["Ya, ini aneh. Selanjutnya ke mana?"]],
				['1'] = [["Nah, apa sebaiknya kita turun?"]],
				['10'] = [[Ambil kotak hitam dan cari pertolongan]]
			}
		},
		['provision_bar'] = {
			['episode'] = {
				['1'] = [["Bagus, kami berharap banyak pada operasi ini. Kontribusimu akan membantu kami mendapatkan informasi penting tentang proyek EMBA. Terima kasih."]]
			}
		},
		['scientist_rescue_bandit'] = {
			['episode'] = {
				['2'] = [[Ilmuwan itu bergegas ke tengah kamp dengan teriakan bahagia dan kembali dengan sebuah kotak logam kecil. Dia mengucapkan terima kasih sebesar-besarnya dan menyembunyikan kotak itu di ranselnya.]],
				['1'] = [[Kami mendekati kamp yang dengan perlengkapan yang kurang dan salah satu bandit keluar untuk menemui kami:<new_line>"Kau datang untuk perangkat kerasnya? Dengar, kami tidak hidup dalam kemewahan di sini, jadi kami tak bisa mengembalikannya begitu saja. Sisihkan sedikit uang untuk roti, dan kau bisa pergi dengan tenang."]],
				['4'] = [[Ilmuwan itu sudah mati. Menjaganya agar tetap aman adalah misi utamaku, dan aku gagal.]],
				['3'] = [[Bandit itu membawakan kami sebuah kotak logam kecil. Ilmuwan itu segera menyembunyikannya di ranselnya dan berterima kasih padaku.]]
			}
		},
		['halloween_def_hard'] = {
			['episode'] = {
				['4'] = [["Aku sudah berjuang berhari-hari tanpa istirahat, dan sekarang aku harus membuat rencana?! Astaga, kau tak berguna. Baiklah, sepertinya monster-monster itu mendekat lagi. Bagaimana kalau aku menaruh mantra padamu, dan kau bisa berusaha agar bisa lebih berguna. Katakan mantra apa yang kamu mau."]],
				['9'] = [["Kau memang sudah mencoba membantuku, tapi tidak banyak gunanya. Mungkin kamu bisa membuat rencana yang sedikit lebih cerdas sekarang? Pilih mantra apa yang kamu inginkan kali ini."]],
				['5'] = [[Si penyihir dengan malas melambaikan tongkatnya.<new_line>Abrakarabrabum. Selesai. Kuharap aku tidak membuang-buang semua kekuatan ini untukmu."<new_line><new_line>Lolongan binatang buas terdengar sangat dekat sekarang.]],
				['7'] = [[Si penyihir dengan malas melambaikan tongkatnya.<new_line>Abrakarabrabum. Selesai. Kuharap aku tidak membuang-buang semua kekuatan ini untukmu."<new_line><new_line>Lolongan binatang buas terdengar sangat dekat sekarang.]],
				['13'] = [["Abrakadabra... Cukup dekat. Itu sudah cukup. Binatang-binatang itu mendekat. Bersiaplah."]],
				['10'] = [[Penyihir itu mendorongku dengan tongkatnya dan berkata:<new_line>"Abrakadabra. Selesai."<new_line><new_line>Gerombolan binatang buas mendekat lagi.]],
				['11'] = [["Kamu hanya berguna 5%. Kenapa aku harus menyia-nyiakan sihir padamu? Bisakah kau berusaha sedikit lebih keras? Pikirkan baik-baik tentang mantra apa yang kamu ingin aku berikan padamu agar kamu benar-benar bisa berguna."]],
				['3'] = [["Cepatlah. Jangan hanya berdiri di sana."]],
				['2'] = [["Jangan hanya berdiri di sana! Apa aku harus mengalahkan gerombolan monster dan mempertahankan desa ini sendirian? Aku juga perlu bantuan, tahu."]],
				['1'] = [["Apa kau di sini untuk membantu atau hanya untuk menonton?"]],
				['14'] = [[Penyihir itu menguap dengan keras.<new_line>"Fiuh. Kerjaku sudah baik. Orang lain harus melihat ini... Sayangnya, Kodeks Penyihir menyatakan bahwa setiap orang yang diduga membantuku harus diberi catatan. Dengan membacanya, mereka bisa memanggilku pada saat mereka membutuhkan, bla bla bla...<new_line>Tapi ingat, jika itu bukan saat yang dibutuhkan, kau akan menyesalinya. Akhir cerita."]],
				['12'] = [["Abrakadabra... Cukup dekat. Itu sudah cukup. Binatang-binatang itu mendekat. Bersiaplah."]]
