		['emba_time_easy'] = {
			['episode'] = {
				['14'] = [[Aku mendengar suara langkah di belakangku. Ternyata salah seorang ilmuwan itu. Dia berbisik,<new_line>"Kau sudah catat semuanya? Coba lihat. Bagus, ya... Luar biasa... Ya, ini observasi yang sangat bagus. Kau melakukan tugasmu dengan sangat baik, terima kasih."]],
				['9'] = [[Ketika aku mengamati, salah satu biomutan itu tiba-tiba tersungkur ke tanah selagi bergerak dan sepertinya mati. Tidak lama kemudian, tubuhnya dililit oleh akar hitam dan diseret ke tengah-tengah bola tumbuhan itu.]],
				['8'] = [[Hal ini mungkin normal bagi biomutan, tapi mereka berperilaku aneh beberapa kali, terdiam di tempatnya dan berdiri seperti itu selama sekitar sepuluh menit.]],
				['7'] = [[Makhluk-makhluk itu mulai saling menyerang satu sama lain beberapa kali. Mereka menyeret sesamanya yang sudah dicabik-cabik ke suatu tempat, jauh di dalam bola hijau itu.<new_line>Aku tak pernah melihat perilaku seperti ini sebelumnya.]],
				['6'] = [[Beberapa makhluk ini tidak bergerak seperti yang lainnya. Gerakan mereka lebih kasar dan menyentak. Mungkin ini efek dari bahan kimia yang disemprotkan itu.]],
				['5'] = [[Sejauh ini tidak ada hal khusus yang terjadi pada perilaku biomutan. Namun, ketika ada sesosok hewan, mungkin seekor serigala, muncul di cakrawala, beberapa makhluk ini berlari mengejarnya.]],
				['4'] = [[Satu hal ganjil yang aku perhatikan adalah ketika salah satu biomutan itu mulai bergerak dengan lebih lemah dan lambat, dan akhirnya jatuh ke atas akar hitam, lalu berhenti bergerak.<new_line>Dengan cepat, akar-akar itu melilitnya dan menarik jauh ke dalam bola hijau itu.]],
				['3'] = [[Biomutan berkeliaran, memakani jamur yang tumbuh di jalinan akar itu.]],
				['2'] = [["Kami melakukan eksperimen tentang perilaku biomutan dalam pengaruh senyawa kimia tertentu.<new_line>Tuliskan apa yang terjadi dalam struktur protein biologis ini dan kami akan kembali dalam beberapa jam. Pastikan kau tidak menyerang mereka, atau eksperimen ini akan terganggu."]],
				['1'] = [["Salam, kamerad! Kau lihat bola jalinan tumbuhan itu? Di dalamnya ada biomutan. Kami diam-diam menyemprotkan cairan khusus ke sana."]],
				['11'] = [[Biomutan itu tak pernah mengetahui keberadaanku, dan tak ada yang menyerangku.<new_line>Namun, salah satu biomutan itu menatap ke arahku selama beberapa menit! Aku tidak tahu apakah itu kebetulan saja atau ia benar-benar melihatku, yang pasti cukup mengerikan.]],
				['10'] = [[Aku melihat sesuatu yang ganjil: salah satu biomutan itu, yang sebelumnya adalah seorang manusia, entah melolong atau meneriakkan sesuatu.<new_line>Lalu, makhluk-makhluk yang lain mematuhinya! Mereka mengikutinya ke suatu tempat, mirip sebuah perintah.]]
			},
			['choice'] = {
				['2'] = [[Mulai observasi]],
				['1'] = [["Untuk apa?"]],
				['4'] = [[Catat observasi]],
				['3'] = [[Lanjutkan observasi]]
			}
		},
		['emba_search_medium'] = {
			['episode'] = {
				['6'] = [["Horee, kita berhasil! Terima kasih, aku merasa lega sekarang. Baiklah, aku harus cepat-cepat melakukan risetku sebelum terjadi masalah lain. Jaga dirimu baik-baik!"]],
				['5'] = [[Ternyata tidak ada siapa-siapa di dalam gua. Situasinya menguntungkan, aku harus mengambil tas itu dan pergi dari sini.]],
				['4'] = [[Ada banyak jejak di dekat mulut gua itu, tapi aku tak melihat biomutan satu pun. Aku harus bergegas dan mengambil tas itu.]],
				['3'] = [["Aku sudah mendapatkan beberapa tas miselium dari atasanku untukmu. Tapi perhatianku teralihkan oleh eksperimen lain saat tas-tas itu dicuri oleh biomutan. Aku akan dapat masalah jika tidak menyelesaikan situasi ini sekarang. Nah, kau mau pergi ke gua itu?"]],
				['2'] = [["Bagus. Ikutlah denganku, ada di dekat sini.<new_line>Nah, aku akan menempatkan makanan di sini untuk memancing mereka menjauh dari sarang, sementara itu kau masuk ke dalam gua, tas berisi miselium itu pasti ada di sana."]],
				['1'] = [["Hai! Ada kejadian yang tidak menyenangkan, apakah kau bisa membantu?<new_line>Tas berisi miselium yang dicuri oleh biomutan itu harus kembali. Aku menemukan sarang tempat mereka membawa sampel eksperimen itu. Tapi, ada banyak mutan yang berkumpul di sana.<new_line>Aku pikir kita bisa memancing mereka agar menjauh dari sarangnya. Kau perlu umpan segar... kau punya?"]],
				['8'] = [[Masalah itu teratasi, sekarang tak ada lagi yang akan menggangguku saat mengambil miselium itu.]],
				['7'] = [[Beberapa biomutan masih ada di dalam gua! Aku bisa menyerang mereka dan berusaha mengulangi siasat menggunakan makanan untuk mengalihkan perhatian mereka, lalu merebut tas itu dan pergi dari sini.]]
			},
			['choice'] = {
				['6'] = [[Alihkan perhatian dengan ikan dan ambil miselium]],
				['5'] = [[Alihkan perhatian dengan daging dan ambil miselium]],
				['4'] = [[Ambil tas dan pergi]],
				['3'] = [[Masuki gua]],
				['2'] = [["Bagaimana ini bisa terjadi?"]],
				['1'] = [[Pergi ke gua]]
			}
