		['embacity_house5'] = {
			['episode'] = {
				['2'] = [[Jamur telah menyebar ke seluruh bangunan, di beberapa tempat bahkan sudah melahap pakaian dan buku yang berserakan. Ada beberapa makhluk yang memakan zat berjamur, tetapi berbahaya untuk mendekati mereka tanpa mengenakan perlindungan khusus.]],
				['1'] = [[Menilai dari suara yang berasal dari reruntuhan rumah, tempat ini dipenuhi dengan makhluk. Aku harus mengenakan pakaian pelindung jika aku akan melawan mereka.]],
				['4'] = [[Dari reruntuhan rumah yang hancur dipenuhi jamur, tangisan mirip manusia bisa terdengar. Apakah penghuni lamanya telah bermutasi atau penghuni yang baru telah tiba, aku memerlukan pakaian pelindung untuk menghadapi mereka.]],
				['3'] = [[Akar yang tebal mencuat keluar jendela, dan biomutan telah mapan di atasnya, menggerogoti jamur yang tumbuh. Satu akar telah tumbuh menembus lemari pakaian seseorang, yang sekarang tergeletak terbalik di balkon. Aku tidak boleh lupa untuk memakai perlindungan khusus.]]
			}
		},
		['embacity_gastronome2'] = {
			['episode'] = {
				['2'] = [[Tanaman hijau belum sepenuhnya mengambil alih gedung, jadi tidak banyak yang bisa dimakan oleh sekawanan besar makhluk. Aku harus memeriksa apakah pertahananku baik-baik saja sebelum masuk.]],
				['1'] = [[Jamur menutupi bangunan itu, dengan bunga merah tumbuh di sana-sini. Nampaknya itu telah menarik perhatian sekelompok biomutan. Aku tidak boleh lupa perlindungan khususku.]],
				['3'] = [[Mayat yang tertutup akar nampak bersandar di pintu bangunan itu. Sekelompok biomutan tampaknya menunggu hingga jamur itu cukup banyak menimbulkan korosi. Aku tidak bisa bertarung tanpa menggunakan perlindungan khusus.]]
			}
		},
		['halloween_fight_medium'] = {
			['episode'] = {
				['2'] = [[Aku menarik perhatian gerombolan itu dengan teriakan keras, kemudian mulai berlari. Rencananya berhasil: hanya sebagian dari binatang buas itu yang berhasil mengejarku. Aku harus mengalahkan mereka secepat mungkin, sebelum yang lain menyusul.]],
				['1'] = [[Ada terlalu banyak binatang buas yang berkumpul di sini. Aku tidak bisa melawan seluruh gerombolan sekaligus. Aku harus membuat mereka berpencar dan mengalahkan mereka dalam kelompok-kelompok yang lebih kecil. Pecahkan dan taklukkan, kalau kata orang.]],
				['4'] = [["Oh, kau lagi, Nak? Kau tidak bisa mengerjakan apa pun sendiri? Saat aku seusiamu, aku sudah melakukan semuanya sendiri. Baiklah. Tunjukkan siapa yang sudah jahat padamu."]],
				['3'] = [[Aku mengalahkan binatang buas tercepat dari kelompok itu, tapi yang lain masih banyak. Lolongan baru sudah terdengar. Binatang buas lainnya mendekat.]]
