				['1'] = [[Berangkat ke istana es]]
			}
		},
		['new_year_boss_6'] = {
			['episode'] = {
				['14'] = [["Kita akan mengobrol nanti, kawan! Setelah pelepasan magis besar itu, seluruh tempat ini mulai runtuh! Ayo bawa Krampus dan keluar dari sini!"]],
				['13'] = [["Huh? Apa yang terjadi?"]],
				['12'] = [["Kekuatan magis kami sudah kembali, dan aku harus memastikan kau tidak mengacaukan segalanya! Um, maksudku... Terima kasih telah memulihkan kekuatan magis kami atau apa pun itu."]],
				['9'] = [["Aku menghancurkan hampir semua Natal dengan tanganku sendiri. Otakku diracuni, dan semuanya sudah terlambat sekarang... tapi aku tak akan membiarkan harapan mati!"<new_line><new_line>Krampus Claus melambaikan tongkatnya dan menyerang bagian yang ditarik Frosty dari senjata yang hancur.]],
				['8'] = [["Hmm, memang kesan pertama tak pernah meleset. Kau MEMANG bodoh. Krampus, berhenti mengutak-atik baterai ajaib dan urus si dungu ini!"]],
				['7'] = [["Tak ada gunanya kita bertarung! Kita bekerja sama saja.<new_line>Aku akan mendengarkan saranmu tentang bagaimana bertindak lebih manusiawi. Aku sendiri punya beberapa ide! Jadi, apa kau setuju?!"]],
				['6'] = [["Ya! Itu perbuatan si idiot tua ini. Saat itu, dia masih disebut 'Sinterklas' dan ingin menyia-nyiakan kekuatan ornamen ini untuk segala macam hal yang tak masuk akal: perlindungan radiasi, sumber energi alternatif. Pfft.<new_line>Tapi dia cukup beruntung bertemu dengan seorang jenius seperti aku dan menunjukkan jalan ke arah yang benar: menuju kekuasaan."]],
				['5'] = [["Dengar! Yang aku butuhkan hanyalah ornamen dari pohonmu itu. Itu adalah bagian utama senjata super. Menurutmu, mengapa tak ada hiasan di pohon itu ketika kau pertama kali menemukannya?"]],
				['4'] = [[Krampus Claus bergidik, dan Frosty tertawa menyebalkan.<new_line>"Ha, benar dugaanku: kau tidak bodoh. Karung sampah ini tidak dapat menanganimu bahkan saat dia punya kekuatan penuh.<new_line>Bayangkan seberapa kuat kita jika kau dan aku menggabungkan kekuatan!"]],
				['3'] = [["Hei! Bagaimana kau bisa melewati... Sepertinya kau tidak sebodoh yang aku kira. Aku punya tawaran untukmu."]],
				['2'] = [[Di ruang bawah tanah, potongan-potongan ornamen dan segala macam baut dan perkakas menumpuk.<new_line><new_line>Dari sisi lain tumpukan, aku mendengar suara Frosty.<new_line>"Tidak, aku yang bertanggung jawab sekarang! Karena kau, kita mulai dari awal lagi di ruang bawah tanah yang pengap ini!"]],
				['1'] = [["Oke, apa kau sudah siap untuk berteleportasi, atau apa aku harus menunggumu dalam cuaca dingin ini?"]],
				['11'] = [["Eee! Aagaaah!"]],
				['10'] = [["Beraninya kau! Dasar orang tua tak berguna! Kekuatan magis Santa adalah milikku! Aku akan menguasai musim dingin nuklir, dan kau akan mati! Kalian semua akan mati!"]]
			},
			['choice'] = {
				['9'] = [["Kau datang!"]],
				['8'] = [["Ya!"]],
				['7'] = [["Krampus, dahulu kau adalah Sinterklas—ayolah!"]],
				['6'] = [["Dasar pembohong, aku akan membunuhmu!"]],
				['5'] = [["Kau memang monster!"]],
				['4'] = [["Krampus Claus mengambilnya?"]],
				['3'] = [["Mustahil."]],
				['2'] = [["Tawaran sama yang kau berikan kepada Sinterklas?"]],
				['1'] = [[Keluar dari balik tumpukan]],
				['10'] = [["Kau berubah menjadi..."]]
			}
		},
		['impostor3'] = {
			['episode'] = {
				['1'] = [[Orang-orang senang melihat saya kembali dengan barang-barang mereka.]]
			},
			['choice'] = {
				['1'] = [[Memasrahkan segala hal]]
			}
