				['12'] = [["Choombas! Konsernya luar biasa! Terima kasih telah memusatkan perhatian pada psikopat cyber itu."]],
				['9'] = [["Usahamu kurang sungguh-sungguh. Jika serius, kau pasti sudah bisa merapal mantra padaku sekarang, dan bukan sebaliknya. Baiklah, aku akan menunjukkan trik baru. Jika kau berusaha sekeras usahaku, mungkin kau akan menguasainya.<new_line>Aku dapat memindahkan musuh ke mana pun aku mau untuk memudahkan kita melawan mereka. Tapi kaulah yang memilih lokasinya."]],
				['8'] = [["Jadi kau memang belajar sesuatu. Memang benar yang mereka katakan: Natal adalah hari penuh keajaiban."]],
				['7'] = [["Abrakadabra, selesai. Semoga setidaknya kau telah belajar sesuatu dan tak menyia-nyiakan sihir untuk omong kosong."<new_line><new_line>Sementara itu, para bandit telah sampai di perimeter dan memulai serangan mereka.]],
				['6'] = [["Kenapa mulutmu ternganga? Bisa kemasukan serangga nanti. Pilih mantra yang kau inginkan."]],
				['5'] = [["Hari ini kau sangat beruntung, idiot. Dan kau perlu berterima kasih kepada Cybersanta untuk itu. Apa kau tidak memperhatikan? Apa kau tak melihat musuh datang?"<new_line><new_line>Aku melihat ke kejauhan, dan musuh memang mendekat.]],
				['4'] = [[Aku melihat sekeliling, tetapi partnerku masih belum terlihat. Namun, begitu mulai berbalik ke Cybersanta, Wizard Santa muncul di belakangku.]],
				['3'] = [[Ada banyak sekali orang di konser itu—mereka berkerumun seperti ikan sarden. Entah bagaimana aku mencapai perimeter, tetapi partnerku tak terlihat.]],
				['2'] = [["Bagus! Lalu menuju ke perimeter. Partnermu sedang menunggu di sana."]],
				['1'] = [["'Hai, choomba! Aku akan menyelenggarakan konser besar-besaran, tetapi ada informan yang memberi tahu bahwa beberapa tamu tak diundang berencana untuk merusaknya.<new_line>Mereka harus dibereskan jika muncul. Apa kau mau membantu mengamankan acara itu?"]],
				['11'] = [[Seperti biasa, Wizard Santa memantraiku. Beberapa menit kemudian, musuh muncul di cakrawala, dan Wizard mulai melafalkan mantra yang rumit.<new_line>"Valar morghulis, ommm... alarmis."<new_line><new_line>Saat dia selesai, aku melihat kilatan putih, dan kami berada di lokasi lain dengan musuh kami.]],
				['10'] = [["Aku sangat ahli, aku bahkan punya sedikit sihir tersisa untuk merapalkan mantra padamu. Pilih yang kau inginkan."]]
			},
			['choice'] = {
				['12'] = [["Sama-sama."]],
				['9'] = [[Menuju ke rawa yang tak tertembus.]],
				['8'] = [[Menuju ke pos terdepan yang aman.]],
				['7'] = [[Menuju depot bahan bakar.]],
				['6'] = [["Aku sudah mencoba."]],
				['5'] = [["Aku ingin terbang seperti burung."]],
				['4'] = [["Jadi begitu."]],
				['3'] = [["Apa kau pasanganku?"]],
				['2'] = [[Pergi ke perimeter]],
				['1'] = [["Tak masalah."]],
				['11'] = [[Serang.]],
				['10'] = [[Menuju ke hutan lebat.]]
			}
		},
		['halloween_boss_2022_2_2'] = {
			['episode'] = {
				['9'] = [["Dan di sanalah aku, mengira kamu akan kebingungan! Lumayan, lumayan.... Aku sudah menyiapkan kontrak berikutnya di sini."]],
				['8'] = [["Menontonmu sangat menyenangkan, Manusia! Cobalah gunakan otakmu, dan kembali lagi nanti—Dokter sudah siap di sini untuk waktu yang lama..."]],
				['7'] = [["Benar. Mereka meracik ramuan tidur yang begitu kuat sehingga dikenal sebagai "Seteguk Kematian Hidup". Kau lebih pintar dari yang kukira... Aku tak sabar untuk membedah otakmu—itulah yang akan kita lakukan sekarang!"]],
				['6'] = [["Tidak. Kau tak tahu. Baiklah, kita coba lagi lain waktu... dan sekarang lihat apa yang terjadi saat memberiku jawaban bodoh."<new_line><new_line>Dokter Wabah melemparkan sesuatu ke dalam kuali, dan uap yang keluar dari kuali itu makin tebal. Saat uapnya hilang, Dokter ini menghilang, dan aku mendapati diriku dikelilingi oleh zombi.]],
				['10'] = [["Oh, kau lagi. Baiklah, apa kita bisa kembali ke percakapan tadi?"]],
				['4'] = [[Dokter dengan kecewa menggelengkan kepalanya saat aku hendak mengambil senjata. Dia melambaikan tangannya dan menghilang ke dalam kabut hijau yang menyelimutinya. Aku harus mencoba lagi saat dia kembali ke kuburan.]],
				['3'] = [["Nah sekarang, ada siapa di sini? Kau sungguh spesimen yang aneh.... Mari kita ngobrol sebentar sebelum aku menanganimu."]],
				['2'] = [[Dokter Wabah sudah bersiap di antara dua kuburan terbuka, dan memasukkan sesuatu ke dalam kuali yang menggelembung. Saatnya untuk mengakhiri ini.]],
				['1'] = [[Di sudut jauh kuburan, batu nisan diterangi oleh kilatan hijau—itu pasti tempat Dokter Wabah melakukan eksperimen kejinya.]],
				['5'] = [["Aku sudah menghabiskan beberapa ramuanku, jadi sekarang aku sedang meracik ramuan baru. Beri tahu aku, apa yang akan kudapatkan jika mencampur bubuk akar asphodel dengan infusi apsintus?"]]
			},
			['choice'] = {
				['6'] = [["Obat anti radiasi."]],
				['5'] = [["Ramuan tidur."]],
				['4'] = [["Racun pembusukan."]],
				['3'] = [["Serum kebenaran."]],
				['2'] = [["Salep untuk paruh yang bau."]],
				['1'] = [[Pergi ke arah kilatan]],
				['7'] = [["Lanjutkan, kalau begitu."]]
			}
		},
		['halloween_boss_2022_4'] = {
			['episode'] = {
				['2'] = [["Tidak ada apa-apa di sini. Kau juga bisa mencari di pabrik dan lumbung. Ini kacau! Ada festival yang sedang berlangsung, dan aku tak punya satu labu pun...."]],
				['1'] = [[Petani itu menemuiku di dekat lumbungnya yang kosong:<new_line>"Semuanya habis, tak ada hasil panen yang tersisa! Padahal tahun lalu aku memenangkan hadiah di pekan raya—untuk labu terbesar!"]]
			},
			['choice'] = {
				['1'] = [["Apa kau punya petunjuk?"]]
			}
		},
		['halloween_boss_2022_5'] = {
			['episode'] = {
				['2'] = [[Aku berlari ke arah orang itu, tapi terlambat—dia sudah kehabisan darah dan meninggal karena luka-lukanya.<new_line>Aku mendengar suara tawa dari belakangku.]],
				['1'] = [[Setelah aku tiba, ada gerombolan monster berkerumun mengelilingi seseorang. Aku tak punya pilihan—mereka sudah menyadari kehadiranku.]],
				['3'] = [["Kau tak bisa membaca, Manusia? Jika nama seseorang ada di halaman itu, ya sudah—tak ada harapan bagi mereka. Jangan buang waktu—Igor hanya mengambil organ dari mayat segar. Terus mencari."]]
			},
			['choice'] = {
				['2'] = [[Lanjut]],
				['1'] = [[Berbalik]]
			}
		},
		['halloween_boss_2022_5_2'] = {
			['episode'] = {
				['9'] = [[Setelah aku mengatasi si monster, aku pergi mencari pria yang kabur itu. Aku menemukannya di sekitar: rupanya, saat dia melarikan diri tanpa melihat ke belakang, dia terjatuh ke jurang. Lehernya patah.]],
				['8'] = [[Si bungkuk bergegas menuju elektroda besar, dan beberapa makhluk lain—tentunya juga eksperimen dari lab Victor Frankenstein—bergabung dengan monster itu.]],
				['7'] = [[Si bungkuk membalas dengan suara cadelnya:<new_line>"Dia sungguh spesimen yang belkualitas. Akan kutunjukkan sesuatu padamu, Kawan—lihatlah elektloda cantik di sini..."]],
				['6'] = [[Aku melihat orang bungkuk pendek dan monster humanoid besar. Mereka menoleh ke arahku, dan dengan terkejut, monster itu melepaskan pria memar yang dia pegang. Pria itu segera bangkit, dan wajah monster itu tersenyum mengancam:<new_line>"Iiiigor! Ini beda cerita. Aku ingin hati MILIKNYA!"]],
				['5'] = [[Aku hampir berada tepat di samping mereka. Terdengar suara cadel itu lagi:<new_line>"Tempat umum apa? Pelgi ke lual ke mana, tepatnya? Kau itu monstel! Dan itu hati!"]],
				['4'] = [[Suara cadel itu dijawab dengan suara menggeram yang menggelegar:<new_line>"Igor, itu hati milik orang mabuk yang aneh. Aku tak mau. Aku malu pergi ke luar, ke tempat umum dengan hati seperti itu!"]],
				['3'] = [[Setelah mendengar suara cadel, aku memutuskan untuk berjalan melewati semak-semak agar tidak membuat takut siapa pun.<new_line><new_line>"Kau sepelti tak peduli sedikit pun! Ini HATI, bodoh!"]],
				['2'] = [[Aku menemukan satu mayat di dalam. Dia terlihat masih utuh, tapi dosimeterku mulai berdengung hebat. Dia mungkin terkurung di sini dan mati kelaparan. Atau mungkin karena radiasi. Sebaiknya aku pergi jika tidak ingin bernasib sama dengannya.]],
				['1'] = [[Seseorang akan meninggal di bangunan ini. Tapi tidak ada seorang pun di sini... kecuali pintu masuk ke satu ruangan yang tertutup penghalang. Mungkin mereka ada di dalam?]],
				['10'] = [["Yah, kau tak bisa lari dari takdir. Apalagi jika itu sudah tertulis di Buku Kematian. Segera temui aku begitu kamu sudah siap, kontrak terakhir menantimu."]]
			},
			['choice'] = {
				['6'] = [["Apa?"]],
				['5'] = [[Serangan dari semak-semak]],
				['4'] = [[Bahkan lebih dekat...]],
				['3'] = [[Merangkak lebih dekat]],
				['2'] = [[Singkirkan penghalang dengan sekop]],
				['1'] = [[Singkirkan penghalang dengan tanganmu]],
				['7'] = [["Jangan bergerak!"]]
			}
		},
		['halloween_boss_2022_6_2'] = {
			['episode'] = {
				['15'] = [["Tidak, kami juga ingin memberimu sesuatu sebagai kompensasi—ini. Dan omong-omong, aku masih punya urusan yang harus diselesaikan di duniamu, jadi kau bisa mampir ke toko kami jika perlu sesuatu."]],
				['14'] = [[“Jangan takut, Temanku. Aku Pencabut Nyawa baru. Aku dikirim ke sini untuk menggantikan rekan kerjaku yang mencurigakan. Semua penipuan dengan kontrak dan subkontrak… sayang sekali…<new_line>Mewakili seluruh dunia kematian, aku minta maaf. Aku akan mengirimmu ke dunia asalmu segera."]],
				['13'] = [[Pencabut Nyawa melihatku meraih Gigawattor dan mendesis dengan marah, "Oh, dasar licik! Tak apa, kau tak bisa menutup semua portalku. Kau cuma manusia biasa, tak cukup kuat!"]],
				['12'] = [[Seolah-olah menegaskan apa yang baru saja kukatakan, petir menyambar di atas kepala dengan suara gemuruh. Petir itu juga mengenai salah satu portal! Dengan kilatan yang menyilaukan, petirnya menghilang. Boleh juga! Jadi artinya Gigawattor-ku bisa menghancurkan portal...]],
				['9'] = [["Aku sangat ragu kau bisa melakukannya—kau sudah menyeberangi Sungai Yang Terlupakan. Dan sekarang teman kita akan datang ke sini dan mencabik-cabikmu."<new_line>Dengan jentikan jari Pencabut Nyawa, portal muncul di sekitar, berkilauan dengan percikan listrik.]],
				['8'] = [["Kau bisa melupakan kontraknya—ada tujuan lain yang ada di pikiranku. Kau sudah mengembangkan kekuatan iblis yang hebat. Kebanyakan Pencabut Nyawa hanya bisa bermimpi untuk memiliki jiwa yang begitu kuat."]],
				['7'] = [["Ini, Manusia. Adalah duniaku. Selamat datang! Dan jangan khawatir tentang Keledai itu, dia berada di tempat yang seharusnya.<new_line>Jadi begini, semua binatang dan monster ini adalah ikan besar, tentu saja... Tapi aku ingin menangkap hiu sungguhan."]],
				['6'] = [[Tepi sungai itu ternyata redup dan sepi, seolah-olah ini berada di dunia lain. Sungai itu sendiri dengan cepat diselimuti oleh kabut. Yang memperparah situasi, hujan juga mulai turun. Dengan kilatan petir, badai petir pun menerpa.<new_line>Keledai itu tidak terlihat di mana pun, tapi Pencabut Nyawa ada di sana.]],
				['5'] = [[Aku melihat ada lebih banyak lagi monster yang muncul dari hutan! Aku tak akan bisa menang melawan gerombolan itu. Tak ada pilihan lagi selain menyeberang ke tepi sungai di sana.]],
				['4'] = [[Aku pergi ke sungai. Anehnya, aku tak ingat pernah melihatnya di peta mana pun.<new_line>Pencabut Nyawa berdiri di tepi sungai yang lain, melambaikan tangan ke arahku:<new_line>"Ada arungan di sini, cepatlah menyeberang, Keledai itu makin menjauh!"<new_line>Segera setelah ia mengatakan itu, aku mendengar ranting-ranting pohon berderak di belakangku.]],
				['3'] = [[Begitu merepotkan, semua karena Keledai sial itu...<new_line>Tapi aku bisa mendengar teriakannya dari dekat sini, dan aku tak akan membiarkannya lolos kali ini!]],
				['2'] = [[Hutan ini makin gelap setiap aku melangkah, dan mata tengkorak itu bersinar merah. Jalannya berbelok tajam, dan di tikungan itu aku hampir menabrak gerombolan berbagai jenis monster.]],
				['1'] = [[Pada setengah perjalananku, aku tersadar ternyata aku sedang berada di hutan yang gelap. Ada tengkorak-tengkorak yang ditancapkan ke tiang-tiang di sepanjang jalan, dan suara-suara menyeramkan melolong dari balik pepohonan.]],
				['11'] = [["Apa-apaan... Bagaimana bisa?! Di sisi lain halaman yang sama?! Tapi aku sendiri yang menyerahkannya padamu..."]],
				['10'] = [["Kekuatanmu memang meningkat sejak aku memberimu kontrak pertama. Tapi ada satu peringatan.<new_line>Aku mulai membuat rencana untukmu saat aku melihat namamu di Buku Kematian. Yang artinya... Kau tak bisa lari dari takdir, ingat tidak?"<new_line>(Pencabut Nyawa mengeluarkan Buku dan mulai membalik halamannya)]]
			},
			['choice'] = {
				['9'] = [["Hah, itu saja?"]],
				['8'] = [[Keluarkan senjata]],
				['7'] = [["Saksikan kekuatan dokumen dan birokrasi!" (Robek halaman dengan namamu di atasnya)]],
				['6'] = [["Mencari ini?" (Keluarkan halaman dengan daftar korban Monster Frankenstein)]],
				['10'] = [[Terus bergerak]],
				['4'] = [["Persetan, aku mau pulang."]],
				['3'] = [["Bicara apa kau? Apa aku sudah memenuhi kontrak, atau apa?"]],
				['2'] = [["Di mana kita? Di mana Keledai itu?"]],
				['1'] = [[Seberangi sungai]],
				['5'] = [["Kau tidak bisa mengalahkanku!"]]
			}
		},
		['halloween_boss_2022_6'] = {
			['episode'] = {
				['5'] = [[Aku mendengar suara benturan dan raungan dari gubuk kayu kecil yang bahkan tidak kulihat sebelumnya. Binatang buas rawa!<new_line>Tapi kemudian Pencabut Nyawa muncul di sebelah gubuk dan menutup pintu dengan sabitnya.<new_line>"Nah, Manusia. Itu terakhir kalinya aku membantumu! Kejar Keledai itu, dia melarikan diri! Aku akan tetap di sini dan menahan pintu ini."]],
				['4'] = [[Keledai mendengarku melompat dan menghindar ke samping.<new_line>Dia berlari, meringkik minta tolong.]],
				['3'] = [[Dia menangkap granat di giginya dan melemparkannya kembali ke arahku. Saat dia melakukannya, aku melihat sesuatu jatuh dari mulutnya.<new_line>Keledai itu lari, meringkik minta tolong.]],
				['2'] = [[Aku melemparkan tali ke lehernya, tapi Keledai itu berhasil menggigitnya sampai terputus! Saat dia melakukannya, aku melihat sesuatu jatuh dari mulutnya.<new_line>Keledai itu lari, meringkik minta tolong.]],
				['1'] = [[Aku berjalan ke sebuah gubuk di rawa. Itu dia—si Keledai! Meraba-raba di semak-semak dan bergumam pada dirinya sendiri:<new_line>"Bunga biru, duri merah..."<new_line>Aku cukup dekat untuk menyerangnya.]]
			},
			['choice'] = {
				['2'] = [[Lempar granat ke arahnya]],
				['1'] = [[Lempar tali ke arahnya]],
				['4'] = [[Ikuti Keledai itu]],
				['3'] = [[Lompat dan tangkap dia]]
			}
		},
		['pumpkin_seed'] = {
			['episode'] = {
				['2'] = [[Sepertinya telah muncul musuh yang sepadan untukku di dunia ini. Pertempuran kami akan melegenda! Sampai jumpa, manusia, labu raksasa tersebut mengaum. Melupakanku, ia berjalan pergi, menyebabkan getaran di setiap langkahnya. Kini saatnya aku kembali ke kedai.]],
				['1'] = [[Aku menanam biji tersebut dan menyiramnya. Dalam hitungan detik, tumbuh kecambah, yang segera berubah menjadi sulur tebal. Tak lama, ia mengeluarkan sebuah labu kecil. Dari labu ini tumbuh mata, gigi, dan tangan kecil. Satu menit kemudian, si labu terlepas dari sulur dan memegang sepatu botku. Aku tak yakin apa yang sedang terjadi, tetapi sudah saatnya aku menghajar labu ini.]]
			}
		},
		['dracula'] = {
			['episode'] = {
				['6'] = [[Kami menemui sejumlah besar perlawanan di dalam kastel, tetapi kami berhasil mengusir musuh kami lantai demi lantai. Halangan terakhir adalah detasemen monster yang berdiri berjaga di luar ruang takhta.]],
				['5'] = [[Kastel itu rusak berat dan lebih tampak seperti reruntuhan yang membara ketimbang istana legendaris. Untuk alasan ini, ledakan yang menghancurkan pintu gerbang dan semua barikade itu tak menimbulkan efek pada keseluruhan pemandangan. Tanpa membuang waktu, pasukan masuk ke dalam jalan yang terbuka untuk melawan monster dan Pemburu dan aku mengikuti mereka.]],
				['4'] = [[„Rencananya sederhana: kamu dan aku menyerbu lantai atas dan mencari pengisap darah itu sementara yang lain membinasakan monster-monster lain di dalam kastel itu. Ikuti aku—kita akan mulai,“ Pemburu itu berkata sambil berbelok menuju siluet kastel yang besar.]],
				['3'] = [[„Master Helsing! Di bawah perintahmu, kami telah menghentikan pengeboman. Segalanya siap untuk menyerbu kastel itu,“ lapor pemburu tak dikenal itu. „Bagus! Ledakkan pintu gerbang dan barikade. Kita akan masuk dan melakukan segalanya sesuai rencana,“ jawab temanku, yang ternyata memiliki pangkat tinggi.]],
				['2'] = [[Ketika aku keluar dari portal, aku berada di tengah-tengah kamp pasukan yang sibuk. Manusia, pemburu, dan makhluk lain hilir mudik di antara tenda-tenda yang diterangi obor. Pemburu yang aku ikuti berdiri di dekatku. Seseorang lain dengan pakaian berburu mendatanginya dan mulai bicara.]],
				['1'] = [[Pemburu mengangguk dan berjalan menuju jalan keluar portal. Dia membisikkan sesuatu dan melemparkan semacam bubuk ke dalam kabut, yang berubah semerah darah dan mulai berputar lebih cepat. Pemburu mengangguk puas. Dia memintaku mengikutinya dengan lambaian tangannya sebelum melangkah masuk ke dalam kabut.]],
				['8'] = [[„Hm, perburuan kita telah selesai—untuk tahun ini. Aku harap bertemu kamu kembali lain waktu,“ kata Pemburu. Di belakangnya, tubuh Pangeran dimasukkan ke dalam peti mati perak. „Sebagai tanda terima kasihku, terimalah busur silang ini. Aku harap ini bisa melayanimu dengan baik.“ Pemburu itu menyerahkan senjatanya kepadaku, „Aku akan mengirimmu kembali, tetapi hati-hati: portal-portal itu akan segera menutup hingga tahun depan. Semoga beruntung di Dunia Atas.“]],
				['7'] = [[Ketika kami masuk, Pangeran itu sedang berdiri di depan singgasana, tampak sama sekali tak gentar. Tanpa sepatah kata, Pemburu mengangkat busur silangnya dan menembak tepat ke arah kepala vampir itu. Dalam satu gerakan cepat yang aneh namun mudah, Drakula menangkap panah itu satu inci di depan wajahnya. „Tidak sopan,“ katanya dengan tersenyum, „Kalian harus belajar sopan santun.“ Tanpa suara, vampir itu menyerbu kami.]]
			},
			['choice'] = {
				['5'] = [[Ambil busur silang itu]],
				['4'] = [[Menyerbu lantai atas]],
				['3'] = [[Rencana apa?]],
				['2'] = [[Dengar]],
				['1'] = [[Ikuti dia]]
			}
		},
		['halloween_fight_easy'] = {
			['episode'] = {
				['2'] = [[Aku sudah sangat dekat dengan gerombolan binatang buas itu. Sekarang aku bisa menyerang.]],
				['1'] = [[Lolongan mengerikan dari binatang buas aneh yang mengamuk di daerah itu bisa terdengar hingga bermil-mil jauhnya. Suara ini tentu saja menimbulkan ancaman bagi orang-orang di sini. Aku bisa mendapat imbalan tinggi untuk mengalahkan mereka.]]
			}
		},
		['new_year_boss_2_3'] = {
			['episode'] = {
				['6'] = [["Sudah lama aku tak mendengar kabar darinya... Dia sibuk dengan masalah pemerintahan di dunianya.<new_line>Tapi dia pasti memiliki pengalaman tempur. Sudah diputuskan! Hiasi pohon lagi untuk membuka portal untuknya."]],
				['5'] = [["Aku tidak percaya. Sebenarnya apa yang terjadi sampai Santa berubah jadi seperti itu... Tapi, apa pun itu, kita perlu bala bantuan untuk menghadapi geng berbahaya itu. Aku tahu seseorang yang cukup kuat."]],
				['4'] = [["Akhirnya, kau berbicara dengan normal! Apa yang terjadi?"]],
				['3'] = [[Aku tertipu lagi! Segera setelah aku memiringkan kepala ke belakang untuk meminum ramuan itu, Cave Santa memukulku dengan tongkatnya lagi!]],
				['2'] = [["Kau benar. Lebih baik jika kau bisa memahami perkataan orang lain daripada perkataanku saja. Minum ramuan ini—itu akan membantu."<new_line><new_line>Cave Santa memberiku cangkir kayu. Aku meminumnya dengan hati-hati. Lumayan enak!]],
				['1'] = [["Ooh ah. Agh ooh!"]]
			},
			['choice'] = {
				['5'] = [["Siapa?"]],
				['4'] = [[Beri tahu dia tentang Krampus Claus]],
				['3'] = [["Bisa tidak kau berhenti melakukannya?!"]],
				['2'] = [[Minum semua]],
				['1'] = [["Dia bicara omong kosong lagi..."]]
			}
		},
		['hunt_quest'] = {
			['episode'] = {
				['9'] = [[Klienku mengangguk puas.<new_line>'Jadi, apakah semuanya berjalan baik-baik saja?']],
				['8'] = [[Wanita itu menunduk.<new_line>'Maaf mengganggumu.'<new_line>]],
				['7'] = [[Wanita itu mengangguk dengan penuh terima kasih.<new_line>'Terima kasih. Ini akan membuatku bertahan beberapa hari ke depan...'<new_line>]],
				['6'] = [[Bosku memeriksa wajahku dengan saksama.<new_line>'Apakah kau membawanya?']],
				['5'] = [[Klien mengerutkan kening.<new_line>'Tidak, sobat, bukan itu kesepakatannya. Dagingnya hampir tidak cukup. Kau tidak mendapatkan uang dariku.']],
				['4'] = [[Wanita itu menatapku, seolah kagum.<new_line>'Terima kasih! Kau sangat baik!']],
				['3'] = [[Wanita itu menatapku dengan keputusan di matanya.<new_line>'Apakah kau bisa... menyisihkan makanan?']],
				['1'] = [[Seorang wanita mendekatiku sambil menggendong seorang anak kecil. Dia tidak mengatakan apa-apa, melainkan hanya berdiri di sampingku, menatap kakinya.]],
				['11'] = [[Kita telah sepakat. Beri aku dagingnya atau enyahlah!']],
				['10'] = [[Oke, kau menang. Aku akan menaikkan tarifmu.']]
			},
			['choice'] = {
				['9'] = [[Aku tidak punya apa-apa']],
				['8'] = [[Ini sedikit makanan kaleng']],
				['7'] = [[Semuanya sudah siap']],
				['5'] = [[Kirim pesanan]],
				['4'] = [[Bawa sisa daging ke klien]],
				['3'] = [[Aku bisa memberimu sedikit daging']],
				['2'] = [[Apakah kau butuh sesuatu?']],
				['1'] = [[Serahkan hasil curiannya]],
				['12'] = [[Pergi ke klien]],
				['10'] = [[Coba tawar menawar]]
			}
		},
		['new_year_boss_1'] = {
			['episode'] = {
				['13'] = [["Dan kau akan menerima hiasan itu sebagai hadiahnya. Bergegaslah, kau harus membuka portal itu untuk pelacak. Cepat mulai bekerja!"]],
				['12'] = [["Ha, sihir pohon Natal ini hanya aktif dengan hiasan khusus yang disimpan orang-orang di rumah mereka dengan penuh kasih dan cinta sepanjang tahun. Sayangnya...kau tak bisa membuatnya di pabrik."]],
				['9'] = [["Sementara itu, aku akan mulai menjual barang di sini. Belum ada tanda munculnya Sinterklas, tetapi hari raya tetap akan dirayakan. Datanglah ke tokoku. Mungkin ada barang yang berguna untukmu."]],
				['8'] = [["Ho ho ho, bagus. Berbuat baiklah dan kau akan mendapat balasan yang setimpal. Itulah cara agar bisa menjadi master Sinterklas."]],
				['7'] = [["Oh, Sinterklas telah memesan kostum spesial dariku. Kostum itu sangat penting. Satunya bisa kau beli hanya dengan... Tidak, lupakan saja, duniamu sedang dalam masalah. Kostum itu bisa kau ambil secara gratis. Setidaknya itu yang bisa aku lakukan."]],
				['6'] = [["Memalukan. Dasar kurang ajar! Satu hal yang pasti: Natal dalam bahaya di duniamu. Semoga rekanku Sinterklas masih hidup. Tapi sampai dia ditemukan, sepertinya kau harus menjalankan tanggung jawabnya."]],
				['5'] = [["Kau bodoh ya? Kalian harus enyah sekarang juga. Tunggu, sepertinya tidak! Krampus suka orang-orang bodoh. Jadi kalian akan dibawa menghadapnya! Cepat, tangkap mereka!"]],
				['4'] = [["Santa sudah lenyap. Pergi dari sini! Sebelum kami membakar jenggot kakekmu juga."<new_line>Sebuah botol melayang dari balik pria itu dan menghantam kakiku dengan keras.]],
				['3'] = [[Pada saat itu, kami mendengar derum mesin, dan beberapa pengendara motor keluar dari rumah yang terbakar itu.<new_line><new_line>Salah satu dari mereka berteriak.<new_line>"Mau apa kalian? Kami yang berkuasa di sini sekarang!"]],
				['2'] = [["Aku bergidik jika memikirkannya. Semoga Sinterklas berhasil melarikan diri. Tetapi di duniamu, preman macam apa yang melakukan hal seperti itu?"]],
				['1'] = [[Kami sampai di sebuah rumah kayu. Dindingnya tampak hangus, kaca jendela warna-warni pecah, dan pintu dengan banyak hiasan telah terlempar ke tanah.]],
				['11'] = [["Hmm, aku tak terlalu paham teknologi... Yang kutahu, pohon Natal harus dihias agar portal bisa digunakan."]],
				['10'] = [["Oh, tepat sekali! Hilangnya Sinterklas jelas ada hubungannya dengan geng yang dipimpin oleh... Krampus? Aku kenal pelacak yang andal. Tapi untuk memanggilnya ke sini, kau perlu menghias pohon Natal lagi."]]
			},
			['choice'] = {
				['9'] = [["Jadi, aku harus berbuat baik..."]],
				['8'] = [["Baiklah, apa kau punya hiasan?"]],
				['7'] = [["Apa hubungannya dengan pohon Natal?"]],
				['6'] = [["Bagaimana jika mencari..."]],
				['5'] = [["Aku? Bagaimana?"]],
				['4'] = [["Hei, jangan menguji kesabaranku. Kalian siapa?"]],
				['3'] = [["Di mana Sinterklas?"]],
				['2'] = [["Banyak orang yang bisa jadi tersangka."]],
				['1'] = [["Apa yang terjadi di sini?"]]
			}
		},
		['new_year_boss_2_2'] = {
			['episode'] = {
				['9'] = [["Sungguh malang! Karena asap yang mengerikan itu, aku tak dapat membantumu."]],
				['8'] = [[Tanpa menjawab, lelaki tua itu tertawa terbahak-bahak lagi. Masih terkekeh, dia berlari ke sepeda motornya dan keluar dari alun-alun.<new_line>Para preman, yang dipimpin oleh tetua yang cacat itu, mendekati aku.]],
				['7'] = [[Asap menghilang. Di samping lelaki tua itu, di mana lelaki tua tadi berada, sekarang berdiri sejenis monster.<new_line>"Semuanya, geng Krampus Claus punya anggota baru! Anak baru, tugas pertamamu adalah menyingkirkan bajingan ini!"]],
				['6'] = [[Banyak dekorasi Natal dilemparkan ke dalam api, yang mengeluarkan aliran asap tebal yang bahkan lebih tebal dari yang pertama. Orang tua itu menarik tetua desa yang malang itu ke arah dirinya sendiri.]],
				['5'] = [[Tiba-tiba, kakek tua itu menoleh ke arahku dan mulai tertawa.<new_line>"Ah, kau ingin berbicara dengan kake tua ini? Hei, kalian, buang semua sampah ini ke dalam api!"]],
				['4'] = [[Sepertinya tak ada yang mendengarku. Hadiah itu mendarat di api, api biru berkobar, dan asap biru keluar. Si kakek dan gengnya berdiri di sekitar api dan menghirup asap, mata mereka berputar ke belakang.]],
				['3'] = [["Hei, kakek! Apa kau sudah mengumpulkan hadiah semua orang? Berikan padaku!"<new_line>Pria tua bertopeng menakutkan itu mengambil kotak berhiaskan pita dari tangan pria itu dan melemparkannya ke dalam api.]],
				['2'] = [[Di alun-alun desa, para preman membuang dekorasi Natal ke tumpukan di dekat api unggun. Beberapa penduduk setempat berbaris di dinding rumah. Mereka diperintah oleh seorang pria muram dengan beberapa hadiah di tangannya.]],
				['1'] = [[Anggota geng yang kabur itu pergi ke sebuah desa yang dihias untuk hari raya. Tepat di depan mataku, para preman sedang melepas tali lampu dari tiang, mencabut ornamen dari pohon, dan menghilang di balik rumah.]],
				['11'] = [["Oh, kau tidak mengenalinya? Orang tua itu adalah Sinterklas! Aku tak tahu bagaimana dia berubah menjadi seperti itu...<new_line>Aku tak paham mengapa dia melakukan kekejian itu. Mungkin Business Claus tahu apa yang harus dilakukan. Kita harus menemukannya.."]],
				['10'] = [["Itu bukanlah intinya. Cara mereka menelannya... sambil menghancurkan semua yang berhubungan dengan Natal. Apakah duniamu serusak ini?"]]
			},
			['choice'] = {
				['6'] = [["Apa yang kau lakukan padanya?!"]],
				['5'] = [[Keluar dari asap]],
				['4'] = [["Jangan ganggu dia!"]],
				['3'] = [[Tanyakan pada kakek itu apa yang terjadi]],
				['2'] = [["Hei, hentikan!"]],
				['1'] = [["Apa itu..."]],
				['8'] = [["Itu semua ulah orang tua itu!"]],
				['7'] = [["Apa asap itu beracun?"]]
			}
		},
		['emba_isaev_surgut'] = {
			['episode'] = {
				['13'] = [[(berbisik) "Di sana kau rupanya, temanku. Kita harus segera pergi dari sini. Aku telah menghubungi rekan-rekanku di S.O.V.A., kita akan pergi ke kota mereka.<new_line>Kami sangat membutuhkan bantuanmu, mohon datanglah ke sana secepat mungkin."]],
				['12'] = [["Baiklah. Ini, ambillah dan biarkan kami pergi."]],
				['9'] = [["Isaev, kau orang yang pintar, berikan kopernya padaku dan pergilah."]],
				['8'] = [["Kita akan menyiapkan pemusnahannya, akan ada beberapa tugas lagi untukmu. Kembalilah dalam beberapa jam."]],
				['7'] = [["Jika kita tidak mengendalikan miselium itu sepenuhnya, kita tidak dapat menggunakannya bahkan sebagian sekalipun. Kita dapat bekerja dengan fragmen organisme ini, tetapi cepat atau lambat bagian lain darinya akan mulai bermutasi.<new_line>Dan Utusan seperti ini akan muncul. Atau Pengembang biak. Dan setelah itu mustahil untuk mengendalikannya sama sekali, bahkan pada bagian miselium kita."]],
				['6'] = [["Ya. Kotak hitam merekam proses pembentukan objek yang kau temui. Rupanya, seluruh miselium itu adalah satu organisme tunggal. Bisa dibilang, seperti jaringan telepon.<new_line>Tidak adanya satu inang tetap tidak menghalanginya sama sekali. Tapi bagi kami ini adalah masalah genting!"]],
				['5'] = [["Apa?! Itu bisa berbicara?! Tidak mungkin, itu omong kosong... Kau bilang itu menyebutkan nama Gerin?<new_line>Ya Tuhan, apa ini sebenarnya? Kita harus segera menghentikan semua pengembangan produk, kita tidak boleh mengembangkan teknologi ini lebih jauh!"]],
				['4'] = [["Satu-satunya hal yang harus dilakukan adalah mencari tahu apa yang disembunyikan kota sains ini. Tolong, ambilkan kotak hitam dari stasiun riset biologi."<new_line>(memberikan selembar kertas berisikan kodenya)]],
				['3'] = [["Sayangnya, tidak disebutkan tentang pengembangan apa pun dalam dokumen. Ini hanya sekadar lab pemrosesan data lainnya.<new_line>Tetapi luar biasanya, kami mendapatkan sinyal radio dari stasiun riset biologi ini! Kami belum mampu memecahkannya, tetapi mungkin ada seseorang di sana yang masih bersamanya."]],
				['2'] = [["Kami sudah berbicara dengan faksi-faksi besar. Kami berencana berbagi teknologi dengan mereka, jika mereka membantu kami mengembangkan sistem keamanan.<new_line>Tetapi jika kami tidak dapat menyelesaikan masalah keamanan ini, kami harus mematikan seluruh program dan menghapus teknologinya. Kau dapat melihat sendiri betapa seriusnya hal ini. Kami tidak bisa mengambil risiko apa pun."]],
				['1'] = [["Kami mengerjakan teknologi penghijauan secara aktif. Dan berkat penelitian terhadap hasil kerja Gerin, kemajuannya sangat pesat!<new_line>Kami masih perlu mengembangkan sistem untuk melindungi dari aktivasi yang tidak semestinya. Sebenarnya, di sinilah kami berharap mendapatkan jawaban untuk bagaimana memecahkan masalah ini."]],
				['11'] = [["Seperti yang telah dibuktikan oleh para ilmuwan Inggris, uang adalah uang, Dok. Jangan pelit, aku tidak ingin membunuhmu."]],
				['10'] = [["Kau tidak mengerti! Teknologi ini tidak stabil dan berbahaya!"]]
			},
			['choice'] = {
				['6'] = [["Baiklah. Semoga berhasil, Dokter."]],
				['5'] = [[Dekati Isaev]],
				['4'] = [["Apa maksudmu?"]],
				['3'] = [["Mengapa? Apakah kau memiliki data baru?"]],
				['1'] = [["Bagaimana kau akan menerapkan teknologinya?"]]
			}
		},
		['halloween_energy_easy'] = {
			['episode'] = {
				['9'] = [["Pertama, kita perlu menggali kebun. Langsung mulai jika kamu sudah siap."]],
				['8'] = [["Tidak... Mungkin kami melakukan sesuatu yang salah. Kami akan mencoba lagi. Terima kasih banyak atas bantuanmu."]],
				['7'] = [["Oh, Kaisar Bermuka Matahari, kembalilah kepada kami. Maafkan kami karena sudah mengecewakanmu. Kami membutuhkanmu. Kembalilah dan kami akan setia kepadamu!"<new_line><new_line>Ritual itu berlangsung beberapa menit. Setelah itu, para kepala labu bubar dengan muka yang murung.<new_line>]],
				['6'] = [["Ini adalah bisnis, dan bisnis harus didahulukan. Baik, ambil ini dan mulailah bekerja."]],
				['10'] = [["Sekarang kita perlu menanam benihnya. Langsung mulai jika kamu sudah siap."]],
				['11'] = [["Semuanya hampir siap. Kita hanya perlu memupuk tanaman."]],
				['3'] = [["Bagus! Sekarang kita perlu menanam benihnya. Langsung mulai jika kamu sudah siap."]],
				['2'] = [["Terima kasih banyak! Pertama, kita perlu menggali kebun. Langsung mulai jika kamu sudah siap."]],
				['1'] = [["Halo, Pengembara! Bisakah kau membantu kami? Kami sangat rindu komandan kami. Situasi di sini sulit tanpanya, jadi kami ingin menumbuhkan komandan baru. Kami sudah menemukan benih dan kebun, tapi kami terlalu ceroboh untuk mengerjakan ini."]],
				['4'] = [["Bagus sekali. Sekarang tinggal memupuk tanaman. Bisakah kamu mengambil pupuk dari Petani? Harusnya dia ada di rumah."]],
				['5'] = [["Jangan lupa menutupnya. Pupuk itu sangat bernilai tinggi bagi para kepala labu. Aku sudah membuatnya selama berhari-hari."]]
			},
			['choice'] = {
				['6'] = [["Apa kau bisa mengambil komandan kembali?"]],
				['5'] = [["Kenapa kamu malah membantu mereka?"]],
				['4'] = [["OK." (Pergi ke Petani)]],
				['3'] = [[Mulai]],
				['2'] = [["Tak ada waktu." (Pergi)]],
				['1'] = [["Memang ceroboh. Ya, aku akan membantu."]]
			}
		},
		['cold_footprints'] = {
			['episode'] = {
				['5'] = [[Kau membunuh mereka semua, bukan? kata wanita itu padaku tiba-tiba, melirik dingin ke cermin. «Apa daya, kalian memang paling hebat dalam hal itu. Tapi tak apa, musim dingin abadi akan segera melanda kalian semua dan penderitaan itu akan berakhir.» Setelah menyelesaikan kalimatnya, dia menghantam lantai dengan tongkatnya, cerminnya retak, dan aku tidak melihat apa-apa lagi.]],
				['4'] = [[Sosok wanita tinggi, berkulit putih dengan tongkat di tangannya muncul di dalam cermin. Dia duduk di singgasana es dan di sampingnya, terikat dalam belenggu es, duduk orang tua berjanggut abu-abu. Itu Kakek Salju!]],
				['3'] = [[Saat aku memasuki tenda sang pemimpin, perhatianku langsung tertuju pada cermin es besar di tengah tenda. Seakan tersihir, aku mendekatinya dan mengusap permukaan halusnya dengan tanganku.]],
				['2'] = [[Selesai mengatasi para penjarah es, satu lagi penjarah muncul dari tenda pusat. Tubuhnya tinggi, dengan kapak besar di tangannya. Bisa jadi dia pemimpinnya. Saatnya bersiap untuk bertarung.]],
				['1'] = [[Tenda besar yang tertutup oleh kulit binatang berlapiskan es, dan beberapa tenda kecil. Semuanya terlindungi benteng salju yang tinggi. Seperti itu penampakan kamp para penjarah es. Mereka sudah mengetahui keberadaanku, saatnya menyerang.]]
			},
			['choice'] = {
				['1'] = [[Apa yang terjadi?]]
			}
		},
		['toy_story'] = {
			['episode'] = {
				['2'] = [[Hentikan! teriak Gadis Salju, berlari ke arah terem. Aku berdiri dikelilingi mainan rusak, dan si Nutcracker terdiam, pedangnya terangkat siap menyerang.]],
				['1'] = [[Pintu terem dengan hiasan megah itu hancur, dan di dalamnya sedang terjadi kekacauan. Aku tidak ada waktu untuk menentukan posisiku sebelum aku mendengar suara asing datang dari balik meja yang terbalik, «Mereka telah kembali! Cepat, isi meriam! Siap! Bidik! Tembak!» Setelah itu, terdengar ledakan dan peluru kecil mendesing melewati telingaku. Apa yang terjadi di sini?]],
				['3'] = [[Satu jam kemudian, setelah selesai berbenah, kami mengetahui bahwa terem itu telah diserang oleh para penjarah es. Mereka merusak tempatnya tapi tidak mengambil apa pun sebelum pergi. Saat aku tiba tadi, mainan-mainan mengira aku adalah salah satu penjarah yang datang kembali. Gadis Salju berjanji akan mencari tahu dari mana datangnya para penjarah itu.]]
			},
			['name'] = [[Terem]]
		},
		['impostor2'] = {
			['episode'] = {
				['2'] = [[Senjata siap, saya pergi ke bandit. penipu berteriak: «Hei, man, menunggu saya tidak ingin berjuang untuk sampah ini tetapi tidak akan hanya memberikan kepada Anda Kau tahu apa Bawa kami tiga botol vodka dan karung ini adalah milikmu Mengatasi!.?.?»]],
				['1'] = [[Bandit dalam setelan duduk di berhenti dan menghitung hal-hal yang dicuri. «Apa sampah!» penipu Moroz diredam. «Saya bilang ide bodoh!»]]
			},
			['choice'] = {
				['1'] = [[Datang mendekat]]
			}
		},
		['lair_beehive'] = {
			['episode'] = {
				['1'] = [[Dalam beberapa kasus, ini pastilah bangunan lima lantai yang biasa saja, tapi lebah mutan telah mengubahnya menjadi sarang mereka. Suara mendengung datang dari dalam. Aku yakin akan bisa menemukan sesuatu yang berguna di dalam, tapi lebah tidak akan begitu saja menyambutku ke rumah mereka dengan mudah.<new_line>]]
			}
		},
		['emba_isaev_tver'] = {
			['episode'] = {
				['13'] = [["Ahli biologi akan membantu dengan itu. Tapi kau membutuhkan biomassa untuk membuat kostum itu.<new_line>Untuk kali pertama, aku akan memberikannya untukmu, tetapi mulai sekarang kau harus mengekstraknya dari miselium dengan ekstraktor. Kau juga bisa mendapatkan ekstraktor dari Ahli biologi."]],
				['14'] = [["Apa kau sudah merakit kit AIDA?"]],
				['17'] = [["Ya, ini dia! Kelompok stasiun riset biologi eksperimental dan metodologis yang membiakkan... spesimen tempur?! Ada koordinat tempat eksperimen semacam itu dilakukan.<new_line>Ekspedisi pertama dalam bahaya, kita harus segera pergi ke kota sains itu! Lokasi kami selanjutnya adalah Tula. Kita akan bergerak segera setelah kau siap."]],
				['18'] = [["Kalau begitu, apa kau siap untuk melanjutkan ekspedisi?"]],
				['12'] = [["Pertumbuhan miselium yang agresif... menyebar keluar stasiun riset biologi... aktivasi spora yang tidak terkendali... disertai dengan gangguan sistem saraf pusat. Kurasa sudah terlambat untuk menyelamatkan mereka.<new_line>Tetapi kalau ekspedisi Progres pertama sudah pindah, maka..."]],
				['9'] = [["Kami tidak tahu kalau miselium dapat melakukan ini ke seluruh kota! Tapi kau tidak perlu khawatir tentang itu, yang paling penting, kita memulihkan teknologinya dan memberikan masa depan pada dunia ini."]],
				['8'] = [["Selamat datang! Kulihat kau berhasil menyelesaikan misimu, kawan. Nah, apa yang kau dapatkan?"]],
				['7'] = [["Bergegas dan berikan kotak hitam ke Ahli biologi dan bantu dia melanjutkan risetnya."]],
				['6'] = [["Nah, bagus. Informasi tentang riset proyek E.M.B.A. disimpan di dalam kotak hitam. Ada di stasiun riset biologi di pusat kota sains. Navigator akan menjadi penyeliamu, dia akan membantu pencariannya. Permisi, sekarang aku harus kembali bekerja."]],
				['5'] = [["Sudahkah kau menerima kit AIDA?"]],
				['4'] = [["Staf kami bersedia menukarkan kit AIDA dan Item Habis Pakai lain dengan disket yang berisikan data.<new_line>Bantu rekanku meneliti miselium dan simpan datamu di dalam disket. Setelah kau mendapatkan cukup banyak data, kunjungi departemen Item Habis Pakai dan beli kostum khusus AIDA."]],
				['3'] = [["Pergi ke kota sains terdekat untuk mencari kotak hitam. Tidak ada radiasi di sana, tetapi kemungkinan tertular spora miselium sangat tinggi.<new_line>Jadi sebelum kita melangkah lebih jauh, ambil kit perlindungan spora – AIDA."]],
				['2'] = [["Kalau begitu, bagus. Tugas utamamu dalam ekspedisi yang akan datang adalah membantu riset: mengumpulkan biomaterial dan mencari dokumen dan kotak hitam."]],
				['1'] = [["Apa kau sudah memberikan dokumen itu ke Ahli biologi dan membantunya melakukan risetnya?"]],
				['11'] = [["Memecahkan kodenya sepenuhnya akan memakan waktu, tetapi kami sudah memiliki potongan data pertama.<new_line>Wah, wah, wah... Sesuatu terjadi di semua kota sains pada waktu yang bersamaan. Sinyal S.O.S. dari stasiun riset biologi terdekat mengkonfirmasi hal ini."]],
				['10'] = [["Semoga tidak. Seharusnya, selama bencana global, Profesor Gerin dan ilmuwan lain dari proyek E.M.B.A. seharusnya dievakuasi seperti kami.<new_line>Tapi kami kehilangan kontak dengan mereka, juga dengan yang lainnya... dan seiring waktu kami berubah menjadi "juru arsip". Tapi sebaiknya kita jangan membicarakan tentang hal-hal yang menyedihkan."]]
			},
			['choice'] = {
				['9'] = [["Para ilmuwan di kota sains telah menjadi mutan?"]],
				['8'] = [["Ya."]],
				['7'] = [["Aku belum siap."]],
				['6'] = [["Aku siap."]],
				['5'] = [["Apa yang bisa kau ketahui dari kotak hitam itu?"]],
				['4'] = [[Laporkan saat ada penampakan]],
				['3'] = [["Di mana aku bisa mendapatkannya?"]],
				['2'] = [["Paham."]],
				['1'] = [["Belum."]],
				['11'] = [["Apa mereka menemukan sesuatu yang baru?"]],
				['10'] = [["Apa yang terjadi?"]]
			}
		},
		['baba_yaga_1'] = {
			['episode'] = {
				['6'] = [[Ketika aku memungut daftar itu dan berbalik, baik pondok dan lingkaran itu telah hilang.]],
				['5'] = [[„Bola benang, tentu saja. Kalian selalu datang untuk itu,“ gerutu nenek tua itu sambil menghilang ke dalam pondok lagi. Satu menit kemudian, gumpalan kertas melayang keluar dari jendela dan melesat melewatiku, diikuti suara Baba Yaga: „Kumpulkan benda di daftar itu dan panggil aku lagi, dan kamu akan mendapatkan bola benangmu.“]],
				['4'] = [[Pondok itu dengan enggan mulai menggerakkan kaki ayamnya dan berputar, tetapi berhenti setengah jalan. Wanita tua yang kesal melongokkan kepalanya dari jendela kecil yang menghadapku dan segera mengajukan pertanyaan bertubi-tubi: „Kamu siapa, Nak? Kamu berani memanggilku untuk apa? Apa yang kamu inginkan?„]],
				['3'] = [[Ketika aku berbalik, pondok berkaki ayam telah muncul di dalam lingkaran membelakangiku, seakan-akan pondok itu selalu berada di sana.]],
				['2'] = [[Aku rasa aku gagal. Aku harus memulainya lagi dari awal.]],
				['1'] = [[Ini tempat yang bagus untuk memanggil Baba Yaga. Aku harus memastikan ritual itu tidak gagal.]]
			},
