			['episode'] = {
				['9'] = [[(Vanya ada di rumah dan tidak sendirian ternyata. Masuk ke sana sekarang adalah hal terakhir yang ingin kulakukan.)]],
				['8'] = [[Dengar, hei, gembel, sekarang Gena tidak ke mana-mana, tetapi minuman keras ini tidak akan menunggu. Apakah kamu berubah pikiran?]],
				['7'] = [[Apa? Dan kamu menyebut dirimu orang Rusia!]],
				['6'] = [[(Vanya tertawa lagi.)<new_line><new_line>Jadi apa, kamu serius akan tetap pergi ke Novomoskovsk untuk menemukan sebuah mayat? (Pemuda itu diam dan kemudian memberiku sesuatu yang menarik.) Dengar, gembel, aku punya ide. Ayo pergi ke tempatku. Aku membawa minuman keras sepulang dari Novomoskovsk. Labelnya tertulis 'Bryanskaya,' pernah mendengar merek itu? Cuma masalahnya adalah: Aku tidak punya teman minum, hanya ada wanita di sini.]],
				['5'] = [[Sebuah gedung apartemen berlantai lima. Di samping garasi-garasi. Di apartemen di lantai tiga. Apa urusanmu?]],
				['4'] = [[(Vanya tertawa dan melemparkan rokoknya ke semak-semak. Lalu dia memberiku tatapan mengejek. Anak nakal.)<new_line><new_line>Dan apa yang ingin kamu ketahui, Tuanku?]],
				['3'] = [[Tentang Gena, kan? Aku melihatmu mengunjungi ibunya. Namun, aku tak menceritakan apapun padamu. Aku harus menceritakan kisah ini kepada semua orang setidaknya tiga kali, aku muak dengan itu.]],
				['2'] = [[(Vanya malah bersiul.)<new_line><new_line>Seorang gembel? Aku tidak berpikir ada idiot yang tersisa yang berjalan di sekitar padang gurun.]],
				['1'] = [[(Vanya sedang duduk di teras dan merokok. Dalam beberapa kata pertamanya, aku menyadari bahwa dia sedikit mabuk.)<new_line><new_line>Hei. Kita belum punya teman baru di sini dalam waktu yang lama. Dari mana kamu berasal, lubang apa yang kamu gunakan untuk merangkak keluar?]]
			},
			['name'] = [[Vanya]]
		},
		['zaycev'] = {
			['choice'] = {
				['16'] = [[Apa yang terjadi?]],
				['15'] = [[Cobalah buat aku mengerti.]],
				['14'] = [[Apa yang mereka kerjakan?]],
				['13'] = [[Silakan, jangan malu.]],
				['2'] = [[Itulah Day R.]],
				['1'] = [[Itu perjanjian kita. Aku memenuhi janjiku, serigala betina sudah mati. Sekarang giliranmu.]],
				['19'] = [[Ya, terima kasih telah berbicara denganku. Aku harus pergi sekarang...]],
				['22'] = [[Tidak, aku sudah mengetahui semua yang kuinginkan. Terima kasih.]],
				['17'] = [[Lalu apa yang kamu lakukan?]],
				['18'] = [[Namun virus itu masih digunakan.]],
				['21'] = [[Ya, ada hal lain. Bagaimana perang nuklir dimulai?]],
				['9'] = [[Apakah tidak ada yang bisa dilakukan?]],
				['8'] = [[Uni Soviet melawan?]],
				['7'] = [[Tapi mereka terlalu banyak...]],
				['20'] = [[Baiklah, Terima kasih sudah berbicara denganku.]],
				['10'] = [[Apakah itu berarti di benua lain juga tidak ada yang tersisa? Apakah seluruh dunia adalah padang gurun?]],
				['11'] = [[Serigala-serigala itu mendapatkan Gavrilov. Kudengarkan.]],
				['3'] = [[Tidak, ada yang lain. Bagaimana perang nuklir dimulai?]],
				['4'] = [[Aku menunggu.]],
				['5'] = [[Dan mereka akan membunuh siapa saja yang selamat.]],
				['6'] = [[Untuk melarikan diri dari negara yang sekarat ini?]],
				['12'] = [[Ceritakan tentang Nesterov dan para ilmuwan lain yang bekerja dengannya. Dan semua sisanya.]]
			},
			['episode'] = {
				['16'] = {
					['2'] = [[Mereka menyembunyikan pekerjaan mereka dari yang lain. Aku curiga bahwa mereka tidak menghabiskan malam di lab hanya untuk minum teh, tetapi aku memilih untuk tidak ikut campur. Tetapi kemudian aku harus melakukannya.]],
					['1'] = [[Genetika molekuler. Mereka mempelajari protein dan gen dari virus yang tersebar. Usaha yang benar-benar tidak berbahaya, selama kamu tidak melebihi batas, tentunya .<new_line>Nesterov melakukannya. Dia mulai bereksperimen dengan mutasi. Dia membuat Kulik dan Karbyshev terlibat di dalamnya.]]
				},
				['15'] = [[Aku bisa menjelaskan, tetapi kamu masih tidak akan mengerti.]],
				['14'] = [[Apa yang kamu ingin aku katakan? Georgy Nesterov adalah ilmuwan nasional yang terkenal, kebanggaan institut kami. Kami berhasil bekerja sama dengan Georgy, Kulik, dan Shashkov selama bertahun-tahun. Karbyshev dipindahkan kepada kami dari Leningrad. Beletsky, anggota tim paling muda, bergabung kemudian, pada akhir tahun 83an.]],
				['13'] = [[(Direktur mengangguk, melepas kacamatanya, dan mulai mengelapnya secara metodis dengan lap. Dia membersihkannya selama beberapa menit.)]],
				['2'] = [[(Zaitsev memperhatikan taring di tangannya dan menatapku. Dia tampak sedikit gelisah.)<new_line><new_line>Di mana Pavel?]],
				['1'] = [[(Mantan direktur Institut Virologi tertidur di kursi yang sama seperti terakhir kali. Aku membangunkannya dan duduk di kursi sebelahnya. Zaitsev menatapku dengan grogi. Lalu aku mengulurkan taring serigala betina kepadanya.)<new_line><new_line>Apa ini?]],
				['19'] = [[Sial, ya. Tampaknya Nesterov sudah berhasil menyerahkan budaya yang terkontaminasi ke CIA. Pada 17 Agustus 1984, agen musuh menyebarkan R17 ke udara di berbagai kota menggunakan kaleng semprot khusus.]],
				['17'] = {
					['2'] = [[Nesterov dan teman-temannya sedang mengembangkan wabah baru. Tidak, bukan wabah, virus ini, R17, jauh lebih berbahaya daripada Yersinia pestis. Dan terlebih lagi – virus itu dibuat sesuai pesanan! Untuk CIA! Nesterov telah menciptakan senjata biologis baru untuk musuh kita!]],
					['1'] = [[Beletsky menemukan wadah rahasia tersembunyi Nesterov. Dia menyeretnya keluar dan membawakanku buku catatan Nesterov. Dan sebuah rekaman suara. Pada awalnya aku bahkan tidak percaya, tetapi itu tulisan tangannya serta suaranya!]]
				},
				['18'] = {
					['2'] = [[Kemudian kami dikunjungi oleh pria dari KGB. Mereka menginterogasi kami dan menggeledah seluruh lembaga. Mereka mengambil semua yang mereka dapat dari lab Nesterov, bahkan tanda di pintu. Mereka menghancurkan semua biomaterial.]],
					['1'] = [[Aku... Aku tak punya pilihan. Kami melaporkannya kepada pihak berwenang. Mereka bertiga. Beletsky dan Shashkov menandatangani pernyataan itu juga.<new_line>Kasusnya bergerak cepat. Nesterov, Kulik, dan Karbyshev ditangkap.]],
					['3'] = [[Sidang diadakan di balik pintu tertutup, tetapi aku diizinkan untuk tinggal sebagai saksi. Itu terjadi dengan cepat. Bukti itu luar biasa, dan Karbyshev membuat pengakuan penuh. Pasal 64, pengkhianatan Tanah Air. Mereka dibawa langsung dari ruang sidang untuk menghadapi regu tembak.]]
				},
				['9'] = [[Ya. Kami meluncurkan seluruh persenjataan kami. Langsung, ke semua target. Meskipun sudah dihancurkan, negara itu tidak ingin mati. Gelombang pengungsi yang terinfeksi memanfaatkan kekacauan dan merepotkan tentara sekutu. Dan itulah bagaimana R17 berhasil menyebar ke seluruh dunia.]],
				['8'] = {
					['2'] = [[Sebuah pertemuan rahasia dari Nuclear Club diadakan pada akhir Oktober dan gerakan membersihkan 'wilayah yang terinfeksi' disahkan. Mereka menggunakan semua senjata yang mereka miliki. Bahkan beberapa desa menjadi sasaran 'desinfeksi' dengan bom. Tapi mereka salah perhitungan lagi.]],
					['1'] = [[Ada. Setiap orang yang mereka bunuh, dua orang datang lagi. Dan perbatasannya sangat luas, sehingga orang yang cukup licik atau nekat akan menemukan jalan keluar. Dan ketika wabah pertama epidemi terjadi di Tiongkok, Mongolia, Turki, Polandia, dan Finlandia, diputuskan bahwa tindakan ekstrem harus diambil.]]
				},
				['7'] = [[Tentu. Dan hampir semua dari mereka terinfeksi. Tetangga kita menutup perbatasan mereka, mereka membentuk darurat militer, mengumpulkan pasukan, dan menembak mati para pengungsi yang jumlahnya ribuan...]],
				['20'] = [[Hmm? Adakah sesuatu yang lain, anak muda?]],
				['10'] = [[(Zaitsev tertawa terbahak.)<new_line>Tidak. Tetapi negara-negara lain melakukan perlawanan. AS mulai mengebom Eropa, India menyerang Cina. Konflik yang berurat akar berkobar... Seluruh dunia seolah-olah telah menjadi gila, satu-satunya tempat yang aman adalah Antartika. Namun, seperti biasa, manusia berhasil membuat segalanya lebih buruk...]],
				['11'] = [[Sejujurnya. Aku tidak tahu. Permisi, anak muda, tapi kita sudah bicara lama, waktunya aku minum pilku.]],
				['12'] = [[Baiklah, seperti yang kamu inginkan. Ingatkan aku, apa yang ingin kamu ketahui?]],
				['3'] = [[Ya, begitulah pandemi dimulai, memusnahkan sebagian besar populasi Uni Soviet.<new_line>Kamu puas sekarang?]],
				['4'] = [[(Direktur menatapku kebingungan, lalu mengangguk lagi dan mulai membersihkan kacamatanya.)]],
				['5'] = [[Baiklah baiklah. Tetapi semua sangat mudah dipahami. Jelasnya, badan intelijen Amerika melebih-lebihkan efektivitas pembunuh kecil mereka, R17. Atau mungkin mereka meremehkannya. Mereka pikir itu akan membuat orang-orang dari Bangsa besar Soviet di ambang kepunahan, kehilangan akal dan dilumpuhkan oleh penyakit...]],
