			['episode'] = {
				['6'] = [[Aku akan segera mati. Di sini aku hanya merepotkan mereka, yang memberiku makan, memberiku obat, dan aku seperti parasit, hanya duduk di sini tanpa melakukan apa-apa. Mereka sangat membuatku senang hari ini: mereka mengizinkanku mencuci pakaianku sendiri. Mereka bilang aku tidak boleh memaksakan diri ketika aku sakit.]],
				['15'] = [[Aku tidak pernah percaya sedetik pun bahwa kamu akan menepati janji.<new_line>Istriku hampir kehilangan akal ketika aku menceritakan tentang bengkel itu. Aku tidak akan mengizinkanmu, katanya. Sudah terlambat sekarang, orang lain bisa mencuci pakaian!]],
				['11'] = [[Apakah kamu akan segera membawakanku material atau apa? Sepertinya aku tidak bisa melakukan cuci perut lagi.]],
				['13'] = [[Tidak ada dinding.]],
				['2'] = [[Aku tidak pergi. Aku punya kamp yang penuh dengan penolong di sini. Mereka membawakanku pil, mereka membawakanku segelas air panas untuk menguap paru-paru. Dan ketika saatnya tiba, mereka akan menangisiku dan menguburku di dalam tanah. Hal terakhir yang aku butuhkan adalah penjaga lain.]],
				['1'] = {
					['2'] = [[Apa yang membuatmu melongo? Pergilah.]],
					['1'] = [[(Perhatianku tertuju pada seorang pria paruh baya berwajah pucat yang duduk di kursi kayu. Dia sedang mencuci pakaian di baskom timah di depannya.<new_line>Tiba-tiba dia mengeluarkan suara keras, batuk seperti menggonggong, lalu menutupi bibirnya dengan kain kasar berwarna abu-abu. Dia sadar aku memandanginya dan pipinya memerah)]]
				},
				['19'] = [[Kalian semua sangat pandai... Baiklah, terima kasih, kawan. Aku berutang budi karena bengkel itu sampai aku mati.<new_line>Jika kamu butuh tungku, pakai saja. Aku akan terus menyalakan api.]],
				['17'] = [[Baiklah, kamu bilang kamu bukan bandit – kamu bukan bandit. Kemari dan lihatlah. Aku hanya mengisi baskom cucian. Bagi istri, agar dia tahu bahwa ini bukan sepenuhnya bencana. Sekarang dia harus mencuci pakaian.<new_line>Bukankah itu cukup?]],
				['18'] = [[Kamu tetap akan memberiku rokok, kan? Aku belum melihat rokok dalam lima tahun ini. Aku bahkan tidak akan menghirupnya, aku hanya ingin merasakannya di mulutku. Tidak akan bahaya.]],
				['21'] = [[Terima kasih untuk semuanya, kawan! Aku merasa seperti hidupku kembali!]],
				['9'] = [[(Mata Ignat menyala sebentar, tapi kemudian dia menurunkan kepalanya lagi)<new_line><new_line>Aku tidak tahu, kawan. Sudah terlalu lama. Dan aku belum pernah bekerja di bengkel pandai besi di suatu negara. Aku melihat kakekku menempa sesuatu. Aku tidak bisa melakukannya.]],
				['8'] = [[Pekerja pengecoran, instalatur, bahkan di satu titik aku pernah menjadi tukang las. Aku bekerja dengan besi di sepanjang hidupku.]],
				['7'] = [[Aku dulu adalah pekerja tugas berat. Orang pertama di pabrik logam kami untuk mendapatkan gelar. Kemudian semuanya menjadi sia-sia. Wabah telah merenggut nyawa anak-anakku. Lalu bom jatuh. Aku pikir aku jauh dari bom itu, tapi tidak. Sekarang paru-paruku tidak berfungsi. Istriku adalah perawat bagiku, dia menyuapiku jika aku mengizinkannya. Tapi aku bukan orang tua, aku bahkan belum berumur empat puluh!]],
				['16'] = [[Terima kasih untuk semuanya, kawan. Kamu benar-benar membantuku, aku bahkan tidak tahu harus berkata apa. Andaikan semua bandit seperti kamu!]],
				['20'] = [[Oh! Sekarang kamu bicara. Terima kasih, kawan!<new_line>Jika kamu butuh tungku, pakai saja. Aku akan terus menyalakan api.]],
				['10'] = [[Aku akan menjadi pandai besi... Seperti kakek. Baiklah, kawan, ini adalah kesepakatan. Kami akan membangun bengkel pandai besi dan aku akan mengajarimu apa yang bisa aku ingat.]],
				['12'] = [[Tidak jauh dari sini, di tepi sungai, ada gudang tua. Bangunan kuat yang terbuat dari batu bata. Kamu tidak bisa mengharapkan ventilasi yang lebih baik.]],
				['3'] = [[Mungkin aku butuh, mungkin tidak. Itu bukan urusanmu. Dari mana kamu berasal? Kamu berasal dari kamp mana?]],
				['4'] = [[Seorang bandit, lalu (Ignat batuk lagi). Tidak ada lagi yang bisa bertahan di gurun. Hei, bandit, kamu merokok?]],
				['5'] = {
					['2'] = [[(Wajah Ignat menjadi abu-abu. Terengah-engah, dia menurunkan kepalanya dan menyeringai. Dia kembali ke kursinya dan duduk, melihat ke lantai. Satu menit kemudian, dia melihat ke arahku dengan agak memohon)<new_line><new_line>Hei, kawan, kemarilah.]],
					['1'] = [[(Tiba-tiba Ignat berdiri dan berjalan ke arahku. Dia mendekat dan meludah di lantai di kakinya, hampir kehilangan sepatuku)<new_line><new_line>Dan bagaimana jika aku membutuhkannya?]]
				},
				['14'] = [[Kamu baru saja mulai dan aku akan membantu.]]
			},
			['name'] = [[Ignat]]
		},
		['spiridon'] = {
			['choice'] = {
				['20'] = [[Terima kasih, kamu benar-benar membantu.]],
				['15'] = [[Itu kesepakatan!]],
				['4'] = [[Boris, mayor dari Stepnoy. Kami berdebat tentang apakah Kugel, insinyur yang menerima pengakuan resmi dari komite regional, akan mampu membangun sistem rudal antitank. Dan Boris berkata bahwa kamu pasti sudah kehilangan keahlianmu sejak lama.]],
				['21'] = [[Aku akan memberitahunya.]],
				['2'] = [[(memanipulasi)<new_line>Kasihan. Sepertinya Boris benar. Kamu semakin tua, insinyur.]],
				['1'] = [[Dan jika aku membawakanmu vodka? Barang yang sesungguhnya, dari penyulingan.]],
				['25'] = [[Baiklah, baiklah, aku tidak akan mengatakan apa pun padanya.]],
				['26'] = [[Halo.]],
				['19'] = [[Terima kasih, kamu benar-benar membantu.]],
				['17'] = [[Bikin santai aja.]],
				['18'] = [[Ayo, ayo.]],
				['28'] = [[Ya, di sini, ambillah. Sebagaimana disetujui, dengan bunga.]],
				['9'] = [[Hei, jangan pukul dia, dia sudah jujur! Aku di sini untuk urusan bisnis. Aku butuh bantuan.]],
				['27'] = [[Tidak, Spiridon, maaf. Aku belum mendapatkannya. Kamu tahu sendiri, kamu tidak akan menemukan benda itu tergeletak di pinggir jalan.]],
				['24'] = [[Empat? Kurang ajar kau. Akan kubawa secepat yang kubisa.]],
				['23'] = [[Di sini, tiga botol, seperti yang dijanjikan.]],
				['22'] = [[Dengar, aku minta maaf, aku tidak punya. Aku akan memberikannya padamu lain kali.]],
				['13'] = [[Aku tidak paham apa yang kamu bicarakan. Aku benar-benar butuh bantuanmu. Aku harus mencari dan mengumpulkan sesuatu. Di Ufa.]],
				['14'] = [[Dan di mana aku akan menemukan insinyur tentara lain?]],
				['3'] = [[Bahwa tidak ada alasan untuk membunuh para insinyur pintar yang sangat dibutuhkan negara... Sayang sekali. Aku harus berusaha sendiri.]],
				['5'] = [[Itu sudah lama sekali, kawan. Minumlah, kawan...]],
				['6'] = [[Tunggu, kamu salah orang. Aku di sini untuk menemui suamimu.]],
				['16'] = [[Ke Ufa? Baik, apa pun yang kamu katakan.]]
			},
			['episode'] = {
				['27'] = [[Kamu lagi? Sudah kubilang, aku tidak akan ke mana-mana, tidak mungkin, tidak akan!]],
				['24'] = [[(Spiridon menyapaku dengan suara yang sengaja dikeraskan, tampak curiga di pintu, yang Lyuba bersembunyi di belakang)<new_line>Halo, halo, halo!<new_line>(Lalu , menurunkan suaranya, dia melanjutkan)<new_line>Ya, sudah dapat vodkanya? Empat botol, ya?]],
				['23'] = [[(Setelah menyelesaikan percakapan singkat tapi tidak menyenangkan dengan Lyuba, aku bisa mengobrol dengan Spiridon lagi)]],
				['16'] = [[Ayolah, cepat, kamu tidak tahu apa yang akan dilakukan istriku padaku?]],
				['15'] = [[Begini ya, maaf. Dengar, Lyuba, di sana tidak ada bagus-bagusnya, tidak ada apa-apanya, yang ada hanya lebah di mana-mana. Aku tidak akan pergi ke Ufa, gila saja!]],
				['14'] = [[Yah, sepertinya, kamu mengingatkanku pada teman lamaku... Tunggu sebentar, apa yang barusan kamu katakan? Ufa? Di sana penuh dengan lebah!<new_line>Tidak mau, aku tidak akan pergi ke Ufa, aku masih ingin hidup! Cari orang bodoh lain saja.]],
				['12'] = [[(Sang istri menampar suaminya lagi, cukup keras untuk membuatnya menjerit)<new_line><new_line>Itu ucapan terima kasih untukmu! Enyahlah, kau bajingan, babi, dia menghancurkan seluruh hidupku, aku memberinya masa-masa terbaik dalam hidupku! Ibuku benar, Tuhan memberkati arwahnya, aku seharusnya menikahi Ptichkin... Lalu mungkin kamu akan mabuk sendiri sampai mati!]],
				['2'] = [[(Kugel membasahi bibirnya dan membuat suara yang keras)<new_line><new_line>Dari penyulingan? Itu bagus. Vodka buatan kami sudah habis.<new_line>Baiklah, sekarang bicaralah. Tiga botol, itulah kesepakatannya. Sementara itu, aku harus mencari tahu di mana aku bisa menyembunyikannya dari Lyuba.]],
				['1'] = [[(Para Kugel tinggal di lantai pertama blok lima lantai. Seorang wanita yang sangat gagah membukakan pintu untukku, ekspresi wajahnya galak)<new_line><new_line>Pulang sana! Keluar dari sini, pemabuk sialan! Kami sudah muak! Untuk terakhir kalinya, aku katakan, dia tidak akan ke mana-mana!]],
				['25'] = [[Aku mengerti... Baiklah, aku akan menunggu sedikit lebih lama.]],
				['26'] = [[Semoga berhasil. Bagaimana kabarmu? Baik? Ya, sampai jumpa, Lyuba tidak dalam suasana hati yang baik hari ini. Dia akan pergi demi kita berdua jika dia bisa.]],
				['19'] = [[Aku tidak butuh terima kasihmu. Bawakan vodka untukku.]],
				['20'] = [[Kamu tidak perlu mengantarku pulang. Aku melihat ada sepeda motor di garasi. Nyaris seperti baru, dan seri terbatas. Aku bisa pulang lebih cepat naik itu. Sudah kumodifikasi, jadi sekarang bahkan lebah tidak akan bisa menggangguku. Sampai jumpa!]],
				['17'] = [[Lihatlah. Cantik, kan? Tripodnya sedikit rusak, jadi kamu harus meletakkan batu bata di bawahnya. Koordinatornya tidak pas di tengah, tapi itu cacat desain, setiap pengiriman memiliki masalah yang sama. Dan itu tidak terpasang rapat di lekukannya, kamu harus memegangnya pelan-pelan. Dan lagi...]],
				['18'] = [[Ngomong-ngomong, beri tahu Boris kalau maha karya masih dipakai! Dia bisa datang berkunjung, Lyuba akan memanggang pai untuknya dan kita bisa duduk dan bernostalgia.]],
				['22'] = [[Terima kasih! Barang berkualitas tinggi! Aku bisa memanjakan para anak laki-laki besok. Bersikaplah layaknya teman dan jangan katakan apa pun di depan istriku. Pukulannya sakit. Siapa yang akan tahu? Dan dia dulu manis seperti anak domba. Eh, itu adalah hari-hari...]],
				['9'] = [[Oh, sekarang aku percaya! Dia juga tidak mengenalmu! Mau membodohiku lagi?<new_line><new_line>(Lyuba Kugal menampar keras suaminya)]],
				['8'] = [[(Seorang pria yang jauh lebih kecil mengintip dari balik wanita besar itu. Dia memiliki wajah yang cerdas tapi tampak lelah)<new_line><new_line>Lyuba, tunggu. Aku tidak kenal dia, dia bukan dari pemukiman kami.]],
				['7'] = [[Aku tahu kenapa kamu di sini! Untuk mabuk, mengaum, dan berteriak, itulah tujuanmu di sini! Jadi, kamu akan pergi atau minta bantuan? Aku bisa melakukannya! Aku akan membantumu...]],
				['21'] = [[(Kugel menggelengkan kepalanya penuh celaan)<new_line><new_line>Baiklah, kawan, lain kali saja. Kamu tidak ingin aku membongkar sistemnya lagi, kan? Hanya saja kamu sudah berutang empat botol kepadaku.]],
				['10'] = [[Minta bantuan? Apa bagusnya dia kepada orang-orang, pemabuk? Tidak ada sama sekali, dia hanya duduk di rumah! Spiridon, kembali ke dapur!]],
				['11'] = [[Apa maksudmu, apa bagusnya aku? Negara ini selalu membutuhkan insinyur yang baik! Aku bahkan punya sertifikat ucapan terima kasih dari komite partai regional!]],
				['3'] = [[Boris siapa? Apa yang benar tentangnya?]],
				['4'] = [[Ah... Maafkan aku... kamu mengerti ... Aku tidak bisa meninggalkan Lyuba di sini sendiri ... Tapi hubungi jika ada sesuatu.]],
				['5'] = [[Sistem rudal? Apa? Aku bisa memperbaiki tangki dengan mata tertutup! Aku bahkan diundang ke Severodvinsk untuk membangun kapal selam nuklir!]],
				['6'] = [[Omong kosong! Keahlian tidak bisa hilang! Dan di bidangku, usia adalah keuntungan. Kamu melihat anak-anak muda hari ini, mereka hanyalah simpanse dengan tulang kering, banyak dari mereka!<new_line>Bersiaplah, kita akan pergi ke Ufa!]],
				['13'] = [[(Lyuba membanting pintu, mengunci dirinya di dapur. Spiridon Kugel mengerutkan kening kepadaku)<new_line><new_line>Dia sudah marah-marah sejak pagi ini karena suatu alasan. Asal kamu tahu, beri tahu orang-orang bahwa aku tidak akan keluar hari ini. Aku tidak bisa, kamu bisa lihat sendiri. Akan kulakukan besok, pasti!]]
			},
			['name'] = [[Spiridon Kugel]]
