		['gavrilov'] = {
			['choice'] = {
				['20'] = [[Kami mengerti satu sama lain.]],
				['10'] = [[Walaupun demikian... Aku ingin tahu kebenarannya.]],
				['11'] = [[Itu urusan saya.]],
				['3'] = [[(Aku batuk untuk mencuri perhatian mereka)]],
				['2'] = [[Baiklah, kita akan membicarakannya di sana.]],
				['1'] = [[Itu tidak penting. Aku harus membicarakan sesuatu denganmu. Tentang ilmuwan dari Institut Virologi.]],
				['19'] = [[Tidak. Aku melihat serigala-serigala itu dan aku tahu kemampuan mereka. Aku bersedia mempertaruhkan hidupku, tapi apa yang kamu usulkan – masuk dalam sarang berjumlah besar – itu sama saja bunuh diri langsung, bukan risiko yang diperhitungkan.]],
				['17'] = [[Aku ikut. Ini harus diselesaikan. Serigala-serigala hitam itu terlalu bahaya.]],
				['18'] = [[Jika kamu benar-benar merasa kacau... Baiklah, aku bersamamu.]],
				['6'] = [[Bagaimana bisa aku membuktikannya? Aku tidak tahu... Aku dapat membantumu membawa tubuhnya. Menjaganya dari serigala.]],
				['9'] = [[Aku ingin tahu tentang para ilmuwan yang dieksekusi. Tentang wabah yang terjadi secara bersamaan di seluruh negeri. Dan tentang 17 Agustus 1984. Day R.]],
				['8'] = [[...]],
				['7'] = [[Tunggu, jangan pergi. Kamu adalah Pavel Gavrilov, penyelidik terdahulu, kan?]],
				['5'] = [[(Bohong) Aku dari Tver.]],
				['4'] = [[Mudah, aku adalah seorang teman.]],
				['15'] = [[Tidak. Aku tidak tahu.]],
				['14'] = [[Dan apa yang kamu mau dariku?]],
				['12'] = [[Aku sudah melalui banyak hal untuk sampai di sini. Aku mempertaruhkan hidupku.]],
				['16'] = [[Aku mengerti. Tapi mengapa kamu tidak bakar saja serigala betina bersama dengan rumah jagal itu? Atau mengusirnya dengan asap?]],
				['21'] = [[Tidak. Masih belum merasa ingin bunuh diri.]],
				['13'] = [[Janjiku? Apa yang kamu bicarakan?]]
			},
			['episode'] = {
				['1'] = [[Nikita, aku mengerti apa yang kamu rasakan, tapi kita harus pergi. Kita akan membawa pulang Kirill dan menguburkannya.<new_line>Nikita, apakah kamu mendengarkanku? Kita pergi. Sekarang. Kita akan pulang ke Gorenichi. Di sini terlalu bahaya. Dan Seva hampir tidak bisa berjalan, dia harus ke dokter!]],
				['12'] = [[Dari mana kamu dengar itu?]],
				['11'] = [[(Mata tidak percaya Gavrilov mengamatiku dari ujung kepala hingga ujung kaki)<new_line><new_line>Tidak, kami akan mengaturnya sendiri.]],
				['20'] = [[Dulu di Kiev, kamu mengatakan bahwa kamu akan membantu kami menghadapi para serigala. Jadi buktikanlah. Lalu kami akan memberitahumu semuanya.]],
				['15'] = [[Mengapa diam saja? Apa yang ingin kamu bicarakan denganku?]],
				['4'] = [[(Pemuda itu mengangkat matanya yang kebingungan dan memerah peda pria itu dan tiba-tiba melompat)<new_line><new_line>Kita akan pergi?! Ini terlalu bahaya? Apa yang kamu katakan, Gavrilov? Kita harus melanjutkannya! Masih ada beberapa monster di luar sana! Kita punya peluru, cukup untuk satu atau dua.]],
				['13'] = {
					['2'] = [[Inilah dia. Anggap saja rumah sendiri, seperti yang mereka katakan. Tapi sebelumnya, izinkan aku memperkenalkan temanku. Dia juga ingin berbicara denganmu. Timofey Zaitsev, mantan direktur Institut Virologi. Seperti aku, dia ingin pergi ke Polandia, tapi dia tidak berhasil melakukannya tepat waktu.]],
					['1'] = [[(Pavel Gavrilov menungguku di kantor manajer pusat komunitas sebelumnya. Ada orang lain di ruangan itu bersamanya, seorang pria berusia enam puluhan, tertidur di kursi berlengan di dekat tungku. Ketika dia melihatku, Gavrilov mengisi segelas air, membangunkan pria tua itu dan memberinya pil untuk diminum)]]
				},
				['23'] = [[Kami sudah mencoba. Ini berarti kami tidak boleh pergi.<new_line>Jadi, apakah kamu akan melakukannya? Jika kamu membantu, kamu akan menyelamatkan banyak nyawa. Dan kami akan memberitahumu semuanya. Jika kamu tidak membantu, kamu harus mencari orang lain.]],
				['24'] = [[Ide bagus. Dilihat dari peralatanmu, kamu adalah petarung yang berpengalaman, jadi kesempatan kami akan lebih baik jika kamu bersama kami. Saat kamu siap untuk berburu, dapatkan amunisi, makanan, dan minuman, serta bergerak ke titik awal. Aku akan menunggu di sana.]],
				['25'] = [[(Mulut Gavrilov tersenyum, seolah dia mengharapkan respons itu)<new_line><new_line>Tentu terserah padamu, tapi itu berarti bibirku tetap tertutup rapat. Kesepakatan adalah kesepakatan. Jika kamu berubah pikiran – jangan ragu untuk kembali. Kami masih punya sedikit waktu.]],
				['26'] = [[Baik? Sudahkah kamu memikirkannya? Tawaranku masih bagus.]],
				['19'] = [[(Penyelidik terdahulu merenungkan hal ini, menatapku dari bawah alisnya)<new_line><new_line>Ini adalah kesepakatan. Kamu akan tahu kebenaran seluruhnya. Tapi kamu harus memenuhi janjimu terlebih dahulu.]],
				['10'] = [[Apa, kau datang jauh-jauh ke sini dari Moskow? Dan mengapa kami harus mempercayaimu? Aku juga tahu cara berbicara permainan yang bagus.]],
				['17'] = [[Kebenarannya? Dan apa yang akan kamu lakukan dengan kebenaran ini, orang gembel tak punya nama? Memberitahukannya kepada semua orang?]],
				['18'] = [[Baiklah (dia menautkan tangannya di belakang punggungnya dan mulai berjalan di sekitar ruangan). Dan apa yang ingin kamu lakukan untuk mengetahui kebenaran ini? Apakah kamu akan mempertaruhkan hidupmu, misalnya?]],
				['6'] = {
					['2'] = [[Kamu... kamu pengecut! Kirill mengorbankan dirinya sendiri sehingga serigala tidak menangkapmu, Gavrilov! Apa, tidak bisa memegang kebenaran? Jadi apa yang akan kita lakukan sekarang adalah pergi ke sana dan membantai semua serigala itu!]],
					['1'] = [[(Pemuda itu tampak mengamati Gavrilov dan pria kedua dengan teliti, kemudian perlahan menarik pistol dan mengarahkannya pada mereka)]]
				},
				['9'] = [[Teman? Entah bagaimana aku tidak mengingatmu. Kamu berasal dari kamp apa?]],
				['8'] = [[(Mereka bertiga langsung berbalik padaku. Sekarang ketiga Makarov menunjuk lurus ke perutku)<new_line><new_line>Hei! Siapa kamu?]],
				['7'] = [[(Pria itu membungkukkan pria muda sembari mengancam)<new_line><new_line>Jika seseorang tidak mengambil pistol terakhir dan mengejar serigala itu seperti orang tolol, Kirill pasti masih hidup!]],
				['5'] = [[Melanjutkan? Hanya tinggal kita bertiga! Dan kita hampir tidak selamat dengan empat orang. Kamu tetap bisa membalas ayah tirimu. Kamu akan mendapatkan kesempatan. Tapi tidak sekarang. Sekarang kita harus kembali!]],
				['3'] = [[Nikita, kami akan kembali. Kamu bisa ikut dengan kami atau membunuh serigala-serigala itu sendiri.]],
				['2'] = [[(Mata Gavrilov menyipit)<new_line><new_line>Kamu telah menggugah minat saya. Baiklah, kita akan membicarakannya. Saat kita kembali. Gorenichi, pusat komunitas.]],
				['21'] = [[Tidak ada yang memberatkan. Ikut saja dalam pemburuan ini. Mungkin kamu tidak tahu apa yang terjadi di sini, kan?]],
				['14'] = [[(Aku mendengar nama itu dan merasakan percikan semangat. Direktur Institut Virologi! Sepertinya sekarang aku akan mendapatkan jawaban atas semua pertanyaanku)]],
				['16'] = [[(Penyelidik dan direktur saling memandang dengan diam. Zaitsev mengerutkan kening dan hendak berbicara, tapi Gavrilov memberi isyarat agar dia tetap diam. Dia mendekat kepadaku dan melakukan pemeriksaan dari ujung kepala hingga ujung kaki)<new_line><new_line>Day R? Mengapa kamu ingin tahu? Apakah mengetahuinya akan mengubah sesuatu? Bagaimanapun juga, itu sudah lama sekali.]],
				['22'] = {
					['2'] = [[Kami melindungi diri sebaik mungkin, tapi jumlah serigala itu terus bertambah sementara kami semakin banyak kehilangan anggota. Minggu lalu, serigala-serigala itu masuk ke suatu rumah di malam hari dan menyerang satu keluarga. Kami mengirim seluruh pasukan untuk menyerang binatang itu, tapi tidak seorang pun yang kembali.]],
					['1'] = [[Hewan-hewan ini, serigala hitam, muncul di tanah kami sekitar tiga tahun yang lalu. Seekor serigala betina dan dua anaknya, besar, hitam seperti malam, dan buta. Dua tahun pertama masih tenang. Tapi kemudian semua permainan liar hilang dari hutan. Mungkin serigala itu terkena rabies dari suatu tempat, atau penyakit lain, tapi bagaimanapun juga, para serigala itu mulai memburu manusia.]],
					['4'] = [[Kami tidak bisa menunda lebih lama lagi. Serigala betina bersembunyi di rumah jagal tua. Kami akan menyerangnya di sana. Ini adalah satu-satunya kesempatan kami untuk memusnahkan mereka sekali dan untuk selamanya.]],
					['3'] = [[Tapi kami masih punya harapan. Semua serigala yang kami bunuh adalah serigala jantan. Itu berarti jika kami membunuh serigala betina, tidak akan ada bayi baru yang lahir.]]
				},
				['27'] = [[Terserah. Aku kira inilah cara bertahan hidup yang lama bagi para penyendiri.]]
			},
			['name'] = [[Gavrilov]]
		},
		['sasha'] = {
			['choice'] = {
				['31'] = [[Itu gila...]],
				['15'] = [[Siapa yang melakukan ini?]],
				['11'] = [[Aku bisa membantumu melarikan diri.]],
				['13'] = [[Kamu tidak bisa membunuhku. Hal terakhir yang kuingat adalah keheningan. Kamu ragu-ragu. Kamu bisa langsung membunuhku dan lari, tapi kamu malah diam. Boris datang dan melukaimu.]],
				['23'] = [[Itulah yang kupikirkan. Jadi jika kamu tidak ingin membahasnya, beri tahu aku tentang apa yang terjadi padamu.]],
				['33'] = [[Kamu salah. Aku bisa mengerti dari mana kamu berasal. Kamu menginginkan rasa hormat dan pengakuan, kamu ingin bebas. Dan teman-teman barumu memberimu itu.]],
				['25'] = [[Dan setelah itu?]],
				['35'] = [[Bukankah itu sedikit ambisius? Hanya tersisa tiga atau empat orang dari kalian.]],
				['19'] = [[Siapa yang punya kunci penjara?!]],
				['29'] = [[(Biarkan dia tenang)]],
				['17'] = [[Tapi kenapa...]],
				['27'] = [[Boris tidak pernah memberitahuku bahwa kamu dicampakkan.]],
				['37'] = [[Dia berbicara kepadamu tentang pertempuran itu?]],
				['39'] = [[Kamu tidak sadar kalau dia mencoba untuk memengaruhimu?]],
				['44'] = [[Dan Ruslan membebaskan mereka?]],
				['43'] = [[Apa yang kamu bicarakan?]],
				['42'] = [[Dan kamu ingin menjadi serigala? Bandit?]],
				['41'] = [[Dia hina! Penjahat! Dia merenggut nyawa orang-orang tak berdosa di tangannya!]],
				['40'] = [[Kamu mulai percaya padanya?]],
				['21'] = [[Bisa kita bicara?]],
				['16'] = [[Lenganmu. Mereka memukulmu?]],
				['32'] = [[Kamu senang dengan apa yang kamu pilih?]],
				['22'] = [[Tentang apa? Mungkin tentang bagaimana kamu ingin membunuhku dan bagaimana aku menyelamatkanmu dari regu tembak?]],
				['30'] = [[Kamu masih belum menceritakan semuanya kepadaku.]],
				['2'] = [[(Menembak gadis itu)]],
				['24'] = [[Berhentilah bercanda. Katakan padaku apa yang terjadi setelah kamu membunuh bandit di rumah kakekmu.]],
				['36'] = [[Kamu akan membunuhku?]],
				['26'] = [[Kamu menangkis serangan itu?]],
				['34'] = [[Apa yang tidak akan kumengerti?]],
				['12'] = [[Seharusnya kamu jangan putus asa, kamu harus menembak saat ada kesempatan. Tapi kamu tidak bisa.]],
				['28'] = [[Tenang!]],
				['18'] = [[Siapa yang punya kunci penjara?]],
				['6'] = [[Sasha, Ayah minta maaf, maafkan Ayah...]],
				['9'] = [[Sasha...]],
				['8'] = [[Sasha... tunggu...]],
				['7'] = [[Mereka... mereka bilang Ayah dipenjara. Aku... Aku berusaha... menyelamatkan Ayah...]],
				['5'] = [[Sasha, aku... Aku tahu ini salahku. Ini salahku sehingga kamu, ibumu, dan...]],
				['4'] = [[Sasha... Aku sudah lama mencarimu...]],
				['3'] = [[Sasha?]],
				['10'] = [[Sasha?]],
				['20'] = [[Kamu keluar malam ini. Bersiaplah.]],
				['38'] = [[Apakah dia menekanmu?]],
				['1'] = [[Jangan takut padaku. Aku akan mengeluarkanmu dari sini!]],
				['14'] = [[Kamu tidak bisa melakukannya.]]
			},
			['episode'] = {
				['31'] = {
					['2'] = [[Aku cocok sekali dengan regu itu. Mereka bilang bahwa mereka menghormatiku, bahwa aku adalah anggota mereka. Dan aku, dengan segala ketololanku, sengaja menjadi sasaran peluru untuk menunjukkan kepada mereka betapa besar nyaliku. (Sasha mengatupkan giginya), Tuhan, aku sangat bodoh ...]],
					['1'] = [[Kami melawan mereka. Setelah itu, pistolku dikembalikan, mereka membiarkanku ikut ekspedisi berburu, dan mereka bahkan memasukkanku dalam regu tempur. Komandan regunya Vasily. Ya, anak laki-laki Boris.]],
					['4'] = [[Kutembak empat dari mereka. Tapi, lalu amunisiku habis dan mereka menangkapku. Mereka ingin membunuhku di tempat, tapi pemimpin mereka menghentikannya. Dia menyuruh mereka untuk membawaku ke markas mereka.]],
					['3'] = [[Singkat cerita, kisah indah berakhir dengan cepat. Kami harus berurusan dengan para Abakan. Ya, mereka menyergap kami. Tapi jumlah kami lebih banyak dan senjata kami lebih baik. Tapi Vasily si babi pengecut itu memerintahkan kami untuk mundur. Meskipun dia tahu bahwa aku sudah terpisah dari regu lainnya.]]
				},
				['15'] = {
					['2'] = [[(Tawa histeris Sasha tiba-tiba berubah menjadi isakan tangis. Aku pernah mendengar suara ini sebelumnya: dulu, ketika aku memberi tahu putriku bahwa aku harus pergi untuk selamanya. Sasha kembali mengendalikan dirinya, menahan air matanya dengan raungan marah dan menjauh dari bilah)]],
					['1'] = [[(Sasha tertawa terbahak-bahak, air mata mengalir di wajahnya)<new_line><new_line>Dan Boris menyebutku pengkhianat kotor?! Aku?! Ayah ratusan kali lebih buruk! Pengkhianatan bagi Ayah semudah bernafas! Boris menyelamatkan nyawa Ayah, dan sekarang Ayah ingin membantu tahanannya melarikan diri?!]]
				},
				['11'] = [[(Penglihatanku mulai kabur dan aku mendengar suara menderu di telingaku saat tubuhku semakin lemah karena kehabisan darah. Sasha mengangkat senapan lagi)<new_line><new_line>Sayang sekali rumah Kakek terbakar. Ayah pasti sudah melihat banyak foto Ayah di sana, iya kan ayah tersayang. Gambar-gambar yang dibuat oleh gadis kecilmu. Dan di setiap... satu... gambar... Ayah sekarat...]],
				['13'] = [[(Di ruang bawah tanah yang gelap, semuanya sangat basah dan dingin, bahkan tikus tidak akan hidup di dalamnya. Dengan cahaya obor, di luar jeruji tebal, aku melihat tiga bangku kayu yang kokoh. Sasha sedang duduk di salah satu bangku itu. Penjaga itu membiarkanku naik ke sel, sementara dia pergi untuk merokok di luar)]],
				['23'] = [[Boris. Apa yang kamu rencanakan?]],
				['33'] = [[(Terdengar suara tembakan dari jauh yang berasal dari arah Novokuznetsk. Lalu satu lagi. Aku mengambil pistolku dan mendaki bukit terdekat, tempat aku mengintip ke kejauhan. Tanah padang gurun abu-abu terbentang di depanku, gersang seperti biasanya.<new_line>Ketika aku kembali ke Sasha, dia sudah tertidur)]],
				['25'] = [[(Sasha tidak mengerang lagi. Sekarang dia tampak mengalami kelelahan yang aneh, matanya berkeliaran bolak-balik tak menentu)]],
				['35'] = {
					['2'] = [[Lalu akhirnya, pemimpin mereka datang menemuiku, Ruslan. Kemudian aku tahu bahwa dia pernah menjadi tahanan politik. Dia diam dalam waktu yang lama, dan kemudian tiba-tiba dia bertanya kepadaku apa kesalahanku, sehingga rekan-rekanku meninggalkan aku.]],
					['1'] = [[Baiklah, dengarkan dulu (dia setuju dengan lelah). Mereka membawaku ke pangkalan Abakan. Mengunciku. Kupikir mereka akan melecehkanku. Tetapi mereka tidak melakukannya. Minggu pertama, seorang gadis datang setiap hari untuk membawakanku makanan. Aku tidak melihat orang lain.]]
				},
				['19'] = [[Melakukan apa?]],
				['29'] = {
					['2'] = [[Tapi itu benar-benar menjijikkan, pekerjaan di dapur. Aku menolak bekerja di sana. Gadis-gadis di sana mulai menyimpan dendam padaku dan mengeluh tentang 'kemalasanku' kepada pemimpin itu. Tapi bajingan itu hanya berkata, 'Biarkan saja, dia akan datang setelah beberapa hari tidak mendapat jatah...']],
					['1'] = [[Aneh. Seingatku, kamu tidak pernah tertarik dengan hidupku sebelumnya. (Sasha menggelengkan kepalanya) Baik. Penduduk Novokuznetsk membawaku ke kamp mereka. Tapi mereka tidak membiarkanku melawan, mereka bahkan mengambil pistol kakekku. Kudatangi mayornya, tapi katanya tidak ada tempat untuk seorang gadis di antara para pejuangnya. Dia menugaskanku untuk melakukan tugas rumah tangga.]]
				},
				['17'] = [[Dasar bodoh, aku... Aku menikmati momen itu!]],
				['27'] = [[(Sasha berbalik)<new_line><new_line>Bukan topik yang bagus.]],
				['34'] = [[(Sasha jauh lebih baik. Dia bisa berdiri dan bahkan berjalan sambil bersandar di lenganku sebagai tumpuan)]],
				['37'] = [[Tidak masalah apa yang aku katakan. Kamu masih tidak akan mengerti.]],
				['47'] = [[Tidak, Ayah. Kami sama sekali bukan bandit. Kami jauh lebih menarik.]],
				['39'] = [[Perbedaan antara hidup dan bertahan hidup! (Mata Sasha tampak gelisah) Kata 'misi,' apa artinya untukmu?<new_line>Kami bukan bandit, Ayah. Kami penjajah. Kami merobek sisa-sisa dunia lama yang busuk dari muka bumi dan membangun dunia baru, yang jauh lebih baik dan lebih adil.<new_line>]],
				['49'] = {
					['2'] = [[(Jeda)<new_line><new_line>Bahkan membunuh monster yang membunuh Kakek tidaklah semanis pembunuhan itu. Kusaksikan mereka bertiga kehabisan darah dan aku merasa bebas. Senang. Kuat. Bukannya haus darah, atau balas dendam atas pengkhianatan mereka. Itu keadilan.]],
					['1'] = [[Ya. Tapi mereka tidak berhasil melakukannya tepat waktu. Kuambil pisau Ruslan dan kugorok leher mereka.]]
				},
				['21'] = [[Dia ingin tahu siapa yang menghancurkan harga diri dan kegembiraannya, Vasily. Aku tidak memberitahunya.<new_line><new_line>(Sasha tertawa miris)<new_line><new_line>Kalau terpaksa, aku akan memberi tahu dia semuanya. Secara terperinci. Sudah senang? Kalau begitu pergilah dari sini.]],
				['46'] = {
					['2'] = [[Lihatlah sekeliling. Tidak ada negara yang tersisa. Tidak ada kandang yang aman, di mana kamu bisa mengunyah tanamanmu dengan tenang dan menumbuhkan wol yang bagus dan tebal untuk cukur. Kita ada di hutan. Dan di sini, kamu adalah seekor domba... atau serigala.]],
					['1'] = [[(Sasha menanggapi dengan tawa sinis)<new_line><new_line>Orang-orang tak berdosa? Orang-orang tak berdosa?! Di mana kamu melihat orang-orang tak berdosa ini?! Atau apakah itu yang kamu sebut domba yang malas, serakah, dan bodoh yang hanya mengembik sia-sia saat dibantai?]]
				},
				['45'] = [[Tidak. Tidak kalau begitu. Tapi aku sadar bahwa dia memahamiku. Dia jauh lebih memahamiku dibanding para bangsat dari Novokuznetsk. Orang-orang yang... yang memanfaatkanku dan membuangku seperti sampah.]],
				['44'] = [[Aku tahu. Tapi asal kamu tahu, apa yang dikatakannya itu benar. Aku juga tahu itu. Dia datang menemuiku setiap hari selama seminggu. Dia memberitahuku tentang dia dan keluarganya. Dan kemudian tentang beberapa pejuang lainnya. Lalu aku bercerita tentang diriku sendiri. Dia benar-benar mendengarkanku, dia bertanya kepadaku.]],
				['43'] = [[Tidak, dia hanya merokok dan berbicara. Dia kembali pada hari kedua. Dia berkata bahwa warga di tempatku menolak membayar tebusan untukku. Dan yang dia minta hanyalah beberapa senapan dan peti kaleng. Katanya tidak ada satu pun upaya untuk menyelamatkanku. Dan sejenisnya. Setelah itu, dia membawakan makanan untukku. Porsi yang jauh lebih besar dari yang didapatkan penduduk kami. Dan rasanya lebih enak.]],
				['42'] = [[Ya. Katanya aku punya nyali, bahwa aku setara dengan sepuluh pejuangnya. Bahwa dia tidak akan pernah melakukan apa yang telah dilakukan Vasily dan membiarkan seorang prajurit melawan seluruh pasukan seorang diri. Dia mengatakan kamp Novokuznetsk memiliki setiap peluang kemenangan, tetapi mereka kehilangan keberanian mereka. Dia mengatakan banyak hal seperti itu. Aku tetap diam. Aku ingin menentangnya, tapi ...]],
				['41'] = {
					['2'] = [[(Salah satu pria membantu Sasha berdiri. Sebuah mesin bergemuruh di semak-semak, dan UAZ tua muncul di tepi sungai berbatu)<new_line><new_line>Kita imbang. Nyawa dibayar nyawa.<new_line>Sekarang pergilah. Pergilah yang jauh, yang cukup jauh, sehingga kita tidak akan pernah berpapasan lagi. Aku tidak ingin melihatmu lagi, atau mendengar suaramu lagi.]],
					['1'] = [[Tidak. Kau menyelamatkanku, meski aku mencoba membunuhmu. Jadi kami akan menunjukkan belas kasihan kepadamu. Itu adil. Keadilan di atas segalanya.<new_line>Ruslan, tolong aku.]],
					['3'] = [[(Ruslan dengan hati-hati mengangkat Sasha ke dalam mobil dan kemudian masuk ke sebelah pengemudi. Bandit ketiga hendak menutup pintu, tetapi Sasha menahan pintunya)<new_line><new_line>Aku tidak akan pernah lupa dan aku tidak akan pernah memaafkanmu karena meninggalkan ibu dan aku. Jika tidak, semua ini tidak akan terjadi. Kamu mengerti? Bukan ini!]]
				},
				['40'] = [[Tidak, jumlah kami banyak sekali. Ribuan. Dan akan segera menjadi lebih banyak lagi. (Sasha menyeringai tiba-tiba) Lagi pula, tiga atau empat orang mampu melakukan beberapa hal. Di belakangmu!<new_line><new_line>(Aku berbalik. Dua bandit berdiri beberapa meter dariku, membidik kepalaku)]],
				['16'] = [[Hah?! Apa lagi ini?]],
				['32'] = [[Ya benar, seolah dia akan segera mengucap sepatah kata kepada miliknya yang berharga. Kamu tahu, dia...]],
				['14'] = [[(Mendengar suaraku, Sasha mengangkat kepalanya dan menyeringai, seolah-olah karena sakit gigi yang tiba-tiba)<new_line><new_line>Ayah?! Aku berharap Ayah mati!]],
				['30'] = [[Lalu... lalu mereka menyerang kami. Di malam hari. Puluhan orang sudah terluka saat alarm membangunkan semuanya. Beberapa bandit berhasil melewati gerbang, para gadis ketakutan karena tidak tahu harus berbuat apa, tapi aku mengalahkan salah satu bajingan itu, kuambil senjatanya dan, kutembak sisanya.]],
				['2'] = [[Jangan mendekat! Selangkah lagi dan aku akan mengeluarkan isi perutmu!]],
				['1'] = [[(Aku menaiki lantai atas. Tubuh para pejuang kami tercabik-cabik, dan di antara mereka ada seorang gadis yang terluka. Wajahnya tertutup campuran cat kamuflase dan darah. Ketika dia melihatku, dia menarik pisau tipis dengan pegangan ungu)]],
				['36'] = [[Keesokan harinya, Ruslan kembali dan meletakkan sebuah pistol di depanku. Dia mengatakan bahwa dia tidak akan memberiku makan gratis lagi. Bahwa dia tidak membutuhkan tahanan, yang dia butuhkan adalah pejuang. Dia memberiku pilihan untuk bergabung dengannya atau kembali ke Novokuznetsk. Dan aku menentukan pilihanku.]],
				['26'] = [[(Sasha sudah muak melihatku dan menghela napas berat)<new_line><new_line>Tentang apa?]],
				['22'] = [[(Sasha tidak bereaksi)]],
				['6'] = [[(Sasha menatapku penuh tanya. Tatapannya menjadi lebih tajam. Akhirnya, wajahnya menangkap sesuatu. Seketika berubah menjadi kebencian yang membara yang belum pernah kulihat sebelumnya. Aku tidak pernah melihat seseorang menatap orang lain seperti itu)<new_line><new_line>Hei. Ayah kembali.]],
				['28'] = [[Apa yang terjadi padaku? Yah, pertama, ibu melahirkanku.]],
				['18'] = [[(Sasha mendengus dan membalikkan punggungnya. Cahaya obor menerangi tangannya sebentar, yang dipenuhi memar. Memar segar, sangat segar)<new_line><new_line>Anngap saja jika itu membuatmu bahagia. Aku tidak peduli. Besok mereka akan menembakku.]],
				['5'] = [[(Gadis itu meludah dan mengusap tetesan darah yang mengalir di wajahnya. Kemudian dia mengambil senapan dari salah satu mayat dan membidik.<new_line>Saat itu aku melihat wajahnya secara utuh.<new_line>Tidak...<new_line>Tidak mungkin...)]],
				['9'] = [[(Bibir Sasha mengerut muak)<new_line><new_line>Euhh. Tapi Ayah tetap melakukannya dan menangis. Mungkin itu akan membantu. Mungkin aku akan memaafkan Ayah. Oh, ya, aku hampir lupa. Pisauku ada pada Ayah. Sekarang aku akan mengambilnya kembali.<new_line><new_line>(Sasha menggenggam pegangan ungu dan menarik belati dari tubuhku. Darah menyembur dari lubang tusukan)]],
				['8'] = [[Ayah, ayolah, jangan konyol. Kenapa kamu minta maaf? Ibu meninggal karena Ayah. Masalah besar. Dan Paman Arthur dan Bibi Marina. Dan, Kakek meninggal. Tapi dia memang sudah tua. Dan sekarang Ayah muncul dan membunuh teman-temanku. Tapi, aku selalu bisa mencari teman baru! Benar kan? Benar kan?]],
				['7'] = [[(Sasha mendekatkan wajahnya hingga hidung kami hampir bersentuhan. Dia tersenyum manis)<new_line><new_line>Ah, Ayah mencariku? Maaf, tapi Ayah sedikit terlambat.]],
				['4'] = [[(Aku menembaknya, tapi dia menghindar, lalu melayang ke arahku dengan ketangkasan luar biasa dan menusukkan pisau ke tubuhku<new_line>Aku terengah-engah, menjatuhkan senjataku dan jatuh ke lantai. Gadis itu menendang wajahku)<new_line><new_line>Bengkok seperti yang lain!<new_line><new_line>(dia menunjuk tubuh-tubuh yang terkoyak)]],
				['3'] = {
					['2'] = [[Kamu tidak pernah tahu. Aku seorang gadis, itu artinya aku hanyalah korban yang tidak berdaya, kan?]],
					['1'] = [[(Kemarahan sirna dari wajahnya seperti topeng. Gadis itu tersentak karena batuk dan dia mengangguk, menurunkan belatinya. Aku mendekat dan mengulurkan tanganku.<new_line>Detik berikutnya, dia menusukkan pisau ke tubuhku.<new_line>Aku terengah-engah, menjatuhkan senjataku dan jatuh ke lantai. Gadis itu melompat dan menendang wajahku)]]
				},
				['10'] = [[(Dia terdiam beberapa saat dan mencondongkan tubuhnya ke arahku lagi)<new_line><new_line>Ayah bahkan tidak bisa membayangkan, betapa aku akan menikmati saat aku menaruh peluru di otak Ayah. Berulang kali, sampai tidak ada lagi peluru di seluruh bangunan sialan ini.]],
				['20'] = [[Dan mengapa mereka tidak akan melakukannya? Kamu pikir mereka akan mengasihaniku, Judas? (nada pahit muncul dalam suaranya selama sesaat) Yah, Boris tidak mengasihaniku.]],
				['48'] = {
					['2'] = [[Ruslan memberi mereka tawaran: beri tahu dia semua yang mereka tahu tentang Stepnoy – orang-orangnya, gudang senjatanya, titik-titik lemah di bentengnya - dan sebagai gantinya, Ruslan akan melepaskannya.<new_line>Dia bahkan belum selesai berbicara ketika pengecut yang pucat dan ketakutan itu menceritakan semuanya padanya.]],
					['1'] = [[Aku belum selesai. Seminggu kemudian, beberapa 'orang tak berdosa” dari tempatmu diseret ke kamarku - Vasily dan dua orang lainnya dari Novokuznetsk.]]
				},
				['38'] = [[Rasa hormat, pengakuan... Tapi itu baru di awal. Kemudian, mereka memberiku lebih banyak lagi. Hal-hal yang tidak akan pernah aku dapatkan dari orang-orang Novokuznetskmu.]],
				['24'] = [[(Sasha duduk jauh di dalam selnya di penjara darurat dan menatapku dengan pandangan yang agak sinis. Sulit untuk tahu apakah dia memercayaiku atau tidak)]],
				['12'] = [[(Genangan darah di kakiku sudah seperti lautan. Lemah dan tak berdaya, yang bisa aku lakukan hanyalah menunggu. Detik demi detik berlalu bagaikan berjam-jam...)]]
			},
			['name'] = [[Sasha]]
		},
		['svetlana'] = {
			['choice'] = {
				['31'] = [[Svetlana, walaupun kamu benar, jika wabah itu buatan manusia, semacam konspirasi... bagaimana caramu mengungkap kebenaran itu? Bertahun-tahun telah berlalu. Ini gila.]],
				['15'] = [[(Diam)]],
				['11'] = [[Itu yang aku katakan, aku tidak tahu. Aku terbangun di Nikel, tidak tahu siapa diriku, dari mana aku berasal, atau bagaimana aku bisa sampai di sana. Dan aku dikelilingi oleh mayat dan reruntuhan yang membusuk... Aku mulai mencari jawaban. Untuk orang-orang yang bisa memberi aku semacam penjelasan. Tapi tidak ada yang tahu apa yang terjadi, atau mengapa.]],
				['13'] = [[Svetlana, terima kasih. Terima kasih atas tawarannya, tapi aku tidak bisa tinggal di sini bersama kalian semua.]],
				['23'] = [[Jangan salah paham, tapi aku... tidak yakin aku punya waktu untuk ini...]],
				['33'] = [[Sejujurnya, tidak juga. Aku baru saja berpikir... Kurasa aku masih akan pergi ke Moskow, ke Institut Virologi. Aku akan mencari beberapa petunjuk di sana. Siapa tahu, mungkin aku akan mendapat beberapa kemajuan dalam pencarianku sendiri.]],
				['25'] = [[Maaf, Svetlana, tapi kamu tidak terlihat seperti seseorang yang sudah berhenti peduli. Bagaimana pun juga, kamu mencoba menyelamatkanku.]],
				['35'] = [[Halo. Tidak, belum apa-apa. Bagaimana denganmu?]],
				['19'] = [[Di Institut Virologi, katamu? Di Moskow? Karena ini adalah satu-satunya petunjukku, sepertinya aku akan pergi ke Moskow.]],
				['29'] = [[Aku tidak paham.]],
				['17'] = [[Di Novgorod, dari sebuah mayat. Tapi ada lagi. Aku menemukan kaleng semprotan berlabel 'Day R' ini juga.<new_line>Apakah kau tahu artinya?]],
				['27'] = [[Oke, lalu apa masalahnya?]],
				['37'] = [[Halo lagi, Svetlana.]],
				['47'] = [[Selamat tinggal (pergi dan diam-diam menutup pintu di belakangmu)]],
				['39'] = [[Ya, benar. Masalahnya adalah... uh... mengapa gadis itu sangat takut padaku?]],
				['46'] = [[Svetlana, dengar... Aku ingat bahwa ibumu mengirimimu surat tentang serangan jantung ayahmu. Tapi mungkin dia hanya tidak mau...]],
				['45'] = [[Apa yang kamu maksud dengan mengapa? Kamulah yang ingin tahu kebenarannya.]],
				['44'] = [[Svetlana?]],
				['43'] = [[(Katakan semuanya padanya)]],
				['42'] = [[Ya, aku setuju.]],
				['41'] = [[Oh, jadi begitu...]],
				['40'] = [[Apa yang pertama baginya?]],
				['21'] = [[Tidak, tunggu sebentar. Aku belum memutuskan apa pun. Tetap saja, prioritasku adalah menemukan istri dan anak perempuanku.]],
				['16'] = [[17 Agustus... Tunggu sebentar... Aku sudah melihat tanggal itu di suatu tempat sebelumnya... (tunjukkan Svetlana telegram milik pria yang mati di Novgorod)]],
				['32'] = [[Aku yakin.]],
				['22'] = [[Baiklah, aku mengerti apa yang bisa aku lakukan.]],
				['30'] = [[Svetlana, ini gila. Siapa yang mau melakukannya dan apa keuntungan mereka?]],
				['2'] = [[Kau benar, tapi aku harus mencoba. Aku tidak bisa meninggalkan keluargaku. Dan setelah itu, mungkin ada permukiman lain. Aku akan terus mencari sampai aku tahu apa yang terjadi.]],
				['24'] = [[Svetlana, ini ide yang buruk. Padang gurun terlalu berbahaya untuk seseorang yang tidak siap. Aku tidak bisa melindungimu. Dan kamu akan meninggalkan orang-orang di sini tanpa seorang dokter. Lebih baik jika kamu tetap di sini.]],
				['36'] = [[Sampai jumpa.]],
				['26'] = [[Ya? Kudengarkan.]],
				['34'] = [[Perlahan-lahan, tapi itu akan... Aku datang hanya ingin melihat keadaanmu.]],
				['12'] = [[Svetlana, aku...]],
				['28'] = [[Aku tidak mengerti. Apa lagi yang harus diungkap? Kamu tahu segalanya. Epidemi mewabah, lalu terjadi perang nuklir...]],
				['18'] = [[Menarik. Siapa ayahmu?]],
				['6'] = [[Mungkin tidak. Tapi, senang bertemu denganmu, Svetlana. Semoga kita berjumpa lagi suatu hari nanti... (pergi)]],
				['9'] = [[Aku mencarimu. Aku menemukan perekammu di Leningrad. Aku juga di Novgorod waktu itu, di markas bandit...]],
				['8'] = [[Svetlana dari Leningrad, kan? Kamu adalah orang yang menemukan obat epidemi, yang tertangkap oleh bandit, dan yang melarikan diri dan berhasil sampai ke Tver dengan berjalan kaki?]],
				['7'] = [[Perkenalkan. Aku seorang pengembara. Aku datang dari utara.]],
				['5'] = [[Tapi kamu tidak melakukan apa-apa? Kamu mengenakan jas dokter. Kamu membantu orang-orang. Kamu belum menyerah.<new_line>Dan ... apakah kamu menyerah ketika bandit membawamu sebagai tahanan? Tidak, kamu meracuni mereka, meskipun kamu mempertaruhkan nyawamu sendiri untuk melakukannya!]],
				['4'] = [[Aku turut prihatin. Dan aku tahu bahwa kesempatanku kecil. Tapi itu kesempatan. Aku akan mencari. Jika harus, aku akan keluar dari Ural, ke negara lain...]],
				['3'] = [[Svetlana, semua yang kamu katakan itu benar, tapi... jika kamu berada di posisiku, bukankah kamu akan tetap pergi dan mencari keluargamu? Tidak peduli betapa berbahayanya itu?]],
				['10'] = [[Aku mencari korban yang selamat. Orang yang bisa mengatakan yang sebenarnya tentang apa yang terjadi. Kembali di tahun 1984. Seperti yang kamu lihat, aku tidak ingat apa-apa... Aku bahkan lupa namaku sendiri,]],
				['20'] = [[Aku akan mencoba. Tapi aku tidak akan menjanjikan apa pun. Prioritas utamaku adalah menemukan istri dan anak perempuanku.]],
				['38'] = [[Ayahmu...]],
				['1'] = [[Aku punya istri dan anak perempuan. Aku tidak tahu di mana mereka berada, tapi aku yakin mereka masih hidup dan sedang menungguku. Aku harus menemukan mereka.]],
				['14'] = [[Tidak, aku harus pergi.]]
			},
			['episode'] = {
				['31'] = [[Aku tidak tahu. Aku telah mencoba mencari tahu semenjak diriku ditahan oleh bandit. Aku mencoba mencari penjelasan yang rasional. Kemudian di sini, di luar Tver, aku bertanya pada para pengungsi lainnya. Aku melihat melalui surat kabar, cerita tentang penyakit itu...]],
				['15'] = [[(Ketika aku bangun, Svetlana sedang berdiri di sampingku. Dia menatapku dengan saksama, dengan gugup menggigit-gigit bibirnya.)<new_line><new_line>Apakah kamu berubah pikiran? Ada kemungkinan kamu akan tinggal?]],
				['11'] = [[(Svetlana menatapku dengan simpati yang tulus)<new_line><new_line>Itu mengerikan! Sungguh ajaib kamu bisa sampai sejauh ini! (Dia tersenyum ramah) Tapi itu semua masa lalu. Kami akan menjagamu. Kami akan memberimu kamar, dan memberimu makan. Dan sedikit demi sedikit, ingatanmu akan kembali, aku yakin.]],
				['21'] = [[(Svetlana menahan napas)<new_line><new_line>Jadi kamu setuju untuk membantuku?]],
				['23'] = [[Aku akan pergi dan bersiap-siap untuk berangkat. Bisa menunggu sedikit lebih lama?]],
				['33'] = [[(Ketika dia melihatku, Svetlana tersenyum sejenak sebelum dia menyembunyikan gejolak hatinya dan memasang ekspresi cuek yang dingin di wajahnya.)<new_line><new_line>Nah, bagaimana kemajuanmu dalam mencari istri dan anakmu?]],
				['25'] = [[Kamu yakin?]],
				['35'] = [[Semuanya baik.<new_line><new_line>(Nada Svetlana dingin dan lugas. Dia jelas kesal.)]],
				['19'] = [[Tidak. Aku tidak tahu. Tapi sepertinya aku pernah mendengar nama itu sebelumnya. Dari ayahku. Ketika dia mengunjungi institut itu, dia berbicara dengan seseorang di telepon dan menyebut ini 'Day R.' Aku bertanya apa artinya, tapi dia hanya mengalihkannya dengan lelucon. Tapi aku ingat.]],
				['29'] = [[Kamu membaca buku harianku, kan? Di salah satu halaman yang hilang, aku menulis ide yang aneh. Aku masih tidak tahu kenapa aku tidak memikirkannya sejak dulu. Tapi ide ini... membuatku terjaga di malam hari.]],
				['17'] = [[(Svetlana mulai tenang dan menurunkan suaranya lagi)<new_line><new_line>Dan lagi, kita masih tahu sesuatu. Wabah mematikan pertama muncul pada 2-3 September. Mempertimbangkan masa inkubasi, sepertinya orang-orang itu terinfeksi sekitar 17-19 Agustus. Artinya...]],
				['27'] = [[(Svetlana menurunkan suaranya, seolah takut akan ada yang mendengar)<new_line><new_line>Ada satu hal yang bisa kamu lakukan. Sudah kumulai sejak lama, tetapi tidak pernah terselesaikan. Aku tidak bisa, aku sudah menyerah di tengah jalan (wajah Svetlana tampak masam). Tapi kamu bisa melakukannya!]],
				['37'] = [[Baik-baik saja. Jika kamu bisa melawan keputusasaan dan hilangnya harapan.]],
				['47'] = [[Ya, kebenarannya. Tapi yang kamu ceritakan dari awal sampai akhir adalah kebohongan yang menjijikkan. Aku tidak tahu siapa yang ada di belakangnya, tapi aku harap itu bukan dirimu. Tetapi aku tidak berniat mendengarnya lagi.]],
				['39'] = [[Tidak perlu terlalu formal. Bagaimana pun juga, kita seperti teman lama sekarang, bukan?]],
				['1'] = [[(Wanita itu menatap ke mataku dengan mata abu-abunya yang sedih dan tersenyum hangat)<new_line><new_line>Halo. Kamu sudah lama tidak bertemu dengan orang-orang baru. Jadi, siapa kamu? Dari mana asalmu?]],
				['22'] = {
					['2'] = [[Tapi kita bisa melakukan satu hal: menemukan kebenaran. Cari tahu siapa pelaku yang telah membunuh jutaan orang dan mengubah negara kita, bahkan mungkin dunia, menjadi padang gurun. Dan cari tahu mengapa mereka melakukannya. Itu penting, kan? Bukan hanya untukku, tapi untuk kita semua?<new_line>Aku tahu kamu tidak perlu melakukannya. Yang bisa aku lakukan adalah meminta. Dan menawarkan hadiah.]],
					['1'] = [[Aku mengerti. Tapi kamu harus memahamiku. Aku tidak punya kerabat dekat, dan tidak ada yang membutuhkan obat untuk penyakit itu lagi. Apa yang harus kulakukan? Bertahan hidup. Tujuan hidupku adalah untuk hidup sedikit lebih lama. Tapi aku sudah muak dengan kehidupan ini!]]
				},
				['46'] = [[(Svetlana berbalik ke arahku. Matanya benar-benar acuh tak acuh, tetapi tangannya gemetar.)<new_line><new_line>Dan mengapa kamu menceritakan semua ini kepadaku?]],
				['45'] = [[(Ketika aku selesai, kuperhatikan Svetlana sedang menatap kosong ke arah dinding belakangku. Aku mengharapkan semacam reaksi, tetapi bukan ini. Aku menggerakkan kepala, mencoba menarik perhatiannya, tetapi dia tidak menanggapi. Keheningan tegang mengikuti.)]],
				['44'] = {
					['2'] = [[(Menatap wajah Svetlana yang terfokus tapi senang, aku tersendat, tidak tahu bagaimana memulainya. Aku menggumamkan sesuatu yang tidak dapat dimengerti. Suaraku tiba-tiba serak. Svetlana mengulurkanku segelas air. Aku meminumnya dalam satu tegukan dan memutuskan.)]],
					['1'] = [[Baiklah. Sepertinya kamu ingin menceritakan sesuatu. Mari bercerita. Apakah kamu tahu sesuatu? Tentang apa yang kita diskusikan?]]
				},
				['43'] = [[Ini bukan hanya sebuah kesenangan baginya. Ini adalah kesempatan yang menyenangkan bagi kita semua. Kita selamat dari epidemi dan perang nuklir. Kemudian kita menghabiskan bertahun-tahun berjuang melawan kelaparan! Tapi ada musuh yang bahkan lebih menakutkan daripada kelaparan, dan itu adalah keputusasaan. Dan kamu tahu? Hari ini musuh belum menang. Tuhanlah yang menentukan akhir segalanya. Itu berarti kita punya masa depan!]],
				['42'] = [[Kamu tahu, pertama baginya. Kamu belum berhasil? Dia hamil!]],
				['41'] = [[(Svetlana mulai tertawa lagi. Dia dalam suasana hati yang sangat baik.)<new_line><new_line>Tidak memperhatikan. Dia hanya kurang sehat. Ini pertama baginya.]],
				['40'] = {
					['2'] = [[(Svetlana melepaskan cengkeramannya dan mulai terisak. Pahit, isak tangis yang membuat dadaku sangat sakit. Dia menangis dan, seperti idiot, aku hanya menatapnya, terdiam. Di sini lain, aku bahkan mulai memaki diriku sendiri dalam hati karena aku tidak membohongi Svetlana saja, bukannya malah membunuhnya dengan kebenaran itu.)]],
					['1'] = [[(Svetlana tiba-tiba meraih bagian depan bajuku. Aku tersentak mundur.)<new_line><new_line>Cukup! Jangan katakan sepatah kata pun tentang ayahku! Dia orang yang hebat! Dia mencintaiku, mencintai ibuku, mencintai negaranya! Dia meninggal karena serangan jantung, paham?! Itu kata ibuku di suratnya! Serangan jantung, kamu dengar... Serangan jantung...]],
					['4'] = [[Terima kasih telah mengungkap kebenaran. Aku minta maaf karena telah meneriakimu. Kamu tidak bersalah di sini. Apa yang kamu katakan: itu rasa sakitku dan juga masalahku. Milikku sendiri.<new_line>Sekali lagi terima kasih dan... sampai jumpa.]],
					['3'] = [[(Svetlana sedikit tenang setelah beberapa saat. Dia menyeka hidungnya dengan sapu tangan dan duduk di sofa. Aku duduk di sampingnya, tetapi dia mengangkat tangannya, menolak. Aku mengangguk dan menuju ke pintu. Svetlana harus memikirkan kembali banyak hal sekarang. Dan aku tidak bisa membantunya dengan semua itu.)<new_line><new_line>Tunggu...]]
				},
				['16'] = [[(Svetlana mengangguk pelan. Jawabanku tidak mengejutkannya. Tapi kenapa dia sangat cemas?)<new_line><new_line>Aku merasa kasihan padamu, pengembara. Orang-orang sepertimu adalah yang pertama mati. Orang-orang yang punya hati. Siapa yang mengambil risiko untuk menyelamatkan orang lain. Kupikir semua orang sepertimu sudah mati di tahun '84. Dan yang tersisa hanyalah bandit dan orang-orang seperti aku, orang-orang yang telah berhenti peduli sama sekali.]],
				['32'] = [[(Svetlana melompat dan menatapku dengan tatapan kemarahan yang aneh.)<new_line><new_line>Aku tahu itu! Tapi lebih gila lagi kalau harus pergi ke padang gurun untuk mencari keluargamu!]],
				['14'] = [[(Pintu ke kamar Svetlana ditutup. Tapi sepertinya dia ada di dalam. Bisa kuketuk, tapi sepertinya dia hanya ingin sendiri untuk sementara waktu)]],
				['30'] = {
					['2'] = [[Aku tadi memeriksanya. Virus itu menyerang siapa pun dan di mana pun secara bersamaan. Tidak ada cara untuk menghentikannya.<new_line>Ini terdengar gila, tetapi mungkinkah ada seseorang yang dengan sengaja memulai epidemi ini? Menginfeksi orang-orang di berbagai kota pada waktu yang bersamaan?]],
					['1'] = [[(Svetlana berbicara lebih tenang)<new_line><new_line>Epidemi itu! Epidemi itu mewabah di saat yang sama, di berbagai kota, di semua ujung Uni Soviet! Sepanjang sejarah, tidak ada virus yang pernah menyebar seperti itu. Penyakit selalu bisa dilokalisasi, kamu bisa membuat karantina, dan mengambil tindakan pencegahan. Setidaknya, kamu bisa mencobanya. Tapi tidak dengan penyakit ini.]]
				},
				['2'] = [[(Svetlana berbalik)<new_line><new_line>Lihatlah ke luar jendela. Terlihat padang gurun. Pertama epidemi, lalu perang nuklir... Bumi mati. Kamu tidak bisa bertahan hidup di sana. Radiasi, pemangsa. Orang-orang gila yang sudah tidak punya apa-apa. Bandit. Kamu tidak akan menemukan siapa pun di luar sana. Tapi jika kamu pergi, kamu tidak akan kembali. Kami tidak akan mencoba menahanmu di sini, tapi berpikirlah sebentar!]],
				['24'] = [[(Svetlana mengerutkan kening, lalu berbalik dan berjalan ke jendela)<new_line><new_line>Kalau begitu kembalilah jika punya waktu.]],
				['36'] = [[(Svetlana menyapaku dengan senyuman.)<new_line><new_line>Halo! Baik? Ada berita?]],
				['26'] = [[(Svetlana tersenyum aneh)<new_line><new_line>Apa pentingnya? Aku tidak mempertaruhkan apa pun. Oke, sial kamu, jika ingin pergi – pergilah. Tapi kalau kamu tetap mau pergi...<new_line><new_line>(Dia berpikir sejenak, memilih apa yang mau dikatakan)]],
				['34'] = {
					['2'] = [[Aku akan pergi dan bersiap-siap untuk berangkat. Bisa menunggu sedikit lebih lama?]],
					['1'] = [[(Senyum Svetlana sekarang bersinar dengan kegembiraan yang jelas.)]]
				},
				['13'] = [[(Svetlana terdiam, lalu dia berdiri dan berjalan ke jendela)<new_line><new_line>Kenapa tidak?]],
				['28'] = [[Aku harus mengungkap kebenaran. Kebenaran tentang malapetaka ini.]],
				['18'] = [[Aktifkan Day R pada 17 Agustus seperti yang diperintahkan'... Apa ini? Dari mana kamu mendapatkannya?]],
				['6'] = [[(Svetlana menggenggam kepalanya dengan tangannya)<new_line><new_line>Kamu tidak mengerti ...]],
				['9'] = [[Baiklah, tapi... kenapa kamu mencariku?]],
				['8'] = [[(Svetlana mengerutkan kening)<new_line><new_line>Ya, itu benar. Tapi bagaimana kamu tahu semua ini?]],
				['7'] = [[Betulkah? (wanita itu tercengang) Ini adalah perjalanan yang panjang dan berbahaya. Masuklah, buat dirimu nyaman. Omong-omong, aku Svetlana.]],
				['4'] = [[(Svetlana menatapku dengan sedih)<new_line><new_line>Kamu tahu, ketika aku tiba di sini, aku juga berusaha menemukan kerabatku. Dan korban hidup lainnya. Aku mencari cara untuk menghubungi rekan-rekanku, untuk mencari tahu lebih jauh tentang epidemi terkutuk ini, tentang pengeboman. Aku melakukan perjalanan dari kota ke kota, sampai aku kehabisan bahan bakar. Tapi semua sia-sia. Semua usahaku hangus!]],
				['3'] = [[Tentu, ada pemukiman lain, tetapi mengapa aku harus meninggalkan Tver? Kami punya cukup makanan. Para bandit meninggalkan kita sendirian. Jangan bodoh, tetap di sini!]],
				['10'] = [[Kamu tidak ingat apa-apa? Apa yang terjadi?]],
				['20'] = [[Dia seorang ahli mikrobiologi. Salah satu yang terbaik di negara ini. Dia bekerja di Moskow, di Institut Virologi. Itu terakhir kali aku melihatnya, dia meninggal karena serangan jantung pada Juni 1984. Ibuku memberitahuku melalui telepon. Aku bahkan tidak sempat mengucapkan selamat tinggal. Aku bahkan tidak pergi ke pemakaman, aku beristirahat di tempat tidur karena sakit amandel.]],
				['48'] = [[Pergilah, kumohon... Ada hal yang harus kukerjakan.]],
				['38'] = [[(Ketika aku mengetuk pintu Svetlana, pintu itu lama terbukanya.<new_line>Seorang gadis muda sedang duduk di sofa kecil di kantor. Dia malu ketika melihatku, mengucapkan terima kasih kepada Svetlana dan berlari keluar. Svetlana tertawa dan berkedip padaku.)<new_line><new_line>Gadis bodoh! Jadi, apa yang baru?]],
				['12'] = [[Tapi di sini, kita semua bekerja untuk kebaikan bersama. Dan kita menjalankan peraturan yang ketat: setiap orang memiliki pekerjaan yang harus dilakukan, tetapi masih banyak pekerjaan yang harus dilakukan. Kita bisa menggunakan bantuan tambahan untuk membantu.]],
				['5'] = [[(Svetlana menatap mataku lama, lalu mengalihkan tatapannya)<new_line><new_line>Aku juga berjuang selama yang aku bisa. Aku percaya bahwa kita masih bisa melakukan sesuatu, kekacauan ini benar adanya...]]
			},
			['name'] = [[Svetlana]]
		}
