	['quests'] = {
		['easter_quest'] = {
			['6'] = [[Ho completato tutti gli ordini dei cestini di uova colorate. È ora di tornare a riscuotere il costume da coniglio.]],
			['5'] = [[Devi ancora completare un ordine per il coniglio pasquale perché possa ottenere il costume da coniglio.<new_line>Ordine n. 4]],
			['4'] = [[Devi ancora completare due ordini per il coniglio pasquale.<new_line>Ordine n. 3]],
			['3'] = [[Devi ancora completare tre ordini per il coniglio pasquale.<new_line>Ordine n. 2]],
			['2'] = [[Uno strano tizio con un costume da coniglio mi ha chiesto di lavorare per lui. Pagherà per ogni cestino di uova colorate che gli porterò. Inoltre, se completo tutti gli ordini, mi darà il suo costume.<new_line>Ordine n. 1.]],
			['1'] = [[Si dice che un tizio parecchio strano stia cercando persone per un lavoro molto remunerativo. Dovrei chiedere ai superstiti dell’accampamento più vicino se sanno qualcosa.]],
			['name'] = [[Ordine di Pasqua]]
		},
		['advice_radiation'] = {
			['name'] = [[Consiglio: radiazioni]],
			['hint'] = [[Riduci il livello di radiazioni a 0]],
			['1'] = [[Le radiazioni che hai accumulato non se ne andranno da sole. Un elevato livello di radiazioni aumenta il rischio di avvelenamento da radiazioni!<new_line>Le radiazioni vengono ridotte da determinate medicine, dall'alcol e dall'acqua pulita. Tè al dente di leone, i gallinacci dei boschi e i mirtilli delle paludi aiutano a combattere le radiazioni.]]
		},
		['easter_quest_2'] = {
			['2'] = [[Ho venduto abbastanza uova al compratore. È ora di tornare a riscuotere la ricompensa.]],
			['1'] = [[Il compratore si è offerto di acquistare uova colorate a un ottimo prezzo. Se gli vendo abbastanza uova entro la fine del periodo pasquale, mi ha promesso una ricompensa.]],
			['name'] = [[A caccia della ricompensa]]
		},
		['halloween2022'] = {
			['13'] = [[Il Mietitore ha tessuto un piano subdolo per farmi fuori, ma io ero un passo avanti a lui! Vediamo cos'avrà in serbo per me il mondo di sotto il prossimo anno.]],
			['2'] = [[Ho accettato un contratto del Mietitore. Devo distruggere il Demone Balthazar.]],
			['10'] = [[Ho accettato un contratto del Mietitore. Devo distruggere il Mostro di Frankenstein e Igor il gobbo.]],
			['9'] = [[Mi sono occupato del Re delle zucche. Devo comprare il prossimo contratto del Mietitore.]],
			['8'] = [[Ho accettato un contratto del Mietitore. Devo distruggere il Re delle zucche. Mi servirà la Spada del boia per sconfiggerlo.]],
			['7'] = [[Mi sono occupato di Dracula. Devo comprare il prossimo contratto del Mietitore.]],
			['6'] = [[Ho accettato un contratto del Mietitore. Devo distruggere Dracula.]],
			['5'] = [[Mi sono occupato del Medico della peste. Devo comprare il prossimo contratto del Mietitore.]],
			['4'] = [[Ho accettato un contratto del Mietitore. Devo distruggere il Medico della peste. Mi servirà una Veste demoniaca per sconfiggerlo.]],
			['3'] = [[Mi sono occupato del Demone. Devo comprare il prossimo contratto del Mietitore.]],
			['name'] = [[Halloween 2022]],
			['1'] = [[Il Mietitore si è offerto di vendermi un contratto demoniaco in cambio di monete d'oro.]],
			['11'] = [[Mi sono occupato del Mostro di Frankenstein. Devo comprare il prossimo contratto del Mietitore.]],
			['12'] = [[Ho accettato un contratto del Mietitore. Devo distruggere l'asino parlante.]]
		},
		['familyMatters'] = {
			['heal_sasha'] = [[<new_line><new_line>Devo:<new_line>- Curare Sasha]],
			['28'] = [[Sono vivo? Sento persone parlare. Sento il materasso duro sotto la schiena e vedo il soffitto bianco e crepato sopra di me. Questa dev'essere l'infermeria di Stepnoy.<new_line>Sasha non mi ha ucciso? Cos'è successo?<new_line>Pare che non si siano risparmiati sulle medicine per me. Il punto in cui Sasha ha infilzato il coltello fa male, ma non tanto male, tutto sommato.<new_line>Uno dei combattenti è venuto nella stanza per dirmi che il maggiore vuole vedermi.<new_line>Mentre vado da Boris, penso a Sasha. No, non è più sicuramente la ragazzina che ricordavo. Ora è un'assassina a sangue freddo.<new_line>Com'è diventata così? Come?]],
			['28_1'] = [[Sono vivo? Sento persone parlare. Sento il materasso duro sotto la schiena e vedo il soffitto bianco e crepato sopra di me. Questa dev'essere l'infermeria di Stepnoy.<new_line>Sasha non mi ha ucciso? Cos'è successo?<new_line>Pare che non si siano risparmiati sulle medicine per me. Il punto in cui Sasha ha infilzato il coltello fa male, ma non tanto male, tutto sommato.<new_line>Uno dei combattenti è venuto nella stanza per dirmi che il maggiore vuole vedermi.]],
			['33'] = [[Sasha inizia a girarsi e rigirarsi, lamentandosi. Borbotta qualcosa a bassa voce. Ho ascoltato attentamente, ma non ho capito le parole. Poi si è addormentata di nuovo.]],
			['25'] = [[Kugel non c'è più. Ora posso tornare a Novokuzneck e attaccare la base dei banditi di Abakan. Spero che Boris non abbia cambiato idea in mia assenza. E, spero, che l'attacco non sia invano. Chissà se la mia Sasha è ancora viva...]],
			['27_2'] = [[Ce l'abbiamo fatta, ma con sacrifici. I due o tre banditi sopravvissuti sono saliti su un UAZ e se ne sono andati di corsa dal cancello principale. Non li abbiamo seguiti perché dovevamo prima prenderci cura dei nostri feriti.<new_line>Poi una granata è scoppiata all'ultimo piano di uno degli edifici. Cosa sarà successo?]],
			['31'] = [[Ho guidato a tutta velocità per la campagna, schiacciando arbusti, prendendo buche e rischiando di ribaltarci a ogni curva. Sasha si lamentava, piegata, e premeva una benda sulla pancia: la ferita era grave. A ogni solco, Sasha urla per il dolore, ma che alternative ho?<new_line>Le ho sussurrato: 'Resisti, tesoro. Andrà tutto bene. Resisti ancora un po'.'<new_line>L'avrei fatta guarire, a qualunque costo.<new_line>Siamo ancora troppo vicini a Stepnoy. Ne sono più che sicuro: dopo tutto quello che è successo, manderanno di sicuro qualcuno per prenderci.]],
			['11'] = [[Trovo una cartella contenente delle liste dei decessi nel cassetto della scrivania nell'ufficio del primario dell'ospedale. Smisero di compilare queste liste quando fu evidente che la malattia avrebbe colpito tutti prima o poi. Mi conviene leggerle.]],
			['21'] = [[Se Sasha è ancora prigioniera dei banditi di Abakan, la salverò. Se non lo è, devo assicurarmi che non debba più cercare nessuno.<new_line>Il capo della città di Novokuzneck ha detto che hanno bisogno di un sistema di missili anti-carro per l'attacco. Mi ha consigliato di controllare a Ufa. Lungo la strada, andrò a Čeljabinsk per parlare con l'ingegnere Kugel.]],
			['2_up'] = [[Sono andato sulla riva del fiume. Ho immerso entrambe le mani nell'acqua fredda. L'acqua che mi scorreva tra le dita mi ha fatto riaffiorare un ricordo...]],
			['3_up'] = [[Ho dato un'occhiata all'appartamento al secondo piano dell'edificio residenziale. C'erano oggetti personali sparsi per tutta la camera da letto, e una grande valigia sul pavimento. Sembra che gli inquilini stessero facendo le valigie quando qualcosa li ha costretti ad abbandonare tutto e scappare dall'appartamento. Nella valigia, sono riusciti a mettere solo un abito da sposa. L'ho steso sul letto e l'ho toccato. Un altro ricordo...]],
			['4_up'] = [[C'era una piccola bambola di pezza vicino alla pattumiera capovolta. Aveva solo un occhio, un pezzo della gamba era stato strappato e fuoriusciva un po' di imbottitura grigia, il vestitino aveva una grande e brutta macchia... Un nuovo ricordo.]],
			['5_up'] = [[Sento un suono acuto e sgradevole provenire da qualche parte sopra le nostre teste. Guardo verso l'alto e vedo una striscia di alluminio piegata sotto al bordo del tetto. Era stato il vento a piegarla, ma si era raddrizzata e ora stava sferragliando. Il suono mi ha ricordato qualcosa.]],
			['6_up'] = [[Sull'asfalto c'è un frammento di una tazza di ceramica. Un altro ricordo...]],
			['15'] = [[Fissando le vetrine distrutte del vecchio bar, mi concentro. Sento l'eco lontana di un pensiero, che però non riesco a far riaffiorare. Improvvisamente mi ricordo di un litigio. Un brutto litigio con Marina, la sorella maggiore di Galja. Non siamo mai andati d'accordo, ma in quell'occasione Marina aveva cercato di criticarmi di fronte a Sasha. Preso dalla rabbia, risposi male a Marina, presi Sasha dalla mano, mi alzai dal tavolo e tornai a casa. Ero furioso. 'Non importa', mi dicevo tra me e me, 'devi sopportarla per un altro giorno e poi quell'idiota di Marina se ne tornerà a casa sua a Perm...'<new_line>Perm! L'appartamento di Marina è a Perm! Forse Galja e Sasha sono andate lì.]],
			['13'] = [[8 pagine... Ci sono molte 'donne non identificate', ma spero che Galja abbia preparato tutti i documenti per il ricovero. Scrupolosa per com'è, ha anche firmato tutte le etichette dei vestiti di Sasha. Devo continuare a leggere.]],
			['26'] = [[Boris ha ragione. Dobbiamo riposare bene prima di andare a combattere. Domani sarà una giornata pesante.<new_line>Qualcosa mi dice che ne varrà la pena. Sasha è viva ed è lì. La vedrò domani. Domani le dirò tutto che volevo dirle e che dovrei dirle.<new_line>Dopodiché, come un uomo accusato in attesa di giudizio, aspetterò la mia sentenza o l'assoluzione.]],
			['9'] = [[Gli sciacalli sono stati qui. Tutti gli armadi e i cassetti sono stati svuotati, la cucina ricoperta di avanzi e di vasetti quasi vuoti di marmellata. Il pavimento della camera da letto è ricoperto dai frammenti di un vecchio servizio da tè. I cuscini sono stati strappati e la libreria ripulita. Non era rimasto quasi nessun libro. È rimasta solo una grande bacinella di alluminio con della cenere... In giro ci sono mozziconi di sigaretta e bottiglie vuote.<new_line>Nella vecchia stanza di Sasha, il letto c'è ancora. Passo subito una mano sotto il cuscino e prendo una bambola di pezza, quella che le aveva fatto la madre. Sasha nascondeva sempre lì la sua bambola, per proteggerla dai mostri...<new_line>Dove sono? Dove sono Galja e Sasha? Sono sopravvissute all'epidemia?<new_line>Se una delle due si è ammalata, forse ci saranno dei documenti nell'ospedale di zona. Devo andare lì.]],
			['8'] = [[Guardando l'edificio numero 10 di via Bakinsky, una tempesta di emozioni mi ha travolto. L'edificio era sopravvissuto quasi intatto ai bombardamenti: le finestre rotte e lo strato di rifiuti nel tragitto verso la porta erano gli unici segni della catastrofe che era avvenuta. E il silenzio. E la penombra. Tutto il resto era esattamente come nel mio ultimo ricordo...<new_line>Sto in piedi davanti alla porta del nostro appartamento. Non ho resistito e ho suonato il campanello. Per un attimo, mi è persino sembrato di sentire dei passi dall'altra parte della porta. Ma no. Abbasso la maniglia e la porta si apre cigolando leggermente.<new_line>La vista mi ha fatto arrabbiare come un matto: l'appartamento, il nostro appartamento, era stato saccheggiato!]],
			['7_up'] = [[Mentre cerco tra gli oggetti, qualcosa mi graffia il palmo della mano. Faccio luce nella borsa ed estraggo una zanna di lupo. Perché ho con me questa cianfrusaglia? La zanna era ingiallita e puzzava di muffa, ma era ancora appuntita...]],
			['7_dream'] = {
				['2'] = [[Tornerai?'<new_line>Non rispondo. Non serve, perché tanto lo capisce.<new_line>'Sai che è meglio così', dico io guardando il pavimento.<new_line>'Forse sì', risponde Galja.<new_line>Alzo il capo e vedo una lacrimuccia.]],
				['1'] = [[...rimango nel corridoio del nostro appartamento con una valigia in mano e uno zaino pesante sulle spalle.<new_line>Galja mi porge un vecchio talismano con una zanna di lupo. Scuoto la testa.<new_line>'È meglio se lo tieni tu. Lo sai che non credo a queste cose.'<new_line>Galja sospira, triste.<new_line>'Andrai a salutare Sasha?'<new_line>'Sta ancora dormendo. Non ha senso svegliarla. Abbiamo parlato di tutto ieri.']],
				['3'] = [[Mi conviene andare', dico con un tono soffocato, girandomi verso la porta.<new_line>'Posso... posso darti un bacio d'addio?'<new_line>'Io... sì... va bene...'<new_line>Quello fu l'ultimo nostro bacio.<new_line>Esco e chiudo la porta dietro di me. Guardo la placca sulla porta, il numero dell'appartamento incorniciato da margherite di metallo. Non lo vedrò mai più. Scendo le scale, e vado verso via Bakinsky e il fresco tramonto di aprile...]]
			},
			['7_down'] = [[Sento una specie di scossa elettrica. Gor'kij, via Bakinsky 10, appartamento 3! Trovato! L'indirizzo del nostro vecchio appartamento!<new_line>Il cuore mi va a mille. Ora so da dove iniziare a cercare.]],
			['6_dream'] = {
				['2'] = [[Galja si siede fiaccamente sulla sedia.<new_line>'E dai!' Adesso non sto parlando, sto gridando.<new_line>Galja grida: 'Che non ti importa niente di me, lo so da tanto tempo, ma Sasha che ti ha fatto? Non vedi che sta crescendo senza un padre? Quando è stata l'ultima volta che l'hai portata da qualche parte? Quando è stata l'ultima volta che hai parlato con lei? Lo sai che la scuola mi chiama tutte le settimane? E io vado dopo il lavoro per sentirmi dire che è fuori controllo e che va male in tutte le materie. Poi torno a casa e provo a farla ragionare. Hai la più pallida idea di cosa sta succedendo?']],
				['1'] = [[Galja, mi sono stancato di questo silenzio da parte tua!' Cammino per l'appartamento come un animale ferito. 'L'unica cosa che faccio in questi giorni è cercare di capire cos'ho fatto per farti arrabbiare!'<new_line>Provo ad afferrare le braccia di Galja.<new_line>Galja con voce stridula dice: 'Non toccarmi! Mollami!' <new_line>'Non andrai da nessuna parte!', dico e blocco la porta con il corpo. 'Dimmi cos'è successo!']],
				['3'] = [[Sento le forze che mi abbandonano. Sento un dolore al petto. Mi sposto dalla porta.<new_line>Galja si alza e indossa un vecchio cappotto.<new_line>'Sai cosa mi ha detto ieri? 'Mamma, possiamo cambiare papà?''<new_line>Mi fischiano le orecchie. Galja se ne va. Abbasso lo sguardo e vedo i pezzi della tazza preferita di Galja.]]
			},
			['1_up'] = [[Alla fermata dell'autobus, vedo qualcosa di strano tra un mucchio di stracci. Era una medaglia annerita di un veterano della Seconda Guerra Mondiale. La raccolgo e mi riaffiora un ricordo...]],
			['5_dream'] = {
				['2'] = [[Galja risponde, sempre a bassa voce: 'Voglio parlare'.<new_line>'Hai già detto tutto quello che c'è da dire. Sei stata molto chiara. Che altro c'è da aggiungere?']],
				['1'] = [[...è buio oltre la finestra, e si sente il rumore della neve. Ma c'è un altro suono: un rumore acuto e intenso che sembra perforarmi il cervello. Uno dei vicini sta sbattendo una specie di rifiuti metallici.<new_line>Galja viene lentamente in cucina. Sembra più vecchia rispetto al mio ultimo ricordo. Sembra che abbia pianto. Sta lì in piedi e mi fissa senza parlare.<new_line>Mi volto e prendo un'altra sigaretta.<new_line>'Cosa vuoi?', le chiedo a bassa voce. Sasha sta dormendo nella stanza accanto.]],
				['4'] = [[La guardo negli occhi e vedo dolore e compassione. Ma cosa vede lei nei miei? Paura e disperazione?]],
				['3'] = [[Galja corre verso il tavolo e stringe la mia mano tra le sue.<new_line>'Tesoro, perdonami. Perdonami per tutte le cose orribili che ho detto! Ho sbagliato, sono una brutta persona, ma ti prometto che non succederà più. Quando non ti ho visto tornare a casa ieri sera, un'altra volta, non ci ho visto più! So quanto sono difficili le cose per te, quanto devi lavorare. So che non sei felice, che volevi altro! Dimmi cosa fare, voglio aiutarti. Ti amo, farò tutto per te, dimmi solo di cosa hai bisogno!']]
			},
			['36'] = [[L'UAZ fa inversione e se ne va, portando via la mia Sasha...<new_line>No, su quella macchina c'era un'altra persona.<new_line>La mia Sasha non c'è più, come Galja. Prendo la piccola bambola di pezza dallo zaino. Era l'unico ricordo che avevo di mia moglie e mia figlia.<new_line>La mano si avvicina alla pistola...<new_line>No! Il suicidio è solo un altro modo per sfuggire dalle mie responsabilità; sarebbe solo un'altra azione stupida e vigliacca di una vita costellata di errori.<new_line>No, non lo farò.<new_line>Nonostante tutto, continuerò a vivere.<new_line>Vivrò e cercherò il mio posto in questo mondo...]],
			['4_dream'] = {
				['2'] = [[Galja si avvicina e mi mette un braccio intorno al collo.<new_line>'Andrà tutto bene, tesoro. Presto potrò tornare a lavorare e avrai meno pesi sulle spalle. Vedrai, avremo abbastanza soldi per una macchina!'<new_line>'Hai mai visto un insegnante di laboratorio possedere una macchina?', sospiro e vado ad augurare la buonanotte a Sasha...]],
				['1'] = [[Sasha, tesoro, guarda cosa ti ha fatto la mamma!', Galja mostra a Sasha la bambola, cucita con pezzi di vecchie magliette. Una manina appare ai piedi del letto e afferra la gamba della bambola.<new_line>'Pare che le cose non vadano poi così male. Perlomeno, ora possiamo risparmiare sulle bambole', dico scherzando e posando nel portafogli le monete nel cassetto della scrivania.]]
			},
			['23_1'] = [[Anche nei dintorni di Ufa, riusciamo a vedere i puntini scuri che ronzano sopra le macerie.<new_line>Arriviamo alla base militare in fretta. Non eravamo neppure entrati, quando Kugel indica il tetto della base. Il suo volto, pallido come un lenzuolo. Un'ape di dimensioni epiche. L'insetto mutante si tuffa contro di noi dopo averci visto.]],
			['19'] = [[Nella casa del generale in pensione, il padre di Marina e Galja, un tipo sempre sospettoso e imbronciato, non ho trovato il generale, né Sasha, Marina o suo marito. Non ho neppure trovato la casa.<new_line>Solo cenere.]],
			['29'] = [[Mentre esco dall'unità di comando, continuo a pensare alla mia conversazione con Sasha nella base di Abakan. Era rimasto un briciolo di mia figlia in quella persona? Ricordo il dolore che è trapelato dai suoi occhi quando mi ha riconosciuto. Forse non era tutto perso? Se la fa soffrire, significa che prova ancora qualcosa!<new_line>Una cosa è certa: dobbiamo parlare un'altra volta. Devo sapere cosa le hanno fatto nella base di Abakan, in che modo l'hanno convinta a cambiare fazione.<new_line>C'è un'altra cosa di cui non riesco a capacitarmi.]],
			['17'] = [[Oltre il tavolo, sul pavimento, noto due paginette accartocciate, ricoperte dalla scrittura un po' sbilenca di mia figlia.<new_line>Leggo entrambe le pagine e mi rincuoro. Galja e Sasha, sane e salve, erano state lì, in quell'appartamento! E ho anche scoperto la loro tappa successiva!<new_line>Rincuorato, leggo di nuovo le pagine del diario di mia figlia. Mh, che scrittura di gallina però! Forse è destinata a diventare un medico. E Galja era proprio il tipo da accogliere e accudire una bambina abbandonata!<new_line>Ma una volta posate le pagine, inizio a pensare. Cos'è successo alla bambina? I genitori sono tornati nella notte, travolti dai sensi di colpa, per prendere la figlia? Perché la lasciarono da sola? E perché Galja la prese con sé? E cosa significava quella porta chiusa?<new_line>Qualunque fosse il significato, devo andare a Kurgan.]],
			['27'] = [[Un missile è stato sufficiente per abbattere il cancello. Kugel non ci ha deluso.<new_line>Boris aveva ragione: eravamo perfettamente in grado di passare senza tante vittime. Ma il leader è stato troppo furbo, purtroppo. Era come se sapesse del piano d'attacco. Quando il missile ha fatto esplodere il cancello, i banditi erano già in posizione di combattimento attorno alla breccia. Alcuni di loro erano armati di lanciagranate. I combattenti di Novokuzneck provenienti dalla parte posteriore sono caduti in un'imboscata, ma non si sono ritirati, neppure quando sono esplose le prime mine anti-uomo.<new_line>Ce l'abbiamo fatta, ma con sacrifici. I due o tre banditi sopravvissuti sono saliti su un UAZ e se ne sono andati di corsa dal cancello principale. Non li abbiamo seguiti perché dovevamo prima prenderci cura dei nostri feriti.<new_line>Poi una granata è scoppiata all'ultimo piano di uno degli edifici. Cosa sarà successo?]],
			['3_dream'] = {
				['2'] = [[Perché sei così convinta che sia una femmina?'<new_line>'Perché altrimenti sarebbe uno spreco di un bel vestito!', disse Galja prima di ridere. Poi, tutt'un tratto, divenne seria. 'Andrà tutto bene tra noi, vero? Per sempre felici e contenti?'<new_line>'Per sempre, felici e contenti', confermai con decisione...]],
				['1'] = [[...uscimmo dal ristorante per prendere un po' d'aria fresca. Era bellissima vestita da sposa!<new_line>'È l'abito da sposa di mia madre', rispose la ragazza, osservando attentamente la manica. 'Mi stava un po' largo, ma io e mia sorella l'abbiamo aggiustato. Secondo te mi sta bene?'<new_line>'Galja, sei bellissima', dissi, abbracciandola. 'Il matrimonio è andato benissimo, vero?'<new_line>'Attento, c'è una cucitura lenta lì!', mi avvisò Galja scherzando. 'Anche nostra figlia si sposerà con questo vestito!']]
			},
			['24'] = [[Un'altra creatura ci attacca mentre l'ingegnere lavora all'unità. Dobbiamo finire e andare via da Ufa prima di attirare l'attenzione di tutto l'alveare.<new_line>Devo prima accompagnare Kugel a casa.]],
			['28_2'] = [[Mentre vado da Boris, penso a Sasha. No, non è più sicuramente la ragazzina che ricordavo. Ora è un'assassina a sangue freddo.<new_line>Com'è diventata così? Come?]],
			['2_dream'] = {
				['2'] = [[Non ho paura. Ti amo.'<new_line>'Allora dimostralo!' La ragazza inizia a saltare sul posto. 'Sali qui, dai!']],
				['1'] = [[Ehi, salta su! Che aspetti?', mi grida la ragazza conosciuta, in piedi sul parapetto della banca. Dietro di lei, scorreva l'acqua del fiume infinito. 'Non c'è niente di cui aver paura!'<new_line>'Tesoro, ho già detto che non mi va di farlo', dico io indietreggiando un po'. 'E tu scendi da lì!'<new_line>'Quindi non hai paura?', la ragazza ride. 'Santo cielo! Quindi hai paura di salire qui, ma non hai paura di amarmi?']]
			},
			['20'] = [[Ho trovato le pagine del diario di Sasha in un vecchio fienile inclinato vicino alla tomba del vecchio. A giudicare dal moccolo della candela, la scatoletta di cibo vuota e il materasso sul pavimento di legno, è qui che Sasha ha vissuto nell'ultimo periodo.<new_line>L'ultima pagina è stata scritta molto più tardi, su carta diversa, con una penna diversa e con una scrittura leggermente diversa.<new_line><new_line>È stato uno shock leggere quanto mia figlia sia cambiata in due anni... Non molto tempo prima, Sasha è stata male alla vista degli zii assassinati, e due anni dopo ha tagliato la gola di dieci uomini senza battere ciglio.<new_line>No, sono contento che mia figlia sia sopravvissuta e che abbia imparato a difendersi, ma... per quanto sembri patetico, non era questo il futuro che speravo per lei.<new_line>Devo seguire il suo itinerario e andare a est.]],
			['10'] = [[Mentre raggiungo l'ospedale, penso a mia moglie e a mia figlia. Anche se le possibilità di vederle sono scarse, mi costringo a credere che un giorno saremo di nuovo tutti insieme.<new_line>Ma... cosa dirò loro? Se sono vive, Sasha non è più una ragazzina e Galja potrebbe stare con un altro uomo. Cosa farò in quel caso?<new_line>Almeno avrò la forza per vivere. Voglio solo sapere se sono vive.<new_line>Mi impongo di credere. Se perdessi la speranza, perderei ogni motivo per vivere.]],
			['22'] = [[Ho convinto Kugel l'ingegnere a venire con me a Ufa per aiutarmi con il lanciamissili. Ma sarà meglio preparare tutto prima di partire: devo controllare le armi e l'armatura nel caso in cui dovessimo incappare in api giganti.]],
			['12'] = [[Ci sono tanti nomi e cognomi... Ogni 'Galja' o 'Sasha' mi fa trasalire, ma non sono loro. Devo leggerla tutta.]],
			['3_down'] = [[Ora ricordo un nome: Galja. Ci siamo sposati, era incinta. Santo cielo, non riesco a ricordare nient'altro. Dov'è? Cosa le è successo? Devo trovarla!]],
			['2_down'] = [[Mi tornano in mente dei frammenti... Una ragazza che cade nell'acqua... Io che mi precipito al parapetto per saltarlo, ma... l'acqua... nera, fredda e senza fondo... Mi odio, perché non riesco a muovermi.<new_line>Un operaio ripesca la ragazza. Per fortuna sta bene. Ma il viaggio verso casa prosegue in totale silenzio.]],
			['5_down'] = [[Andrò a cercarle. Galja e Sasha. E se sono vive, le troverò! Devo ricordare qualche altro dettaglio, anche solo il nome di una città!]],
			['4_down'] = [[Mia figlia si chiama Sasha. Dov'è adesso? È viva? Quanti anni ha? Ogni ricordo che ho di lei è più doloroso dell'altro...]],
			['16'] = [[Con mia sorpresa, trovo subito l'appartamento di Marina. Ero stato lì solo due volte, ma la memoria non mi aveva tradito.<new_line>Lo stato dell'appartamento fa pensare che Marina e suo marito Arthur abbiano fatto i bagagli e se ne siano andati in fretta e furia. Non c'era disordine, anche il letto era fatto per bene, e l'armadio era decorato con forme stupide di vetro colorato.<new_line>Trovo un vecchio disegno a matita sul tavolino della seconda stanza. Capisco immediatamente che era di Sasha. Mia figlia ha sempre avuto un vero talento artistico, ma disegnava raramente. Non ne aveva la pazienza.<new_line>Era il disegno di un prato, con tre persone sedute a mangiare allegramente dei panini su una coperta da picnic beige. Galja, Sasha e... io.<new_line>Devo perlustrare ogni centimetro di questo appartamento.]],
			['35'] = [[Credo che Sasha tornerà presto alla normalità. Ha una costituzione incredibilmente forte e i farmaci stanno facendo effetto.]],
			['14'] = [[Ho letto per molte ore.<new_line>La lista è finita e ora sono seduto qui intontito. Mi fa male la testa. Non so dove andare...<new_line>Devo ricordare di più! Devo fare una passeggiata in città per risvegliare altri ricordi.]],
			['34'] = [[Sasha si è lamentata di nuovo per il dolore, ma questa volta ha aperto leggermente gli occhi. Mi sono chinato su di lei.<new_line>'Sasha, mi senti?'<new_line>Sasha respira con affanno e chiude di nuovo gli occhi.]],
			['32'] = [[La condizione di Sasha è peggiorata. La testa le penzola per dietro, il respiro è affannoso e irregolare, e ha perso colorito.<new_line>Mi sono fermato per soccorrerla, prima che fosse troppo tardi.<new_line>Ho fermato la macchina tra la vegetazione sul letto del fiume Chumysh, ho preso in braccio Sasha e l'ho stesa su un tappetino. Ha perso i sensi. Le ho inumidito le labbra con l'acqua e individuato tutte le sue ferite. Il proiettile era uscito. Le ho disinfettato e fasciato le ferite, e poi sono andato alla macchina per prendere la pistola. Il nemico sarebbe potuto arrivare a momenti.]],
			['30'] = [[È un pensiero folle. Ma più ci penso, più mi convinco di avere ragione.<new_line>Sasha non mi ha ucciso quando ne ha avuto l'occasione. E io non avrei consentito che la sua vita finisse per mano di un plotone di esecuzione. No, fuori discussione. Boris e gli altri possono condannarmi, darmi la caccia, ma io devo far uscire Sasha da lì.<new_line>Andremo lontano da qui. E quando saremo fuori pericolo, finalmente saprò la verità.]],
			['1_dream'] = {
				['2'] = [[Grazie per il tè, Anna Sergeyevna, torneremo a trovarti la prossima settimana', disse la ragazza dai capelli biondo scuro che mi teneva la mano.<new_line>'Grazie, cara, per essere venuta a trovarmi e perché mi pensi sempre. Sarei morta di disperazione tanto tempo fa senza di te... Mio figlio si è completamente dimenticato di me. Non mi chiama da un anno...'<new_line>L'anziana ci accompagna alla porta. La ragazza mi sussurra:<new_line>'Io devo partire quest'estate, ma andrai a trovarla, vero? Non le è rimasto nessuno...']],
				['1'] = [[Mi alzo ed esamino la medaglia. Una vecchia signora la osserva insieme a me.<new_line>'Era di mio marito', spiega. 'Ha combattuto persino nella battaglia per Berlino. Era sempre in prima linea contro i fascisti! È morto di polmonite un anno dopo lo scoppio della guerra.']],
				['3'] = [[Certo, se per te è così importante.'<new_line>La ragazza sorride e mi dà un bacio sul naso.]]
			},
			['23_2'] = [[La lotta finisce abbastanza presto, ma l'ingegnere riesce a trovare il tempo per maledirmi più volte nel frattempo.<new_line>Dobbiamo tornare al lavoro prima che arrivino altre api.]],
			['name'] = [[Strada per Casa]],
			['27_1'] = [[Un missile è stato sufficiente per abbattere il cancello. Kugel non ci ha deluso.<new_line>Boris aveva ragione: eravamo perfettamente in grado di passare senza tante vittime. Ma il leader è stato troppo furbo, purtroppo. Era come se sapesse del piano d'attacco. Quando il missile ha fatto esplodere il cancello, i banditi erano già in posizione di combattimento attorno alla breccia. Alcuni di loro erano armati di lanciagranate. I combattenti di Novokuzneck provenienti dalla parte posteriore sono caduti in un'imboscata, ma non si sono ritirati, neppure quando sono esplose le prime mine anti-uomo.]],
			['1_down'] = [[Ho tremato e aperto gli occhi. Attorno a me, la stessa città morta.<new_line>Cos'è successo qui? Chi era quella ragazza?]],
			['18'] = [[Ho appena finito di leggere l'ultima frase quando le forze mi abbandonano e mi accascio a terra. Ora so tutto. Galja è morta... Sarebbe stato meglio non averla mai ricordata. Ironia della sorte! Sarebbe stato meglio non ricordare niente!<new_line>Ma... quando ha contratto l'infezione? E da chi?<new_line>Da quella bambina, Nika... Sarà stato per questo che i suoi genitori l'avevano abbandonata. Per questo gli occhi di Galja erano gonfi e la porta era chiusa.<new_line>Galja sapeva che la ragazzina stava per morire? L'avrà intuito. Ma la mia Galja non avrebbe mai lasciato una bambina a morire da sola in un corridoio. Mai.<new_line>Galja ha sacrificato la sua vita per il bene della figlia di un estraneo. E io... Io ho abbandonato mia figlia e mia moglie. Marina aveva ragione: se fossi rimasto, Galja sarebbe ancora viva. È colpa mia. Solo mia...<new_line>Andrò a Barnaul per cercare Sasha. Se non è stata infettata, ho una probabilità di trovarla.<new_line>Ma mi perdonerà mai per la morte di sua madre? Riuscirò mai a perdonare me stesso?]]
		},
		['death_recovery'] = {
			['4'] = [[Sono riuscito a sconfiggere i predoni e a riprendermi le mie cose.]],
			['3'] = [[Sono riuscito a recuperare almeno una parte delle cose rubate. I predoni hanno già portato il resto al loro deposito nelle vicinanze. So dove si trova grazie alla mappa che ho trovato addosso a uno di loro. Immagino che sia ben sorvegliato: meglio prepararsi a combattere.]],
			['2'] = [[Come sospettavo, i predoni sono già passati e hanno preso quasi tutto. Fortunatamente hanno lasciato delle tracce, e poi non possono essere troppo lontano. Non dovrebbe essere difficile trovarli.]],
			['1'] = [[Non so come, ma sono sopravvissuto, e sono riuscito a raggiungere un porto sicuro. Credo di aver perso delle mie cose mentre ero privo di conoscenza. Devo tornare indietro per recuperarle.]],
			['name'] = [[Oggetti smarriti]],
			['hint'] = [[Ricordati di prendere le cose recuperate.]]
		},
		['belaz'] = {
			['2'] = [[Questo tipo è folle. Vuole montare un reattore atomico nel suo carrello da miniera! Mi ha dato la lista delle parti di ricambio e la mappa. A quanto pare sono folle quanto lui...]],
			['1'] = [[Mi hanno parlato di un tizio che cerca degli assistenti per riparare un carrello da miniera. Dovrei informarmi.]],
			['name'] = [[Città ambulante]],
			['3'] = [[Fatto.<new_line>Il proprietario del carrello da miniera, Vladimir, era veramente grato per il mio aiuto... Ha finalmente realizzato il suo sogno. Ha accettato di farmi viaggiare insieme a lui e alla sua famiglia e di portarmi ovunque vorrò. Vuole provare la sua personale città ambulante. Ora devo solo decidere la destinazione...]]
		},
		['emba_intro'] = {
			['5'] = [[Sto partecipando alla spedizione dell'organizzazione Progresso alla ricerca di tecnologie segrete. Devo parlare con il dottor Isaev.]],
			['4'] = [[Ho eliminato la minaccia nel bunker. Devo tornare dal Navigatore.]],
			['3'] = [[La spedizione ha bisogno di stabilirsi nell'insediamento, ma i suoi abitanti trattano gli scienziati con sospetto. Rimedierò eliminando la minaccia nei pressi dell'insediamento.]],
			['2'] = [[La spedizione ha bisogno di stabilirsi nell'insediamento, ma i suoi abitanti trattano gli scienziati con sospetto. Devi aiutarli a ottenere la loro fiducia. Il Navigatore della spedizione mi aggiornerà una volta raggiunta la località indicata.]],
			['1'] = [[Aiuto una spedizione di scienziati dell'organizzazione Progresso. Stanno studiando nuove anomalie biologiche.<new_line>Il dottor Isaev, il capo della spedizione, potrebbe conoscere l'origine di queste anomalie e sapere se sono una minaccia per gli uomini superstiti. Lo posso trovare nei pressi di Tver.<new_line>(Le missioni del dottor Isaev nei pressi di Tver sono disponibili a partire dal livello 25)]],
			['name'] = [[Sistemazione della spedizione]]
		},
		['halloween2020'] = {
			['name'] = [[Viaggio in ombra]],
			['10'] = [[La testa del cavaliere non mi ha rivolto nemmeno una parola. Vediamo se l'oste sarà in grado di farle sputare il rospo.]],
			['9'] = [[Sono riuscito a salvare la ragazza dalla mummia, ma lei è scomparsa senza dire una parola. Finalmente posso tornare a cercare il cavaliere. Spero che l'oste abbia delle informazioni utili.]],
			['8'] = [[Quando ha tentato di scappare, Igor è stato trascinato negli abissi, ma il cavaliere senza testa è giunto cavalcando sul pelo dell'acqua per recuperare il suo libro. Ho trovato il diario di bordo del vascello, ma la sfortuna ha voluto che fosse scritto in codice. È ora di tornare alla taverna.]],
			['7'] = [[Continuo a rifiutarmi di credere a ciò che ho visto, ma ormai non ha più importanza. Tutte le creature in cui mi sono imbattuto lavorano per Dracula e so che sta pianificando qualcosa. Per quanto riguarda Igor, invece, dovrei chiedere all'oste cosa significa che non si trova più sulla terraferma]],
			['6'] = [[Igor non era con Bloody Mary, ma sono sicuro di aver visto una marionetta dall'aria sospetta. L'oste potrebbe saperne qualcosa.]],
			['5'] = [[La missione era una trappola. Avrei dovuto capirlo. Devo sbrigarmi a raggiungere l'oste e raccontargli tutto.]],
			['4'] = [[Sono riuscito a distruggere il mostro, ma Igor è fuggito. Ha preso con sé un enorme tomo decorato con l'immagine di un occhio circondato da una stella obliqua.]],
			['3'] = [[So chi sta dissotterrando i cadaveri. Un uomo di nome Igor sta radunando pezzi di corpi umani nel tentativo di creare un mostro di Frankenstein. Il mio compito è porre fine a questa pazzia.]],
			['2'] = [[Oltrepasso il portale e mi ritrovo in una taverna chiassosa, ricolma di cacciatori che condividono i tavoli con i mostri più improbabili. Nessuno sembra notarmi, fatta eccezione per un oste dall'aspetto demoniaco che mi invita a raggiungerlo con un cenno della mano.]],
			['1'] = [[Ho ricevuto un invito a una battuta di caccia nel regno delle Tenebre. Devo raggiungere il portale più vicino.]],
			['11'] = [[Non so se il piano dell'oste funzionerà, ma è la nostra unica speranza.]],
			['12'] = [[Siamo riusciti per miracolo a salvare il confine delle Tenebre dalla distruzione. Non voglio nemmeno immaginare cosa il prossimo anno avrà in serbo per noi.]]
		},
		['new_tutorial'] = {
			['9'] = [[Mentre stavo raccogliendo i pezzi di selce, ho sentito un fruscio alle mie spalle. Un ratto stava sedendo sul bordo del cratere. Guardando meglio, ho visto altri tre ratti. Non si muovevano, mi osservavano con i loro piccoli occhi rossi e le loro zampette tremanti.<new_line>I ratti erano pelle e ossa, ma di enormi dimensioni. Mi arrivavano al ginocchio. Ho sentito un brivido scendermi per la schiena. Lentamente, cercando di non respirare, sono risalito dal cratere e ho iniziato ad arretrare fino all'edificio più vicino. <new_line>I ratti si sono avvicinati. Erano sempre più numerosi. Cinque... no, sei... Sembrava che anche loro fossero spaventati, ma erano molti e la fame può avere il sopravvento sulla paura.<new_line>Sette... nove...<new_line>Per un istante mi sono fermato, poi ho iniziato a correre via dalla città.<new_line><new_line>- Abbandona la città.<new_line>- Raggiungi il bosco.<new_line>- Se un ratto ti attacca, allontanati da lui.]],
			['8'] = [[Ho raggiunto questo cratere molto più rapidamente del primo. Mi sentivo meglio, nonostante quel liquame nauseabondo. Quando l'unica alternativa è la morte, il corpo umano è in grado di adattarsi a qualsiasi cosa.<new_line>Da questo cratere non proveniva alcun odore, il che mi ha dato speranza.<new_line>Ma è stato inutile: al suo interno non c'era neanche una goccia d'acqua! Ho raccolto un frammento di pietra, bello liscio, e l'ho esaminato più da vicino. Sembra un pezzo di selce. Mi aiuterà ad accendere un fuoco.<new_line><new_line>- Raccogli tutta la selce in questo luogo.<new_line>- Nel menu ]],
			['7'] = [[Mi sono fatto coraggio e ho bevuto. Ho iniziato a sentirmi male dopo un paio di sorsi, ma ora nulla mi trattiene dal bere. La sete è una motivazione estremamente potente. <new_line>Ho scavalcato il bordo del cratere e mi sono seduto. Mi sembrava di essere sul punto di vomitare, ma la sensazione è presto passata.<new_line>Ho bisogno di più acqua, ma la fetida sostanza che riempie questo cratere mi ucciderà ancora prima della sete. Che ci siano altri crateri pieni di acqua pulita?<new_line>Devo trovarli. Poi potrò lasciare la città. La vista di queste rovine mi fa rabbrividire.<new_line><new_line>- Visita un altro cratere.]],
			['10'] = [[I ratti non mi hanno seguito. Ho corso fino a quando ho raggiunto un gruppo di cespugli avvizziti, poi mi sono lasciato cadere mollemente sul terreno spaccato.<new_line>Il mio corpo esausto aveva bisogno di dormire, ma mi sono imposto di alzarmi di nuovo. Devo controllare di essere al sicuro. E devo trovare del cibo.<new_line>Oltre a questi cespugli secchi c'è un bosco. Devo spezzare dei rami e accendere un fuoco, così potrò tenere alla larga gli ospiti indesiderati.<new_line>Se ci fossero molti rami, potrei persino costruirmi una capanna.<new_line><new_line>- Apri il menu ]],
			['11'] = [[Mentre stavo spezzando i rami secchi dagli alberi, ho avuto l'impressione di vedere delle ombre che si muovevano rapidamente fra i tronchi. Potrebbe essere la mia mente che mi gioca qualche brutto tiro, ma non si sa mai...<new_line>Ho fatto un mucchio con quello che ho raccolto. Così proprio non va! A questo ritmo, per costruire il falò, impiegherò tutta la notte. Cosa posso fare? Forse potrei costruire un'ascia usando la selce?<new_line><new_line>- Apri il menu ]],
			['12'] = [[Il risultato finale ha solo una vaga somiglianza con un'ascia, ma non ho tempo per ricominciare.<new_line>È ora di tornare al lavoro.<new_line><new_line>- Apri l'inventario.<new_line>- Seleziona l'ascia in selce.<new_line>- Tocca ]],
			['16'] = [[Non avrei mai pensato che del grano saraceno bollito, senza sale, latte, né olio, potesse essere tanto buono! L'ho mangiato rapidamente e con ingordigia, come se temessi che qualcuno potesse venire a rubarmelo.<new_line>È ora di dormire, non sarei in grado di fare nient'altro.<new_line>Quando mi sveglierò, dovrò andarmene. Devo cercare delle persone.<new_line>E delle risposte.<new_line><new_line>- Chiudi il menu ]],
			['15'] = [[Ho acceso il fuoco nel giro di qualche secondo. Ho steso le mani di fronte ad esso e mi è sembrato di sentire scorrere di nuovo la vita nelle mie vene. Ho sentito un piacevole calore diffondersi per tutte le mie stanche membra. Ero pronto per entrare nella mia piccola capanna e dormire, quando il mio stomaco ha brontolato. <new_line>Ho dato un'occhiata ai grossi funghi bianchi, guardando con sospetto la peluria putrida che li copriva. No, non è il caso di rischiare, è meglio preparare un po' di grano saraceno. <new_line>Per farlo, mi servirà dell'acqua pulita. Mi è bastato ripensare alla fetida acqua del cratere per farmi tornare la nausea. <new_line>Devo bollire l'acqua in un recipiente vuoto.<new_line><new_line>- Apri l'inventario.<new_line>- Seleziona l'acqua sporca.<new_line>- Tocca ]],
			['14'] = [[Dopo aver esaminato attentamente il coltello e averlo riposto nella cintura, mi sento un po' più fiducioso. <new_line>Ora devo accendere il fuoco e costruire una capanna. La temperatura è scesa molto e ho iniziato a tremare all'interno della mia giacca leggera.<new_line><new_line>- Apri il menu ]],
			['13'] = [[Nonostante avessi solo un'ascia di fortuna, ho potuto lavorare molto più rapidamente. Ho continuato a tagliare rami finché le braccia e la schiena hanno iniziato a dolere per la stanchezza. La legna da ardere raccolta mi sarebbe bastata per qualche tempo. L'ho ammucchiata in una catasta e sono tornato nel bosco.<new_line>L'ascia ha svolto un buon lavoro, userò il secondo pezzo di selce per costruire un coltello. Se i ratti mi attaccheranno, potrò difendermi.<new_line><new_line>- Costruisci un coltello in selce.]],
			['6'] = [[Nonostante avessi una sete incredibile, mi è venuta la nausea appena l'acqua ha toccato le mie labbra. Il suo odore acre e le chiazze gialle oleose sulla superficie non mi danno molta fiducia, ma non ho scelta. Se non berrò, collasserò dentro a questo cratere. E non mi rialzerò più.<new_line><new_line>- Apri l'inventario.<new_line>- Seleziona l'acqua sporca.<new_line>- Tocca ]],
			['5'] = [[Zoppicando verso il cratere, mi sono sentito soffocare per l'aria piena di polvere. La gola pungeva come se fosse stata attaccata da una colonia di vespe arrabbiate. Le mie labbra erano secche e spaccate e i miei occhi lacrimavano fino quasi a oscurarmi la vista. <new_line>Con le mani graffiate e sanguinanti mi sono trascinato fino all'orlo del cratere e ho guardato cosa c'era al suo interno. <new_line>Avevo ragione: acqua!<new_line><new_line>- Apri il menu ]],
			['4'] = [[Il cibo ha parzialmente ripristinato le mie forze. Anche il dolore è diminuito.<new_line>E ora? Dove andrò? <new_line>Ho dato una rapida occhiata alla foto che ritrae i proprietari dell'appartamento, la cornice coperta di polvere. Devo cercare altre persone. C'è ancora qualcuno in questa città distrutta? In caso contrario, che ne è stato della popolazione? Cosa diavolo è successo qui? <new_line>Se in città non c'è più nessuno, troverò qualcuno altrove. Prima, però, devo far scorta di cibo e acqua. Non c'è acqua in questo appartamento. <new_line>Attraverso una finestra rotta, ho visto un cratere dall'altro lato della strada. Al suo interno ho visto scintillare qualcosa. Che fosse acqua?<new_line><new_line>- Val alla mappa.<new_line>- Oppure premi il pulsante ]],
			['3'] = [[Le verdure nel frigorifero sono marcite molto tempo fa, formando una poltiglia puzzolente, ma nel ripiano inferiore della credenza ho trovato dei fiammiferi, del grano saraceno e una lattina di carne in umido. Questa scatoletta mi fa sentire come se avessi appena vinto la lotteria!<new_line><new_line>- Apri l'inventario.<new_line>- Scegli ]],
			['2'] = [[Mentre camminavo, con le gambe che riuscivano a stento a spostarsi in avanti, nuove domande hanno affollato la mia mente. Domande a cui non ero in grado di rispondere.<new_line>Chi sono? Da dove vengo? Come sono arrivato qui?<new_line>Ho cercato di rivangare anche un solo ricordo del mio passato, ma la memoria si è rifiutata di collaborare.<new_line>L'edificio è vuoto e sembra esserlo da molto tempo. Le porte degli appartamenti erano chiuse, ma sono riuscito a entrare in uno di essi grazie a un buco in una parete.<new_line>Mobili rotti, piante morte, frammenti di piatti per terra, libri caduti da una libreria distrutta... Sedendo sull'unica sedia rimasta intatta, ho sentito il dolore attanagliarmi la pancia. Devo mangiare.<new_line><new_line>- Apri il menu ]],
			['1'] = [[Ho aperto gli occhi e ho respirato a fondo. L'aria gelida mi ha ustionato i polmoni. <new_line>Ogni movimento era doloroso. Ho piantato il gomito sull'asfalto ruvido, ho stretto i denti e mi sono sollevato un po'. La foschia nera di fronte a me è arretrata e ho scorto qualcosa fra la nebbia.<new_line>Rovine senza vita... <new_line>Neanche una persona...<new_line>Cos'è successo? Dove sono? Cosa farò ora?<new_line>Stavo tremando come una foglia. Dovevo decidere cosa fare, dovevo muovermi finché ne avevo ancora le forze. Il vento ha iniziato a ululare, gettandomi in faccia sabbia e polvere, riempiendomi occhi, naso e bocca. Il mio corpo è stato scosso dalla tosse.<new_line>C'era un edificio accanto a me. Lì avrei trovato un po' di rifugio. Il dolore era insopportabile, ma mi sono alzato e, barcollando, ho mosso i primi passi.<new_line><new_line>- Tocca la mappa per scegliere una direzione.<new_line>- Oppure premi il pulsante ]],
			['name'] = [[Andiamo!]],
			['17'] = [[Ora devi cavartela autonomamente. Buona fortuna!<new_line><new_line>Alcuni ultimi consigli:<new_line><new_line>- Hai ricevuto delle nuove missioni, dacci un'occhiata.<new_line><new_line>- I numeri in alto indicano lo stato del tuo personaggio. Tocca l'icona accanto a un numero per maggiori informazioni.<new_line><new_line>- Nei prossimi centri che incontrerai ci sarà un elevato tasso di radiazioni. Fai attenzione: non lasciare che tale valore superi 40 unità.<new_line><new_line>- Cerca di non spostarti né cercare oggetti di notte: potresti romperti braccia o gambe.<new_line><new_line>- Trova cibo e acqua prima di iniziare un lungo viaggio.<new_line><new_line>- Il tuo personaggio può svenire se non riposa abbastanza spesso.<new_line><new_line>- Non avvicinarti all'acqua se non hai una zattera.<new_line><new_line>- Pensa a cosa stai per fare prima di farlo e vivrai più a lungo...]]
		},
		['smithcraft'] = {
			['5'] = [[Io e Ignat abbiamo costruito una bella forgia! Spero che questo lo renda felice...]],
			['4'] = [[Ora è il momento perfetto per andare al vecchio magazzino e costruire la forgia!]],
			['3'] = [[Ho raccolto tutti i materiali per la forgia. Andrò a dirlo a Ignat. Sarà al settimo cielo.<new_line><new_line>- Porta tutti i materiali a Ignat]],
			['2'] = [[Ho stretto un accordo con Ignat dell'accampamento di Tula. Gli costruirò una forgia e lui, in cambio, mi insegnerà a lavorare da fabbro. Devo trovare i materiali necessari e portarli all'accampamento.<new_line><new_line>(Puoi recuperare mattoni refrattari smantellando il forno nella casa di legno)]],
			['1'] = [[Nell'accampamento di sopravvissuti di Tula, la mia attenzione viene attirata da un uomo pallido di mezz'età, seduto su uno sgabello di legno. Stava lavando dei panni in una bacinella di metallo di fronte a lui.]],
			['name'] = [[Lavori da fabbro]]
		},
		['advice_trauma'] = {
			['name'] = [[Consiglio: antidolorifici]],
			['hint'] = [[Assumi antidolorifici]],
			['1'] = [[Gli antidolorifici ti permettono di ignorare gli effetti di una ferita per 24 ore. Se una frattura alla gamba ti impedisce di trasportare gli oggetti, prendi degli antidolorifici!]]
		},
		['new_year2020'] = {
			['14'] = [[Unendo le forze, io e la fanciulla di neve abbiamo salvato Nonno Gelo e il capodanno.]],
			['13'] = [[La regina d'inverno è stata sconfitta, ma ora Nonno Gelo è intrappolato in una prigione di ghiaccio. La fanciulla di neve ha un piano. Mi ha chiesto di finire di addobbare l'albero.]],
			['2'] = [[La fanciulla di neve ha scoperto come arrivare al terem di Nonno Gelo. Mi ha chiesto di recarmici per dare un'occhiata in giro.]],
			['10'] = [[Se voglio quella piuma, devo attirare l'uccello di fuoco. La fanciulla di neve mi ha dato delle mele incantate per aiutarmi.]],
			['9'] = [[Per proteggerci dalla regina d'inverno, ho bisogno della piuma di un uccello di fuoco. Mentre la fanciulla di neve elabora un piano su come ottenerla, continuerò ad addobbare l'albero.]],
			['8'] = [[La sfera che ho trovato si è rivelata la misteriosa stella polare. La fanciulla di neve mi ha suggerito di recarmi nelle terre desolate in una notte limpida e di alzare la sfera al cielo.]],
			['7'] = [[La fanciulla di neve sta esaminando la sfera mistica. Dovrei approfittarne per continuare ad addobbare l'albero.]],
			['6'] = [[Un pupazzo di neve gigante sta attraversando le terre desolate. Devo fermarlo prima che combini qualche disastro.]],
			['5'] = [[Nonno Gelo è stato rapito da una donna misteriosa che blatera di un inverno perenne che colpirà tutta l'umanità. Mentre continuo ad addobbare l'albero di capodanno, la fanciulla di neve riflette sul da farsi.]],
			['4'] = [[Abbiamo trovato l'accampamento dei briganti. Forse riuscirò a scoprire cos'è successo a Nonno Gelo.]],
			['3'] = [[Il terem è stato saccheggiato dai briganti di ghiaccio. Farò meglio a continuare ad addobbare l'albero di capodanno per aiutare la fanciulla di neve a scovare il loro accampamento.]],
			['name'] = [[Miracolo festivo]],
			['1'] = [[La fanciulla di neve ha bisogno del mio aiuto. Nonno Gelo è scomparso e, con lui, la magia delle feste. Se voglio salvarlo, devo seguire le istruzioni della fanciulla e addobbare l'albero di capodanno.]],
			['11'] = [[Per prepararmi all'incontro con la regina d'inverno, devo finire di appendere gli ultimi addobbi all'albero di capodanno.]],
			['12'] = [[È tutto pronto. È ora di usare la stella polare e recarci al palazzo di ghiaccio. Nonno Gelo, arriva la cavalleria!]]
		},
		['halloween_hunt'] = {
			['1'] = [[Un cacciatore di mostri ferito mi ha chiesto di trovare ed eliminare un'antica creatura: una mummia. Devo raggiungere e perlustrare il luogo segnato sulla mappa.]],
			['2'] = [[La mummia è morta. Per davvero, questa volta (ho controllato). Non resta che tornare dal cacciatore.]],
			['9'] = [[Ora devo sfrecciare fianco a fianco al cavaliere senza testa e sconfiggerlo. Non mi resta che guidare e attendere.]],
			['8'] = [[Ho sconfitto il re delle zucche. Non resta che portare la sua testa al cacciatore.]],
			['7'] = [[Devo seguire le istruzioni che mi ha dato il cacciatore per evocare il re delle zucche e distruggerlo.]],
			['6'] = [[Ho eliminato il mostro di Frankenstein, finalmente posso tornare dal cacciatore. Questa volta, mi darà la ricompensa che mi spetta.]],
			['5'] = [[Sono entrato in possesso del gomitolo incantato. Non resta che utilizzarlo per localizzare il mostro di Frankenstein.]],
			['4'] = [[Per acquisire il gomitolo di lana incantato, devo trovare tutti gli oggetti della lista di Baba Yaga e invocarla di nuovo.]],
			['3'] = [[Il mostro di Frankenstein può essere localizzato solo tramite il gomitolo incantato di Baba Yaga. Il cacciatore mi ha dato le istruzioni per invocarla. Devo fare in modo di non sbagliare il rituale, altrimenti dovrò ricominciare daccapo.]],
			['name'] = [[Caccia ai mostri]],
			['10'] = [[Ora che il cavaliere è stato sconfitto, il suo destriero servirà me. Devo tornare dal cacciatore per raccontargli tutto.]],
			['11'] = [[Il nostro ultimo obiettivo è il conte Dracula. Il cacciatore mi ha detto di informarlo quando sono pronto per la battaglia.]],
			['12'] = [[Dracula è stato sconfitto e la caccia è terminata. Il cacciatore ha detto che potremo rivederci l'anno prossimo.]]
		},
		['project_emba'] = {
			['48'] = [[Ho aiutato gli archivisti a occuparsi della minaccia biologica e abbiamo messo in salvo il micelio reliquia.<new_line>Le conseguenze catastrofiche del progetto E.M.B.A. hanno influenzato i piani di Progresso. Gli scienziati hanno deciso di cambiare attività e di proteggere le tecnologie, invece di inventarne di nuove e ripararle. Spero che un giorno incontrerò di nuovo il dottor Isaev.]],
			['47'] = [[L'operazione di rinverdimento non ha avuto successo, ma Isaev ha altri incarichi per me, quindi devo tornare a parlargli.]],
			['9'] = [[Ho trovato la scatola nera. Ora dobbiamo prendere gli oggetti necessari per le ricerche e aiutare il Biologo a migliorare il laboratorio.<new_line>Dopo dovrò parlare con il dottor Isaev.]],
			['8'] = [[Devo trovare la scatola nera per continuare le ricerche di Progresso. La scatola nera si trova presso la stazione biologica nella città della scienza. Però, per ottenere la scatola nera, dovrò occuparmi del biomutante che vive lì.]],
			['name'] = [[Progetto E.M.B.A.]],
			['6'] = [[Devo trovare la scatola nera. Il Navigatore mi aiuterà a farlo. Devo parlargli.]],
			['5'] = [[Devo svolgere delle commissioni per conto degli archivisti per trovare i documenti segreti. Devo consegnare i documenti al Biologo e aiutarlo con le sue ricerche.<new_line>Puoi ottenere gli ordini nel bar dell'insediamento.]],
			['4'] = [[Sto partecipando alla spedizione dell'organizzazione Progresso alla ricerca di tecnologie segrete.<new_line>Ho ottenuto i dati necessari. Devo raggiungere la località degli archivisti successiva e parlare con il dottor Isaev.]],
			['3'] = [[Il personale di Progresso è pronto a partire per il prossimo insediamento. Non appena tutto sarà pronto, dovrò fare rapporto al dottor Isaev.]],
			['2'] = [[Ho aiutato il Biologo con le ricerche, ora devo tornare dal dottor Isaev.]],
			['1'] = [[Ho trovato la scatola nera. Ora devo prendere gli oggetti necessari per le ricerche e aiutare il Biologo a migliorare il laboratorio.]],
			['24'] = [[Devo trovare la scatola nera per continuare le ricerche di Progresso. La scatola nera si trova presso la stazione biologica nella città della scienza.]],
			['101'] = [[Aiuta il Biologo con le sue ricerche.]]
		},
		['emba_briefing'] = {
			['6'] = [[Devo portare la scatola nera al Biologo e aiutarlo con le ricerche. Prima, però, non guasterebbe prendere della biomassa per creare armi o altri oggetti utili al laboratorio del Biologo.<new_line>Il Navigatore ha trovato un posto dove vive un gruppo di biomutanti, quindi potrò ottenere del micelio da loro.]],
			['5'] = [[Ho ispezionato la stazione biologica e trovato la scatola nera, ora devo tornare dal Navigatore.]],
			['4'] = [[Devo trovare la scatola nera. Si trova al centro della città della scienza, in una cassaforte nascosta nella stazione biologica.]],
			['3'] = [[Devo trovare la scatola nera. Il Navigatore mi aiuterà a farlo. Devo parlargli.]],
			['2'] = [[Devo ottenere una tuta protettiva speciale dell'AIDA. Permette di entrare nelle zone a rischio biologico. È possibile scambiare il kit con il personale di Progresso in cambio di dischetti nella sezione Consumabili.<new_line>Devo tornare dal dottor Isaev con il kit AIDA.]],
			['1'] = [[Devo portare i documenti al Biologo per aiutarlo con le ricerche. Dopo devo parlare con il dottor Isaev.]],
			['name'] = [[Sommario]]
		},
		['new_year2022'] = {
			['8'] = [[Dobbiamo trovare Cyberbabbo e convincerlo ad aiutarci. Ha detto qualcosa riguardo a un concerto.]],
			['13'] = [[Abbiamo salvato il Natale! Celebrerò questa vittoria insieme ai miei nuovi amici.<new_line>Ora lavoreremo insieme per riportare in vita il Natale e per portare lo spirito delle festività alla gente.]],
			['6'] = [[Orso Natale mi ha detto di "occuparmi" del magazzino dei regali nel parco di divertimenti. Una volta che sarà sotto il mio controllo, daremo il via al suo piano per distribuire equamente le risorse.]],
			['1'] = [[Mi trovo in uno strano luogo. Al centro c'è un meraviglioso albero di Natale. Il mio intuito mi dice che l'ornamento che ho trovato dovrebbe essere appeso lì.]],
			['4'] = [[A quanto pare, Babbo Naclava è il segugio. Sembra che sia sulle tracce di Babbo Natale. Dobbiamo seguirlo.]],
			['3'] = [[Devo fare delle buone azioni per riportare la magia in questo mondo. Otterrò gli ornamenti come ricompense e li userò per decorare l'albero di Natale. Poi attiveremo la prossima piattaforma magica.]],
			['name'] = [[Capodanno 2023]],
			['10'] = [[Per attaccare l'arma di Krampus, prima dovrò distruggere le sue chiuse energetiche di emergenza.]],
			['2'] = [[Dopo aver decorato l'albero di Natale, è comparso uno strano tizio. Assomiglia a Babbo Natale, ma dice di non provenire da questo mondo. Ho acconsentito ad aiutarlo a trovare il nostro Babbo Natale.]],
			['12'] = [[Tutto è pronto per la battaglia contro Frosty. Babbo Stregone attende il mio segnale per mandarmi al nascondiglio di Frosty.]]
		},
		['searchOfTruth'] = {
			['48'] = [[Più mi avvicino alla destinazione, più il suono diventa limpido: qualcuno stava sparando un AK. Ma chi? E chi era il bersaglio?<new_line>A un incrocio, ho notato tre cadaveri recenti: il primo era un omone sulla cinquantina, con il corpo ricoperto di morsi e le braccia amputate; il secondo era un lupo nero, come quelli che ho già incontrato, ma più piccolo, con il muso e il collo squarciati da un coltello; il terzo non è neppure un corpo, ma dei brandelli di un corpo: pezzi di carne e pelliccia nera sparpagliati per tutto l'incrocio. Era come se il lupo fosse stato sgretolato da un esplosivo al plastico o qualcosa di simile.<new_line>Poi la mitragliatrice ha cominciato di nuovo a sparare.]],
			['38'] = [[Devo riportare il corpo di un ragazzo a sua madre. Una missione molto triste.<new_line>È sorprendente come va la vita, a volte. Sopravvivi a un'epidemia, a una guerra nucleare, a cinque anni di carestia, e poi la vita ti crolla addosso per via di uno stupido incidente. Succederà anche a me?<new_line>Devo trovare questo Vanya, il compagno di Gena, e scoprire in quale edificio è avvenuto il crollo.]],
			['58'] = [[Il barcaiolo ha menzionato un istituto scientifico i cui dipendenti lavoravano a un qualche tipo di virus, ma non mi ha detto dove si trova. Dice che è troppo pericoloso e che non sono ancora pronto. Probabilmente ha ragione. Sono appena riuscito a ricordare il mio passato e ho ancora un po' di confusione in testa.<new_line>Potrebbe essere una buona idea completare qualche compito al villaggio di pescatori per guadagnare qualcosa e rimettermi in forma.]],
			['28'] = [[C'è qualcosa che non va. Sembra che la dottoressa non sia arrivata a destinazione. Dovrei tornare a quella strana barriera che ho visto mentre venivo qui. Aveva decisamente qualcosa di sospetto.]],
			['18'] = [[Ho trovato gli ingredienti per la polvere da sparo. Ora devo andare al punto di incontro e occuparmi di questi pazzi una volta per tutte.]],
			['45'] = [[Ovviamente non ho alcuna intenzione di tornare indietro per Varya. La porterò da sua zia, chiederò alla gente del posto di Gavrilov e me ne andrò.<new_line>Uno strano dettaglio mi ha distratto dai miei pensieri: l'erba vicino a dove avevo lasciato la mia roba era calpestata. Ho detto a Varya di aspettarmi lì e ho cercato di vedere da dove potesse essere arrivato l'ospite indesiderato.<new_line>Non ho dovuto allontanarmi molto: c'erano impronte nella terra bagnata oltre l'erba.<new_line>E avevo già visto quelle impronte, all'Istituto di virologia. Erano simili a quelle di un lupo, ma troppo grandi per un lupo.<new_line>Un grande lupo nero cieco, contro cui i proiettili sono inutili...<new_line>Dobbiamo andare via da qui in fretta.]],
			['55'] = [[Sono pronto. È ora di trovare il barcaiolo e scoprire dove si trova questo istituto scientifico.]],
			['25'] = [[Ho trovato un registratore nell'istituto. Se sono fortunato, forse apparteneva a uno di questi scienziati. Devo cambiargli le batterie.]],
			['35'] = [[Quando me ne sono andato dall'istituto, la prima cosa che ho fatto è stata esaminare il trofeo più importante: la lettera dalla scorta nella Collezione Virologica di Stato. Come temevo, la carta scadente si era inumidita e l'inchiostro si era sbiadito, sia sulla busta che sulla lettera.<new_line>Ma quel poco che sono riuscito a decifrare è intrigante. 'Non voglio spaventarti, ma la situazione è critica. Nesterov non si è presentato in laboratorio oggi...'. Sembra che ci fosse un grosso segreto in questo posto. Devo andare a Tula e parlare con questa Nadezhda Kulik. Spero che sappia qualcosa, e che sia ancora viva.<new_line>Se sono fortunato, troverò anche l'autore della lettera.]],
			['31'] = [[All'apparenza, il diario apparteneva a una prigioniera, una dottoressa di nome Svetlana. Forse è lei la ricercatrice di Leningrado?<new_line>Significa che non è stata ucciso alla barriera, ma l'hanno portata qui. Tenevano i prigionieri nell'edificio e li costringevano a prestare cure mediche ai banditi.<new_line>Sembra che alcuni - i banditi e la dottoressa - siano immuni dal virus. È un peccato che neppure Svetlana sappia chi ha bombardato l'URSS.<new_line>Ha anche scritto che l'epidemia è scoppiata contemporaneamente in tutto il Paese, ma non può essere vero.<new_line>In ogni caso, devo scoprire come sono morti questi banditi e cosa è successo a Svetlana.]],
			['41'] = [[Ci ho messo un'eternità a trovare l'edificio di Larisa Shashkova. È un miracolo che Nadezhda Kulik si ricordasse l'indirizzo esatto.<new_line>L'edificio ha resistito ai bombardamenti, quasi intatto. Ho salito le scale, controllando attentamente tutti i piani. La mia fortuna continuava: sotto il campanello di ogni porta c'era la targhetta con il cognome dell'inquilino.]],
			['11'] = [[Cadaveri, case distrutte... Sembra un incubo.<new_line>Monchegorsk è più avanti. Spero di riuscire a trovare dei sopravvissuti lì.]],
			['21'] = [[Non capisco. Cos'è successo davvero su quella rompighiaccio?<new_line>Spero che i sopravvissuti dell'accampamento sappiano qualcosa. Il barcaiolo mi ha detto che l'accampamento si trova a ovest di Petrozavodsk. Dovrei mettermi in marcia.]],
			['15'] = [[Il barcaiolo mi ha chiesto di trovare il suo amico. Poi mi aiuterà ad attraversare queste terre desolate radioattive per arrivare a Belomorsk.]],
			['6'] = [[A pochi chilometri da Murmansk la ferrovia si è interrotta davanti a un enorme cratere lasciato da un'esplosione. <new_line><new_line>Non c'è modo di oltrepassarlo. È ora di lasciare la draisina ed esplorare la città a piedi.]],
			['5'] = [[Mentre camminavo sono incappato in una draisina vecchia quanto la ferrovia stessa. Nonostante il suo aspetto sgangherato, appena ho premuto la leva è partita senza difficoltà. <new_line><new_line>Di sicuro arriverò a Murmansk più in fretta con questo ferrovecchio che sulle mie gambe.]],
			['4'] = [[A giudicare dal piano di evacuazione, gli abitanti di Zapolyarny se ne sono andati da molto tempo. Sono partiti in treno verso Murmansk, una città più vicina alla civiltà, con grandi ospedali e risorse adeguate. <new_line><new_line>Devo seguire la ferrovia verso est. Magari troverò un modo più rapido per arrivare a Murmansk.]],
			['3'] = [[Stando a quello che ho visto, la città è stata colpita da una qualche malattia. Dev'essere stata un'epidemia a tutti gli effetti: hanno perfino adibito un ospedale da campo nella scuola locale per cercare di gestire l'afflusso di malati.]],
			['hint6'] = [[Ispeziona la casa, il cratere e la farmacia. Prendi gli oggetti che trovi.]],
			['1'] = [[Dovrei cercare qualcuno e chiedere cos'è accaduto. Spero che questa città mi dia delle risposte.]],
			['33_1'] = [[Dopo tutto quello che ho visto, Tver sembra un vero bastione di civiltà.<new_line>L'accampamento non solo sopravvive, ma prospera! Ovunque, le persone vivono la propria vita. Vedo un porto in lontananza e i lavoratori portuali stanno scaricando casse di rifornimenti dalle navi.<new_line>Quante persone! Mi sembra di sognare. È come se la guerra non ci fosse mai stata e tutto fosse come dovrebbe essere.]],
			['39'] = [[Ora so dove cercare il corpo di Gena. Farò meglio a sbrigarmi, prima che i ratti o altri insetti arrivino prima di me. E l'idea di mostrare a una madre il corpo mutilato del figlio? No, rinuncio a questo piacere.<new_line>Quando arriverò lì, dovrò recuperare il corpo dai detriti. Devo portare un piede di porco e una pala.]],
			['hint4'] = [[Esplora il centro di comando della base militare.]],
			['19'] = [[Beh, ecco fatto. La città è finalmente libera dai pazzi. Ora devo tornare alla barca, così potremo partire per Belomorsk evitando la zona contaminata.]],
			['29'] = [[Come ho fatto a non accorgermene prima? Questa non è una barriera ferroviaria! È solo un ostacolo improvvisato, una semplice asse avvolta in un nastro rosso. Ma c'è un particolare ancora più raccapricciante nel burrone: un mucchio di scheletri.<new_line>Forse tra loro c'è la dottoressa. Tuttavia, neppure gli abiti, ormai monocromatici per lo sporco, possono servire a capirci qualcosa.<new_line>Tutta questa gente dev'essere stata uccisa dai banditi. Dopo essersi resi conto che la morsa della legge si era indebolita, i banditi hanno deciso di agire e di posizionare un 'posto di blocco'. I banditi hanno derubato e ucciso queste persone, hanno preso le loro auto, la loro roba. La dottoressa non avrà avuto una sorte migliore...<new_line>Un sentiero che porta fuori strada... Forse mi condurrà all'accampamento dei banditi. Qui gli eventi sono successi tanto tempo fa, quindi il posto dovrebbe essere sicuro. Ma devo fare attenzione.]],
			['37_1'] = [[Se Svetlana e i suoi compagni si sono rifugiati nell'edificio, allora i sopravvissuti del posto hanno costruito un accampamento di rifugi di legno. A giudicare dal fumo, qualcuno è persino riuscito a costruire una cucina funzionante.<new_line>Sono circa 25 persone, per lo più donne di diversa età.]],
			['34'] = [[Un'epidemia creata dall'uomo. Sembrano i farfugliamenti di un pazzo, ma chi dice che è impossibile?<new_line>Abbiamo alcune cose da cui partire. Day R, il telegramma, la bomboletta, il padre di Svetlana, l'Istituto di virologia a Mosca. Almeno ora so dove andare. E la ricompensa finale ne varrà la pena.<new_line>Ma dove posso cercare la mia famiglia? Dove?]],
			['24'] = [[Il barcaiolo ha avuto una bella idea: dovrei andare a cercare l'Istituto scientifico di Leningrado. Troverò di sicuro più informazioni, lì. Forse riuscirò anche a capir che diavolo è successo nell'autunno del 1984.]],
			['54'] = [[Deve essere insopportabile scoprire che l'uomo che ti ha dato la vita è un traditore. Ma sapevo che Svetlana era una donna forte. Sopporterà il dolore e imparerà a conviverci.<new_line>Mi è dispiaciuto per lei, ma non potevo fare niente per aiutarla.<new_line>Sono uscito dall'edificio e ho guardato il cielo: era freddo e ricoperto da nuvole grigio cenere. Ho pensato al passato lontano, che è nascosto dallo stesso velo cupo e senza vita.<new_line>Galja e Sasha. Ancora non so dove cercarle, ma non importa. Le troverò a tutti i costi, anche se dovrò passare il resto della vita cercandole.]],
			['44'] = [[Dove sarà andato l'ispettore? Ho ribaltato tutta la casa per cercare di trovare qualche indizio, ma non c'era niente. Se la dacia fosse stata abbandonata, probabilmente avrei trovato qualcosa qui. Ma gli ospiti indesiderati sono riusciti a lasciare il segno.<new_line>Mi conviene liberare la prigioniera. Ho preso la chiave dal corpo di Tema e sono andato nel fienile.]],
			['20'] = [[A Belomorsk ho trovato lo stesso tetro paesaggio.<new_line>In lontananza riesco a vedere il profilo di un'enorme nave. La vecchia rompighiaccio del pazzo è ancora nel porto.]],
			['10'] = [[Ho trovato il diario di un inserviente. Dev'essere successo qualcosa di brutto alla gente di questo posto prima dei bombardamenti. Dopotutto, hanno allestito un ospedale nell'edificio del municipio. Un'epidemia, forse?<new_line>Troverò una situazione identica anche a Olenegorsk?]],
			['40'] = [[Dopo la conversazione con Nadezhda, sono completamente distrutto. Ma non importa, perché pare che sono diretto a Oryol. E quali erano le probabilità di trovare qualcosa di utile a casa della sorella di Shashkov, una sorella con cui non sono neppure certo che fosse legato?<new_line>E poi ho avuto un pensiero inaspettato. C'è forse un collegamento tra il tradimento di Andrej Kulik e l'epidemia che ha colpito il Paese? E se c'è un collegamento, qual è?<new_line>Ho letto di nuovo il telegramma dell'uomo che si è tolto la vita a Novgorod. Day R...<new_line>No, finché c'è anche una minima possibilità di successo, io devo continuare!]],
			['30'] = [[Ho trovato un accampamento abbandonato dai banditi. Ci sono diverse auto e un sacco di roba rubata o sottratta ai morti, tra cui molte armi.<new_line>Nella sala da pranzo, ho trovato otto corpi di uomini in decomposizione. Erano piegati su loro stessi, come se fossero morti di un mal di pancia atroce.<new_line>Di fianco a uno di loro, c'era la pagina di un diario. Chissà se era la sua o se l'ha presa a qualcun altro...]],
			['hint5'] = [[Ispeziona la scuola]],
			['14'] = [[Mentre rovistavo nella stanza, ho trovato una bomboletta con su scritto 'Day R' e dei documenti di un ufficiale navale che aveva prestato servizio su una rompighiaccio a Belomorsk.<new_line>I marinai non perdono facilmente la ragione. Se la rompighiaccio è ancora nel porto di Belomorsk, forse potrò scoprire qualcosa.<new_line>Ma prima devo capire cosa sta succedendo con questo segnale radio da Kandalaksha.]],
			['41_lock'] = [[L'appartamento dei Shashkov, all'ultimo piano, mi ha riservato una sorpresa: la porta dell'appartamento è d'acciaio e la serratura non cede, neppure con la forza.<new_line>C'è solo una soluzione: farla esplodere.]],
			['hint2'] = [[Ispeziona il treno e leggi il giornale.]],
			['23'] = [[Un appassionato di film e natura ha filmato l'esplosione. Un'esplosione nucleare è inconfondibile!<new_line>Un'epidemia, bombe atomiche... Cos'è successo qui? Spero di trovare delle risposte alla base di pesca.]],
			['33_2'] = [[Devo parlare con Svetlana.]],
			['43'] = [[Ho trovato un nuovo fascio di lettere nell'ultimo cassetto della scrivania. Era più che altro sciocca corrispondenza personale, ma l'ultima lettera mi ha incuriosito.<new_line>Allora cos'è successo? Gli altri scienziati hanno scoperto il tradimento e l'hanno denunciato alle autorità. Non hanno solo denunciato Kulik, ma anche Nesterov e altri.<new_line>Qualcosa non torna. La lettera è stata spedita da Shashkov a fine aprile 1984, ma a giugno di quell'anno, la madre di Svetlana le ha scritto che il padre, Nesterov, era morto di infarto.<new_line>O ho capito male qualcosa oppure non si è trattato di infarto. Sarà stato giustiziato insieme a Kulik. La madre di Svetlana avrà voluto risparmiare un dispiacere alla figlia e ha evitato di rivelarle che il padre fosse un traditore. Santo cielo, che cosa le dirò?<new_line>Ma ancora non so come tutto questo sia collegato all'epidemia.<new_line>Non troverò altro qui. Credo che andrò a Brjansk e proverò a localizzare questo ispettore, Gavrilov.]],
			['53'] = [[Avevo versioni contrastanti nella testa su come sarebbe dovuta andare, ma il racconto del direttore mi ha chiarito le idee. Gli ultimi vuoti sono stati riempiti.<new_line>La verità mi porterà una specie di epifania personale? Improbabile. Odierò tutte le persone che hanno distrutto la civiltà con le proprie mani e vorrò vendetta? No. Sono troppo esausto per emozioni come quelle. Sento solo un quieto dolore.<new_line>L'unica domanda è: come dirò a Svetlana che il responsabile di tutto è... suo padre?<new_line>Non c'è più niente che mi trattiene a Kiev. È ora di tornare a Tver e di affrontare Svetlana.]],
			['13'] = [[Non era una persona che speravo di incontrare.<new_line>Chiunque fosse questo pazzo prima dell'esplosione, ora ha perso qualsiasi traccia di umanità.<new_line>La sua pazzia è causata da queste terre desolate?]],
			['47'] = [[Proprio come Mosca, Kiev è stata colpita duramente dai bombardamenti. Decine di edifici sono stati ridotti a cumuli di mattoni e cemento e, ogni decina di minuti, mi imbattevo in un altro cratere.<new_line>Improvvisamente, ho sentito il rumore distante di un'esplosione. Non ci potevo credere. Cosa poteva essere esploso dopo tutti questi anni?<new_line>Mi sono diretto di corsa verso il centro della città.]],
			['57'] = [[Ho scoperto che la parte centrale del paese è stata seriamente danneggiata dalla guerra nucleare ma ci sono ancora persone che abitano vicino a Tver.<new_line>Devo andare a cercare Svetlana. il capo delle guardie ha detto che probabilmente vive ancora a Tver.]],
			['9'] = [[Nel novembre del 1984, delle bombe atomiche furono sganciate su città e villaggi dell'URSS. I colpevoli di questi attacchi e i loro motivi sono tuttora sconosciuti.<new_line>Ho intercettato un messaggio radio da Kandalaksha. È un lungo viaggio, ma devo arrivarci. Prima, però, devo passare da Pushnoy a fare rifornimenti.]],
			['8'] = [[La base militare è in pessimo stato: alcuni edifici sono distrutti, altri sono degli scheletri bruciati. Gli interni sono tutti andati in fiamme e il legno carbonizzato e il metallo annerito sono coperti da uno spesso strato di polvere. Ma non ci sono cadaveri. Dove sono andati tutti i residenti della base?]],
			['hint9'] = [[Trova i reagenti nel negozio di elettronica e sviluppa la pellicola.<new_line>(Oggetti nella categoria Missioni.)]],
			['56'] = [[Nell'accampamento di turisti mi hanno avvisato che la parte centrale del paese ha raggiunto livelli estremamente alti di radiazioni. Devo prepararmi meglio e, già che ci sono, aiutare gli abitanti locali.]],
			['46'] = [[Dopo aver lasciato Varya dalla zia, che in realtà era una donna molto gentile e amichevole, ho iniziato a chiedere in giro dell'ispettore Gavrilov.<new_line>A quanto pare è molto conosciuto da queste parti. Al culmine dell'epidemia, Pavel Gavrilov e sua moglie abbandonarono tutti e andarono a Kiev. Probabilmente, speravano di fuggire in Polonia mentre le frontiere erano ancora aperte.]],
			['36'] = [[Il numero 28 di Krasnoarmeysky Prospekt non esiste più, insieme tutti gli altri numeri nel raggio di 500 metri.<new_line>Mi sono ritrovato nel cuore di un enorme campo deserto, pieno di frammenti di mattoni, cemento e rifiuti carbonizzati. Ogni tanto spuntavano dal terreno dei patetici resti di pareti e lastre di cemento. Suppongo che proprio in questo punto sia esplosa una bomba atomica nel 1984.<new_line>Nessuno avrebbe potuto sopravvivere, ma forse Nadezhda Kulik è riuscita a scappare dalla città prima dell'esplosione?]],
			['52'] = [[La lupa è morta, ma la sua vita ci è costata cara!<new_line>Quando sono tornato al mattatoio, ho visto i corpi di Gavrilov e degli altri, che dovevano essere seppelliti, ma siamo rimasti in pochi e i lupi rimasti sarebbero potuti comparire da un momento all'altro.<new_line>Un'ora dopo eravamo già di ritorno a Gorenichi. Una pira gigante ardeva dietro di noi, divorando i corpi dei coraggiosi guerrieri che avevano sacrificato la vita per famiglia e amici.]],
			['42'] = [[La porta si è scardinata facendo un rumore assordante ed è caduta dentro l'appartamento. Ho aspettato che la polvere si depositasse e ho iniziato a dare un'occhiata in giro.<new_line>L'appartamento ha due stanze, di cui una vuota e l'altra con un divano con lo scheletro di un uomo seduto sopra. All'inizio, ho pensato che fosse il marito di Larisa, ma nella tasca dei pantaloni ho trovato il pass d'accesso dell'Istituto di virologia di Leonid Shashkov. Pare che io abbia trovato il mio scienziato, che è stato ucciso dalla malattia.<new_line>La valigetta di Shashkov era vuota, e non ho trovato niente di utile nella sua stanza. Devo cercare in un'altra.]],
			['32'] = [[Ho trovato dove hanno tenuto Svetlana. È rimasta prigioniera fino a gennaio 1985. In seguito si è diretta a Tver. Anche se sono passati 5 anni, è possibile che sia ancora lì. <new_line>Ma prima devo tornare all'accampamento di turisti e scoprire se esiste un insediamento di sopravvissuti in quella direzione.]],
			['22'] = [[Nella periferia della città ho trovato il cadavere di un uomo che teneva stretta tra le mani una telecamera. Stava chiaramente filmando qualcosa prima di morire.<new_line>Non so se troverò qualcosa di nuovo, ma devo controllare. Devo trovare un laboratorio fotografico per sviluppare la pellicola.]],
			['12'] = [[Nella periferia della città ho visto la carcassa un giovane lupo ucciso da poco con una staffa di ferro! Devono esserci dei sopravvissuti qui! Devo controllare negli edifici... Non possono essere andati lontano!]],
			['hint1'] = [[Devo perlustrare Zapolyarny, non morire e dirigermi verso il treno abbandonato.]],
			['16'] = [[Vasily è morto. Devo riferirlo al barcaiolo e dargli il suo diario.]],
			['7'] = [[Murmansk è morta. Non c'è luce né acqua, ma ci sono scheletri ovunque: nelle case, nelle strade, dappertutto. Cercherò qualcosa che spieghi le orribili morti e il motivo per cui sono sopravvissuto.<new_line>Devo controllare la base militare. L'esercito sapeva sicuramente cosa stava succedendo!]],
			['17'] = [[Il barcaiolo vuole vendicarsi. Mi ha chiesto di rovistare nel negozio di ferramenta per trovare zolfo e salnitro per preparare della polvere da sparo.]],
			['37_2'] = [[In risposta alla mia domanda su Nadezhda Kulik, mi hanno mandato verso la baracca più piccola. Sembra che questa volta la fortuna mi sorrida.<new_line>Una cosa mi spaventa però: perché la gente del posto parla di Nadezhda con degli sguardi così tristi?]],
			['50'] = [[Sono sempre a un passo dalla soluzione. Per la prima volta, ho incontrato una persona direttamente coinvolta in quegli eventi. L'ispettore capo deve per forza sapere tutto.<new_line>Devo andare a Gorenichi!]],
			['51'] = [[Quando me ne sono andato dal centro sociale, mi è tornato in mente un muso nero insanguinato. Mi sono ricordato gli incontri precedenti con i lupi neri. Ne avevo già sconfitto uno una volta, ma questo lupo non sarebbe stato da solo. Inoltre, gli animali tendono a essere più feroci del solito quando proteggono la propria madre.<new_line>Molte persone non torneranno da questa caccia. Forse non tornerò neppure io.<new_line>Ma non sento paura. Era un sentimento diverso. Ho visto il corpo decapitato del cacciatore, fatto a pezzi, come se stesse succedendo in quel momento. E sono inerme.]],
			['26'] = [[Finalmente qualcosa di utile! È il diario di una scienziata: stava studiando la malattia sconosciuta.<new_line>A quanto pare ha avuto successo... Fantastico, significa che ha trovato un modo per contrastarla! Ma cosa le è successo? Ha scritto qualcosa riguardo a Novgorod.<new_line>Però mi chiedo: se è riuscita a curare dei pazienti, dove sono ora? Forse sono morti durante i bombardamenti? Spero di no. Probabilmente sono fuggiti dalla zona dell'impatto.<new_line>È arrivato il momento di andare all'ospedale di Novgorod.]],
			['27'] = [[Entrando in città, mi sono imbattuto nei resti bruciati di una macchina Volga e dal cadavere di un uomo. I miei occhi sono stati attratti dal piccolo foro rotondo nell'osso parietale del cervello. Non avevo mai visto nulla del genere. Sembrava che l'uomo avesse voluto farla finita e si fosse sparato in bocca, ma non c'erano armi nei paraggi. Ho controllato se nelle tasche dei pantaloni bruciati ci fossero delle cartucce, ma ho trovato un pezzo di carta.<new_line>Day R? L'avevo già visto da qualche parte. Era sulla bomboletta dello psicopatico di Monchegorsk.]],
			['name'] = [[Alla ricerca della verità]],
			['49'] = [[Il rumore della mitragliatrice mi ha condotto a una statua di Lysenko, il famoso compositore. Davanti alla statua c'era un altro lupo nero in una pozza di sangue scuro, in fin di vita.<new_line>Un ragazzo armato di Kalashnikov corre verso il lupo e, in preda a una sorta di follia, comincia a sparare al corpo quasi senza vita dell'animale. I suoi compagni, due uomini più grandi, si sono fermati un po' più lontano ad aspettare.<new_line>Dopo aver esaurito le munizioni, il ragazzo lascia cadere l'arma sui gradini, totalmente esausto e barcollante, e si siede accanto e avvicina le mani al viso. Gli uomini si sono avvicinati e hanno appoggiato una mano sulla spalla del ragazzo.]],
			['2'] = [[La città mi ha accolto con un silenzio di tomba. Ho proseguito e mi sono imbattuto in un brutto cratere lasciato da un'esplosione. C'è stata una guerra?<new_line><new_line>Devo raccogliere tutto quello che riesco a trovare: farmaci, cibo, vestiti. Iniziamo dalla farmacia.]],
			['hint3'] = [[Raggiungi Murmansk.]]
		},
		['common_complite'] = [[Ho trovato tutto il necessario. Posso tornare indietro a riscuotere la mia ricompensa.]]
	},
