				['13'] = [["Accidenti... Puoi almeno far apparire una bevanda forte? Posso pagare..."<new_line>Il contadino offrì delle monete allo stregone.]],
				['6'] = [["A chi servono? Ho controllato... Il metallo non vale niente, perciò nessuno lo accetterà. Dobbiamo bere ancora un po' per far lavorare i nostri cervelli."<new_line>Il contadino riempì entrambi i bicchieri.]],
				['5'] = [["Sto cercando di decidere... Cosa fare con le mie ricchezze..."]],
				['4'] = [["Avanti... Questa roba ti farà screscere i peli sul petto... Un brindisi all'amicizia!"<new_line>Il contadino riempì entrambi i bicchieri.]],
				['3'] = [[Mandai giù il distillato in un solo sorso. Era incredibilmente forte e mi bruciava in gola.<new_line>"A... allora... Ti pia... hic... piace il mio... distillato clandestino?"]],
				['2'] = [[Il contadino mise un altro bicchiere sul tavolo e lo riempì con il distillato clandestino.<new_line>"B... bel lavoro..."]],
				['1'] = [["Oh, sei tu. Insomma, quello... Beh, al diavolo... Ecco, con te... No, accidenti... Ho vinto alla lotteria e non farò più il contadino! Ma il montepremi è in valuta straniera. Non ho idea di cosa potrei fare, perciò sto cercando una soluzione. Potremmo... pensarci su insieme."]],
				['17'] = [[Mi risvegliai in un luogo vuoto e sconosciuto. Ho un mal di testa martellante. Come sono finito qui? Cos'è successo ieri?]]
			}
		},
		['emba_intro2'] = {
			['choice'] = {
				['6'] = [["Avete idea di cosa sia successo al mondo?"]],
				['5'] = [["Queste anomalie... Da dove provengono?"]],
				['4'] = [["Va bene."]],
				['3'] = [["Comunque, chi sareste?"]],
				['2'] = [["Come posso essere d'aiuto?"]],
				['1'] = [[Avvicinati]],
				['7'] = [["No, è tutto chiaro."]]
			},
			['episode'] = {
				['6'] = [["Stiamo raccogliendo dati per scoprirlo. Per ora possiamo solo ipotizzare che siano dovute alla catastrofe globale. Noi, per così dire, lavoriamo sul campo, mentre i nostri colleghi vicino a Tver si occupano della parte teorica.<new_line>Potrai parlare con loro quando arriverai lì. Nel frattempo, vorremmo che ci aiutassi con la raccolta dei dati."]],
				['5'] = [["Altre domande?"]],
				['4'] = [["Siamo la spedizione dell'organizzazione Progresso. Ora i nostri capi si trovano a Tver. Potrai incontrarli non appena sarai pronto.<new_line>Nel frattempo, ti chiederemmo un po' di tempo per aiutarci a raccogliere dei dati. Sono sicuro che ti interessano le tecnologie più avanzate, che siamo disposti a scambiare per dei dischetti con i dati."<new_line><new_line>(È richiesto il livello 25 per incontrare i capi della spedizione a Tver)]],
				['3'] = [["Siamo interessati a tutto ciò che ha a che fare con fauna e flora mutate di recente. Salva i dati su dei dischetti e portaceli. Ecco un dispositivo di archiviazione.<new_line>Inoltre, gli abitanti del posto faticano a occuparsi delle nuove minacce biologiche. Aiutali: se facendolo dovessi ottenere delle informazioni sulle nuove anomalie, sarà doppiamente utile."]],
				['2'] = [["Ciao, compagno! Stiamo studiando delle anomalie biologiche, ultimamente ce ne sono state molte.<new_line>Aiutaci con le ricerche e ti daremo accesso ai nostri più recenti sviluppi."]],
				['1'] = [[Ho sentito il rumore di un motore e ben presto ha fatto la sua comparsa un grosso ATV arancione. Il veicolo si è fermato accanto a me e ne è uscito un uomo con una tuta particolare. Mi ha fatto cenno: vuole parlare.]],
				['8'] = [["Splendido, non vediamo l'ora di ricevere notizie da te! Prendi nota di tutte le informazioni sulle nuove piante e i biomutanti, e consegnacele. Immagino che gli avanzamenti che siamo disposti a darti in cambio possano interessarti.<new_line>Quando sarà tutto pronto, vai a Tver e parla con il dottor Isaev su ricerche più generali."<new_line><new_line>(È richiesto il livello 25 per incontrare i capi della spedizione a Tver)]],
				['7'] = [["No. Per ora stiamo studiando solo le anomalie biologiche. Al momento sono il nostro principale obiettivo scientifico. E i veri scienziati non dovrebbero fare supposizioni senza informazioni.<new_line>Però è probabile che tutto sia collegato. Magari potresti parlarne con il dottor Isaev, il capo della spedizione. Ma per farlo dovrai raggiungerlo nei pressi di Tver."]]
			}
		},
		['ice_brigands_camp'] = {
			['episode'] = {
				['1'] = [[È evidente che l'accampamento non appartiene a dei comuni briganti: non ci sono falò né chiacchiere di sottofondo. Con il volto seminascosto da barbe ghiacciate, i briganti vagano in silenzio tra le tende coperte di neve e si preparano al loro prossimo assalto.]]
			}
		},
		['help_hunter2'] = {
			['episode'] = {
				['1'] = [[Trovo il cacciatore imprigionato in una ragnatela, circondato da numerosi ragni morti. Non appena finisco di liberarlo, una moltitudine di creature a otto zampe si lancia verso di noi.]]
			}
		},
		['headless_horseman2020'] = {
			['episode'] = {
				['2'] = [[Quando sconfiggo il fantasma, quello scompare, lasciando solo la sua testa a ridere sguaiatamente a terra. 'È tutto inutile, cacciatore. Dracula si è già impossessato del libro. Inseguirmi è stato uno spreco di tempo. Ormai è inarrestabile...'. Con queste parole, la testa si dissolve nella nebbia crescente.]],
				['1'] = [[Non appena finisco di posizionare il gomitolo, lo vedo schizzare in lontananza. Mi affretto a seguirlo, fino a quando di fronte a me non appare il cavaliere. Quando lo raggiungo, lo spettro allunga il capo mozzato verso di me. ]]
			}
		},
		['provision_bar'] = {
			['episode'] = {
				['1'] = [["Fantastico, abbiamo grandi speranze per questa impresa. Il tuo contributo ci aiuterà a ottenere informazioni importanti sul progetto EMBA. Grazie."]]
			}
		},
		['embacity_radio2'] = {
			['episode'] = {
				['2'] = [[Il negozio è ricoperto da corde di muffa e grandi boccioli scarlatti spuntano dai germogli verdi delle radici. Mentre li guardavo, quasi non ho fatto caso ai biomutanti. Devo controllare se la mia tuta protettiva è a posto.]],
				['1'] = [[Il negozio è completamente invaso dalla vegetazione, ma non è in cattive condizioni. Non ci sono molte tracce di mutanti in giro, ma sicuramente c'è qualcosa all'interno. Devo controllare che le mie difese siano a posto prima di entrare.]],
				['4'] = [[Nelle restanti vetrine dei negozi, di tanto in tanto si scorgono le sagome di biomutanti vaganti. Non è una buona idea avvicinarsi ad essi senza una tuta protettiva.]],
				['3'] = [[La muffa ha praticamente distrutto l'edificio, trasformando il cemento e il ferro in un terreno fertile per i biomutanti. Avrò bisogno di una tuta protettiva per affrontarli.]]
			}
		},
		['new_year_boss_6_2'] = {
			['episode'] = {
				['6'] = [["Ti aiuteremo, socio. Non riesco a credere a ciò che sto per dire, ma ne ho abbastanza degli affari!<new_line>Ci meritiamo una festa tutta nostra. Dobbiamo festeggiare!"]],
				['5'] = [["Per tutto questo tempo, ti sei fatto carico delle mie responsabilità in maniera egregia. Molto di più di quanto abbia fatto io... Orso ha ragione: ormai sono utile solo se vado in pensione."]],
				['4'] = [["Esatto! Dobbiamo ricostruire molto se vogliamo che la gente festeggi il Natale. Ma non credo che Babbo Natale sia nelle condizioni di fare una cosa del genere."]],
				['3'] = [["La gente non fa che inventare scuse! Ma chi metterà a posto le cose?! Il Natale arriverà da solo anche senza Babbo Natale?"]],
				['2'] = [["Va sempre così, choomba."]],
				['1'] = [["Amici, non so che cosa dire in mia difesa. All'inizio, volevo fare ciò che era più giusto. Ma poi... Per quanto riguarda ciò che è successo dopo, non ho nessuna scusa."]],
				['8'] = [["Ee eeee!"]],
				['7'] = [["Va bene, festeggeremo, ma poi ci rimetteremo al lavoro! Tutti amate festeggiare, ma come sempre sono io quello che deve mettere a posto le cose. Comunque, buon Natale!"]]
			},
			['choice'] = {
				['6'] = [["Buon Natale!"]],
				['5'] = [["Puoi dirlo forte."]],
				['4'] = [["Ma soltanto per un anno!"]],
				['3'] = [["Che cosa vorresti dire?"]],
				['2'] = [["Abbiamo molti Babbi."]],
				['1'] = [["Ciò che conta è che tu sia tornato."]],
				['7'] = [["Urrà!"]]
			}
		},
		['emba_isaev_tver'] = {
			['choice'] = {
				['9'] = [["Gli scienziati della città della scienza sono diventati mutanti?"]],
				['8'] = [["Sì."]],
				['7'] = [["Non sono ancora pronto."]],
				['6'] = [["Sono pronto."]],
				['5'] = [["Cos'hai scoperto dalla scatola nera?"]],
				['4'] = [[Segnala gli avvistamenti]],
				['3'] = [["Dove posso ottenerlo?"]],
				['2'] = [["Ho capito."]],
				['1'] = [["Non ancora."]],
				['11'] = [["Hanno trovato qualcosa di nuovo?"]],
				['10'] = [["Cos'è successo?"]]
			},
			['episode'] = {
				['18'] = [["Bene, allora vuoi a continuare la spedizione?"]],
				['17'] = [["Sì, ci siamo! Un gruppo sperimentale e metodologico di stazioni biologiche che allevano... esemplari da combattimento?! Ci sono le coordinate del luogo in cui sono stati condotti tali esperimenti.<new_line>La prima spedizione è in pericolo, dobbiamo raggiungere subito quella città della scienza! La nostra prossima destinazione è Tula. Partiremo non appena sarai pronto."]],
				['14'] = [["Hai già assemblato un kit AIDA?"]],
				['13'] = [["Il Biologo ti aiuterà. Ma per fare la tuta ti serve biomassa.<new_line>Per questa volta te la darò io, ma d'ora in poi dovrai estrarla dal micelio con un estrattore. Puoi ottenerne uno anche dal Biologo."]],
				['10'] = [["Spero di no. In teoria, durante la catastrofe globale, il professor Gerin e gli altri scienziati del progetto E.M.B.A. avrebbero dovuto essere evacuati come noi.<new_line>Ma abbiamo perso i contatti con loro, così come con tutti gli altri... e col tempo ci siamo trasformati in "archivisti". Ma non parliamo di cose tristi."]],
				['9'] = [["Non avevamo idea che il micelio potesse fare questo a un'intera città! Ma non devi preoccuparti di questo: l'importante è ripristinare la tecnologia e dare un futuro a questo mondo."]],
				['8'] = [["Bentornato! Vedo che hai portato a termine con successo la tua missione, compagno. Ebbene, cos'hai lì?"]],
				['7'] = [["Presto, porta la scatola nera al Biologo e aiutalo a continuare le sue ricerche."]],
				['6'] = [["Beh, è fantastico. Le informazioni sulla ricerca per il progetto E.M.B.A. sono conservate in una scatola nera. Si trova nella stazione biologica al centro della città della scienza. Il tuo supervisore sarà il tuo Navigatore e ti aiuterà nella ricerca. Se vuoi scusarmi, ora devo tornare al lavoro."]],
				['5'] = [["Hai già ricevuto il kit AIDA?"]],
				['4'] = [["Il nostro staff è disposto a scambiare un kit AIDA e altri consumabili in cambio di dischetti contenenti dati.<new_line>Aiuta i miei colleghi a studiare il micelio e salva i dati sui floppy disk. Quando ne avrai raccolti abbastanza, passa al reparto Consumabili e acquista una tuta speciale AIDA."]],
				['3'] = [["Vai alla città della scienza più vicina per cercare la scatola nera. Lì non ci sono radiazioni, ma la probabilità di contrarre le spore del micelio è estremamente alta.<new_line>Quindi, prima di procedere, procurati un kit di protezione dalle spore, AIDA."]],
				['2'] = [["Bene, ottimo. Il tuo compito principale nella prossima spedizione sarà quello di aiutare nella ricerca: dovrai raccogliere materiali biologici e cercare documenti e scatole nere."]],
				['1'] = [["Hai già consegnato i documenti al Biologo e lo hai aiutato con la sua ricerca?"]],
				['11'] = [["Per decifrarlo completamente ci vorrà del tempo, ma abbiamo già la prima parte di dati.<new_line>Bene, bene, bene... È successo qualcosa in tutte le città della scienza nello stesso momento. I segnali S.O.S. delle stazioni biologiche più vicine lo confermano."]],
				['12'] = [["Crescita aggressiva del micelio... diffusione all'esterno della stazione biologica... attivazione incontrollata delle spore... accompagnata da un'alterazione del sistema nervoso centrale. Credo che fosse troppo tardi per salvarli.<new_line>Ma se la prima spedizione Progresso è andata avanti, allora..."]]
			}
		},
		['lair2'] = {
			['episode'] = {
				['1'] = [[Non appena varco la porta, l'odore pungente di oli e altri ingredienti mi fa arricciare il naso. La stanza è avvolta nell'oscurità. Improvvisamente, quella che credevo essere una statua si lancia verso di me e io riesco a evitarla per miracolo. È il medico della peste.]]
			}
		},
		['impostor2'] = {
			['choice'] = {
				['1'] = [[Avvicinati]]
			},
			['episode'] = {
				['2'] = [[Armi pronte, sono andato ai banditi. L'impostore gridò: ]],
				['1'] = [[Banditi in abiti erano seduti a una battuta d'arresto e contando le cose rubate. «Che spazzatura!» impostore Moroz disattivato. «Ti ho detto che l'idea era stupido!»]]
			}
		},
		['emba_isaev_chelyabinsk'] = {
			['choice'] = {
				['6'] = [["Penso che abbia trasmesso la tecnologia anche a Kurganov."]],
				['5'] = [["Cosa hai scoperto?"]],
				['4'] = [["Perché si tratta di una minaccia?"]],
				['3'] = [["Da dove?"]],
				['2'] = [["Ma i biomutanti sono apparsi a causa del lancio del progetto."]]
			},
			['episode'] = {
				['9'] = [["Gerin non ha creato il micelio. Lo sospettavo da molto, ma ora la mia ipotesi è stata confermata. Il micelio è una specie biologica a sé stante e Gerin stava cercando di modificarlo per adattarlo all'uso agricolo."]],
				['8'] = [["Se è accaduto una volta, può accadere di nuovo. Dobbiamo scoprire le cause di quello che è successo. Non interromperemo le ricerche, ma questa minaccia va eliminata."]],
				['7'] = [["Il segnale è arrivato da un altro posto. Non si è limitato a lanciare il progetto, ma prima ha anche apportato delle mutazioni al micelio in tutte le stazioni biologiche.<new_line>Naturalmente, ciò non assolve Gerin, ma in ogni caso è una grave minaccia per le nostre ricerche."]],
				['6'] = [["È orribile. Facevamo molto affidamento su Gerin... Però ci sono notizie ancora peggiori. L'ordine per il lancio non programmato del progetto di rinverdimento delle terre desolate non è arrivato da questa stazione biologica."]],
				['5'] = [["Sì, ho già mandato dei dottori agli scienziati. Ma aspetta, non è possibile! Hai la certezza che fosse lui a controllare gli scienziati? Il professor Gerin... ha dato la vita affinché noi potessimo avere un futuro!"]],
				['4'] = [["Se riuscissimo a mettere le mani sulla scatola nera di questo laboratorio, potremmo fare grandi progressi nelle nostre ricerche. In fondo, sembra che il nostro lavoro sia già stato fatto, anche se non abbiamo avuto il tempo di finirlo.<new_line>Abbiamo decifrato il codice di accesso dello scomparto segreto del laboratorio. Eccolo, l'ho scritto qui."<new_line>(Ti porge un pezzo di carta con il codice)]],
				['3'] = [["Sì, come hai detto, il progetto è stato lanciato. Potrebbe essersi trattato di un errore a cui Gerin sta cercando di rimediare."]],
				['2'] = [["La situazione sta iniziando a farsi più chiara. In questa stazione biologica gli scienziati agli ordini di Gerin stavano lavorando a un progetto in grado di rinverdire il mondo dopo una guerra nucleare.<new_line>Ma non avevano completato il lavoro, quindi non hanno potuto lanciare il progetto."]],
				['1'] = [["Gerin deve trovarsi nella stazione biologica più vicina. Dobbiamo ottenere il suo codice di accesso. Devo chiederti di nuovo di completare i nostri incarichi di ricognizione per recuperare i documenti del progetto segreto E.M.B.A."]],
				['10'] = [["Stiamo lavorando al meccanismo di rinverdimento, dobbiamo continuare a indagare sul disastro alla città della scienza. Abbiamo trovato prove di attività sospette presso un'altra stazione biologica.<new_line>Però, stando ai rapporti, lì gli scienziati non stavano eseguendo esperimenti sulle mutazioni. Partiremo non appena sarà tutto pronto."]]
			}
		},
		['new_year_search_fight'] = {
			['episode'] = {
				['2'] = [["Non provare a fregarmi! Non ci muoveremo da qui finché non ci avrai dato la nostra parte degli ornamenti, e neanche tu! !"]],
				['1'] = [[In una delle stanze, mi sono imbattuto in un gruppo di furfanti. Credo che anche loro stiano cercando qualcosa. Uno di loro si è rivolto verso di me esclamando:<new_line>"Ehi, dove sono finiti gli ornamenti natalizi? Non li avrai già presi tutti quanti?!"]],
				['3'] = [["Oooh, sì! Regaaaaali! Qualcuno ha un fiammifero? Andiamo!"<new_line><new_line>I furfanti si sono completamente dimenticati di me e hanno tagliato la corda.]]
			},
			['choice'] = {
				['2'] = [[Consegna gli ornamenti]],
				['1'] = [["Come avrei fatto a prenderli?"]]
			}
		},
		['madhouse_kandalaksha'] = {
			['choice'] = {
				['2'] = [[Aspetta qui. Torno presto']],
				['1'] = [[Dai lo zolfo e il salnitro]],
				['4'] = [[Ok, arriverò presto']],
				['3'] = [[Dai inizio all'attacco]]
			},
			['episode'] = {
				['2'] = [[I pazzi si sono stabiliti nella caserma distrutta.<new_line>Lì vicino ho trovato anche il barcaiolo: era nascosto e mi aspettava con un paio di fucili.]],
				['1'] = [[Delle urla risuonano dall'interno della casa. I pazzi.<new_line>Ce ne sono troppi. Entrare sarebbe un suicidio...]],
				['4'] = [[Il barcaiolo ha raccolto i pezzi rimanenti dell'attrezzatura radio e ha raggiunto la porta.<new_line>'Suggerisco di dare un'occhiata qui in giro. Non si sa mai, magari qualche cosa potrebbe tornarci utile. Ti aspetto alla barca.'<new_line><new_line>]],
				['3'] = [[Dopo aver assemblato le cartucce, il barcaiolo ha esclamato:<new_line>'Entriamo lì dentro e distruggiamoli.']]
			}
		},
		['blood_trail'] = {
			['episode'] = {
				['5'] = [[Un'ora dopo, l'uomo ha iniziato a riprendere conoscenza. Gli ho raccontato cos'era successo e, per ringraziarmi, mi ha rivelato dove trovare il suo tesoro nelle vicinanze.]],
				['4'] = [[Ho trattato e fasciato le ferite, e ho ripulito la pozza di sangue come meglio ho potuto. Può darsi che il giovane sopravviva.]],
				['3'] = [[Dentro ho trovato una persona accasciata sul pavimento. Era un giovane in mezzo a una pozza di sangue. Sembra che avesse tentato di fasciarsi le ferite, ma dev'essere svenuto prima di riuscirci. Va aiutato immediatamente.]],
				['2'] = [[Come temevo, dentro c'erano dei lupi che sbranavano affamati il cadavere di uno sconosciuto. Non si sono nemmeno accorti di me.]],
				['1'] = [[Sulla soglia ho notato un'impronta insanguinata diretta verso l'interno della casa. Chissà cosa mi attende là dentro.]]
			},
			['choice'] = {
				['5'] = [[Lasciagli dell'acqua pulita e vai avanti.]],
				['4'] = [[Aspetta che riprenda conoscenza.]],
				['3'] = [[Usa medicine per aiutare.]],
				['2'] = [[Non è più un residente. Ispezionalo e prendi tutte le sue cose.]],
				['1'] = [[Segui le tracce]]
			}
		},
		['new_year_search_medium'] = {
			['episode'] = {
				['2'] = [["Ok, mi serve un'altra cosa. La metterò in una vetrina. Una reliquia del genere farà andare in estasi gli abitanti del villaggio.<new_line>Che cos'hai da guardare? Quando diventerai vecchio come me, ti accorgerai che non sarà tutto rose e fiori. Devo fare un secondo lavoro in nero in un negozio di magia."]],
				['1'] = [["Smettila di fare qualsiasi cosa tu stia facendo. Ho un lavoro per te.<new_line>Portami della spazzatura. Con un po' di magia, posso trasformarla nell'artefatto più potente presente sul mercato."]],
				['3'] = [["Finalmente. Gli assegni della mia pensione arrivano più velocemente di te. E va bene, in segno di ringraziamento, ti rivelerò un segreto da stregone.<new_line>Puoi trasformare qualsiasi pezzo di spazzatura in un artefatto di grande valore se scrivi "maledetto" sul cartellino. Osso maledetto, bacca maledetta, moneta maledetta. Decidi tu."]]
			}
		},
		['halloween_search_hard'] = {
			['choice'] = {
				['1'] = [["Darò un'occhiata in giro." (Vattene)]]
			},
			['episode'] = {
				['1'] = [["Resisti, ragazzino. Sto cercando di capire se qualcuno nel tuo mondo di stupidi sia riuscito a creare qualcosa di interessante. Portami qualcosa da poter studiare."]]
			}
		},
		['pumpkin_seed'] = {
			['episode'] = {
				['2'] = [[Sembra che in questo mondo sia finalmente apparso un degno avversario. Il nostro scontro sarà leggendario! Addio, umano, tuona la zucca gigante. Ignorandomi totalmente, la vedo marciare in lontananza, facendo tremare il terreno a ogni passo. Decido di tornare alla taverna.]],
				['1'] = [[Pianto il seme nella terra e lo annaffio. Qualche istante dopo, appare un germoglio, che in un attimo si trasforma in un grosso viticcio da cui cresce una piccola zucca. Con mio grande stupore, le appaiono occhi, denti e zampette. Un minuto dopo, la zucca si stacca dal viticcio e mi afferra uno stivale. Non sono sicuro di cosa stia accadendo, ma è ora di far vedere a questo ortaggio chi comanda.]]
			}
		},
		['quest_passenger'] = {
			['choice'] = {
				['1'] = [[Accetta la ricompensa e vattene]]
			},
			['episode'] = {
				['1'] = [[Siamo arrivati alla base del mio compagno. Era ovviamente sollevato.<new_line>'Grazie. Naturalmente, non mostrerò la mia riconoscenza solo a parole.']]
			}
		},
		['emba_fight_easy3'] = {
			['episode'] = {
				['2'] = [[Quindi, ecco qual era il problema: i biomutanti avevano litigato con un branco di pericolosi predatori. La forbice ha trovato il sasso!<new_line>Queste sono le condizioni ideali per distruggere entrambi nella confusione.]],
				['1'] = [[Ho notato i biomutanti da lontano. Si stanno comportando in modo piuttosto insolito.]]
			}
		},
		['new_year_boss_5_4'] = {
			['episode'] = {
				['6'] = [["Come se ci fosse un'altra opzione. Coraggio, ragazzino. Penso tu abbia una minima possibilità. Decora l'albero un'ultima volta mentre preparo tutto il resto."]],
				['5'] = [["Sì, quello su cui sei rimasto per tutto questo tempo, genio. Non ci è rimasta molta energia.<new_line>Perciò, una volta che sarai nel nascondiglio di Frosty, potrai fare affidamento solo sulle tue forze."]],
				['4'] = [["Oh, e va bene. Visto che sono comunque bloccato qui con voi... Posso suggerire un piano del tutto folle.<new_line>Immagino di poter reindirizzare il tuo portale verso il nascondiglio di Frosty."]],
				['3'] = [["Che altra scelta abbiamo? Abbiamo già fatto tutto ciò che potevamo!"]],
				['2'] = [["Abbiamo usato quasi tutta la nostra energia con l'aspirapolvere! Finire dritti nella trappola di Frosty non è soltanto folle, è un suicidio."]],
				['1'] = [["Riesco a percepire una traccia della magia che ci è stata rubata... Frosty si è rintanato da qualche parte qui vicino e sta aspettando la nostra prossima mossa."]]
			},
			['choice'] = {
				['2'] = [["Stai dicendo che dovremmo arrenderci?"]],
				['1'] = [["Allora attacchiamo!"]],
				['4'] = [["Posso farcela?"]],
				['3'] = [["Il mio portale?"]]
			}
		},
		['embacity_restaurant2'] = {
			['episode'] = {
				['2'] = [[Un gruppo di mutanti si è radunato davanti all'edificio invaso dalla vegetazione. I biomutanti si stanno scontrando tra loro per la carcassa ricoperta di muffa. Avrò bisogno di una tuta protettiva per affrontarli.]],
				['1'] = [[Dalle rovine ricoperte di muffa proviene il rumore dei numerosi abitanti dell'edificio. Devo ricordarmi di indossare una tuta protettiva per eliminare i biomutanti.]],
				['3'] = [[Tra le escrescenze ammuffite vedo dei bellissimi fiori rossi. Ma non credo che questo gruppo di mutanti sia venuto qui per ammirarli, quindi devo pensare a una protezione speciale.]]
			}
		},
		['emba_isaev_tula'] = {
			['choice'] = {
				['6'] = [["Dove trovo questi documenti?"]],
				['5'] = [["No, non c'erano cadaveri."]],
				['4'] = [[Segnala gli avvistamenti]],
				['3'] = [["Qualche informazione su come trattare questo 'oggetto'?"]],
				['2'] = [["Ok. Cos'hai scoperto dai documenti?"]],
				['7'] = [["Che altro hai scoperto?"]]
			},
			['episode'] = {
				['14'] = [["Per prima cosa, ci servono i documenti segreti. Contengono informazioni sulla ricerca per il progetto E.M.B.A. e il codice di accesso decriptato per il laboratorio della città della scienza locale."]],
				['13'] = [["Oh, quasi dimenticavo! Abbiamo decifrato il codice di accesso dello scomparto segreto del laboratorio. Eccolo, l'ho scritto qui."<new_line>(Ti porge un pezzo di carta con il codice)]],
				['9'] = [["Abbiamo scoperto che l'unicità del micelio non è dovuta solo al suo impatto ambientale: i suoi geni sono incredibilmente facili da modificare.<new_line>Tuttavia, una volta modificato il genoma, iniziano mutazioni incontrollabili, molto difficili da prevedere."]],
				['8'] = [["Allora c'è la possibilità che siano ancora vivi... Durante il ridispiegamento, cercherò di contattarli di nuovo. Ora posso rispondere alle tue domande."]],
				['7'] = [["Un biomutante allevato artificialmente proprio in laboratorio! È pazzesco.<new_line>Oh, per caso lì hai visto i cadaveri dei miei colleghi della prima spedizione Progresso?"]],
				['6'] = [["Sì, questa stazione biologica ha torrette automatiche da combattimento. Ti aiuteranno molto in combattimento se saprai usarle. Il Navigatore se ne occuperà. Vai là e inizia a cercare la scatola nera."]],
				['5'] = [["Molte informazioni scientifiche. Ci aiuteranno sicuramente nelle nostre ricerche. Con la parte che abbiamo applicato, siamo riusciti a capire l'ubicazione della città della scienza e abbiamo scoperto che c'è un esemplare da combattimento alla stazione biologica.<new_line>Viene chiamato "Oggetto Spaccaossa". C'è scritto che ha un ceppo di micelio integrato nel sistema nervoso."]],
				['4'] = [["Congratulazioni per aver completato con successo la fase di ricerca. Ora dobbiamo iniziare a cercare la scatola nera nella stazione biologica della città della scienza."]],
				['3'] = [["I nostri esploratori ti aiuteranno a trovare i documenti segreti, vai al bar e segui le loro istruzioni.<new_line>Le già trovate? Se sì, consegna i documenti al Biologo e aiutalo nelle sue ricerche."]],
				['2'] = [["Siamo costantemente alla ricerca di documenti, ma è un compito lungo e complesso: non si sa in anticipo quale ricerca o informazione porterà agli archivi giusti o dirà dove cercarli. <new_line>Per questo motivo, ti chiedo di aiutare i miei collaboratori con i loro incarichi, puoi trovarli al bar locale."]],
				['1'] = [["Molto bene, eccoti qui. Pronto a metterti al lavoro?"]],
				['10'] = [["Purtroppo questa scatola nera è molto danneggiata e ora dobbiamo ricostruirla. Ma siamo riusciti a scoprire informazioni su alcune rotte logistiche.<new_line>Ora dobbiamo andare a Kiev. Partiremo non appena sarai pronto."]],
				['12'] = [["Bene, allora vuoi a continuare la spedizione?"]]
			}
		},
		['embacity_biolab_6'] = {
			['choice'] = {
				['1'] = [["È la tua voce quella di cui parlava Gerin?"]]
			},
			['episode'] = {
				['2'] = [["Possiamo dire così, sì. Chiamami Emissario. Vedo che sei già al corrente di qualcosa e che hai fatto molto. Ma non avresti dovuto interferire con i miei piani, ho passato tanto tempo a lavorare sull'Ibridatore. Quindi ora tocca a te morire."]],
				['1'] = [["Una nuova seccatura... Che c'è, quell'idiota di Gerin non ce l'ha fatta? In ogni caso, sono felice quando vi uccidete."]],
				['3'] = [[Nella sala ho visto un uomo vestito con una tuta protettiva pesante che si aggira tra le autoclavi della sala.<new_line>Notandomi, si prepara ad attaccare.]]
			}
		},
		['emba_craft_easy3'] = {
			['choice'] = {
				['5'] = [[Lancia gli esplosivi]]
			},
			['episode'] = {
				['2'] = [[Il condotto di ventilazione è troppo stretto per infilarcisi. Quindi, l’origine di queste piante rimarrà un mistero. Per ora, posso solo distruggere l'imbocco del condotto in superficie.<new_line>Potrei lanciare dentro qualcosa di infiammabile o esplosivo, non avendo altre opzioni.]],
				['1'] = [[L'apertura del condotto di ventilazione è al centro di una sfera di piante intrecciate. È piuttosto strano che stiano crescendo così e siano emerse in superficie qui.]],
				['3'] = [[C'è stato un rombo sottoterra, poi è crollato qualcosa. Sembrava che i gambi e le radici nere tremassero da soli e non per la scossa.<new_line><new_line>Per un paio di secondi c'è stato il silenzio assoluto. Tutto questo verde appassirà e morirà, ora che è separato dalle sue radici. Già adesso, le foglie e le escrescenze ammuffite sembrano essersi accartocciate. Ottimo lavoro.]]
			}
		},
		['frost_tamb_event'] = {
			['episode'] = {
				['2'] = [[La temperatura è scesa rapidamente, le nubi hanno iniziato ad avvicinarsi e un'ondata di neve ha ricoperto ogni cosa. Posso sentire la rugiada ghiacciata che si infrange sotto i miei piedi.]],
				['1'] = [[Posso fare la danza sciamanica dei nostri antenati con un tamburello per evocare la giusta condizione meteo. Richiederà una grande quantità di energia.]],
				['4'] = [[Delle nuvole leggere e veloci si sono riunite e hanno iniziato a bagnare il terreno con una pioggia calda. Dopo una pioggia del genere, solitamente cresce un raccolto molto abbondante.]],
				['3'] = [[In un istante, si è manifestata una brezza fresca e calda che ha spazzato via tutte le nubi e il vapore radioattivo. Si è stabilita la calma.]]
			},
			['choice'] = {
				['2'] = [[Danza della dissipazione della tempesta]],
				['1'] = [[Danza gelida]],
				['4'] = [[Rimuovi il tamburello]],
				['3'] = [[Danza della pioggia fertile]]
			}
		},
		['halloween_def_hard'] = {
			['choice'] = {
				['9'] = [["Grazie." (Vattene)]],
				['8'] = [[Attacca]],
				['7'] = [["Voglio correre come un lupo."]],
				['6'] = [["Voglio avere la vista di un'aquila."]],
				['5'] = [["Voglio essere forte come Ercole."]],
				['4'] = [["Voglio volare come un uccello."]],
				['3'] = [["Qual è il piano? Che cosa facciamo?"]],
				['2'] = [["Sono solo di passaggio."]],
				['1'] = [["Sono qui per dare una mano."]]
			},
			['episode'] = {
				['7'] = [[Lo stregone agitò pigramente il suo bastone.<new_line>Abracarabraboom. Ecco fatto. Spero di non aver sprecato tutto questo potere per niente."<new_line><new_line>L'ululato delle bestie sembra molto vicino.]],
				['9'] = [["Hai fatto del tuo meglio per aiutarmi, ma non è servito a molto. Forse potresti farti venire in mente un piano leggermente più intelligente? Scegli l'incantesimo che vuoi stavolta."]],
				['14'] = [[Lo stregone sbadigliò rumorosamente.<new_line>"Fiu, me la sono cavata bene. Gli altri dovrebbero prendere spunto... Mio malgrado, secondo quanto riportato, il Codice degli stregoni stabilisce che chiunque mi assista debba ricevere un appunto. Leggendolo, si ottiene la capacità di evocarmi nell'ora del bisogno e bla, bla, bla...<new_line>Ma ricorda che se non si tratta della vera ora del bisogno, te ne pentirai. Fine della storia."]],
				['13'] = [["Abracadabra... Quasi. Può bastare. Le bestie si avvicinano. Preparati."]],
				['1'] = [["Sei qui per dare una mano o solo per guardare?"]],
				['5'] = [[Lo stregone agitò pigramente il suo bastone.<new_line>Abracarabraboom. Ecco fatto. Spero di non aver sprecato tutto questo potere per niente."<new_line><new_line>L'ululato delle bestie sembra molto vicino.]],
				['4'] = [["Ho combattuto per giorni e giorni senza riposare, e adesso pretendi che mi inventi un piano?! Mio Dio, quanto sei inutile. Bene, credo che i mostri si avvicinino ancora. Che ne dici se facessi un incantesimo su di te per renderti un po' più utile? Dimmi quale incantesimo vorresti."]],
				['3'] = [["Muoviti, non startene lì impalato."]],
				['2'] = [["Non startene lì impalato! Pensi che possa sconfiggere le orde di mostri e difendere il villaggio da solo? Potresti alzare anche solo un dito per aiutarmi. Così, per dire."]],
				['10'] = [[Lo stregone mi diede un colpetto col suo bastone e disse:<new_line>"Abracadabra. Fatto."<new_line><new_line>Le bestie si avvicinano ancora.]],
				['11'] = [["Sei stato utile solo al 5%. Perché dovrei sprecare la mia magia con te? Potresti almeno impegnarti un po' di più? Se vuoi essere utile, pensa bene a quale incantesimo potrei usare su di te."]],
				['12'] = [["Abracadabra... Quasi. Può bastare. Le bestie si avvicinano. Preparati."]]
			}
		},
		['newyearEvent_fight_hard'] = {
			['episode'] = {
				['9'] = [[La legna secca bruciava a meraviglia. Il fuoco ha subito raggiunto gli esplosivi, e la fortezza è stata avvolta dalle fiamme.<new_line>Dopo un po', il fuoco si è spento e mi ha permesso di scendere verso l'entrata segreta. Come mi aspettavo, la porta era stata spazzata via. La strada per il magazzino dei furfanti era libera.]],
				['8'] = [[Finalmente è tutto pronto per la demolizione. Non mi resta che dar fuoco alla legna.]],
				['7'] = [[Al di là delle mura della fortezza, si è sentito il rombo dei motori. Evidentemente, qualcuno ha scoperto che ho fatto irruzione. Direi che mi attende una battaglia.]],
				['6'] = [[La fortezza è fatta di ghiaccio, perciò il miglior modo per distruggerla sarebbe innescare un incendio. Riempirò i piani superiori di legna da ardere e piazzerò gli esplosivi nel piano interrato.]],
				['5'] = [[Dopo aver eliminato le guardie, ho esplorato la stanza e ho trovato un'altra porta segreta. Non ha nessuna serratura ed è saldamente sigillata.<new_line>Potrebbe essere una buona idea far esplodere la porta, e di conseguenza tutti i piani superiori. Devo escogitare un piano.]],
				['4'] = [[Nel piano interrato, mi sono imbattuto negli abitanti della fortezza. A quanto pare, stanno facendo la guardia a qualcosa di valore.]],
				['3'] = [[Mentre esploravo ogni angolo e fessura della fortezza, mi sono ritrovato davanti a una porta nascosta. C'era una lunga scalinata che conduceva sottoterra.]],
				['2'] = [[Con un rimbombo interminabile, i cancelli si sono aperti, e la fortezza mi ha accolto con un silenzio glaciale. Non mi resta che esplorarla per scoprire come distruggerla al meglio.]],
				['1'] = [[Lungo le distese delle terre desolate innevate, si trova una ragguardevole fortezza di furfanti. I suoi cancelli sono sbarrati da un'enorme serratura ghiacciata. A quanto pare, gli abitanti della fortezza non sono a casa in questo momento.<new_line>Sarebbe meglio distruggere questo posto per impedire ai furfanti di ritornare il prossimo anno. Tuttavia, entrare non sarà così facile. Avrai bisogno di una chiave.]]
			},
			['choice'] = {
				['6'] = [[Piazza gli esplosivi]],
				['5'] = [[Posiziona la legna da ardere]],
				['4'] = [[Escogita un piano (8 ore)]],
				['3'] = [[Scendi]],
				['2'] = [[Esplora la fortezza (12 ore)]],
				['1'] = [[Usa la chiave]],
				['8'] = [[Prendi le scorte e vattene]],
				['7'] = [[Dai fuoco alla legna]]
			}
		},
		['newyearEvent_fight_medium'] = {
			['choice'] = {
				['6'] = [[Combatti]],
				['5'] = [[Prova a sfuggire all'imboscata]],
				['3'] = [[Fogne]],
				['2'] = [[Posto di blocco del lato sinistro]],
				['1'] = [[Posto di blocco del lato destro]]
			},
			['episode'] = {
				['2'] = [[Mentre ripulivo la base, i furfanti sono riusciti a far scattare l'allarme e a chiamare i rinforzi. Mi sono ritrovato circondato.]],
				['1'] = [[A debita distanza dal resto della civiltà c'è un enorme blocco di ghiaccio che funge da nascondiglio per i furfanti. È qui che compiono i rituali e dove portano gli oggetti rubati.<new_line>Se riuscissi a scacciare gli abitanti del blocco di ghiaccio, i furti nei villaggi cesserebbero per un po'. Ma non sarà facile scacciare i furfanti dalla loro base.]],
				['4'] = [[Non sono riuscito a sfuggire all'imboscata, quindi dovrò combattere in queste condizioni.]],
				['3'] = [[Sono riuscito a sfuggire all'imboscata, ma devo ancora combattere.]]
			}
		},
		['emba_commander_easy'] = {
			['choice'] = {
				['2'] = [["Non posso farlo."]],
				['1'] = [["Posso aiutarti."]]
			},
			['episode'] = {
				['2'] = [["Eccellente! Non ti useremo come carne da macello, ovviamente. Devi solo mantenerti a una distanza di sicurezza e inviare segnali alle torrette mentre facciamo le nostre osservazioni.<new_line>Hai esperienza, quindi capirai come funziona. Andiamo!"]],
				['1'] = [["Ciao! Vedi quei biomutanti? Li abbiamo cosparsi con una soluzione speciale e dovrebbero comportarsi diversamente in battaglia.<new_line>Abbiamo bisogno di qualcuno che contenga i loro attacchi mentre effettuiamo le nostre osservazioni."]],
				['3'] = [["Eccellente, eccellente. Dobbiamo cambiare i radicali liberi... e aumentare la diffusione intorno ai biomutanti...<new_line>Ah, sei ancora qui! Grazie per l'aiuto. La prossima volta che vieni, proveremo una nuova formula."]]
			}
		},
		['halloween_def_medium'] = {
			['choice'] = {
				['9'] = [["Andate avanti, io vi copro."]],
				['8'] = [["Io vado avanti, voi attaccateli ai fianchi!"]],
				['7'] = [["Io vado avanti, voi seguitemi!"]],
				['6'] = [["Gli abitanti hanno paura. Contrattacchiamo!"]],
				['5'] = [["Basta con questa insubordinazione!"]],
				['4'] = [["Fornite un resoconto della situazione!"]],
				['3'] = [["Certo. Mettetevi in riga!"]],
				['2'] = [["Fornite un resoconto della situazione."]],
				['10'] = [["Io li distraggo e voi li attaccate alle spalle!"]],
				['11'] = [["Non ci serve la furbizia, ma il coraggio!"]],
				['12'] = [["Chiederò aiuto a un alleato."]]
			},
			['episode'] = {
				['16'] = [["Sì! Abbiamo vinto!"]],
				['15'] = [["Sì, signore! Ci muoveremo al suo ordine."]],
				['17'] = [["Il contrattacco è stato un successo, e le perdite sono esigue. Tuttavia, il nemico pianifica di riconquistare la zona perduta con nuove truppe. Dobbiamo escogitare un piano. Che cosa facciamo, comandante?"]],
				['18'] = [["Sì, signor comandante, signore! Il nemico si avvicina."]],
				['5'] = [["Sì, signor comandante, signore! Riesco a sentire le bestie. Quali sono le nostre tattiche?"]],
				['13'] = [["Ci aspettiamo un attacco nemico su larga scala dalla direzione del bagno pubblico! Il nemico sfrutterà tutte le forze a disposizione. Gli abitanti del villaggio hanno paura. Se il nemico si avvicinasse, potrebbero spaventarsi ancora di più. Dobbiamo contrattaccare."]],
				['19'] = [["Oh, di nuovo tu, ragazzino? Possibile che tu abbia sempre bisogno di una mano? Quando avevo la tua età, facevo tutto da solo. E va bene, fammi vedere chi si è comportato male con te."]],
				['2'] = [["Sì, signore!"<new_line>Le teste-di-zucca si sono messe in riga.]],
				['1'] = [["Salve! Grazie per aver risposto alla chiamata. Queste bestie sono diventate una spina nel fianco. Prima riuscivamo a tenerle a bada da soli, ma in una recente schermaglia hanno catturato il nostro comandante. Temo che senza di lui questa possa essere la nostra ultima battaglia. Sembri un uomo capace. Potresti prendere il posto del nostro comandante per la prossima battaglia?"]],
				['14'] = [["Sì, signore! Poniamo fine all'insubordinazione!"]],
				['12'] = [["Grazie per l'aiuto! Ti ricorderemo come il miglior comandante che abbiamo mai avuto. Dopo il nostro preferito, ovviamente!"]]
			}
		},
		['witch_delivery'] = {
			['episode'] = {
				['2'] = [[Dopo aver saputo che provengo dalla superficie, la strega mi fa accomodare al tavolo, mi offre da mangiare e mi interroga su cosa stia accadendo oltre il portale. Alla fine, mi dà le pozioni per la taverna e torna alle sue faccende. È ora di togliere il disturbo.]],
				['1'] = [[Al mio arrivo, vengo accolto da una donna di mezza età. Non sembra per nulla una strega. 'Beh? Cosa mi hai portato? Cosa c'è in quel pacco? Appoggialo sul tavolo, non essere timido'.]]
			},
			['choice'] = {
				['1'] = [[Dare gli ingredienti]]
			}
		},
		['newyearEvent_energy_medium'] = {
			['choice'] = {
				['9'] = [["Rubare non è sbagliato?"]],
				['8'] = [[Metti gli ornamenti nel sacco]],
				['7'] = [[Piazza le mine nella stanza]],
				['6'] = [[Fuggi attraverso un portale]],
				['5'] = [[Distruggi ogni cosa con un martello]],
				['4'] = [[Segui Babbo Naffare]],
				['3'] = [["Sono dei pochi di buono... Ma sei sicuro che ci siano loro dietro i problemi alla fabbrica?"]],
				['2'] = [["Sono pronto."]],
				['1'] = [["Quali concorrenti? Come sono arrivati fin qui?"]],
				['10'] = [[Trova un'altra uscita]]
			},
			['episode'] = {
				['9'] = [[Dopo aver finito di raccogliere gli ornamenti, ci siamo messi i sacchi in spalla e siamo andati verso l'uscita. Tuttavia, una banda di furfanti ci bloccava la strada. Dopo esserci riparati dietro l'angolo, Naffare ha sussurrato:<new_line>"Possiamo trovare un'altra uscita, ma sarà difficile perché i sacchi sono pesanti. O magari è meglio occuparsi di questo gruppetto e procedere da questa parte?"]],
				['8'] = [["Non preoccuparti, mio caro amico. È da tantissimo tempo che dono gentilezza e miracoli alla gente. So perfettamente distinguere ciò che è giusto da ciò che è sbagliato. Prendi un sacco e riempilo di ornamenti."]],
				['7'] = [[Dopo aver piazzato le mine, abbiamo proseguito e siamo finiti in una stanza piena di decorazioni natalizie.<new_line>"Oh, che splendido regalo! Per fortuna siamo venuti qui. Getta tutto nei sacchi."<new_line>Dopo aver detto queste parole, Naffare ha tirato fuori due sacchi rossi.]],
				['6'] = [[Una volta finito di distruggere i veicoli, abbiamo continuato la nostra esplorazione. La stanza seguente era il deposito di carburante.<new_line>"Ottimo, qui possiamo piazzare delle mine! Prendi i fuochi d'artificio."<new_line>Naffare mi ha consegnato una scatola di fuochi d'artificio.]],
				['5'] = [[A quanto pare nel blocco di ghiaccio ci sono innumerevoli entrate nascoste, e noi ne abbiamo varcato una. Siamo finiti in quello che sembra essere un garage con una vasta gamma di veicoli.<new_line>"Prendi il martello e distruggi tutto ciò che vedi!"]],
				['4'] = [[Abbiamo dovuto camminare qualche miglio prima di arrivare a un enorme blocco di ghiaccio. Naffare mi ha sussurrato delle parole.<new_line>"Seguimi. Oh, tieni questa pergamena portale. Se ti rendi conto di non potercela fare, leggi la pergamena ed essa ti tirerà fuori da qui."]],
				['3'] = [["Ne sono sicuro al 100%! La competizione è il bene più prezioso. La nostra civiltà si fonda su dei pilastri. E come facciamo a sapere chi è il pilastro e chi l'ultimo arrivato se non facciamo del nostro meglio per competere l'uno con l'altro? O saranno loro a vincere e a dimostrare di avere ragione, oppure saremo noi! Allora, siete pronti?"]],
				['2'] = [["Furfanti. La nostra fabbrica sfrutta il Natale e i miracoli per fare pubblicità, e a loro non sta bene. Per questo motivo, sono i nostri diretti concorrenti e dobbiamo occuparcene. Siete pronti a ristabilire la giustizia?"]],
				['1'] = [["Oh oh salve, mio caro amico! Nella nostra fabbrica i problemi non mancano di certo, ma ho finalmente scoperto cosa c'è che non va. I nostri maledetti concorrenti stanno interferendo con la nostra operazione! Potresti aiutarmi a sabotare un po' le loro fabbriche?"]],
				['11'] = [[Ho estratto la pergamena e ho iniziato a leggerla. Quando Naffare mi ha visto, ha iniziato a fare lo stesso in fretta e furia. Dopo aver finito di leggerla, è apparso un lampo blu luminoso e qualche secondo dopo mi sono ritrovato in una terra desolata.]],
				['10'] = [[Dopo esserci allontanati a distanza di sicurezza dal blocco di ghiaccio, Naffare ha tirato fuori il detonatore e l'ha premuto. Si è sentita un'esplosione assordante.<new_line>"Ecco fatto! Abbiamo fatto un buon lavoro e una buona azione! La sconfitta dei nostri concorrenti farà prosperare la fabbrica!"]]
			}
		},
		['newyearEvent_craft_easy'] = {
			['choice'] = {
				['6'] = [[Allontanati dalla casa]],
				['5'] = [[Consegna il regalo]],
				['4'] = [["No, grazie, devo andare." (Vattene)]],
				['3'] = [["Molto bene, andiamo."]],
				['2'] = [["Vedrò cosa posso fare." (Vattene)]],
				['1'] = [["Tieni, prendi."]]
			},
			['episode'] = {
				['9'] = [["C'è stato un piccolo malinteso con il regalo, ma sto ancora imparando delle cose sul tuo mondo, e mi mancano da morire i miei fedeli elfi. La prossima volta rifletterò sui miei errori e faremo un regalo migliore. Ad ogni modo, hai fatto una buona azione!"]],
				['8'] = [[La donna ha strappato la carta in fretta e furia e ha aperto il regalo. "Oh, è davvero per me? Che bello, grazie. Forse mi sbagliavo su di te. Farò un pensiero sul lavoro."]],
				['7'] = [[La donna ha strappato la carta in fretta e furia e ha aperto il regalo.<new_line>"Mi prendete in giro? Vi ringrazio, ovviamente, ma penso sia meglio che ve ne andiate prima che il mio cane si svegli."]],
				['6'] = [["Oh, è per me? Di che si tratta?"]],
				['5'] = [["Volevo presentarti al nuovo Babbo Natale della zona. Ti ha portato un piccolo regalo. Da adesso sarà lui a occuparsi della magia e dei miracoli qui."]],
				['4'] = [[Una donna ha aperto la porta.<new_line>"Chi è? Oh, sei... Ti ho già detto mille volte che non verrò alla tua fabbrica!"]],
				['3'] = [[Ho preso il regalo e ci siamo diretti verso una delle case del villaggio. Naffare ha bussato alla porta.<new_line>"Oh oh oh!"]],
				['2'] = [["Oh, interessante! Oh, che bello... Perché non consegniamo questo regalo insieme? Credo che il destinatario sarà molto felice di vedere l'artigiano che l'ha realizzato."]],
				['1'] = [["Oh oh oh! Salve, mio caro amico! Mi serve il tuo aiuto. Vedi, i bambini non sono gli unici a meritare i regali. Anche alcuni adulti li meritano. Tuttavia, la mia fabbrica non può produrre dei regali per accontentare gli adulti di qui. Non ho né i progetti né tantomeno i materiali. Potresti fare qualcosa per me?"]]
			}
		},
		['help_hunter1'] = {
			['episode'] = {
				['1'] = [[Un'orda di zombi ha messo il cacciatore con le spalle al muro. Non ha alcuna possibilità di farcela da solo.]]
			}
		},
		['frozen'] = {
			['episode'] = {
				['1'] = [[Riesco ad avvicinarmi ai briganti mentre loro sono indaffarati a caricare blocchi di ghiaccio sulle loro slitte. Sotto le lastre gelate, riconosco le sagome dei prigionieri. Devo liberarli al più presto.]]
			}
		},
		['letter_of_hope'] = {
			['episode'] = {
				['5'] = [[Raven gracchiò ad alta voce, lasciò cadere la lettera e sbatteva le ali.]],
				['4'] = [[Il corvo non sembra interessato.]],
				['3'] = [[Il corvo è appollaiato su un albero. Quando la sua testolina nera si volta per fissarmi, noto la lettera nel suo becco. Sono a corto di idee: non posso far altro che proporre uno scambio.]],
				['2'] = [[A quanto pare, la lettera si trova in una bottiglia sul fondo di un fosso, sotto uno spesso strato di ghiaccio. Non ho idea di come sia finita laggiù, ma ci vorrà del tempo per recuperarla.]],
				['1'] = [[Per ironia della sorte, la lettera si trova in una cassetta postale. Il problema è che non esiste più alcuna casa. Di tutto l'edificio, è rimasta solo una parete, dalla quale spunta la cassetta. Devo trovare un modo per aprirla.]]
