			['episode'] = {
				['9'] = [["Aaah!"<new_line><new_line>(Ci fu un boato e un'esplosione mi scaraventò contro il muro. Sbattei dolorosamente la testa e non vidi più nulla.)]],
				['8'] = [["C'è uno scomparto che contiene la scatola nera. Inserisci il codice che ci hanno dato gli scienziati, prendi la scatola e andiamocene prima che succeda qualcosa.<new_line>Io farò la guardia qui e mi occuperò del sistema elettronico. Forse riuscirò a capire cosa sta succedendo."]],
				['7'] = [["E poi c'è questa sala. Wow, guarda la radice che cresce laggiù! Scommetto che è spessa come la testa del nostro amico Isaev.<new_line>Maledizione, è anche vuota... Basta scherzare. Non mi piace affatto, dobbiamo agire in fretta. Vedi la porta con la serratura a codice, laggiù?"]],
				['10'] = [["Chi è che si infiltra in una struttura militare? Un nuovo combattente per il mio esercito? Formazione!"]],
				['11'] = [["Rinsavire? "Tornare in sé da cosa? Solo un altro po' e sistemeremo tutto. E cominceremo con te. Ho imparato un metodo meraviglioso da uno dei miei colleghi scienziati..."]],
				['12'] = [["Non parlerò dei miei affari con un soldato semplice! Lo scoprirai quando ti unirai al mio esercito.<new_line>Il tuo amico curioso ha già avuto modo di incontrare i miei esplosivi e ora non può più fare nulla. Mi occuperò io di te, però. Nella sala operatoria."]],
				['16'] = [["A giudicare dalla tua descrizione, questa è la prima spedizione Progresso scomparsa. Sono stati salvati, Isaev non lo dimenticherà... Per me è troppo tardi. È un bene che ci siamo incontrati."]],
				['15'] = [["Ti rendi conto del potere che ho nelle mie mani ora? Ricostruirò il nostro Paese in rovina dalle ceneri!<new_line>E poi diffonderò una nuova piaga nel mondo. Ma tu non sarai qui per assistervi. Disubbidire agli ordini è punibile con la morte."]],
				['14'] = [["Bene, combattente. Sembra che la mia missione finisca qui. Cosa è successo dopo l'esplosione?"]],
				['13'] = [["Ha detto che stava alludendo al professore? Pare che si siano incontrati dopo la catastrofe. Vorrei sapere qual è il ruolo di Gerin in tutto questo..."]],
				['6'] = [["Hanno costruito un intero palazzo sottoterra! Hai visto i tubi nel soffitto? È così che la biomassa arriva qui dall'alto. Ma perché non c'è nessuno qui...?<new_line>Se non è stato lo Spaccaossa a mangiare la biomassa, allora chi è stato? E quella porta si è aperta da sola?"]],
				['5'] = [["Forza, andiamo."]],
				['4'] = [[Al centro della sala, circondata da mutanti umanoidi, una figura spettrale era china su un tavolo da laboratorio.<new_line>Dall'oscurità della tromba delle scale, ho osservato i corpi mutilati dal micelio e sentito i loro grugniti animaleschi. Nessuno merita un simile destino.]],
				['3'] = [[Le scale dietro la porta mi hanno condotto in una spaziosa stanza sotterranea. Sopra, resti di biomassa verde scorrevano da tubi in fossati metallici e radici nodose si estendevano dal pavimento al soffitto, dirigendosi verso la superficie della città.]],
				['2'] = [["Oh, ecco che arriva il capo idraulico del Progresso! Quando hai pompato tutta la biomassa fuori dal serbatoio, sotto quella luce rossa laggiù, la porta si è aperta, hai visto?<new_line>C'è una scala dietro di essa. Non so come sia collegata. Forse anche dietro quella porta c'è un mostro affamato e tu l'hai lasciato senza cibo."]],
				['1'] = [[Ho sbirciato con cautela all'interno del laboratorio. La prima cosa che mi ha colpito è stata la luce rossa lampeggiante sopra la porta sulla parete di fondo della stanza.<new_line>Quella porta si è rivelata essere estremamente pesante e controllata automaticamente. Ma al momento è leggermente socchiusa.]],
				['17'] = [["Non hai idea di quanto ti dobbiamo tutti. Finisci il lavoro. È stato un piacere percorrere questa strada con te, soldato."]]
			}
		},
		['halloween_boss_2022_4_2'] = {
			['episode'] = {
				['6'] = [["Lo so. Ho portato in vita tutte le zucche affinché potessero sfuggirgli. Quel contadino stava coltivando i miei sudditi nel bel mezzo di una terra radioattiva e senza alcuna protezione. Così ho preso il controllo della zucca che aveva piantato in quella città in rovina e orribilmente radioattiva per vincere il premio."]],
				['5'] = [[Arrivai in una radura nella foresta. Al centro, c'era una figura alta con un'enorme zucca al posto della testa, e sopra di essa vi era appoggiata una corona. Delle fiamme fuoriuscivano dalle fessure degli occhi. La creatura si voltò verso di me: "Perché insegui i miei sudditi, umano?"]],
				['4'] = [[Provai ad attraversare il campo di granturco senza fare rumore, ma subito dopo aver calpestato qualcosa sentii: "Presto, corriamo verso la foresta! Oh mio Dio, andare a finire in una torta di zucca è la mia più grande paura!"]],
				['3'] = [["Aaargh! Scappiamo! Berrà tutto il nostro succo di zucca!"<new_line>Ho visto dei piccoli omini dalla testa rotonda nascosti dietro al muro. Non ho fatto in tempo e vederli per bene perché sono scomparsi subito dietro degli alberi.]],
				['2'] = [["Silenzio! Il mangiatore di zucche se n'è andato? Non vedo niente."]],
				['1'] = [[Non feci neanche in tempo a entrare nell'edificio che sentii un bisbiglio provenire da dietro l'angolo: "Shh, è venuto a fare una zuppa con noi. Shh, silenzio!"]],
				['8'] = [["Bene, ora la fiera della contea è salva! A proposito, passa a trovarmi quando hai tempo. Ho da proporti un affare in esclusiva per te dal mondo dei morti."]],
				['7'] = [["Volevo punire il contadino quando sarebbe venuto. Tuttavia, dato che ti sei fatto vedere tu, faremo in modo che il tuo destino gli serva da lezione."]]
			},
			['choice'] = {
				['5'] = [[Avvicinati]],
				['4'] = [[Ascolta]],
				['3'] = [["E adesso?"]],
				['2'] = [["Il raccolto del contadino è andato perduto."]],
				['1'] = [[Segui i fuggitivi]]
			}
		},
