				['7'] = [["Non possiamo usufruirne in qualche modo?"]],
				['6'] = [["Che cosa devo fare ora?"]],
				['5'] = [["Che cosa diavolo hai..."]],
				['4'] = [[Mettiti all'opera (10 ore)]],
				['3'] = [["Sembra tutto chiaro e tondo."]],
				['2'] = [["Non ho tempo." (Vattene)]],
				['1'] = [["Che cosa facciamo?"]],
				['11'] = [["Ciao."]],
				['12'] = [[Mettiti all'opera (14 ore)]]
			},
			['episode'] = {
				['14'] = [["Beh, me ne vado da qui. Al tuo mondo servono le rivoluzioni! Alla prossima!"]],
				['13'] = [[Finito il lavoro, qualcuno mi ha dato un colpetto sulla spalla.<new_line>"Salve, compagno! Sono venuto a chiederti scusa per averti fatto smantellare questo posto... ma sono incapace di rompere qualsiasi cosa. Tra l'altro, mi sono ripreso solo ora dal colpo di Babbo Naclava. Permettimi di condividere la cena con te."]],
				['10'] = [[Devo smantellare le trappole. Ci vorranno circa 8 ore.]],
				['9'] = [["Sfortunatamente, mio caro amico, dovremo smantellare tutto questo. I furfanti devono aver dato il progetto a Orso, quindi avete costruito il loro nascondiglio."]],
				['8'] = [["Ooh aaaaavaaaaah..."<new_line>Dopo il colpo di Naclava, lui e Orso potevano capirsi a vicenda. Avevo l'impressione che Orso si stesse lamentando di qualcosa con Naclava. Ormai persi nella loro bizzarra conversazione, hanno lasciato il villaggio.<new_line><new_line>Io e Naffare siamo rimasti da soli.]],
				['7'] = [[Naffare si è messo le dita tra i capelli, e un momento più tardi si è compiuto un miracolo: è apparso un portale dal quale è uscito un Babbo Naclava altrettanto inferocito.<new_line>"Ah oovaaah?!"<new_line>Naclava ha inveito contro Orso, per poi colpirlo con la sua clava.]],
				['6'] = [["Non ho alcuna intenzione di discutere del mio lavoro con gli sfruttatori!"]],
				['5'] = [[Prima che potessi dire qualcosa, alle mie spalle ho sentito il rombo di un motore. Era un'auto d'epoca piccola e rossa. Dopo essersi fermata, ho visto scendere Naffare con uno sguardo inferocito.<new_line>"Che cosa hai fatto, maledetto Marxista? Hai una vaga idea di chi ti abbia assegnato il progetto, vero?"]],
				['4'] = [[Orso ha lavorato molto velocemente. Mentre ero impegnato con un capanno, lui ha costruito tutto ciò che lo circonda. Abbiamo finito la costruzione, ma i risultati del nostro lavoro erano piuttosto strani.<new_line>Orso ha costruito dei blocchi di ghiaccio, un mucchio di trappole e un braciere per i rituali.<new_line>"Come si dice: 'Proletari di tutti i Paesi, riscaldatevi!'"]],
				['3'] = [["Riposati per tutto il tempo che ti serve, poi potremo iniziare. Il lavoro richiederà circa 10 ore."]],
				['2'] = [["È piuttosto semplice. Mi è stato affidato un progetto, e noi dobbiamo intraprendere le costruzioni seguendo le istruzioni!"]],
				['1'] = [["Salve, compagno! Il meteo non è mai abbastanza brutto per non lavorare, giusto? Infatti, mi manca davvero il lavoro estenuante! Ti andrebbe di aiutarmi?"]],
				['11'] = [["Vorrei davvero poterlo utilizzare in qualche modo, ma non abbiamo scelta. Gli abitanti del posto o se ne sono andati o sono stati catturati. Non c'è nessuno che possa occupare questo nascondiglio, eccetto i furfanti, naturalmente. Per favore, dai una ripulita."]],
				['12'] = [[Ora devo smantellare le fortificazioni. Ci vorranno circa 12 ore.]]
			}
		},
		['emba_energy_easy'] = {
			['choice'] = {
				['2'] = [[Taglia le radici]],
				['1'] = [["Come è successo?"]],
				['3'] = [[Accumula le radici]]
			},
			['episode'] = {
				['6'] = [["Uff, abbiamo finito! Questa spazzatura brucerà tutta e io potrò occuparmi di nuovo della fattoria.<new_line>Grazie mille, sei stato fantastico. Ti compenserò per il lavoro come posso."]],
				['5'] = [[È come se il micelio stesse cercando di avviluppare il fienile in una sfera di rami e radici, ma ce l'abbiamo fatta.<new_line>Non resta che raccogliere tutta questa vegetazione mutante in un mucchio e bruciarla. Il contadino è andato a prendere la benzina e mi ha detto di raccogliere le radici.]],
				['4'] = [[Abbiamo fatto la prima parte e ripulito i campi, ma non è tutto, bisogna passare alla stalla.]],
				['3'] = [["Amico, mi hai salvato!<new_line>Guarda. Devi tagliare tutti questi rami in una volta sola, ammucchiarli e dargli fuoco. Non c'è tempo per riposare, altrimenti questa robaccia attecchirà di nuovo e spargerà le sue spore dappertutto. Cominciamo dai campi."]],
				['2'] = [["Ho fatto una stupidaggine! Il mio vicino mi aveva raccontato storie su questo micelio che fertilizza il terreno per ottenere raccolti migliori. L'ho piantato nelle serre ed è cresciuto così bene che ora non riesco nemmeno a tagliarlo. Per favore aiutami!"]],
				['1'] = [["Ehi aspetta! Ho bisogno di aiuto. Queste maledette radici hanno ricoperto l'intera fattoria. I mutanti verdi hanno già iniziato a venire qui, come se fossimo la loro mensa personale."]],
				['7'] = [["Oh, al diavolo! Non ha senso, ricrescerà di nuovo e ricomincerà tutto da capo. Comunque, grazie per averci provato, ovviamente, ma devi riconoscere i tuoi limiti..."<new_line><new_line>Il contadino si dirige verso la sua capanna sconsolato, prendendo a calci una radice che sta crescendo nel terreno proprio davanti ai tuoi occhi.]]
			}
		},
		['frankenstein'] = {
			['episode'] = {
				['2'] = [[Non vedo alcuna porta... ah, no, eccola: è a terra, di fronte all'ingresso. L'interno della struttura odora fortemente di ozono e vi sono cavi attorcigliati ovunque. Li seguo, addentrandomi sempre più nell'edificio. I cavi mi conducono a una stanza: il mostro è sdraiato su un tavolino di metallo e un inquietante ometto gobbo si muove freneticamente attorno a lui. Non appena mi vede, lancia un urlo di terrore e aziona un interruttore sulla parete. Una scarica di elettricità colpisce il lettino e il mostro si sveglia.]],
				['1'] = [[Il gomitolo si arresta di colpo e capisco che il mostro è vicino. Una nube nera copre un edificio poco lontano e scarica fulmini su un parafulmine piegato sul tetto.]]
			}
		},
		['construction_team_pests'] = {
			['episode'] = {
				['1'] = [[Questo è il posto che il capo ha segnato sulla mappa. Non devo cercare le loro tane, gli insetti sono già usciti fuori e si dirigono verso di me.]]
			}
		},
		['embacity_biolab_helecopter'] = {
			['episode'] = {
				['1'] = [[Diversi biomutanti si aggirano intorno all'elicottero. È strano: non ci sono molti pollini e sporafiori che amano. A quanto pare, è il centro della città ad attrarli.]]
			}
		},
		['order_delivery_quest_pt2'] = {
