		['easter_rabbit'] = {
			['choice'] = {
				['19'] = [["Non trovo le uova."]],
				['9'] = [["D’accordo. Ma dove trovo le uova e la vernice?"]],
				['8'] = [["Così va meglio."]],
				['7'] = [["Perché pensi che mi interessi?"]],
				['23'] = [["Vorrei chiederti qualcos’altro."]],
				['24'] = [["Cosa sono quelle tane, e perché dovrei trovarci della vernice?"]],
				['25'] = [["Forse potresti scoprire il tuo gioco? Chi sei?"]],
				['26'] = [["Potresti darmi la ricompensa gratuitamente?"]],
				['20'] = [["Ricordami dove trovare la vernice."]],
				['10'] = [["E le uova?"]],
				['11'] = [["Ho capito."]],
				['12'] = [["Quali sono le condizioni?"]],
				['22'] = [["Non ho altre domande."]],
				['21'] = [["Parlami di te."]],
				['16'] = [["Passavo solo da queste parti." (Vattene)]],
				['15'] = [["Avrei un paio di domande."]],
				['14'] = [["Vorrei consegnarti alcuni cestini."]],
				['13'] = [["D’accordo." (Vattene)]],
				['6'] = [["Perché dovrebbe importarmi?"]],
				['5'] = [["Davvero?"]],
				['4'] = [["E a cosa ti servirebbero delle uova colorate?"]],
				['3'] = [["Dici sul serio?"]],
				['2'] = [["Allora, che cosa vuoi da me, coniglio rosa?"]],
				['1'] = [["E comunque, chi saresti?"]],
				['18'] = [["Per il momento è tutto."]],
				['17'] = [[Consegna i cestini]]
			},
			['episode'] = {
				['30'] = [["Nemmeno le mosche volano gratis. Se mi porti qualcosa, farò beneficenza e darò tutto al prossimo. Così farai una buona azione."]],
				['29'] = [["Ora sono un’altra persona, il passato non conta più. Se dovessi trovare altri cestini, puoi portarmeli, le feste non sono ancora finite. Inoltre, c’è qualcosa che vorrei darti dai miei negozi."]],
				['19'] = [["Ben fatto, sono soddisfatto. Ecco la tua parte. Hai altro?"]],
				['9'] = [["Ti darò comunque il costume, ma solo se mi porterai una quantità di cestini adeguata per soddisfare la domanda. Ho messo molto impegno nella realizzazione del costume."]],
				['8'] = [["Non lo so, ma non ne troverai uno in queste terre desolate, è piuttosto raro. Vedo che le tue rotelle iniziano a muoversi, quindi che ne diresti se dividessi con te il ricavato di ogni cestino?<new_line>Ho anche altri oggetti rari che potrei condividere."]],
				['7'] = [["Ti darò il mio costume da coniglio."]],
				['23'] = [["Cercale nelle terre desolate o nelle aree radioattive, a volte è possibile trovarvi dei polli. Dove ci sono polli, ci sono anche le uova."]],
				['24'] = [["Potresti acquistarle al triplo del prezzo, ma solo se non riesci a trovarle da nessun’altra parte."]],
				['25'] = [["Recati in una città abbandonata e perlustra ogni angolo. Scava dove trovi tane di coniglio e prendi la vernice. Non dimenticare di procurarti una pala."]],
				['26'] = [["Da dove viene il latte delle mucche? Se non poni domande sciocche, non dovrai sentire risposte altrettanto sciocche."]],
				['20'] = [["Oh, bene, ecco la tua parte. Hai altro?"]],
				['10'] = [["Prendi questa pala. Esplora le città, cerca tane di conigli negli edifici abbandonati e scava. In questo modo troverai la vernice."]],
				['11'] = [["Arrangiati. Potresti comprarle. Tuttavia, la domanda è alle stelle, proprio come i prezzi. Potresti cercarle da qualche parte."]],
				['12'] = [["Allora non restartene lì immobile e vai a completare gli ordini, ho già molti clienti in attesa."]],
				['28'] = [["Beh, sembra che tu mi abbia portato abbastanza cestini e che i clienti si stiano esaurendo. Significa che ti meriti una ricompensa, e penso che aggiungerò pure qualcosa. Mi sono guadagnato una ricca pensione."]],
				['27'] = [["Ho trascorso un periodo in prigione. Mi sono sposato due volte. Poi mi hanno arruolato e ho dovuto vivere per due anni nel posto più schifoso del Kazakistan per nessun motivo.<new_line>Ho subito una frattura al viso, mi hanno rimosso un rene e ho una scheggia d’osso nella caviglia che non guarirà mai. Ho visto parecchia merda nella mia vita..."]],
				['22'] = [["Ho sentito di persone scomparse dalle fattorie abbandonate nelle terre desolate. Dicono che sia opera di polli mutanti. Potresti farci un salto, se ci sono dei polli, ci saranno anche delle uova."]],
				['21'] = [["Beh..."]],
				['16'] = [["Che altro mi porti?"]],
				['15'] = [["Nessuna condizione speciale, mi limiterò a comprare le uova a un prezzo migliore di quello offerto da quel pazzo. Se me ne vendi a sufficienza, ti darò una ricompensa. Il prezzo è comunque generoso, tienilo a mente."]],
				['14'] = [["Senti, perché lavorare per quello psicopatico? Potresti vendere le uova a me, mi assicurerò di pagarle il giusto prezzo. Sono piuttosto richieste, al momento. E nessuno farà entrare quello spostato in casa sua."]],
				['13'] = [["Psst, potresti avvicinarti un secondo?"]],
				['6'] = [["La tua incredulità mi sta facendo impazzire! Accendi il cervello! La Pasqua sta arrivando, e per Pasqua si regalano le uova. Ho creato un costume da coniglio e voglio guadagnarci.<new_line>Farò visita alle famiglie benestanti e farò loro dei doni per ricavarne un profitto. Il tuo lavoro sarà quello di dipingere le uova e comporle in cestini regalo."]],
				['5'] = [["Perché a un pinguino serve cagare? Mi hanno espulso dal Ministero dei conigli, perciò voglio riguadagnarmi la loro fiducia e tornare morbido e adorabile."]],
				['4'] = [["Potrei essere più serio?"]],
				['3'] = [["Non sei molto sagace, vero? Cosa credi che serva a un coniglio rosa? Uova colorate, ovviamente."]],
				['2'] = [["Cosa vorrebbe dire che non sai chi sono? Togliti le ragnatele dagli occhi! Coniglio rosa al tuo servizio."]],
				['1'] = [["Salve. Mi serve il tuo aiuto, avvicinati!"]],
				['18'] = [["Avresti potuto portare anche qualche cianfrusaglia. Ecco la tua parte. Hai altro?"]],
				['17'] = [["Bene. Mostrami cosa hai portato."]]
			}
		},
		['mummy'] = {
			['episode'] = {
				['1'] = [[Raggiungo la tana con le orecchie ben tese. A una decina di metri da me, vedo una figura con indosso una tunica nera e una maschera con un becco adunco. Gli squittii disperati che riecheggiano tra le pareti della grotta mi suggeriscono che quel tale ha appena catturato un ratto.]],
				['9'] = [[Un istante più tardi, raggiungo una stanza piccola con il tetto a volta e un baule al centro.]],
				['8'] = [[Torno al bivio e decido dove altro andare.]],
				['7'] = [[Dall'altra parte dell'arcata, una gradinata scende verso una stanza minuscola con file e file di ratti in gabbia. Decine di occhietti rossi sono puntati su di me. Capendo di non aver altra via d'uscita, mi volto, ma calpesto una placca di pietra: un secondo più tardi, sento le gabbiette aprirsi di scatto e lo squittio di centinaia di roditori affamati.]],
				['6'] = [[Mi avvicino piano per osservare meglio, ma la mummia si accorge della mia presenza. Il suo collo produce un rumore secco nel momento in cui scatta verso di me e la sento mormorare un'antica maledizione egizia.]],
				['5'] = [[Sul blocco di pietra è adagiata una mummia avvolta nelle bende. Il medico di prima se ne sta in un angolo a borbottare, mentre è intento a mescolare un liquido verde e viscoso in un calderone arrugginito. L'aria è impregnata di un distinto odore di ortica e piantaggine.]],
				['4'] = [[L'arcata centrale conduce a un labirinto di corridoi; sono costretto a utilizzare l'aroma delle erbe per orientarmi. L'olfatto non mi delude: dopo qualche minuto, raggiungo una stanza ampia con, al centro, un blocco di pietra.]],
				['3'] = [[Proseguo a camminare per alcuni minuti, fino a quando mi trovo di fronte a un bivio. Vi sono tre arcate e conducono tutte verso l'oscurità. Vado dritto, svolto a sinistra o svolto a destra?]],
				['2'] = [[La figura chiude il ratto in una gabbietta e lo conduce senza fretta in una casa vicina. Attendo un minuto prima di entrare a mia volta e la scena che mi attende mi lascia senza fiato: di fronte a me si estende un lungo corridoio illuminato da torce appese alle pareti di pietra. Un forte olezzo di muffa invade le mie narici, ma percepisco distintamente anche un piacevole aroma di erbe appena colte.]],
				['10'] = [[Sono a un passo dal baule, quando il coperchio si apre quel tanto che basta per mettere in mostra una fila di denti aguzzi: „Giù le mani!“ esclama una voce roca dall'interno del baule. Io sobbalzo, tirando indietro la mano con uno scatto deciso. „È la tua prima volta? Mi porti un ratto?“ chiede la voce.]],
				['11'] = [[„Bene, portalo qui“ dice il baule, aprendo la sua morsa letale. Senza nemmeno guardare dentro, getto la carcassa del ratto all'interno. Il coperchio si chiude di colpo e osservo il baule masticare rumorosamente, prima di sputare alcuni sacchetti di dolciumi verso di me. „Ora sparisci“.]],
				['12'] = [[„Allora vattene e non tornare indietro“ borbotta il baule prima di chiudersi.]]
			},
			['choice'] = {
				['6'] = [[Continua a osservare]],
				['5'] = [[Svolta a destra]],
				['4'] = [[Svolta a sinistra]],
				['3'] = [[Vai dritto]],
				['2'] = [[Percorri il corridoio]],
				['1'] = [[Seguilo]],
				['8'] = [[Torna indietro]],
				['7'] = [[Apri il baule]]
			}
		},
		['mad_man_base'] = {
			['episode'] = {
				['1'] = [[La recinzione della casa sgangherata era imbrattata di sangue. Sembra opera dei pazzi. Amano collezionare cianfrusaglie, quindi troverò sicuramente qualcosa di interessante all'interno. Ma non credo che saranno molto disposti a separarsi dai loro tesori.]]
			}
		},
		['scientist_rescue_bar'] = {
			['episode'] = {
				['2'] = [["Vivi e vegeti? Ottimo! Sappi che hai salvato la vita di un brav'uomo. Grazie anche per il dosimetro, è un modello raro. Un lavoro coi fiocchi, meriti un piccolo extra."]],
				['1'] = [["Vivi e vegeti? Ottimo! Sappi che hai salvato la vita di un brav'uomo. Ecco la tua ricompensa."]],
				['3'] = [["Vivi e vegeti? Ottimo! Sappi che hai salvato la vita di un brav'uomo. Ho sentito parlare dei tuoi servizi medici, perciò eccoti un piccolo extra. Apprezziamo chi si prende cura della nostra gente."]]
			}
		},
		['monstrous_bag'] = {
			['episode'] = {
				['1'] = [[Per diverse ore ho ascoltato a chi ha fame rombo insacca alle spalle. Sembra che la sua pazienza si è esaurito. Ho sentito denti aguzzi nella mia spalla! Ho lasciato cadere la borsa. Ma ha odore del sangue e ottenuto completamente folle. Devo mostrare chi comanda qui.]]
			}
		},
		['newyearEvent_commander_medium'] = {
			['choice'] = {
				['13'] = [["Felice di aiutare." (Vattene)]],
				['10'] = [[Mettiti in una posa difensiva]],
				['9'] = [[Urla il grido di battaglia per attaccare]],
				['8'] = [[Mostra come si usa un fucile a canne mozze]],
				['7'] = [[Mostra come si usa una lancia]],
				['6'] = [[Mostra come si usa una granata]],
				['5'] = [[Mostra come si usa un fucile]],
				['4'] = [["Va bene."]],
				['3'] = [["No, c'è un altro modo!" (Bevi la vodka)]],
				['2'] = [[Lascia che ti colpisca in testa]],
				['1'] = [["Che sta succedendo qui?"]],
				['11'] = [[Mostra come si contrattacca]],
				['12'] = [[Mostra come si schiva]]
			},
			['episode'] = {
				['6'] = [[Anche gli altri miliziani hanno capito la lezione e hanno iniziato a fare pratica. Dopo un po', Babbo Naclava è venuto verso di me.<new_line>"I furfanti si avvicinano. Gli abitanti del villaggio dovrebbero affrontarli per dare prova dei tuoi insegnamenti. Mostragli come si combatte."]],
				['5'] = [[Due dei quattro miliziani hanno capito perfettamente la mia lezione e hanno iniziato a fare pratica. Gli altri due, invece, sembra che non abbiano imparato niente. Uno di loro ha chiesto:<new_line>"Ah ooomaroov?"]],
				['4'] = [[La milizia non è molto grande, sono solo quattro persone. Uno di loro viene verso di me e grugnisce una domanda.<new_line>"Ooogrooooom ah ooom?"]],
				['3'] = [[Il mio quoziente intellettivo è diminuito, perciò inizio a capire i discorsi di Naclava.<new_line>"Grazie agli dei puoi capirmi! Voglio insegnare agli abitanti del villaggio come si affrontano i nemici. Sono persino riuscito a formare una milizia. A essere sincero, non sono un granché come insegnante. Aiutami."]],
				['2'] = [[Alla mia vista, Naclava salta di gioia e scuote la clava sulla mia testa.<new_line>"Oooooooooooooh."]],
				['1'] = [[Circondato dagli abitanti del villaggio, Babbo Naclava cerca di spiegare loro qualcosa come è solito fare.<new_line>"Oooorgh", dice gesticolando con la clava.<new_line>"Brrrrr!", insiste indicando la foresta.]],
				['8'] = [[Durante la battaglia, il mio quoziente intellettivo ha iniziato a tornare alla normalità un po' alla volta.<new_line>Dopo la battaglia, uno dei miliziani si è avvicinato verso di me.<new_line>"Grazie a te, ora saremo in grado di proteggerci nell'ora del bisogno. Non esiste regalo più grande!"]],
				['7'] = [["Hanno svolto un ottimo lavoro, ma all'orizzonte vedo un altro gruppo di furfanti. Dovresti impartirgli un'altra lezione veloce. Forse alcuni degli abitanti la impareranno e la useranno a loro vantaggio in battaglia."]]
			}
		},
		['pumpkin_king'] = {
			['episode'] = {
				['5'] = [[Proseguo a distruggere altre zucche e presto il cerchio viene illuminato dagli occhi fiammeggianti del re delle zucche, alto e maestoso. Il suo sguardo sembra ipnotizzarmi. Mi preparo a combattere.]],
				['4'] = [[Dopo aver estinto le fiamme arancioni dall'ultima testa di zucca, mi pulisco il volto con una manica. Il terreno attorno a me è cosparso di semi e polpa, così come i miei abiti e l'arma.]],
				['3'] = [[Il mio trionfo è seguito da un urlo rauco e gutturale, un misto di rabbia e sorpresa. Finalmente ho attirato l'attenzione di Jack. Ormai non posso più tornare indietro.]],
				['2'] = [[Ho sconfitto i suoi sudditi, ma il re continua a osservarmi. Proseguo a distruggere le zucche per provocarlo e non vengo deluso: un nuovo servitore appare al mio cospetto.]],
				['1'] = [[Eseguo le istruzioni alla lettera: entro nel cerchio e comincio a distruggere le zucche. Una dopo l'altra, si aprono con un rumore secco e la loro polpa si riversa sul terreno. Dopo qualche secondo, sento un fruscio e, con la coda dell'occhio, vedo alcune zucche intagliate fluttuare nell'aria. Sotto di esse, si stanno formando dei corpi di nebbia.]]
