			['episode'] = {
				['9'] = [[Sono arrivato alle coordinate dell'edificio fornitemi dagli Archivisti. La porta è sigillata. Sarà necessario usare gli esplosivi.]],
				['8'] = [[Ho i documenti segreti e devo consegnarli alla base del Progresso e aiutare il Biologo con le sue ricerche.]],
				['7'] = [[Il percorso è libero. Ho continuato la ricerca del laboratorio e finalmente ho trovato una cassaforte con i marchi EMBA.]],
				['6'] = [[Queste persone non erano dell'umore giusto per parlare. Sono corso fuori per ricordare al loro capo il nostro accordo, ma non c'era più nessuno.<new_line>Quelli del laboratorio mi stanno già inseguendo.]],
				['5'] = [[Queste persone non erano dell'umore giusto per parlare. Una sparatoria in un laboratorio non è una buona idea, quindi sono corso fuori.<new_line>I saccheggiatori mi hanno seguito.]],
				['4'] = [[Il laboratorio occupava una piccolissima parte dell'edificio. Probabilmente era stato utilizzato come deposito per i documenti d'archivio.<new_line>Mentre esploravo un altro dei suoi angoli, ho sentito delle voci umane.]],
				['3'] = [[Il percorso è libero, quindi posso perlustrare il laboratorio ora.]],
				['2'] = [["Quindi cosa facciamo? Siamo venuti qui per primi. Se ci paghi lo stesso, ti lasceremo queste rovine. In caso contrario, possiamo spiegarti la situazione in modo molto più sgradevole."]],
				['1'] = [[Ho scoperto un gruppo di persone all'ingresso dell'edificio. Il loro capo mi ha ascoltato e ha scosso la testa.<new_line>"Devi capire, non siamo venuti qui per fare una passeggiata, ma per raccogliere informazioni rare ordinate da persone importanti."]],
				['11'] = [[Ho attirato gli insetti in superficie, ma non volevano abbandonare la tana e gradualmente hanno iniziato a strisciare di nuovo dentro.]],
				['10'] = [[Il laboratorio occupava una piccolissima parte dell'edificio. Probabilmente era stato utilizzato come deposito di documenti d'archivio.<new_line>Mentre esploravo un altro angoletto, mi sono imbattuto in una tana di insetti.]]
			}
