		['newyearEvent_time_easy'] = {
			['choice'] = {
				['9'] = [["Li ripulirò." (14 ore)]],
				['8'] = [["Facciamo in fretta." (6 ore) (+ radiazioni)]],
				['7'] = [[Preparati]],
				['6'] = [["Voglio fare le bambole." (8 ore)]],
				['5'] = [["Voglio preparare le torte." (8 ore)]],
				['4'] = [[Vai alla postazione di lavoro]],
				['3'] = [["Non ho tempo." (Vattene)]],
				['2'] = [["Hai un sacco di soldi. Non c'è proprio nessuno che abbia voglia di scommettere?"]],
				['1'] = [["Ok, lavorerò per un po'."]],
				['10'] = [[Mettiti all'opera (8 ore)]]
			},
			['episode'] = {
				['9'] = [["Ho oh oh, che belli! I bambini saranno sicuramente entusiasti. Ti ringrazio per aver fatto una buona azione!"]],
				['8'] = [["Ecco qui i materiali. Se sei stanco, prenditi pure una pausa. Ma fa' che sia breve, e poi rimettiti subito all'opera!"]],
				['7'] = [["Bene, dobbiamo realizzare i giocattoli anche per i giovani boy scout. Ho trovato delle parti per creare dei soldatini giocattolo. Tuttavia, i materiali sono leggermente radioattivi. Potresti dargli una ripulita, oppure non ti spaventa contrarre una piccola malattia?"]],
				['6'] = [["Dunque, che cosa abbiamo qui? Che meraviglia! Sembra quasi che tu faccia questo lavoro da una vita. Di' un po', sotto quei capelli non nascondi delle orecchie a punta, vero?"]],
				['5'] = [["Ecco qui i materiali. Quando sarò di ritorno, i risultati del tuo lavoro dovranno abbellire il tavolo. Se sei pronto, non indugiare. Inizia pure."]],
				['4'] = [["Ti ho assegnato un lavoro fatto apposta per te! Prima dovrai realizzare i regali per le giovani componenti del gentil sesso. Scegli pure ciò che ritieni più opportuno realizzare."]],
				['3'] = [["Oh oh oh! Così si fa! Presto, raggiungi la tua postazione di lavoro!"]],
				['2'] = [["Questo denaro serve a espandere le nostre operazioni, ma in questo mondo gli abitanti del posto ne vogliono molto di più rispetto al costo di lavoro medio. Se iniziassi a pagarli con la stessa quantità di denaro che chiedono, andremmo in bancarotta in men che non si dica, e i bambini verrebbero privati dello spirito delle festività."]],
				['1'] = [[Sono sceso vicino a un piccolo edificio con un'insegna con su scritto TLtoys Factory.<new_line>"Finalmente sei qui, mio caro amico. Avevo un immediato bisogno di aiuto. Nella fabbrica non ci sono lavoratori! Ho assolutamente bisogno che lavori per un paio di turni, altrimenti i bambini non riceveranno alcun giocattolo..."]]
			}
		},
		['catch_thief_quest'] = {
			['choice'] = {
				['48'] = [[Vai a prendere la refurtiva]],
				['47'] = [[Torna al riparo]],
				['49'] = [[Vai dai banditi]],
				['19'] = [[Cura la ferita]],
				['9'] = [[Nasconditi vicino alla staccionata]],
				['28'] = [[Lascia andare il ladro e manda il lupo all'inseguimento]],
				['27'] = [[Tendi un'imboscata: 'Non muoverti!']],
				['34'] = [[Ehi, ragazzi! Che ne dite di collaborare?']],
				['24'] = [[Arrenditi!']],
				['36'] = [[Dov'è la mia parte?']],
				['44'] = [[Aspetta il capo del magazzino]],
				['31'] = [[Prendi la ricompensa]],
				['32'] = [[Insegui]],
				['22'] = [[Aspetta che il ladro torni]],
				['21'] = [[Tendi un'imboscata]],
				['46'] = [[Scava una fossa]],
				['43'] = [[Prendi qualcosa di utile e scappa]],
				['42'] = [[Non c'è speranza. Non ho possibilità contro a così tanti nemici. Meglio andare via.]],
				['40'] = [[Spiega il tuo piano]],
				['14'] = [[Torna al magazzino e aspetta il mattino]],
				['29'] = [[Attacca]],
