		['toy_story'] = {
			['episode'] = {
				['2'] = [[Fermi! sbraita la fanciulla di neve, precipitandosi all'interno del terem. Sono circondato da giocattoli rotti. Uno schiaccianoci si immobilizza con la sciabola sguainata.]],
				['1'] = [[La porta del terem, così minuziosamente decorato, è sfondata. All'interno regna il caos. Ancor prima di avere il tempo di guardarmi attorno, sento una strana vocetta provenire da dietro il tavolo rovesciato: «Sono tornati! Presto, caricate il cannone! Pronti, puntare, fuoco!». Dopodiché, sento un scoppio e proiettile minuscolo mi sfreccia vicino a un orecchio. Che sta succedendo?]],
				['3'] = [[Un'ora più tardi, dopo aver dato una ripulita, scopriamo che i briganti di ghiaccio hanno attaccato il terem. Hanno messo tutto a soqquadro, ma non hanno rubato nulla. Vedendomi, i giocattoli avevano creduto che fossi uno di loro. La fanciulla di neve ha promesso di aiutarli a scoprire da dove sono venuti i briganti.]]
			},
			['name'] = [[Terem]]
		},
		['boatman'] = {
			['choice'] = {
				['9'] = [[Sono di Murmansk, ma sto andando a Belomorsk']],
				['8'] = [[Sì, andiamo']],
				['7'] = [[Ho ancora da fare qui']],
				['6'] = [[Alla prossima']],
				['5'] = [[Posso andare']],
				['4'] = [[Sembra che ce ne siamo molti. Ci serve un piano']],
				['3'] = [[Aspetta... Vieni dall'accampamento?']],
				['2'] = [[Ok, affare fatto!']],
				['1'] = [[Sei Vasily? Ho sentito il tuo segnale radio']],
				['10'] = [["Alla prossima."]]
			},
			['episode'] = {
				['9'] = [[Non c'è una strada da Kandalaksha a Belomorsk. Troppe radiazioni. C'è solo un modo per andare a sud da qui: sull'acqua.<new_line>Ascolta, pensi di poter trovare Vasily? Portalo qui e attraverseremo la zona radioattiva insieme.']],
				['8'] = [[Non ho altro da dire al barcaiolo.]],
				['7'] = [[Il barcaiolo ha messo l'attrezzatura di Vasily in una cassa di legno e mi ha chiesto la mappa.<new_line>'Questo è il mio villaggio. Ti porterò a Belomorsk come promesso. Non so perché tu voglia andare in quella città infernale, ma sono fatti tuoi.']],
				['6'] = [[Ecco, prendi questo piede di porco. È vecchio, ma potrai aprire la porta.<new_line>Tu vai, io prenderò delle armi: ci serviranno. Incontriamoci al manicomio.']],
				['5'] = [[Abbiamo un piano, ma non le munizioni. Ascolta, c'è una ferramenta in città. È chiusa, ma scommetto che dentro ci sono zolfo e salnitro. Posso usarli per fare della polvere da sparo...']],
				['4'] = [[Quando il barcaiolo ha visto che ero solo, la sua faccia si è rabbuiata. Gli ho dato il diario e gli ho detto cos'era successo.<new_line>'Quei bastardi... Hanno preso anche la radio. Dobbiamo vendicarci e riprenderci ciò che hanno rubato.']],
				['3'] = [[Beh, dall'accampamento... il villaggio di pescatori. È a ovest di Petrozavodsk, un po' lontano da qui. Di solito non vado così lontano, ma dovevo venire a prendere Vasily. Siamo entrambi fanatici della radio, per cui l'abbiamo usata per comunicare per un po'. Di dove sei?']],
				['2'] = [[Il barcaiolo si è girato di scatto.<new_line>'No, Vasily è mio amico. Sono qui per portarlo al nostro insediamento, ma non risponde.']],
				['1'] = [[Un uomo con una maschera antigas stava in piedi accanto alla barca.]],
				['10'] = [["Beh, ce l'abbiamo fatta. Buona fortuna con la tua ricerca. Ti aspetterò al villaggio di pescatori."]]
			}
		},
		['set_up_devices'] = {
			['episode'] = {
				['2'] = [[Gli Archivisti volevano collocare l'attrezzatura proprio qui. E capisco perché: ho visto biomutanti in lontananza.]],
				['1'] = [[Sono in posizione. Ora devo piazzare e mimetizzare attentamente l'attrezzatura.]],
				['3'] = [[Ho terminato l'installazione e ho cercato di mimetizzare l'attrezzatura. I biomutanti hanno iniziato ad avvicinarsi gradualmente. L'attrezzatura probabilmente li influenza in qualche modo e c'è il rischio che possano danneggiarla.<new_line>Gli Archivisti apprezzeranno il fatto che mi sia preso cura della loro attrezzatura. D'altra parte, ho già completato il mio incarico principale.]]
			}
		},
		['mirror_lady'] = {
			['episode'] = {
				['2'] = [[Dopo lo scontro, inizio a perlustrare la casa in cerca delle tracce di Igor. La marionetta di ceramica sul comò attira la mia attenzione e ho la sensazione che mi stia fissando. Quando mi avvicino per osservarla meglio, la marionetta mi colpisce al volto e, scalciando furiosamente i suoi piedini, si divincola e scompare alla mia vista.]],
				['1'] = [[L'intera casa è tappezzata di specchi. Seguendo le istruzioni dell'oste, accendo una candela di fronte a uno di essi e invoco Bloody Mary. Inizialmente, percepisco solo un profumo stucchevole, ma poi sento dei passi e una voce pacata... 'Bene, bene, bene... Chi abbiamo qui?'.]]
			}
		},
		['gravedigger'] = {
			['episode'] = {
				['2'] = [[Il beccamorti non voleva saperne di parlare, ma frugando nel suo mantello trovo uno strano messaggio: una specie di lista della spesa piena di parti umane (braccia, gambe e teste) con in calce la firma ]],
				['1'] = [[È stato facile trovare il beccamorti: un uomo dall'aria inquietante, fermo accanto al cancello del cimitero. Nella sua mano destra regge una pala, nella sinistra... una gamba in putrefazione. Scrutandomi con un ghigno beffardo, scaglia l'arto in un vicino carretto traboccante di membra, si getta in spalla la pala e corre nelle profondità del cimitero. È ora di porre fine a questo scempio!]]
			}
		},
		['brick_fall'] = {
			['episode'] = {
				['2'] = [[Dopo aver scacciato i topi, ho trovato degli oggetti utili tra le macerie dell'edificio.]],
				['1'] = [[Mentre camminavo per le strade deserte della città, è crollata la parete di un edificio di mattoni pericolante. Purtroppo mi trovavo lì accanto, e un mattone mi è caduto proprio in testa.]]
			},
			['choice'] = {
				['2'] = [[Prendi il mattone come ricordo]],
				['1'] = [[Controlla le macerie]]
			}
		},
		['new_year_boss_2_2'] = {
			['episode'] = {
				['9'] = [["È terribile! A causa di quel terribile fumo, non sono riuscito a venire in tuo aiuto."]],
				['8'] = [[In risposta, il vecchio è di nuovo scoppiato a ridere. Sogghignando, è corso verso la sua motocicletta ed è fuggito dalla piazza.<new_line>I delinquenti, guidati dall'anziano sfigurato, si sono avvicinati verso di me.]],
				['7'] = [[Il fumo si è dissipato. Accanto al vecchio, dove prima c'era l'anziano del villaggio, è comparso una specie di mostro.<new_line>"Furfanti, la banda di Krampus ha una nuova recluta! Novellino, il tuo primo compito è sbarazzarti di questo teppistello!"]],
				['6'] = [[Un mucchio di decorazioni natalizie sono finite tra le fiamme, causando l'emissione di un fumo acre persino più denso del precedente. Il vecchio ha spinto a sé il povero anziano del villaggio.]],
				['5'] = [[D'un tratto, il vecchio si è girato verso di me e ha iniziato a ridere.<new_line>"Ah, vuoi parlare con l'anziano? Ehi, furfanti, gettate tutta questa spazzatura nel fuoco!"]],
				['4'] = [[Sembrava che nessuno mi avesse sentito. Appena il regalo è stato gettato nel fuoco, sono divampate delle fiamme blu che hanno dato origine a un fumo dello stesso colore. Il vecchio e la sua banda erano intorno al falò e hanno inalato il fumo. I loro occhi si sono rigirati nella testa.]],
				['3'] = [["Ehi, anziano! Hai raccolto tutti i regali? Portali qui!"<new_line>Il vecchio con una maschera spaventosa in volto ha preso una scatola con un fiocco festivo dalle mani dell'uomo e l'ha gettata tra le fiamme.]],
				['2'] = [[Nella piazza del villaggio, i delinquenti gettano le decorazioni natalizie in un mucchio situato accanto a un falò. Alcuni abitanti del posto sono raggruppati contro i muri delle case. Sono guidati da un uomo austero con un paio di doni in mano.]],
				['1'] = [[Il fuggitivo mi ha condotto a un villaggio decorato per le festività. Davanti ai miei occhi, i delinquenti srotolavano le luci dai pali, toglievano gli ornamenti dagli alberi e sparivano dietro le case.]],
				['11'] = [["Ah, non l'hai riconosciuto? Quel vecchio è Babbo Natale! Non so come abbia fatto a diventare così...<new_line>Non riesco proprio a comprendere un tale abominio. Forse Babbo Naffare sa cosa fare. Dobbiamo trovarlo."]],
				['10'] = [["Non si tratta di questo. Il modo in cui lo inalano... mentre distruggono qualsiasi cosa che abbia a che fare con il Natale. Il tuo mondo è corrotto fino a tal punto?"]]
			},
			['choice'] = {
				['6'] = [["Che cosa gli hai fatto?!"]],
				['5'] = [[Salta fuori dal fumo]],
				['4'] = [["Lascialo in pace!"]],
				['3'] = [[Chiedi all'anziano cosa sta succedendo]],
				['2'] = [["Ehi, smettetela!"]],
				['1'] = [["Che cosa..."]],
				['8'] = [["È tutta opera di quel vecchio!"]],
				['7'] = [["È tossico?"]]
			}
		},
		['halloween_boss_2022_2'] = {
			['episode'] = {
				['2'] = [["Dal vecchio cimitero, naturalmente. Abbiamo messo qualcuno a sorvegliare quel posto. In fondo, sappiamo tutti che cosa succede quando il Medico della peste si imbatte nelle tombe..."]],
				['1'] = [["Salute, cacciatore! Per il momento riusciamo a tenere testa agli zombi, ma ne arrivano sempre di più."]],
				['3'] = [["Beh, quella canaglia deve aver messo qualcosa nei bicchieri delle guardie e si sono addormentate. È riuscito a entrare nel cimitero e ora non riusciamo a catturarlo. Si nasconde dietro una nebbia velenosa e un'orda di morti. Avanti, dagli una bella lezione!"]]
			},
			['choice'] = {
				['2'] = [["E allora come fanno gli zombi a passare?"]],
				['1'] = [["Da dove arrivano?"]]
			}
		},
		['halloween_boss_2022_5'] = {
			['episode'] = {
				['2'] = [[Sono corso verso quella persona, ma ormai era troppo tardi. Era già morta dissanguata a causa delle ferite.<new_line>A quel punto, ho sentito un sogghigno provenire da dietro di me.]],
				['1'] = [[Dopo il mio arrivo, mi sono imbattuto in un gruppo di mostri accalcati attorno a qualcuno. Non avevo scelta, si erano già accorti di me.]],
				['3'] = [["Non sai leggere, mortale? Se il nome di qualcuno è scritto sulla pagina, non c'è alcuna speranza che possa salvarsi. Non sprecare il tuo tempo. Igor raccoglie solo gli organi dei cadaveri più freschi. Continua a cercare."]]
			},
			['choice'] = {
				['2'] = [[Procedi]],
				['1'] = [[Girati]]
			}
		},
		['emba_isaev_rostov'] = {
			['choice'] = {
				['6'] = [["E come ha fatto Kurganov a controllare il team di spedizione?"]],
				['5'] = [[Parla dell'esplosione]],
				['4'] = [["Pensi che sia tutta colpa di Kurganov?"]],
				['3'] = [["Erano controllate da Kurganov."]],
				['2'] = [["Che cosa devo fare ora?"]],
				['1'] = [["Cosa?! Chi potrebbe essere?"]],
				['7'] = [["Dobbiamo chiederglielo!"]]
			},
			['episode'] = {
				['10'] = [["Abbiamo scoperto che gli scienziati hanno usato la tecnologia moderna, tra le altre cose, per stabilizzare il micelio".<new_line>Le macchine di calcolo con determinati algoritmi hanno chiaramente aiutato a frenare i cambiamenti incontrollabili del virus. Ora stiamo lavorando attivamente in questa direzione."]],
				['9'] = [["Ma non è in ospedale tra i feriti. Questo significa che... è morto in servizio. È una notizia molto deprimente; abbiamo perso un brav'uomo e un dipendente molto prezioso. Tuttavia, dobbiamo continuare a lavorare affinché la sua morte non sia stata vana."]],
				['8'] = [["Assolutamente. Credo che il Generale abbia costretto Gerin a continuare lo sviluppo militare. Se salviamo Gerin, il nostro compito sarà molto più facile. Non ho ricevuto alcun rapporto dal Navigatore, hai idea di quale sia il problema?"]],
				['7'] = [["Li ha usati per gli esperimenti?! È inconcepibile! Deve aver sfruttato anche Gerin, dobbiamo trovarlo immediatamente."]],
				['6'] = [["Santo cielo, li hai salvati! I miei colleghi sono vivi, ma lo sapevo già. Ho mandato da loro i nostri migliori medici. Che cosa gli è successo, comunque?"]],
				['5'] = [["Sono d'accordo. Quindi ti suggerisco di andare immediatamente dal Navigatore. Parla con lui e inizia a cercare la scatola nera. Abbiamo decifrato il codice di accesso dello scomparto segreto del laboratorio. Eccolo, l'ho scritto qui."<new_line>(Ti porge un pezzo di carta con il codice)]],
				['4'] = [["Nulla di utile dal punto di vista pratico, per ora. Ma abbiamo scoperto che il Navigatore deve sapere di Kurganov, l'uomo il cui codice di accesso è stato usato per entrare nella precedente stazione biologica."]],
				['3'] = [["Devo chiederti di nuovo di completare i nostri incarichi di ricognizione per recuperare i documenti del progetto segreto E.M.B.A. Questo è necessario innanzitutto per decifrare il codice di accesso al laboratorio."]],
				['2'] = [["Presumo che siano le stesse persone che lavoravano al progetto prima della guerra. Secondo i registri, la precedente stazione biologica è stata violata da un uomo che ha usato il codice di accesso del colonnello Kurganov. Cercheremo di saperne di più su di lui."]],
				['1'] = [["La scatola nera che hai riportato dall'ultima città della scienza era in condizioni migliori della precedente. Le nostre ipotesi sono state confermate. Qualcuno sta ancora lavorando in alcune stazioni biologiche."]],
				['11'] = [["Dobbiamo ancora scoprirlo, ma molto probabilmente il centro della questione è la simbiosi tra biologia e cibernetica. Forse, però, sono coinvolte altre tecnologie segrete."]],
				['12'] = [["Dobbiamo salvare Gerin. Secondo i registri, potrebbe essere molto lontano da qui. Lungo la strada, visiteremo un'altra città della scienza: lì c'è un certo oggetto su cui Kurganov sta raccogliendo informazioni. Credo che ci sia un motivo per il suo interesse e non possiamo ignorarlo. Partiremo non appena sarai pronto."]]
			}
		},
		['embacity_biolab_reservoir_2'] = {
			['episode'] = {
				['2'] = [[Un'enorme cisterna occupa gran parte della stazione biologica. Al momento è vuota.<new_line>Ad essa è collegata una stazione di pompaggio: macchine simili sono presenti nelle cisterne più piccole alla periferia della città.]],
				['1'] = [[Un'enorme cisterna occupa gran parte della stazione biologica, piena fino all'orlo di un liquido verde.<new_line>Ad essa è collegata una stazione di pompaggio: macchine simili si trovano nelle cisterne più piccole alla periferia della città.]],
				['3'] = [[Un'enorme cisterna occupa gran parte della stazione biologica: è piena per metà di un liquido verde.<new_line>Ad essa è collegata una stazione di pompaggio: macchine simili sono presenti nelle cisterne più piccole alla periferia della città.]]
			}
		},
		['halloween_fight_medium'] = {
			['choice'] = {
				['3'] = [[Evoca lo stregone]],
				['1'] = [[Urla per attirare l'attenzione]]
			},
			['episode'] = {
				['2'] = [[Ho attirato l'attenzione dell'orda urlando più forte che potessi e ho iniziato a correre. Il piano ha funzionato, poiché solo una parte delle bestie è riuscita a inseguirmi. Devo sconfiggerle in fretta, prima che le altre mi raggiungano.]],
				['1'] = [[Ci sono troppe bestie qui. Se affrontassi l'orda da solo, non avrei speranze. Devo trovare un modo per separarle e sconfiggerle in piccoli gruppi. "Divide et impera", è così che si dice.]],
				['4'] = [["Oh, di nuovo tu, ragazzino? Possibile che tu abbia sempre bisogno di una mano? Quando avevo la tua età, facevo tutto da solo. E va bene, fammi vedere chi si è comportato male con te."]],
				['3'] = [[Ho messo fuori gioco le più veloci del gruppo, ma non è ancora finita. Riesco già a sentire gli ululati. Le altre bestie si avvicinano.]]
			}
		},
		['infected_shop'] = {
			['episode'] = {
				['1'] = [[A giudicare dall'esterno dell'edificio, gli insetti mutanti si erano comodamente installati nel vecchio negozio. Probabilmente all'interno c'era qualcosa di valore, ma sarebbe stato un lavoro pericoloso.]]
			}
		},
		['icebreaker_short'] = {
			['choice'] = {
				['2'] = [[Trova la cabina del capitano]],
				['1'] = [[Sali sulla barca]]
			},
			['episode'] = {
				['2'] = [[Ogni porta della nave era stata spalancata e il pavimento era ricoperto di bossoli. C'erano cadaveri ovunque e gli scaffali erano pieni di cianfrusaglie della nave. Ma le medicine che ho trovato erano intatte. L'equipaggio non aveva nemmeno provato a scappare dalla nave. Nessuno aveva curato i feriti. Era tutto così strano.]],
				['1'] = [[Sembra che il pazzo di Monchegorsk abbia davvero prestato servizio su questa nave.]],
				['3'] = [[Nella cabina del capitano ho trovato il diario di bordo. Era fradicio, ma sono riuscito a distinguere la data dell'ultima annotazione: 22 settembre 1984.<new_line>La mia attenzione è stata attirata da qualcosa che brillava tra i mucchi di borsoni nell'armadio: una familiare bomboletta spray con l'etichetta 'Day R'. Continuando a scavare tra i detriti, ne ho trovate altre decine, tutte uguali.<new_line>Cosa sono queste bombolette spray? Cos'è successo all'equipaggio?<new_line>È come se tutti a bordo avessero perso la testa e si fossero uccisi a vicenda. Devo andarmene da questo posto orrendo.]]
			}
		},
		['emba_intro_bunker'] = {
			['choice'] = {
				['1'] = [[Torna dal Navigatore]]
			},
			['episode'] = {
				['2'] = [[Ho eliminato i biomutanti dal laboratorio sotterraneo e ho trovato documenti con scritto "Riservato". Devo tornare dal Navigatore.]],
				['1'] = [[La vecchia porta del bunker è socchiusa e da sotto senti provenire uno sgradevole scricchiolio e un rumore metallico. Sembra che lì dentro ci sia qualcosa di pericoloso.]]
			}
		},
		['comrade_in_trouble_rad'] = {
			['episode'] = {
				['1'] = [[L'esploratore giace a terra. Se la sarà cercata, ma è un modo orribile per andarsene.]]
			}
		},
		['emba_commander_medium2'] = {
			['choice'] = {
				['9'] = [[Avvia l'operazione]],
				['8'] = [[Lavora sulle mie armi]],
				['7'] = [[Trova informazioni sui biomutanti]],
				['6'] = [["Non sono ancora pronto."]],
				['5'] = [["Posso provare."]],
				['4'] = [["Cosa ti serve che io faccia?"]],
				['3'] = [["Non avrò paura, cosa bisogna fare?"]],
				['2'] = [["Per noi due?"]],
				['1'] = [["Cosa vuoi che faccia?"]]
			},
			['episode'] = {
				['9'] = [["Sapevi che lucertole e varani sono mangiatori di carogne e superano facilmente qualsiasi ostacolo? Almeno non volano.<new_line>I rettili mi sono sempre interessati. E questi hanno tutte quelle capacità... Spero di catturarne uno stavolta.<new_line>Uhm, comunque, basta parlare. È ora di andarsene. Ecco la radio. Ci muoviamo?"]],
				['8'] = [["Eccellente. Mentre aspettiamo che faccia buio, posso dirti qualcosa sui biomutanti.<new_line>Hai pulito la tua arma di recente? Mi sembra sporca e non mi sorprenderebbe se si bloccasse a ogni secondo colpo."]],
				['7'] = [["Dovrò agire rapidamente, quindi non posso appesantirmi, dovrò nascondermi senza attrezzature extra.<new_line>Ecco perché voglio che tu prenda un mirino per la visione notturna, ti metta in posizione nelle vicinanze con una radio e mi riferisca la situazione sul campo di battaglia. Agirò in base ai tuoi rapporti."]],
				['6'] = [["Hai fretta di andare da qualche parte? Allora, il punto è che voglio attaccare i biomutanti al buio e vedere come fanno a orientarsi senza luce. Un altro esperimento."]],
				['5'] = [["Sì. Allora, che ne dici di una piccola avventura? Non hai paura del buio, vero?"]],
				['4'] = [["Pochi l'hanno fatto, ecco perché voglio sapere come agiscono queste creature nell'oscurità.<new_line>Potrei anche catturarne qualcuna! Sarebbe una svolta per il mio progetto.<new_line>Ma questo è un lavoro per due cacciatrici."]],
				['3'] = [["Bene, mettiamoci al lavoro. L'operazione è complicata e il Progresso non riesce a trovarmi un partner esperto.<new_line>Quindi dovrai farlo tu... Hai mai dato la caccia ai biomutanti di notte?"]],
				['2'] = [["Aspetta. Sto lavorando a un progettino e potrei aver bisogno di un po' di aiuto. Mi serve qualcuno che sappia distinguere un fucile d'assalto da una vanga.<new_line>Ma prima c'è qualche altra faccenda da risolvere. Mi serve una corda, nel caso ne dovessi catturare qualcuno vivo. Ne hai una? L'ATV è troppo lontano."]],
				['1'] = [["Aspetta. Sto lavorando a un progettino e potrei aver bisogno di un po' di aiuto. Mi serve qualcuno che sappia distinguere un fucile d'assalto da una vanga.<new_line>Ma prima c'è qualche altra faccenda da risolvere. Sto esaurendo la vernice mimetica. Puoi portarmi la base per una nuova porzione, mentre io diluisco la vernice?"]],
				['11'] = [["Non hai abbassato la guardia, posso contare su di te. Sarebbe stato bello catturare qualcosa di vivo, ovviamente... per curiosità.<new_line>Beh, pazienza. La prossima volta. Grazie per l'aiuto."]],
				['10'] = [[Ci siamo tenuti impegnati per un po'. Quando ha iniziato a fare buio, la Cacciatrice si è alzata, mi ha dato la radio e mi ha detto di iniziare.]]
			}
		},
		['halloween_def_easy'] = {
			['choice'] = {
				['2'] = [["Non ho tempo." (Vattene)]],
				['1'] = [["Certo, darò una mano."]],
				['4'] = [["Usa i barili."]],
				['3'] = [["Posiziona le trappole."]]
			},
			['episode'] = {
				['2'] = [["Ti ringrazio, non me ne dimenticherò. Mettiamoci all'opera. Potrei preparare dei barili di carburante in modo che tu possa sparargli, o potrei posizionare delle trappole. Mi raccomando, non farti prendere da nessuna di esse."]],
				['1'] = [["Gentile straniero, sei davvero qui per aiutarmi? Questi demoni sono una vera seccatura. Hanno divorato tutto il mio raccolto. I miei vicini non credono si tratti di demoni. Mi considerano un pazzo che si inventa le cose e dicono che sia stato io stesso a mangiare il raccolto. Potresti aiutarmi? Farò in modo che ne valga la pena."]],
				['3'] = [["Oh! L'hai sentito anche tu? I mostri si avvicinano. Illuminiamoli un po'!"]]
			}
		},
		['easter_buyer'] = {
			['choice'] = {
