		},
		['halloween_fight_hard'] = {
			['choice'] = {
				['2'] = [[Controlla la zona]],
				['1'] = [[Avvicinati]],
				['3'] = [[Attira l'attenzione su di te]]
			},
			['episode'] = {
				['6'] = [[Ho perlustrato l'area con attenzione e ho scoperto che ci sono altre bestie nelle vicinanze. Adesso ho l'elemento sorpresa, anche se questi mostri sembrano più pericolosi degli altri.]],
				['5'] = [[Non sento più nessuna bestia. Non ho idea di come siano riuscite ad attirarmi in una trappola. Devo assicurarmi che non ce ne siano altre nascoste qui intorno.]],
				['4'] = [[Mi sono sbarazzato dei mostri, ma dubito fortemente che siano finiti qui. Gli ululati delle bestie continuavano ad aumentare intorno a me.]],
				['3'] = [[Calpestai un ramo e le bestie intorno a me iniziarono a ululare. Era una trappola!]],
				['2'] = [[Mentre mi avvicinavo cautamente alla fonte del rumore, quest'ultimo iniziava ad attenuarsi a poco a poco. Appena raggiunsi la fonte, il rumore era completamente svanito.]],
				['1'] = [[Il rauco baccano delle bestie si sente da molto vicino. A quanto pare, ce ne sono parecchie. È meglio fare attenzione.]],
				['7'] = [[Preparai il campo di battaglia in caso di attacco e piazzai delle semplici trappole. Meglio di niente, quantomeno. In seguito, feci un po' di rumore, e le bestie rimanenti corsero verso di me. Purtroppo, erano estremamente pericolose.]]
			}
		},
		['halloween_boss_2022_3_2'] = {
			['episode'] = {
				['9'] = [[Mi sono seduto sul divano e per poco non mi addormentavo ascoltando il quartetto.]],
				['8'] = [[Il cameriere mi ha porto un bicchiere di vino:<new_line>"Lo assaggi, è Falerno."]],
				['7'] = [[Il cameriere mi ha servito un piatto dicendo:<new_line>"Persico al naturale, una vera delizia. Bon appétit!"]],
				['6'] = [[Il giardino è allestito con tavoli pieni di cibo e bevande. C'è un quartetto d'archi che suona sul palco circondato da soffici divani.]],
				['5'] = [["Stiamo organizzando una serata. Mangia, bevi, festeggia. E una volta che ti sarai divertito abbastanza, torna da me e in maniera cordiale... porremo fine a... insomma, capisci che cosa intendo."]],
				['4'] = [["Ebbene, quando vivi per centinaia di anni, i soliti discorsi rauchi e il cercare di intimorire la gente iniziano a stancarti. Perché, invece, non ci comportiamo da gentiluomini?"]],
				['3'] = [["Io sono davvero il conte Dracula, e mi sono sistemato in questa meravigliosa dimora insieme alle mie bellissime mogli. E di certo non nego che uccidiamo dei viaggiatori occasionali che si avvicinano a casa nostra."]],
				['2'] = [[Le porte della villa si sono aperte, e un uomo alto è corso verso di noi:<new_line><new_line>"Fermi, fermi, fermi! Perdonate la mia sposa, è giovane ed è un'amante delle sceneggiate. Venite con me al mio giardino e vi spiegherò ogni cosa."]],
				['1'] = [[Ci siamo addentrati in una villa lussuosa. "Questo è il luogo in cui andrete incontro alla vostra fine, miei cari. Avete ottenuto udienza presso il conte Dracula. Come spuntino, per vostra sfortuna."]],
				['11'] = [["Era un uomo veramente cortese, quasi mi dispiace per lui. Oh, che cosa stavo dicendo...? Giusto, non dimenticare di aumentare il tuo livello demoniaco! Altrimenti, non riuscirai a portare a termine il prossimo contratto."]],
				['10'] = [["Molto bene, mio caro ospite. Spero che la permanenza sia stata di tuo gradimento. Ora, iniziamo..."]]
			},
			['choice'] = {
				['9'] = [["In che senso?"]],
				['8'] = [[Torna da Dracula]],
				['7'] = [[Ascolta la musica sul divano]],
				['6'] = [[Assaggia la bevanda]],
				['5'] = [[Assaggia il cibo]],
				['4'] = [["Affare fatto."]],
				['3'] = [["Perché mi stai dicendo questo?"]],
				['2'] = [[Segui l'uomo]],
				['1'] = [["Staremo a vedere!"]]
			}
		},
		['ice_brigands'] = {
			['episode'] = {
				['1'] = [[I briganti saltano fuori dalla neve per tendermi un agguato. Non c'è traccia di umanità nei loro volti pallidi.]]
			}
		},
		['rival'] = {
			['episode'] = {
				['2'] = [[Secondo me, quando l'uomo ha visto il mio equipaggiamento, ha capito di aver sbagliato e ha battuto in ritirata.]],
				['1'] = [[Prima che potessi entrare, un uomo armato mi è venuto incontro. Mi ha detto che era arrivato lui per primo e che avrei dovuto cercarmi un altro posto.]]
			},
			['choice'] = {
				['1'] = [[Minaccialo]]
