		['embacity_biolab_7'] = {
			['episode'] = {
				['2'] = [[Il ronzio dei computer e dell'armadio di controllo al centro è cessato. Il grumo di micelio non mostra segni di vita. Chissà cos'era... Ma lasciamo che se ne occupino gli scienziati. Ho fatto tutto il possibile, ora devo solo portare a loro la scatola nera.]],
				['1'] = [[Il piano sotterraneo di questa stazione biologica un tempo fungeva da sala di controllo. Ma ora il pavimento è bucherellato, le radici si estendono oltre le antenne dei computer e sui tavoli sono ammassati mucchi di biomassa.<new_line>Al centro della stanza, sull'armadio di controllo, ho notato un movimento. I computer hanno immediatamente iniziato a ronzare e dei biomutanti sono emersi dall'ombra della parete.]]
			}
		},
		['guard_leningrad'] = {
			['choice'] = {
				['6'] = [["Sì."]],
				['5'] = [["Non ancora."]],
				['4'] = [["Va bene. Alla prossima."]],
				['3'] = [["Perché ti interessa?"]],
				['2'] = [["Ho cose importanti da fare. Sto cercando una persona."]],
				['1'] = [["Voglio sapere se c'è un insediamento di sopravvissuti a Tver."]],
				['7'] = [["Grazie."]]
			},
			['episode'] = {
				['6'] = [["Molto bene, allora dirigiti a sud. C'è un insediamento di sopravvissuti vicino al porto di Tver. Vivevamo lì una volta, sai? Conosco anche Svetlana. Ha salvato molte vite. Penso che sia ancora lì.<new_line><new_line>Prendi la tua attrezzatura, usala saggiamente e tieni d'occhio il contatore Geiger. È fin troppo semplice finire in mezzo a una terra desolata radioattiva."]],
				['5'] = [["Quindi ora sei meglio preparato?"]],
				['4'] = [["Non siamo rimasti in molti. Dobbiamo aiutarci a vicenda. E tu mi sei molto più utile da vivo."]],
				['3'] = [["Senti, l'area attorno a Mosca è pericolosa. Tver inclusa. Tutta la zona è stata uno dei principali bersagli del nemico ed è stata colpita duramente dal nucleare. Ora è piena di radiazioni!<new_line><new_line>Mi è appena venuta un'idea. Dammi una mano: potrai prepararti per questo viaggio pericoloso che hai intenzione di fare e, per ringraziarti, ti darò anche dell'attrezzatura. In questo modo, non cadrai a terra morto non appena arriverai a destinazione."]],
				['2'] = [["Di cosa hai bisogno? Ci farebbe comodo il tuo aiuto qui."]],
				['1'] = [["Cerchi qualcosa?"]]
			}
		},
		['new_year_boss_3'] = {
			['episode'] = {
				['6'] = [["Orso, significa che anche io ti ho frainteso. Dopotutto, nel tuo mondo hai condotto una rivoluzione dopo l'altra!"]],
				['5'] = [[Orso Natale si è messo gli artigli sulla testa.<new_line>"Intendevo dire che avresti dovuto proteggere il parco nel caso in cui Krampus si fosse fatto vivo! Chi si impadronisce della proprietà con la forza?!"]],
				['4'] = [["Allora che cos'è successo? Oh no... Sei stato tu a distruggere questo posto?!"]],
				['3'] = [[Quando gli ultimi colpi si sono attenuati, mi sono guardato attorno e ho visto i miei compagni in piedi davanti alla porta.<new_line>"Siamo arrivati troppo tardi, compagno? La banda di Krampus ha già razziato il parco?"]],
				['2'] = [[Ho sfondato la porta che conduce al settore interno del parco. Spero che il magazzino dei regali sia qui.]],
				['1'] = [[Il parco non è aperto. Il cancello è chiuso e nessuno ha risposto dopo che ho bussato. Non ho altra scelta, devo irrompere nel parco.]],
				['8'] = [["Ok... Un problema alla volta. Naffare ha stretto un accordo con il gestore, il quale condividerà i suoi regali. Speriamo di riuscire a insabbiare tutto questo vandalismo più tardi.<new_line>Andiamo a negoziare con la banda."]],
				['7'] = [["È il lato oscuro del mio passato. Ora supporto la lotta di classe."]]
			},
			['choice'] = {
				['5'] = [[Varca la soglia]],
				['4'] = [["Che cosa facciamo?"]],
				['3'] = [["Ehm..."]],
				['2'] = [["Sei stato tu a dirmi di "occuparmi" del parco."]],
				['1'] = [["No, è tutto a posto."]]
			}
		},
		['halloween_fight_easy'] = {
			['episode'] = {
				['2'] = [[Mi sono avvicinato molto alle bestie. Ora posso attaccare.]],
				['1'] = [[Gli ululati raccapriccianti delle bestie mostruose che vagano per la zona si sentono da miglia di distanza. Sono una vera minaccia per la gente del luogo. Se le sconfiggo, sarò ben ricompensato.]]
			}
		},
		['santa_bear'] = {
			['episode'] = {
				['6'] = [["Babbo Naffare contratterà con il gestore, mentre io mi metto in contatto con la banda. Il tuo compito è quello di "occuparti" del magazzino dei regali nel parco. Bene, diamo inizio all'operazione prima che Krampus faccia la sua mossa!"]],
				['5'] = [["Mmh, se facessimo un'offerta vantaggiosa a Krampus... C'è un parco di divertimenti non molto lontano da qui.<new_line>Conosco piuttosto bene chi lo gestisce. Lo rifornivo di regali e decorazioni. Forse la banda non ha ancora trovato il parco."]],
				['4'] = [["Non so come funzionino le cose nelle vostre fabbriche, ma qui? Guardate a cosa ha portato! I lavoratori non dovrebbero essere alienati dai mezzi di produzione. Dobbiamo ridistribuire i beni materiali."]],
				['3'] = [["Ci risiamo, ancora con questa storia dello sfruttamento..."]],
				['2'] = [["Sapete cosa c'è che non va col vostro sistema socialista? Avete sfruttato l'intero complesso industriale delle festività per anni e anni."]],
				['1'] = [["Salve, compagno! Naffare, vecchia volpe, come stai? Oh, Naclava!"]]
			},
			['choice'] = {
				['2'] = [["Che cosa intendi esattamente?"]],
				['1'] = [["È una tradizione natalizia."]],
				['3'] = [["Quindi andiamo da lui e prendiamo i regali?"]]
			}
		},
		['halloween_search_medium_1'] = {
			['episode'] = {
				['1'] = [["Salve, giovanotto. Oggi sto preparando una pozione molto complicata. Per caso hai qualcuno degli ingredienti?"]]
			}
		},
		['handcar'] = {
			['choice'] = {
				['38'] = [[Seppellisci il cadavere]],
				['28'] = [[Ispeziona gli altri vagoni]],
				['18'] = [[Apri la porta a colpi di ascia]],
				['23'] = [[Lanciagli la bottiglia]],
				['33'] = [[Guarda nella borsa]],
				['25'] = [[Ispeziona la carrozza pilota]],
				['35'] = [[Ispeziona il dispositivo]],
				['31'] = [[Esamina il cadavere]],
				['41'] = [[Dovrò controllare]],
				['11'] = [[Cerca di accelerare]],
				['21'] = [[Spaventalo con la torcia]],
				['15'] = [[Non dovrei starmene qui seduto. È ora di rimettersi in cammino.]],
				['13'] = [[Salta giù dalla draisina]],
				['6'] = [[Prosegui attraverso la nebbia]],
				['5'] = [[Fermati e accampati]],
				['4'] = [[Devo proseguire.]],
				['3'] = [[Cerca erbe utili]],
				['2'] = [[Raccogli le bacche dai cespugli]],
				['1'] = [[Prosegui]],
				['37'] = [[Sposta il cadavere dai binari]],
				['39'] = [[Non toccarlo più]],
				['19'] = [[Continua a ispezionare]],
				['29'] = [[Cerca altri resti]],
				['8'] = [[È ora di rimettersi in cammino.]],
				['7'] = [[Dormi]],
				['34'] = [[Togligli la maschera antigas]],
				['24'] = [[Guarda il treno più da vicino]],
				['36'] = [[Accendi il dosimetro]],
				['26'] = [[Esamina i vagoni merci]],
				['20'] = [[Prosegui]],
				['32'] = [[Apri il giornale]],
				['22'] = [[Sparagli con la pistola]],
				['30'] = [[Corri sulla draisina!]],
				['16'] = [[Ispeziona la stazione]],
				['14'] = [[Sono morto e le terre desolate mi hanno divorato...]],
				['42'] = [[Vai verso Murmansk]],
				['40'] = [[Prossima fermata: Murmansk!]],
				['17'] = [[Arrampicati ed entra dalla finestra]],
				['27'] = [[Cerca nei vagoni passeggeri]],
				['12'] = [[Spara in faccia al riccio con la pistola]],
				['10'] = [[Cosa diavolo...]],
				['9'] = [[Esamina la nebbia]]
			},
			['episode'] = {
				['38'] = [[Era una specie di radiolina da cui partiva un cavo con un tubicino di metallo all'estremità. Nella custodia di pelle c'era il libretto di istruzioni: era un Dosimetro-Radiometro DP-5V.]],
				['28'] = [[Nei vagoni merci rovesciati non c'era niente di utile tranne alcune ferraglie.]],
				['18'] = [[Ho cercato di fare attenzione, ma mi sono comunque strappato i pantaloni e graffiato con una sbarra.]],
				['23'] = [[Ho tirato fuori la pistola e le ho sparato. La pallottola ha colpito la draisina, sprigionando un nugolo di scintille. Spaventato, il felino è balzato via scomparendo nella nebbia. Adesso posso andarmene.]],
				['33'] = [[Ero quasi arrivato a Murmansk quando ho notato qualcosa sui binari davanti a me. Ho fermato la draisina e mi ci sono avvicinato. Era un cadavere.]],
				['25'] = [[La nebbia si è ritirata e le paludi hanno ceduto il passo ai boschi. Dal cuore della foresta è emersa un'altra ferrovia a scartamento ridotto, parallela a quella su cui mi trovavo. Un po' più avanti ho visto i resti di un treno deragliato.]],
				['35'] = [[Le trattative con gli Stati Uniti si fanno tese...]],
				['31'] = [[La scia di ossa portava al bosco. All'improvviso un ululato ha squarciato il silenzio tra gli alberi. Meglio a darsela a gambe!]],
				['41'] = [[A costo di espormi alle radiazioni, non volevo che quel cadavere finisse in pasto agli animali selvatici. Ho preso delle pietre e ho costruito un piccolo tumulo sopra l'uomo.]],
				['11'] = [[Apro gli occhi. Sono circondato da un muro di nebbia. Nel silenzio sento solo il rumore del mio respiro affannato. Devo essermi addormentato aggrappato alla leva della draisina. Che cos'era quell'assurda bestia che ho visto in sogno?!]],
				['21'] = [[Ho ispezionato l'interno da cima a fondo trovandoci solo una corda e una bottiglia di vodka che qualcuno aveva nascosto. Quando sono tornato alla draisina c'era una grossa lince che girovagava nei paraggi. Appena si è accorta di me ha soffiato inarcando la schiena.]],
				['15'] = [[Mi sono svegliato ai piedi del terrapieno. Probabilmente mi ero addormentato ed ero caduto dalla draisina. Per colpa di quell'assurdo sogno, avevo il corpo ricoperto di lividi e bernoccoli. Ancora un po' e ci rimettevo l'osso del collo.]],
				['13'] = [[Quando ho riaperto gli occhi ero riverso sulla draisina. Dovevo essermi assopito. Durante quell'incubo delirante avevo anche buttato la pistola chissà dove. Sono tornato indietro e dopo qualche ricerca l'ho finalmente trovata sui binari.]],
				['6'] = [[Al mattino la nebbia formava ancora un muro bianco intorno al mio piccolo accampamento. Ero comunque felice di essermi potuto riposare, e mi sono rimesso in viaggio con rinnovato vigore.]],
				['5'] = [[Ho fermato la draisina. Non volevo certo addormentarmi e cadere dai binari mentre mi ci trovavo sopra. Ho acceso un falò ai piedi del terrapieno e mi sono preparato per la notte.]],
				['4'] = [[È scesa la notte. Nell'aria aleggiava una foschia, e nel giro di qualche chilometro la nebbia ha inghiottito tutto il mondo circostante. Ero stanco e iniziavo ad assopirmi.]],
				['3'] = [[Dopo avere ispezionato il prato, ho raccolto qualche foglia di piantaggine. Tuttavia, mentre ero impegnato nell'intento, il cielo si è fatto completamente buio. Stava calando la sera.]],
				['2'] = [[Ho mangiato fragole fino a scoppiare e ne ho raccolte un po' per il viaggio. Quando ho finito, però, il sole era già quasi scomparso dietro l'orizzonte. Si stava facendo buio.]],
				['1'] = [[Dopo aver percorso alcuni chilometri sulla draisina, ho notato un corso d'acqua ai piedi del terrapieno. Era bordato da un prato fitto di erbe selvatiche e cespugli di fragole che crescevano a pelo dell'acqua. Un angolo puro e incontaminato.]],
				['37'] = [[Nella borsa c'erano delle bende, oltre a un piccolo dispositivo in una custodia di pelle con una tracolla.]],
				['39'] = [[Quando ho premuto l'interruttore, il dosimetro ha iniziato a ronzare. Ho mosso il tubicino del sensore e quando l'ho passato sopra il cadavere il ronzio si è fatto più forte. Il cadavere era estremamente radioattivo!]],
				['19'] = [[Per risolvere il problema della porta chiusa a chiave sono bastati un paio di colpi di ascia. Sono entrato.]],
				['29'] = [[Nei vagoni passeggeri ho trovato solo un paio di valigie ammaccate, una confezione di bende e... un sacco di macchie di sangue. Ma dove sono i viaggiatori?]],
				['8'] = [[Dalla nebbia è emerso un riccio delle dimensioni di un camion. Aveva gli occhi che brillavano come due riflettori e una nuvola di vapore che gli usciva dalle fauci. Ci è mancato poco che il suo ruggito assordante non mi facesse cadere dalla draisina.]],
				['7'] = [[Anche se avevo gli occhi che si chiudevano dal sonno, ho proseguito. Tutt'a un tratto ho sentito uno clangore risuonare alle mie spalle, come se stesse arrivando un treno a tutta velocità. Attraverso la nebbia ho intravisto i contorni di una sagoma enorme...]],
				['34'] = [[Indossava un cappotto lurido, una maschera antigas senza un filtro e un borsone intorno alle spalle. Nelle tasche del cappotto c'era un giornale arrotolato.]],
				['24'] = [[La bottiglia di vodka che avevo trovato con tanta fatica si è fracassata davanti alla lince che, spaventata, è balzata via ed è scomparsa nella nebbia. Adesso posso andarmene.]],
				['36'] = [[Attraverso la maschera antigas si intravedeva il volto emaciato di un uomo che non aveva ancora iniziato a decomporsi.]],
				['26'] = [[La parte anteriore della carrozza pilota era affondata nel terreno, ai piedi del terrapieno. I vagoni merci subito dietro erano un ammasso di lamiere accartocciate, ma sui binari restavano alcuni vagoni passeggeri.]],
				['20'] = [[Ho trovato senza troppa fatica un oliatore mezzo vuoto. Sono tornato alla draisina, ho lubrificato la leva dello scambio e ho deviato i binari. Adesso posso rimettermi in viaggio... o restare nei paraggi e continuare a ispezionare.]],
				['32'] = [[Il resto della muta si è unito all'ululato. Sono corso sulla draisina e ho premuto la leva a tutta velocità, cercando di non voltarmi.]],
				['22'] = [[Ho gridato e le ho agitato contro la torcia. Lei si è spaventata ed è balzata via, scomparendo nella nebbia. Adesso posso andarmene.]],
				['30'] = [[Quando sono uscito dal vagone, ho notato uno scheletro umano nell'erba. Gli animali selvatici sembravano essersi accaniti sui superstiti del deragliamento.]],
				['16'] = [[Di lì a qualche ora, dopo una biforcazione, dalla nebbia è emersa la sagoma di una piccola stazione. Lo scambio dei binari convoglia la draisina al capolinea, ma la leva è troppo arrugginita. Devo trovare un modo per sbloccarla.]],
				['14'] = [[Ho fatto un balzo e sono rotolato giù per il terrapieno prima che il riccio scardinasse la draisina dai binari. L'ultima cosa che ho visto è stata la grossa leva di metallo che mi piombava addosso con tutto il suo peso.]],
				['43'] = [[Più resto vicino al corpo, più sarò esposto alle radiazioni. Mi conviene tornare alla draisina appena possibile.]],
				['42'] = [[Mentre me ne andavo, ho notato un foglietto che gli spuntava dalla tasca della borsa. Sopra c'era l'indirizzo di una casa nella città di Kola. Che cosa ci avrei trovato? Dei superstiti? O delle provviste?]],
				['40'] = [[Ho trascinato il corpo ai piedi del terrapieno. Adesso sapevo cosa aveva ucciso quel poveretto: l'avvelenamento da radiazioni lo aveva consumato da dentro.]],
				['17'] = [[Una piattaforma di cemento e un palazzo fatiscente. I resti delle sbarre sporgono dalle finestre rotte, mentre profonde tracce di artigli segnano la porta chiusa a chiave.]],
				['27'] = [[La carrozza pilota era vuota, ma nella cabina di guida ho trovato un piccolo pacchetto con una manciata di proiettili. In compenso non sono riuscito a trovare la pistola.]],
				['12'] = [[Ho sparato un paio di colpi in faccia al riccio, ma l'enorme creatura non ha battuto ciglio. Ha spalancato le fauci e mi è balzata addosso, divorando me e un pezzo della draisina.]],
				['10'] = [[Fuori di me dal terrore, ho iniziato a premere la leva con tutte le mie energie, ma ben presto ho percepito il fiato caldo del riccio sul collo. È stata la mia ultima sensazione prima che le tenebre inghiottissero tutto ciò che mi circondava.]],
