				['4'] = [["Ho capito."]],
				['3'] = [["Dove posso trovare queste monete?"]],
				['2'] = [["Che cosa vuoi in cambio dei contratti?"]],
				['1'] = [["E tutto questo che cosa ha a che fare con me?"]],
				['7'] = [["Un asino parlante? Sul serio?"]]
			}
		},
		['forest_pithouse'] = {
			['episode'] = {
				['1'] = [[Nella foresta ho trovato un rifugio scavato. Le braci del fuoco erano ancora calde, quindi qualcuno deve vivere qui e non può trovarsi molto lontano. Dall'interno non proviene alcun rumore.]],
				['13'] = [[Proprio quando stavo per andare via con tutto il bottino, un intero branco di lupi è apparso da dietro gli alberi. Dev'essere stato il rumore dell'esplosione e del mio rovistare ad attrarre la loro attenzione. Dovrò difendermi.]],
				['9'] = [[Dentro il rifugio ho trovato un sacco di scorte: cibo, medicine, attrezzi, munizioni e altro. Sono dovuto scappare prima che tornasse il padrone.]],
				['8'] = [[La porta era chiusa con un semplice chiavistello, probabilmente per tenere alla larga gli animali. Dentro faceva piuttosto caldo. Negli angoli si trovavano sacchi e casse piene di vari oggetti.]],
				['7'] = [[Quando ho detto di voler andare, mi ha proposto di scambiare sale, sigarette o munizioni.]],
				['6'] = [[Quando l'uomo ha capito che non ero pericoloso, si è calmato. Abbiamo chiacchierato amabilmente, mangiato e bevuto. Abbiamo parlato a lungo del passato e condiviso i nostri piani per il futuro.]],
				['5'] = [[Mi ha guardato con sospetto, ma poi si è rilassato. <new_line>]],
				['4'] = [[Ti consiglio di continuare a camminare]],
				['3'] = [[Di punto in bianco, l'uomo si è rizzato in piedi e ha puntato il fucile verso di me.<new_line>Mi ha chiesto: ]],
				['2'] = [[Prima il padrone del rifugio è tornato. Era un uomo basso con un fucile e una cesta piena di funghi. Si è guardato in giro cautamente e poi ha attizzato le braci del fuoco.]],
				['10'] = [[Non appena sono uscito con il sacco pieno di roba, ho visto il padrone del rifugio: un uomo che puntava un fucile verso di me. Dopo un attimo ho sentito uno sparo e un dolore lancinante alla spalla destra.]],
				['11'] = [[Vicino al rifugio ho trovato bacche, carne, pelli lasciate ad asciugare e un cesto di carbone.]],
				['12'] = [[Ho sistemato la granata e mi sono nascosto dietro ai cespugli. Poco dopo è apparso il padrone del rifugio. Appena è entrato, la granata è esplosa. Quando mi sono avvicinato era già morto. Ora posso ispezionare bene il posto.]]
			},
			['choice'] = {
				['1'] = [[Nasconditi e aspetta]],
				['9'] = [[Esci]],
				['8'] = [[Saluta e vai via]],
				['7'] = [[È da tanto che non vedo gente. Mi piacerebbe chiacchierare un po'.]],
				['6'] = [[Passavo da queste parti]],
				['5'] = [[Esci dai cespugli e saluta]],
				['4'] = [[Metti una granata con filo]],
				['3'] = [[Ispeziona ovunque]],
				['2'] = [[Entra]],
				['10'] = [[Cosa ti prende? Volevo solo parlare un po'!]],
				['11'] = [[Ok, me ne vado.]],
				['12'] = [[Ispeziona dentro e fuori]]
			}
		},
		['base_restaurant'] = {
			['episode'] = {
				['1'] = [[La base dei banditi è nell'edificio del ristorante. Probabilmente è lì che tengono la merce rubata, ma non sarà facile entrare: ci sono sentinelle di guardia sul tetto.]]
			}
		},
		['halloween_energy_easy'] = {
			['choice'] = {
				['6'] = [["Sei riuscito a riportare indietro il comandante?"]],
				['5'] = [["Si può sapere perché li aiuti?"]],
				['4'] = [["Ok." (Va' dal contadino)]],
				['3'] = [[Inizia]],
				['2'] = [["Non c'è tempo." (Vattene)]],
				['1'] = [["Che zucche vuote. Certo, darò una mano."]]
			},
			['episode'] = {
				['9'] = [[Prima, dobbiamo scavare un giardino. Inizia appena sei pronto."]],
				['8'] = [["No... Forse abbiamo sbagliato qualcosa. Riproveremo. Grazie infinite per l'aiuto."]],
				['7'] = [[Oh, Imperatore Solvolto, torna da noi. Perdonaci per averti deluso. Abbiamo bisogno di te. Torna da noi e ti prometteremo la nostra lealtà!"<new_line><new_line>Il rituale durò diversi minuti. In seguito, le teste-di-zucca si divisero e avevano uno sguardo triste.<new_line>]],
				['6'] = [["Questi sono affari, e gli affari vengono prima di tutto il resto. Ok, prendilo e mettiti all'opera."]],
				['5'] = [["Mi raccomando, chiudilo. Per le teste-di-zucca il fertilizzante vale tanto oro quanto pesa. Ci ho lavorato per giorni interi."]],
				['4'] = [["Ben fatto. Ciò che rimane farà da fertilizzante per le piante. Ti dispiacerebbe farti prestare del fertilizzante dal contadino? Dovrebbe essere a casa."]],
				['3'] = [["Ottimo! Ora dobbiamo piantare i semi. Inizia appena sei pronto."]],
				['2'] = [["Grazie infinite! Prima, dobbiamo scavare un giardino. Inizia appena sei pronto."]],
				['10'] = [["Ora dobbiamo piantare i semi. Inizia appena sei pronto."]],
				['11'] = [["È quasi tutto pronto. Dobbiamo solo dare il fertilizzante alle piante."]],
				['1'] = [["Salve, avventuriero! Puoi aiutarci? Ci manca molto il nostro comandante. Senza di lui è molto dura, perciò vogliamo farne crescere uno nuovo. Abbiamo già trovato i semi e un giardino, ma siamo troppo maldestri per farlo da soli."]]
			}
		},
		['prologue_new_year'] = {
			['episode'] = {
				['5'] = [[Ti ho stilato un lista di compiti. Portali a termine e vedrai che è tutto vero. Dopo che avrai ripristinato parte della magia, mi occuperò della fase successiva. Ti auguro buona fortuna. Grazie in anticipo per l'aiuto!]],
				['4'] = [[Non proprio. Occorrono addobbi speciali... magici. Appariranno ogni volta che commetterai una buona azione. Dovrai farne molte se vuoi addobbare tutto l'albero.]],
				['3'] = [[La scomparsa di Nonno Gelo ha fatto svanire tutta la magia festiva. Per ripristinarla, dobbiamo addobbare l'albero di capodanno.]],
				['2'] = [[Qualcuno ha rapito Nonno Gelo. Sto cercando di salvarlo, ma non posso farcela da sola. Aiutami, ti prego! mi supplica la ragazza. Non credo alle sue parole, ma decido di stare al gioco.]],
				['1'] = [[In questi giorni, senza alcun preavviso, la temperatura è scesa repentinamente e la neve ha iniziato a ricoprire ogni cosa. La situazione ha preso una svolta ancora più inaspettata quando, per strada, mi sono imbattuto in una ragazza vestita da fanciulla di neve.]]
			},
			['choice'] = {
				['2'] = [[È sufficiente addobbare un albero?]],
				['1'] = [[Cosa devo fare?]],
				['3'] = [[Da dove dovrei iniziare?]]
			}
		},
		['titanium_mine'] = {
			['choice'] = {
				['2'] = [[Dai il permesso e prosegui]],
				['1'] = [[Raggiungi l'ingresso]],
				['3'] = [[Che tipo di permesso?']]
			},
			['episode'] = {
				['6'] = [[Dopo aver eliminato i mutanti, ho continuato a correre lungo le gallerie finché non ho trovato un carro pieno di titanio.]],
				['5'] = [[Proseguendo, ho individuato un ascensore. Mentre scendevo ancora più in profondità e cercavo di orientarmi in quelle catacombe, mi sono imbattuto in un carro pieno di titanio.<new_line>]],
				['4'] = [[Non stiamo estraendo titanio solo per divertimento. È una risorsa preziosa. Se vuoi il tuo, compra un permesso a Magnitogorsk. Niente permesso, niente ingresso.']],
				['3'] = [[Proseguendo, ho individuato un ascensore. Mentre scendevo ancora più in profondità e cercavo di orientarmi in quelle catacombe, mi sono imbattuto in alcuni mutanti.]],
				['2'] = [[Sono stato fermato da un uomo con un'armatura pesante:<new_line>'Hai un permesso?']],
				['1'] = [[Vicino all'entrata della miniera di titanio c'era un punto di controllo fortificato. Dei mitragliatori erano puntati verso l'interno della miniera. A quanto pareva, la minaccia non veniva da un intruso esterno.]],
				['8'] = [[Correndo lungo le gallerie delle catacombe, ho trovato un ascensore e sono tornato al punto di controllo.]],
				['7'] = [[Ho continuato a correre lungo quelle catacombe alla ricerca di un'uscita. Poi, all'improvviso, un gruppo di mutanti mi ha attaccato.]]
			}
		},
		['emba_intro'] = {
			['episode'] = {
				['1'] = [[Attenzione, per favore!<new_line>Sono il dottor Victor Isaev, il capo della spedizione di ricerca. Siamo Progresso: un'organizzazione scientifica che ha l'obiettivo di ripristinare il mondo utilizzando progressi scientifici all'avanguardia. <new_line><new_line>Al momento, la nostra spedizione sta cercando gli sviluppi segreti del progetto E.M.B.A. Se dovessimo riuscirci, inizierebbe una nuova era di prosperità: radiazioni, malattie, carestie e il declino generale della civiltà umana sarebbero solo ricordi del passato. <new_line>Abbiamo bisogno di un combattente esperto che lavori in aree a rischio elevato. Avrai il compito di neutralizzare un possibile nemico e ottenere dati sulle ricerche condotte presso le stazioni biologiche E.M.B.A.<new_line><new_line>Progresso ti fornirà maggiori informazioni all'arrivo alle coordinate indicate. Solo insieme potremo condurre il mondo a un futuro radioso. <new_line>Passo e chiudo.]]
			}
		},
		['embacity_reservoir_2'] = {
			['choice'] = {
				['1'] = [[Attiva la pompa]]
			}
		},
		['emba_isaev_surgut'] = {
			['choice'] = {
				['6'] = [["Va bene. Buona fortuna, dottore."]],
				['5'] = [[Avvicinati a Isaev]],
				['4'] = [["Che cosa intendi?"]],
				['3'] = [["Perché? Hai ottenuto nuovi dati?"]],
				['1'] = [["Come applicherai la tecnologia?"]]
			},
			['episode'] = {
				['13'] = [[(sussurra) "Eccoti qui, amico mio. Dobbiamo andarcene subito da qui. Ho contattato i miei colleghi dell'A.S.S.A., andremo nella loro città.<new_line>Abbiamo davvero bisogno del tuo aiuto, ti prego di raggiungerci lì il prima possibile."]],
				['10'] = [["Non capisci! Questa tecnologia è instabile e pericolosa!"]],
				['9'] = [["Isaev, sei un uomo intelligente: dammi la valigetta e vai per la tua strada."]],
				['8'] = [["Inizieremo subito a preparare la cessazione dello sviluppo, ma ci sarà ancora qualche incarico per te. Torna tra qualche ora."]],
				['7'] = [["Se non controlliamo completamente il micelio, non possiamo usarlo nemmeno parzialmente. Possiamo lavorare con un frammento di questo organismo, ma prima o poi un'altra parte di esso comincerà a mutare.<new_line>E nascerà un Emissario come questo. O un Ibridatore. E non potremo esercitare più alcun controllo, nemmeno sulla nostra parte di micelio."]],
				['6'] = [["Sì. La scatola nera ha registrato il procedimento di formazione dell'oggetto che hai trovato. A quanto pare, l'intero micelio è un unico organismo. Possiamo paragonarlo a una rete telefonica.<new_line>L'assenza di un ospite fisso non lo ostacola in alcun modo. Ma per noi questo è un problema critico!"]],
				['5'] = [["Cosa?! Stava parlando?! Non può essere, è un'assurdità... Stai dicendo che ha parlato di Gerin?<new_line>Buon Dio, con cosa abbiamo a che fare? Interromperemo immediatamente lo sviluppo del prodotto: non possiamo sviluppare ulteriormente questa tecnologia!"]],
				['4'] = [["Non ci resta che scoprire cosa nasconde questa città della scienza. Per favore, recupera la scatola nera dalla stazione biologica."<new_line>(Ti porge un pezzo di carta con il codice)]],
				['3'] = [["Sfortunatamente, i documenti non parlano di alcuno sviluppo. Si tratta di un altro laboratorio di elaborazione dati.<new_line>Ma ecco la cosa più sorprendente: riceviamo segnali radio da questa stazione biologica! Non siamo ancora stati in grado di decifrarli, ma potrebbe esserci ancora qualcuno lì."]],
				['2'] = [["Siamo già in trattative con le principali fazioni. Abbiamo intenzione di condividere la tecnologia con loro, se ci aiuteranno a sviluppare un sistema di sicurezza<new_line>Ma se non riusciamo a risolvere il problema con la sicurezza, dovremo chiudere l'intero programma e sbarazzarci della tecnologia. Immagino tu capisca quanto sia seria la situazione. Non possiamo correre alcun rischio."]],
				['1'] = [["Stiamo lavorando attivamente sulla tecnologia di rinverdimento. E grazie allo studio del lavoro di Gerin, abbiamo fatto passi da gigante!<new_line>Dobbiamo ancora sviluppare un sistema di protezione contro l'attivazione non autorizzata. In effetti, speriamo di ottenere risposte su come risolvere questo problema."]],
				['11'] = [["Come hanno dimostrato gli scienziati britannici, i soldi non puzzano, dottore. Non fare lo spilorcio, non voglio ucciderti."]],
				['12'] = [["Va bene. Ecco, prendi e lasciaci andare."]]
			}
		},
		['emba_navigator'] = {
			['choice'] = {
				['9'] = [["Affare fatto. Incontriamoci al laboratorio."]],
				['17'] = [["Un'altra domanda: perché abbiamo bisogno di pezzi di micelio?"]],
				['7'] = [["Ma non c'è una rete."]],
				['10'] = [["Non ancora. Abbiamo abbandonato il vecchio piano?"]],
				['11'] = [["Come procediamo?"]],
				['12'] = [["Il dottore stava parlando del colonnello Kurganov..."]],
				['16'] = [["Cos'hanno che non va?"]],
				['15'] = [["Che tipo di laboratorio è questo?"]],
				['14'] = [["Bene, sono pronto a procedere."]],
				['13'] = [["Il suo codice di accesso è stato usato per entrare nel laboratorio, di recente."]],
				['6'] = [["Sono pronto."]],
				['5'] = [["Cosa ne pensi del progetto E.M.B.A.?"]],
				['4'] = [["Perché dovrebbe uscire?"]],
				['3'] = [["Dobbiamo entrare lì e ucciderlo?"]],
				['2'] = [["Dove si trova esattamente lo Spaccaossa?"]],
				['18'] = [["Ho capito."]],
				['8'] = [["Anch'io. Qual è il piano?"]]
			},
			['episode'] = {
				['19'] = [["Eccoti qui, amico mio! Spero che il tuo cervello non sia andato in pappa mentre risolvevi i problemi scientifici del mondo con il dottore."]],
				['9'] = [["Ottimo lavoro! Porta la scatola nera al Biologo. Ma per la ricerca in laboratorio, probabilmente avrai bisogno di questi... sporafiori e genofrutti. Li puoi trovare in vari edifici della città della scienza.<new_line>Beh, te l'avevo detto. La cosa importante, se vuoi combattere all'interno della città della scienza, è che ricordi di indossare una speciale tuta AIDA. E fai scorta di diserbante: lo puoi ottenere dagli scienziati del reparto Consumabili."]],
				['8'] = [["Veniamo al punto. In questo momento ci interessa la scatola nera. Dovrebbe trovarsi al centro della città della scienza, presso la stazione biologica, sotto una grande cupola. Puoi trovarla solo se hai il livello massimo di accesso.<new_line>I codici di accesso sono contenuti nei documenti segreti del progetto E.M.B.A. Il codice di questo laboratorio era nei documenti che hai trovato nel bunker, ricordi? L'ho decriptato e l'ho scritto per te.<new_line>Ah, e non dimenticare di indossare il kit AIDA nelle città della scienza. Le spore volano dappertutto e non avrai molte possibilità in combattimento senza di esso. Allora, pioniere, sei pronto a partire?"<new_line>(Ti porge un pezzo di carta con il codice)]],
				['7'] = [["Non è estremamente pericoloso, è più che altro tutto ricoperto di vegetazione. Avrai bisogno di un diserbante per poter entrare e prendere i genofrutti.<new_line>Puoi ottenerlo scambiandolo per dei dischetti presso gli archivisti, nel loro reparto Consumabili."]],
				['23'] = [["Oh, cielo! Posso dirti con certezza una cosa: apprezza l'amicizia e la dedizione, quindi deve essersi fatto degli amici anche nel suo nuovo posto di lavoro.<new_line>Kurganov stesso è un guerriero molto esperto, quindi potrebbe benissimo essere sopravvissuto e aver continuato ad aiutare gli altri nel progetto E.M.B.A."]],
				['24'] = [["Beh, allora cos'è tutto questo clamore? Muoviamoci. Incontriamoci alla stazione biologica del centro."]],
				['25'] = [["Bene. Ci sono altre domande?"]],
				['26'] = [["Beh, allora cos'è tutto questo clamore? Muoviamoci."]],
				['20'] = [["No, non credo che questa volta funzionerà. Questa stazione biologica non ha una torretta. Ci sarà sicuramente qualche altra trappola."]],
				['10'] = [["Oh, sono quelle che si estraggono dai biomutanti? Beh, puoi estrarre la biomassa dal micelio. Ne avrai bisogno per creare ogni sorta di cose utili nel laboratorio Progresso.<new_line>Estrarre la biomassa dai pezzi di micelio si può fare alla vecchia maniera, con un coltello. Ma è più efficace con questo aggeggio scientifico, l'estrattore, perché otterrai più biomassa.<new_line>L'estrattore, così come i kit AIDA e il diserbante, possono essere scambiati con i dischetti dagli archivisti, quindi assicurati di passare dal loro reparto Consumabili."]],
				['11'] = [["Ciao! Mentre tu eri lì dentro ad ascoltare la lezione del dottore, io ero in perlustrazione, non a bighellonare. Sono entrato nel sistema di controllo delle torrette, così posso darti supporto con la potenza di fuoco."]],
				['12'] = [["A giudicare dai miei dati, si è stabilito al centro della città della scienza. Accanto alla cupola della stazione biologica c'è un enorme serbatoio pieno di biomassa. Lo Spaccaossa si è insediato sotto la stazione biologica e sta sbafando tutte le scorte."]],
				['28'] = [["A proposito, non è sempre facile entrare nelle città della scienza a causa dei problemi con le spore. Ma ci sono molti biomutanti anche nelle normali terre desolate. Vai in uno di questi punti e raccogli qualche pezzo di micelio.<new_line>Sono sicuro che tu non veda l'ora di ottenere la biomassa e qualche bella arma creata dal Biologo. Ho segnato la posizione sulla mappa: vai a combattere un po', soldato."]],
				['22'] = [["Generale Colonnello, per favore prendi nota. Ho prestato servizio nella divisione da lui comandata.<new_line>Era un tipo intelligente e, soprattutto, molto leale con i suoi compagni. Poi, ricordo che fu promosso e qualche anno dopo vidi sui giornali che faceva parte del progetto E.M.B.A. per l'esercito."]],
				['21'] = [["Cominciamo lo stesso, alla vecchia maniera. Pompa la biomassa dal serbatoio centrale. Ha funzionato prima per liberare la strada verso il basso, quindi chissà, magari funzionerà anche adesso.<new_line>Nel mentre io darò un'altra occhiata al piano superiore del laboratorio. Cercherò di collegarmi al loro sistema di controllo elettronico per vedere cosa ci aspetta al piano inferiore."]],
				['16'] = [["Questo è il problema. L'intera rete radio è fuori uso dopo gli attacchi nucleari. Al Vettore stiamo ancora ricostruendo la rete nelle terre desolate. Quindi chi sia riuscito a creare un segnale del genere, ma soprattutto il come, è un mistero."]],
				['15'] = [["Credo che quello che è successo in tutte le stazioni biologiche non sia stato casuale. Come segnalatore esperto, posso dire che un'esplosione di attività sincrona è possibile solo in presenza di un potente segnale radio."]],
				['14'] = [["Non è così semplice. Non sono riuscito a entrare nella parte sotterranea della stazione biologica. E sembra che sia lo Spaccaossa che la scatola nera si trovino lì. Dobbiamo portare lo Spaccaossa all'ultimo piano del laboratorio, proprio davanti alle torrette, in modo che la strada per la scatola nera sia libera."]],
				['13'] = [["Ho pensato a tutto: userò la mia testa per qualcosa di più utile che mangiare fiocchi d'avena. Alla periferia della città, c'è una piccola cisterna con delle pompe. Raggiungila e mettile in funzione.<new_line>Pomperanno la biomassa dalla cisterna centrale della stazione biologica in questa piccola cisterna. Lo Spaccaossa non avrà nulla da mangiare e striscerà al piano di sopra, fino a noi.<new_line>Non dimenticare la nostra tattica: attira lo Spaccaossa sulla linea di tiro delle torrette!"]],
				['6'] = [["Allora cominciamo. Nelle città della scienza ci sono molte cose insolite e utili. Oltre al laboratorio biologico al centro, ci sono altri edifici.<new_line>Quelli ricoperti di fiori rossi sono solitamente abitati da biomutanti. Ma anche noi abbiamo bisogno di questi fiori rossi, gli "sporafiori", per la ricerca nel laboratorio Progresso.<new_line>Ti serviranno anche questi frutti gialli, che gli scienziati chiamano "genofrutti", per migliorare il laboratorio. Li troverai in altri edifici: ai biomutanti non piace aggirarsi nelle loro vicinanze. Ma c'è un altro problema."]],
				['5'] = [["Salve! Sei pronto a partire alla ricerca della scatola nera? Ti farò un briefing."]],
				['4'] = [["Ottimo lavoro, soldato. Di' al dottor Isaev che ce l'abbiamo fatta. Poi torna da me, organizzeremo un'ispezione off-site della città della scienza. Ora mettiti al lavoro, ti darò i dettagli più tardi."]],
				['3'] = [["Probabilmente ha a che fare con quel misterioso progetto che il compagno Isaev sta cercando. Ho fatto una piccola ricognizione... a malapena sono tornato vivo.<new_line>A quanto ho capito, un archivio di documenti è rimasto intatto. Prendili, sono sicuro che ti torneranno utili. E, naturalmente, uccidi tutti i mutanti. A quel punto gli abitanti dell'insediamento dovrebbero accoglierci come eroi.<new_line>L'ingresso del laboratorio è ben mimetizzato, ma te lo segnerò sulla mappa. Bene, ora è tutto nelle tue mani!"]],
				['2'] = [["Gli abitanti locali non amano i luminari della scienza, dobbiamo far cambiare loro idea.<new_line>Onestamente, è un miracolo che questo insediamento sia sopravvissuto: non solo c'è una città della scienza a un paio di centinaia di chilometri di distanza, ma ho anche trovato un laboratorio da campo abbandonato proprio accanto ad esso.<new_line>I biomutanti strisciano fuori da lì come formiche da un formicaio."]],
				['1'] = [["Sono il Navigatore della task force Vettore, stiamo assistendo la spedizione Progresso nella sua missione. Sono lieto che ti sia unito a noi."]],
				['18'] = [["Qui c'è solo una torretta, ma ha un asso nella manica: due cannoni. Per avere vantaggio tattico, che ne dici di dirmi come sparare?<new_line>Ma prima, pompa la biomassa fuori dal serbatoio centrale. Come l'ultima volta."]],
				['17'] = [["Eccoti qui! Mi mancavano le nostre gite in campeggio."]]
			}
		},
		['halloween_boss_2022_5_2'] = {
			['episode'] = {
				['9'] = [[Dopo essermi occupato del mostro, partii alla ricerca dell'uomo che era fuggito e lo trovai nelle vicinanze. A quanto pare, mentre stava scappando era caduto in un burrone. Il suo collo era spezzato.]],
				['8'] = [[Il gobbo corse verso il grande elettrodo, e numerose altre creature si unirono al mostro. Erano chiaramente altri esperimenti dal laboratorio di Victor Frankenstein.]],
				['7'] = [[Il gobbo rispose:<new_line>"È un esssemplare davvero affassscinante. Lassscia che ti mossstri qualcosssa, amico mio. Guarda quell'elettrodo laggiù..."]],
				['6'] = [[Vidi un gobbo basso e un gigantesco mostro umanoide. Si voltarono verso di me, e sorprendentemente il mostro lasciò cadere un uomo ferito che trasportava. L'uomo se la diede subito a gambe, e sul volto del mostro apparve un sorriso minaccioso:<new_line>"Iiiigor! Questo non ha niente a che vedere con quell'altro. Voglio il SUO fegato!"]],
				['5'] = [[Gli ero quasi vicino. A quel punto, sentii di nuovo la zeppola:<new_line>"In giro? Dove pensssi di andare? Sei un mossstro! E quello è un fegato!"]],
				['4'] = [[La zeppola venne sovrastata da una voce tonante e ruggente:<new_line>"Igor, è il fegato di un viscido ubriacone. Non lo voglio. Mi vergognerei a farmi vedere in giro con un fegato del genere!"]],
				['3'] = [[Sentendo qualcuno parlare con la zeppola, decisi di dirigermi verso i cespugli per non spaventare nessuno.<new_line><new_line>"È come ssse non ti importassse! È un fegato, ssstupido!"]],
				['2'] = [[Ho trovato un cadavere all'interno. Sembrava non avesse un graffio, ma poi il dosimetro ha iniziato a impazzire. Probabilmente è stato murato qui dentro ed è morto di fame. O magari a causa delle radiazioni. È meglio che me ne vada, se voglio evitare di fare la stessa fine.]],
				['1'] = [[Qualcuno morirà in questo edificio. Qui però non c'è nessuno... anche se la porta di una delle stanze è bloccata. Forse sono qui dentro?]],
				['10'] = [["Beh, non si sfugge al destino. Specialmente se è scritto nel Libro dei morti. Passa a trovarmi non appena sei pronto. Ti attende il tuo ultimo contratto."]]
			},
			['choice'] = {
				['6'] = [["Che cosa?"]],
				['5'] = [[Sferra un attacco dai cespugli]],
				['4'] = [[Avvicinati di più...]],
				['3'] = [[Avvicinati strisciando]],
				['2'] = [[Libera il passaggio con una pala]],
				['1'] = [[Libera il passaggio a mani nude]],
				['7'] = [["Fermo!"]]
			}
		},
		['halloween_boss_2022_6'] = {
			['episode'] = {
				['5'] = [[Sentii un frastuono e un ruggito provenire da una piccola baracca di legno che non avevo mai notato prima. La bestia della palude!<new_line>Ma poi il Mietitore apparve accanto alla baracca e chiuse la porta con la sua falce.<new_line>"Bene, mortale, questa è l'ultima volta che ti aiuto! Corri, l'asino sta scappando! Io resto qui a trattenere la porta."]],
				['4'] = [[L'asino mi sentì saltare e riuscì a schivare di lato.<new_line>Scappò di nuovo ragliando aiuto ad alta voce.]],
				['3'] = [[Riuscì ad afferrare la granata con i denti e a rilanciarla verso di me. Subito dopo, notai che qualcosa era caduto dalla sua bocca.<new_line>L'asino scappò ragliando aiuto ad alta voce.]],
				['2'] = [[Lanciai una corda intorno al suo collo, ma l'asino riuscì a morderla! Subito dopo, notai che qualcosa era caduto dalla sua bocca.<new_line>L'asino scappò ragliando aiuto ad alta voce.]],
				['1'] = [[Riuscii a raggiungere una capanna nella palude ed è lì che trovai l'asino! Non faceva che muoversi con foga tra i cespugli e borbottare tra sé e sé:<new_line>"Fiori blu, spine rosse..."<new_line>Mi avvicinai abbastanza da fare la mia mossa.]]
			},
			['choice'] = {
				['2'] = [[Lancia una granata verso l'asino]],
				['1'] = [[Lancia una corda verso l'asino]],
				['4'] = [[Segui l'asino]],
				['3'] = [[Salta e afferra l'asino]]
			}
		},
		['emba_craft_easy2'] = {
			['choice'] = {
				['2'] = [["Buona fortuna!"]]
			},
			['episode'] = {
				['2'] = [["Bisogna tenere gli occhi aperti con questi nuovi mutanti. Stiamo preparando all'attacco ogni persona competente dell’accampamento, ma non abbiamo abbastanza armi per tutti. Avresti qualcosa che i ragazzi saprebbero usare?"]],
				['3'] = [["Grazie mille! Diamo una lezione a quelle bestie!"]]
