		['comrade_in_trouble'] = {
			['choice'] = {
				['10'] = [[Perlustra la casa]],
				['15'] = [[Riporta il corpo all'insediamento]],
				['14'] = [[Presta il primo soccorso]],
				['13'] = [[Sposta le macerie dall'ingresso]],
				['16'] = [[Prendi la cassa]],
				['9'] = [[Condividi la benzina]],
				['8'] = [[Combatti!]],
				['7'] = [[Cerca aiuto]],
				['6'] = [[Dai il medicinale per le radiazioni]],
				['5'] = [[Allontanati di più dal proiettile]],
				['4'] = [[Cerca l'esploratore nei paraggi]],
				['3'] = [[Esamina l'oggetto]],
				['2'] = [[Getta la legna nel fuoco]],
				['1'] = [[Riporta le notizie all'insediamento]],
				['11'] = [[Prendi di mira i banditi e grida]],
